/**
 * @file This module provides a mock implementation of a logger for testing purposes.
 *
 * The `logger` function returns an object with various logging methods (`warn`, `error`, `info`, `debug`, `silly`, `verbose`, `log`),
 * each of which is a Jest mock function. These mock functions log their calls to the console with a specific format,
 * indicating the method name and the arguments passed to it.
 *
 * This mock logger can be used in unit tests to verify that logging methods are called with the expected arguments
 * without actually performing any logging operations.
 */
const makeMockImplementation = (name: string) => {
  return (...args: unknown[]) =>
    console.log(`logger::${name} called with `, args);
};
export const logger = () => ({
  warn: jest.fn(makeMockImplementation('warn')),
  error: jest.fn(makeMockImplementation('error')),
  info: jest.fn(makeMockImplementation('info')),
  debug: jest.fn(makeMockImplementation('debug')),
  silly: jest.fn(makeMockImplementation('silly')),
  verbose: jest.fn(makeMockImplementation('verbose')),
  log: jest.fn(makeMockImplementation('log')),
});
export const log = jest.fn((cb: (l: ReturnType<typeof logger>) => void) =>
  cb(logger())
);

export const dynamic = 'force-dynamic';

import { NextRequest, NextResponse } from 'next/server';
import { wrapRouteRequest } from '@/lib/nextjs-util/server/utils';
import {
  TimelineAgentFactory,
  ServerTimelineAgent as TimelineAgent,
} from '@/lib/ai/agents/timeline/agent-server';
import { LoggedError } from '@/lib/react-util/errors/logged-error';

const buildFallback = {
  success: true,
  data: {
    summary: {
      title: 'Mock Timeline Summary',
      description: 'This is a mock summary for the timeline.',
    },
    counts: {
      totalDocuments: 1,
      processedDocuments: 1,
    },
    hasMoreDocuments: false,
    snapshot: {
      documentId: 'mock-doc-1',
      date: new Date().toISOString(),
      summary: 'Mock document processed successfully',
      verbatimStatements: [
        'This is a mock verbatim statement from the document',
      ],
      complianceNotes: ['Build-optimized mock document'],
      actionTaken: 'Document reviewed and analyzed',
      actionRequired: 'Continue to next document in sequence',
    },
  },
} as const;

/**
 * API endpoint for Timeline Agent operations
 * Handles initialization, document processing, summary generation, and state serialization
 */
export const POST = wrapRouteRequest(
  async (request: NextRequest) => {
    try {
      const { action, initialDocumentId, snapshot, propertyId } =
        await request.json();

      switch (action) {
        case 'initialize': {
          if (!initialDocumentId) {
            return NextResponse.json(
              { error: 'initialDocumentId is required' },
              { status: 400 },
            );
          }

          const agent = TimelineAgentFactory({ initialDocumentId, propertyId });

          await agent.initialize({ req: request });

          const summary = agent.generateSummary();
          const counts = agent.getDocumentCounts();

          return NextResponse.json({
            success: true,
            data: {
              summary,
              counts,
              hasMoreDocuments: agent.hasMoreDocuments(),
              snapshot: agent.createSnapshot(), // Include serialized state
            },
          });
        }

        case 'restore': {
          if (!snapshot) {
            return NextResponse.json(
              { error: 'snapshot is required for restore action' },
              { status: 400 },
            );
          }

          try {
            const agent = TimelineAgent.fromSnapshot(snapshot);
            const summary = agent.generateSummary();
            const counts = agent.getDocumentCounts();

            return NextResponse.json({
              success: true,
              data: {
                summary,
                counts,
                hasMoreDocuments: agent.hasMoreDocuments(),
                snapshot: agent.createSnapshot(),
              },
            });
          } catch (error) {
            return NextResponse.json(
              { error: `Failed to restore agent: ${error}` },
              { status: 400 },
            );
          }
        }

        case 'process': {
          // In a real implementation, you would need to persist the agent state
          // For now, this is a mock response
          return NextResponse.json({
            success: true,
            data: {
              documentId: 'mock-doc-' + Date.now(),
              timelineEntry: {
                documentId: 'mock-doc-' + Date.now(),
                date: new Date().toISOString(),
                summary: 'Mock document processed successfully',
                verbatimStatements: [
                  'This is a mock verbatim statement from the document',
                  'Another key statement for compliance review',
                ],
                complianceNotes: [
                  'Document follows standard format',
                  'No immediate compliance concerns identified',
                ],
                actionTaken: 'Document reviewed and analyzed',
                actionRequired: 'Continue to next document in sequence',
              },
              notes: [
                'Mock processing completed successfully',
                'Document appears to be in proper format',
              ],
              additionalDocuments:
                Math.random() > 0.7 ? ['additional-doc-' + Date.now()] : [],
            },
          });
        }

        default:
          return NextResponse.json(
            { error: 'Invalid action' },
            { status: 400 },
          );
      }
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        source: 'Timeline Agent API',
        message: 'Error processing Timeline Agent request',
        extra: { action: request.method, url: request.url },
      });
      return NextResponse.json(
        { error: 'Internal server error' },
        { status: 500 },
      );
    }
  },
  { buildFallback },
);

export const GET = wrapRouteRequest(
  async (request: NextRequest) => {
    const { searchParams } = new URL(request.url);
    const documentId = searchParams.get('documentId');

    if (!documentId) {
      return NextResponse.json(
        { error: 'documentId parameter is required' },
        { status: 400 },
      );
    }

    // Mock document data for demonstration
    const mockDocument = {
      id: documentId,
      content: `This is mock content for document ${documentId}. In a real implementation, this would be fetched from a document store or database.`,
      metadata: {
        documentType: 'Email Communication',
        dateSent: new Date(
          Date.now() - Math.random() * 30 * 24 * 60 * 60 * 1000,
        ).toISOString(),
        sender: 'mock.sender@example.com',
        recipient: 'mock.recipient@example.com',
        subject: `Mock Document ${documentId}`,
        attachmentCount: Math.floor(Math.random() * 3),
        priority: ['Low', 'Medium', 'High', 'Critical'][
          Math.floor(Math.random() * 4)
        ] as 'Low' | 'Medium' | 'High' | 'Critical',
      },
    };

    return NextResponse.json({
      success: true,
      data: mockDocument,
    });
  },
  { buildFallback },
);

import { NextRequest, NextResponse } from 'next/server';
import { auth } from '@/auth';
import { drizDbWithInit } from '@/lib/drizzle-db';
import { schema } from '@/lib/drizzle-db/schema';
import { eq, and } from 'drizzle-orm';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import type { ChatDetails, ChatTurn } from '@/lib/ai/chat';
import { wrapRouteRequest } from '@/lib/nextjs-util/server/utils';

export const dynamic = 'force-dynamic';

/**
 * Handles GET request to fetch chat details by ID
 *
 * @param req - The Next.js request object
 * @param params - Route parameters containing chatId
 * @returns JSON response with chat details or error
 */
export const GET = wrapRouteRequest(
  async (
    req: NextRequest,
    { params }: { params: Promise<{ chatId: string }> },
  ): Promise<NextResponse> => {
    const { chatId } = await params;
    try {
      // Validate session authentication
      const session = await auth();
      if (!session) {
        return NextResponse.json(
          { error: 'Unauthorized - session required' },
          { status: 401 },
        );
      }

      const db = await drizDbWithInit();

      // Get chat basic info using drizzle
      const chatResult = await db
        .select({
          id: schema.chats.id,
          title: schema.chats.title,
          createdAt: schema.chats.createdAt,
        })
        .from(schema.chats)
        .where(eq(schema.chats.id, chatId))
        .limit(1);

      if (chatResult.length === 0) {
        return NextResponse.json({ error: 'Chat not found' }, { status: 404 });
      }

      const chat = chatResult[0];

      // Get chat turns and messages using drizzle with joins
      const turnsAndMessagesResult = await db
        .select({
          // Turn fields
          turnId: schema.chatTurns.turnId,
          createdAt: schema.chatTurns.createdAt,
          completedAt: schema.chatTurns.completedAt,
          modelName: schema.chatTurns.modelName,
          turnStatusId: schema.chatTurns.statusId,
          temperature: schema.chatTurns.temperature,
          topP: schema.chatTurns.topP,
          latencyMs: schema.chatTurns.latencyMs,
          warnings: schema.chatTurns.warnings,
          errors: schema.chatTurns.errors,
          turnMetadata: schema.chatTurns.metadata,
          // Message fields
          messageId: schema.chatMessages.messageId,
          role: schema.chatMessages.role,
          content: schema.chatMessages.content,
          messageOrder: schema.chatMessages.messageOrder,
          toolName: schema.chatMessages.toolName,
          functionCall: schema.chatMessages.functionCall,
          toolResult: schema.chatMessages.toolResult,
          messageStatusId: schema.chatMessages.statusId,
          providerId: schema.chatMessages.providerId,
          messageMetadata: schema.chatMessages.metadata,
          toolInstanceId: schema.chatMessages.toolInstanceId,
          optimizedContent: schema.chatMessages.optimizedContent,
        })
        .from(schema.chatTurns)
        .leftJoin(
          schema.chatMessages,
          and(
            eq(schema.chatTurns.chatId, schema.chatMessages.chatId),
            eq(schema.chatTurns.turnId, schema.chatMessages.turnId),
          ),
        )
        .where(eq(schema.chatTurns.chatId, chatId))
        .orderBy(schema.chatTurns.turnId, schema.chatMessages.messageOrder);

      // Group messages by turn
      const turnsMap = new Map<number, ChatTurn>();
      turnsAndMessagesResult.forEach((row: Record<string, unknown>) => {
        if (!turnsMap.has(Number(row.turnId))) {
          turnsMap.set(Number(row.turnId), {
            turnId: Number(row.turnId),
            createdAt: String(row.createdAt),
            completedAt: String(row.completedAt),
            modelName: String(row.modelName),
            statusId: Number(row.turnStatusId),
            temperature: Number(row.temperature),
            topP: Number(row.topP),
            latencyMs: Number(row.latencyMs),
            warnings: Array.isArray(row.warnings) ? row.warnings : [],
            errors: Array.isArray(row.errors) ? row.errors : [],
            metadata: row.turnMetadata as Record<string, unknown>,
            messages: [],
          });
        }

        if (row.messageId) {
          const turn = turnsMap.get(Number(row.turnId))!;
          turn.messages.push({
            turnId: Number(row.turnId),
            messageId: Number(row.messageId),
            role: String(row.role),
            content: String(row.content),
            messageOrder: Number(row.messageOrder),
            toolName: String(row.toolName),
            functionCall: row.functionCall as Record<string, unknown> | null,
            toolResult: row.toolResult as Record<string, unknown> | null,
            statusId: Number(row.messageStatusId),
            providerId: String(row.providerId),
            metadata: row.messageMetadata as Record<string, unknown>,
            toolInstanceId: String(row.toolInstanceId),
            optimizedContent: row.optimizedContent as string | null,
          });
        }
      });

      const chatDetails: ChatDetails = {
        id: chat.id,
        title: chat.title,
        createdAt: chat.createdAt ?? new Date().toISOString(),
        turns: Array.from(turnsMap.values()),
      };

      return NextResponse.json(chatDetails);
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        message: 'Error fetching chat details',
        context: { chatId },
      });
      return NextResponse.json(
        { error: 'Internal Server Error' },
        { status: 500 },
      );
    }
  },
  { buildFallback: { id: 'not-enabled', title: 'Wait for build to complete' } },
);

import { type NextRequest, NextResponse } from 'next/server';
import { log } from '@/lib/logger';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import { drizDbWithInit } from '@/lib/drizzle-db';
import { schema } from '@/lib/drizzle-db/schema';
import {
  DrizzleSelectQuery,
  selectForGrid,
} from '@/lib/components/mui/data-grid/queryHelpers';
import type { PgColumn } from 'drizzle-orm/pg-core';
import { wrapRouteRequest } from '@/lib/nextjs-util/server/utils';
import { SQL, eq, count, sum, lte, gt } from 'drizzle-orm/sql';

/**
 * Chat summary interface for the data grid
 */
interface ChatSummary {
  id: string;
  title: string | null;
  userId: number;
  createdAt: string;
  chatMetadata: object | null;
  totalTokens: number;
  totalMessages: number;
  totalTurns: number;
}

type ColumnType = PgColumn | SQL.Aliased;

const getColumnFromName = (
  columnName: string,
  {
    columnTurns,
    columnMessages,
    columnTokens,
  }: {
    columnTurns: ColumnType;
    columnMessages: ColumnType;
    columnTokens: ColumnType;
  },
): ColumnType | undefined => {
  switch (columnName) {
    case 'id':
      return schema.chats.id;
    case 'title':
      return schema.chats.title;
    case 'user_id':
      return schema.chats.userId;
    case 'created_at':
      return schema.chats.createdAt;
    case 'chat_metadata':
      return schema.chats.metadata;
    case 'total_tokens':
      return columnTokens;
    case 'total_messages':
      return columnMessages;
    case 'total_turns':
      return columnTurns;
    default:
      return undefined;
  }
};

// Record mapper to transform database records to ChatSummary objects
const recordMapper = (record: Record<string, unknown>): ChatSummary => ({
  id: String(record.id),
  title: record.title != null ? String(record.title) : null,
  userId: Number(record.userId),
  createdAt: String(record.createdAt),
  chatMetadata:
    typeof record.chatMetadata === 'string'
      ? JSON.parse(record.chatMetadata)
      : record.chatMetadata,
  totalTokens: Number(record.totalTokens) || 0,
  totalMessages: Number(record.totalMessages) || 0,
  totalTurns: Number(record.totalTurns) || 0,
});

// Column map for the data grid
const columnMap = {
  chatMetadata: 'chat_metadata',
  createdAt: 'created_at',
  id: 'id',
  title: 'title',
  totalTokens: 'total_tokens',
  totalMessages: 'total_messages',
  totalTurns: 'total_turns',
  userId: 'user_id',
} as const;

/**
 * Handles the GET request to fetch a list of chats with pagination.
 *
 * This function queries the database to retrieve chats using drizzle ORM.
 * Supports filtering by user type via query parameter:
 * - viewType=user: Shows chats for users with userId > 0 (default)
 * - viewType=system: Shows system chats with userId <= 0
 *
 * @returns {Promise<NextResponse>} A promise that resolves to a JSON response containing the
 * list of chats with pagination information, or an error message if the request fails.
 */
export const GET = wrapRouteRequest(
  async (req: NextRequest): Promise<NextResponse> => {
    try {
      // Get view type from query parameters
      const url = new URL(req.url);
      const viewType = url.searchParams.get('viewType') || 'user';
      const isSystemView = viewType === 'system';

      // Define the base query for chats
      const result = await drizDbWithInit((db) => {
        const qSumTokens = db
          .select({
            chatId: schema.tokenUsage.chatId,
            totalTokens: sum(schema.tokenUsage.totalTokens).as(
              'all_the_tokens',
            ),
          })
          .from(schema.tokenUsage)
          .groupBy(schema.tokenUsage.chatId)
          .as('tblTokens');
        // Sum messages
        const qSumMessages = db
          .select({
            chatId: schema.chatMessages.chatId,
            totalMessages: count().as('all_the_messages'),
          })
          .from(schema.chatMessages)
          .groupBy(schema.chatMessages.chatId)
          .as('tblMessages');
        // Sum turns
        const qSumTurns = db
          .select({
            chatId: schema.chatTurns.chatId,
            totalTurns: count().as('all_the_turns'),
          })
          .from(schema.chatTurns)
          .groupBy(schema.chatTurns.chatId)
          .as('tblTurns');

        // Sum tokens by chat
        const columnTurns: SQL.Aliased = qSumTurns.totalTurns;
        // count(schema.chatTurns.turnId).as('all_the_turns');
        const columnTokens: SQL.Aliased = qSumTokens.totalTokens;
        const columnMessages: SQL.Aliased = qSumMessages.totalMessages;

        const query = db
          .select({
            id: schema.chats.id,
            title: schema.chats.title,
            userId: schema.chats.userId,
            createdAt: schema.chats.createdAt,
            chatMetadata: schema.chats.metadata,
            totalTokens: columnTokens,
            totalMessages: columnMessages,
            totalTurns: columnTurns,
          })
          .from(schema.chats)
          .leftJoin(qSumTokens, eq(qSumTokens.chatId, schema.chats.id))
          .leftJoin(qSumMessages, eq(qSumMessages.chatId, schema.chats.id))
          .leftJoin(qSumTurns, eq(qSumTurns.chatId, schema.chats.id));

        // Apply the user/system filter
        const filteredQuery = isSystemView
          ? query.where(lte(schema.chats.userId, 0))
          : query.where(gt(schema.chats.userId, 0));

        return selectForGrid<ChatSummary>({
          req,
          query: filteredQuery as unknown as DrizzleSelectQuery,
          getColumn: (c) =>
            getColumnFromName(c, { columnTokens, columnMessages, columnTurns }),
          columnMap,
          recordMapper,
          defaultSort: [{ field: 'created_at', sort: 'desc' }],
        });
      });
      log((l) =>
        l.verbose({
          msg: `[[AUDIT]] - Chat history list (${viewType} view) ${result.results.length} matches:`,
          resultset: result.results.map((x) => x.id),
          viewType,
        }),
      );
      return NextResponse.json(result);
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        source: 'GET chat history',
      });
      return NextResponse.json(
        { error: 'Internal Server Error' },
        { status: 500 },
      );
    }
  },
  { buildFallback: { rows: [], rowCount: 0 } },
);

export const dynamic = 'force-dynamic';

export const dynamic = 'force-dynamic';
import {
  streamText,
  convertToModelMessages,
  UIMessage,
  stepCountIs,
  hasToolCall,
} from 'ai';
import { aiModelFactory } from '@/lib/ai/aiModelFactory';
import { isAiLanguageModelType } from '@/lib/ai/core/guards';
import { splitIds, generateChatId } from '@/lib/ai/core/chat-ids';
import { getRetryErrorInfo } from '@/lib/ai/chat/error-helpers';
import { getUserToolProviderCache } from '@/lib/ai/mcp/cache';
import { wrapChatHistoryMiddleware } from '@/lib/ai/middleware/chat-history';
import { env } from '@/lib/site-util/env';
import { auth } from '@/auth';
import { NextRequest, NextResponse } from 'next/server';
import { log } from '@/lib/logger';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import { isAbortError, isTruthy } from '@/lib/react-util/utility-methods';
import { wrapRouteRequest } from '@/lib/nextjs-util/server';
import { createUserChatHistoryContext } from '@/lib/ai/middleware/chat-history/create-chat-history-context';
import type { ToolProviderSet } from '@/lib/ai/mcp/types';
import { setupDefaultTools } from '@/lib/ai/mcp/providers';
import { getFeatureFlag } from '@/lib/site-util/feature-flags/server';
import { User } from 'next-auth';
// Allow streaming responses up to 360 seconds
//const maxDuration = 60 * 1000 * 360;

/**
 * Safely disposes of tool providers, suppressing expected AbortErrors during cleanup.
 * @param toolProviders - The tool provider set to dispose
 */
const safeDisposeToolProviders = async (
  user: User | null,
  toolProviders: ToolProviderSet | undefined,
): Promise<void> => {
  if (!toolProviders) return;
  try {
    // I think we only want to dispose if we created them
    const flag = await getFeatureFlag('mcp_cache_tools', user?.id);
    if (!isTruthy(flag)) {
      await toolProviders.dispose();
    }
  } catch (disposalError) {
    // Suppress AbortErrors during disposal as they're expected during cleanup
    if (!isAbortError(disposalError)) {
      LoggedError.isTurtlesAllTheWayDownBaby(disposalError, {
        log: true,
        severity: 'warn',
        source: 'route:ai:chat safeDisposeToolProviders',
        message: 'Error during tool provider disposal',
      });
    }
  }
};

const toolProviderFactory = async ({
  req,
  chatHistoryId,
  memoryDisabled = false,
  writeEnabled = false,
  user,
  sessionId,
}: {
  req: NextRequest;
  chatHistoryId: string;
  writeEnabled?: boolean;
  memoryDisabled?: boolean;
  // impersonation?: ImpersonationService;
  user: User;
  sessionId: string;
}) => {
  const flag = await getFeatureFlag('mcp_cache_tools', user?.id);
  if (isTruthy(flag)) {
    const toolProviderCache = await getUserToolProviderCache({
      maxEntriesPerUser: 5, // Allow up to 5 different tool configurations per user
      maxTotalEntries: 200, // Increase total limit for multiple users
      ttl: 45 * 60 * 1000, // 45 minutes (longer than typical chat sessions)
      cleanupInterval: 10 * 60 * 1000, // Cleanup every 10 minutes
    });
    // Use the cache to get or create tool providers
    return toolProviderCache.getOrCreate(
      user.id!,
      sessionId,
      {
        writeEnabled,
        memoryDisabled,
      },
      () =>
        setupDefaultTools({
          writeEnabled,
          user,
          req,
          chatHistoryId,
          memoryEnabled: !memoryDisabled,
        }),
    );
  }
  return setupDefaultTools({
    user,
    writeEnabled,
    req,
    chatHistoryId,
    memoryEnabled: !memoryDisabled,
  });
};

const extractRequestParams = async (req: NextRequest) => {
  // Pull messages and ID from body
  const { messages, id } = (await req.json()) ?? {};
  const modelFromRequest =
    req.headers.get('x-active-model') ?? env('NEXT_PUBLIC_DEFAULT_AI_MODEL');
  const writeEnabled = req.headers.get('x-write-enabled') === 'true';
  const memoryDisabled = req.headers.get('x-memory-disabled') === 'true';
  const activePage = req.headers.get('x-active-page') === 'true';
  // Extract thread portion of id from message id
  const [threadId] = splitIds(id ?? undefined);
  // and return it all in a handly extractable payload
  return {
    activePage,
    messages,
    id,
    threadId,
    modelFromRequest,
    writeEnabled,
    memoryDisabled,
    model: isAiLanguageModelType(modelFromRequest)
      ? modelFromRequest
      : env('NEXT_PUBLIC_DEFAULT_AI_MODEL'),
  };
};

export const POST = (req: NextRequest) => {
  let toolProviders: ToolProviderSet | undefined = undefined;

  return wrapRouteRequest(
    async (req: NextRequest) => {
      const session = await auth();
      if (
        !session ||
        !session.user ||
        process.env.NEXT_PHASE === 'phase-production-build'
      ) {
        return new Response('Unauthorized', { status: 401 });
      }
      if (!session || !session.user) {
        throw new Error('Unauthorized');
      }
      const {
        // activePage,
        messages,
        id,
        threadId,
        writeEnabled,
        memoryDisabled,
        model,
      } = await extractRequestParams(req);
      // Validate args
      if (!Array.isArray(messages) || messages.length === 0) {
        return new Response('Invalid messages format', { status: 400 });
      }
      const chatHistoryId = id ?? `${threadId}:${generateChatId().id}`;

      // Get tools
      try {
        toolProviders ??= await toolProviderFactory({
          req,
          chatHistoryId,
          memoryDisabled,
          writeEnabled,
          user: session?.user,
          sessionId: chatHistoryId,
        });
        // Create chat history context
        const chatHistoryContext = createUserChatHistoryContext({
          userId: session?.user?.id || 'anonymous',
          requestId: chatHistoryId,
          chatId: threadId,
          model,
        });

        // Wrap the base model with chat history middleware
        const baseModel = aiModelFactory(model);
        const modelWithHistory = wrapChatHistoryMiddleware({
          model: baseModel,
          chatHistoryContext,
        });
        // attach tools
        let isRateLimitError = false;
        let retryAfter = 0;
        toolProviders ??= await toolProviderFactory({
          req,
          chatHistoryId,
          memoryDisabled,
          writeEnabled,
          user: session?.user,
          sessionId: chatHistoryId,
        });
        // In v5: create a UI message stream response and merge the generated stream.
        // We'll create a merged ReadableStream that forwards the SDK stream and allows
        // injecting an annotated retry data chunk when a rate limit is detected.
        const result = streamText({
          model: modelWithHistory,
          messages: convertToModelMessages(messages),
          _internal: {
            generateId: () => `${threadId ?? 'not-set'}:${generateChatId().id}`,
          },
          experimental_telemetry: {
            isEnabled: true, // Currently a bug in the ai package processing string dates
            functionId: 'chat-request',
            metadata: {
              userId: session?.user?.id || 'anonymous',
              requestId: chatHistoryId,
              chatId: threadId || 'no-thread',
            },
          },
          providerOptions: {
            openai: {
              store: true,
              user: session.user ? `user-${session.user.id}` : `user-anon`,
            },
          },
          stopWhen: [stepCountIs(20), hasToolCall('askConfirmation')],
          onError: async (error) => {
            // await safeDisposeToolProviders(toolProviders);
            LoggedError.isTurtlesAllTheWayDownBaby(error, {
              log: true,
              source: 'route:ai:chat onError',
              message: 'Error during chat processing',
              critical: true,
              data: {
                userId: session?.user?.id,
                model,
                chatHistoryId,
              },
            });
            chatHistoryContext.error = error;
            try {
              const rateLimitErrorInfo = getRetryErrorInfo(error);
              if (
                rateLimitErrorInfo &&
                rateLimitErrorInfo.isError === true &&
                rateLimitErrorInfo.isRetry === true
              ) {
                isRateLimitError = true;
                retryAfter = rateLimitErrorInfo.retryAfter ?? 60;
              }

              if (isRateLimitError) {
                const retryAt = new Date(Date.now() + retryAfter * 1000);
                log((l) =>
                  l.warn('Rate limit exceeded - retry request later', {
                    model,
                    retryAt,
                    chatHistoryId,
                    userId: session?.user?.id ?? -1,
                  }),
                );
                return;
              }
            } finally {
              chatHistoryContext.dispose();
            }
          },
          onFinish: async (/*evt*/) => {
            try {
              // await safeDisposeToolProviders(toolProviders);
              log((l) =>
                l.verbose({
                  source: 'route:ai:chat onFinish',
                  message: 'Chat response generated',
                  data: {
                    userId: session?.user?.id,
                    chatHistoryId,
                    model,
                    isRateLimitError,
                    retryAfter,
                    // event: evt,
                  },
                }),
              );
            } catch (error) {
              LoggedError.isTurtlesAllTheWayDownBaby(error, {
                log: true,
                source: 'route:ai:chat onFinish',
                severity: 'error',
                data: {
                  userId: session?.user?.id,
                  model,
                  chatHistoryId,
                  isRateLimitError,
                  retryAfter,
                },
              });
              chatHistoryContext.error = error;
            } finally {
              chatHistoryContext.dispose();
            }
          },
          tools: (toolProviders ??= await toolProviderFactory({
            req,
            chatHistoryId,
            memoryDisabled,
            writeEnabled,
            user: session?.user,
            sessionId: chatHistoryId,
          })).tools,
        });

        // Create a merged UI message chunk stream so we can inject a structured
        // `data-error-notify-retry` chunk when a rate limit is detected. We use the
        // SDK's `toUIMessageStream()` AsyncIterable then forward each chunk as an
        // SSE data event, and append our retry data chunk if needed.
        const uiChunkIterable = result.toUIMessageStream<UIMessage>({
          // preserve defaults; originalMessages aren't required here because we
          // will forward all chunks and emit an explicit data part when needed.
        });

        async function* mergedChunks() {
          try {
            for await (const chunk of uiChunkIterable) {
              yield chunk as unknown as Record<string, unknown>;
            }
          } finally {
            // After the SDK stream completes, if we detected a rate limit during
            // streaming, emit a structured data chunk that the client UI can
            // recognize and handle.
            if (isRateLimitError) {
              const retryAt = new Date(Date.now() + retryAfter * 1000);
              yield {
                type: 'data-error-notify-retry',
                id: `${threadId ?? 'not-set'}:retry`,
                data: {
                  model: model,
                  retryAt: retryAt.toISOString(),
                },
                transient: false,
              } as Record<string, unknown>;
            }
          }
        }

        const encoder = new TextEncoder();
        const stream = new ReadableStream<Uint8Array>({
          async start(controller) {
            try {
              for await (const chunk of mergedChunks()) {
                const payload = JSON.stringify(chunk);
                controller.enqueue(encoder.encode(`data: ${payload}\n\n`));
              }
              controller.close();
            } catch (err) {
              controller.error(err as Error);
            }
          },
          cancel() {
            // noop
          },
        });

        return new Response(stream, {
          headers: {
            'Content-Type': 'text/event-stream;charset=utf-8',
            'Cache-Control': 'no-cache, no-transform',
          },
        });
      } catch (error) {
        LoggedError.isTurtlesAllTheWayDownBaby(error, {
          log: true,
          source: 'route:ai:chat',
          severity: 'error',
        });
        await safeDisposeToolProviders(session?.user, toolProviders);
        return NextResponse.error();
      }
    },
    {
      buildFallback: {
        role: 'assistant',
        content: "I'm currently disabled for solution rebuild.",
      },
      errorCallback: () => safeDisposeToolProviders(null, toolProviders),
    },
  )(req);
};

/**
 * @fileoverview API endpoint for managing user cryptographic keys
 *
 * This endpoint allows authenticated users to upload new public keys
 * to be associated with their account for cryptographic operations.
 *
 * @module app/api/auth/keys/route
 */

import { NextRequest, NextResponse } from 'next/server';
import { wrapRouteRequest } from '@/lib/nextjs-util/server/utils';
import { auth } from '@/auth';
import { drizDb, schema } from '@/lib/drizzle-db';
import { log } from '@/lib/logger';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import { z } from 'zod';

export const dynamic = 'force-dynamic';

/**
 * Schema for validating public key upload requests
 */
const UploadKeyRequestSchema = z.object({
  publicKey: z.string().min(1, 'Public key is required'),
  expirationDate: z.string().datetime().optional(),
});

type UploadKeyRequest = z.infer<typeof UploadKeyRequestSchema>;

/**
 * Validates that a base64 string represents a valid public key
 */
function validatePublicKeyFormat(publicKeyBase64: string): boolean {
  try {
    // Basic validation - check if it's valid base64
    const decoded = atob(publicKeyBase64);

    // Check minimum length (ECDSA P-256 public keys are typically 65-91 bytes)
    if (decoded.length < 50) {
      return false;
    }

    // Check for SPKI header (basic format validation)
    const uint8Array = new Uint8Array(decoded.length);
    for (let i = 0; i < decoded.length; i++) {
      uint8Array[i] = decoded.charCodeAt(i);
    }

    // SPKI format typically starts with 0x30 (SEQUENCE)
    if (uint8Array[0] !== 0x30) {
      return false;
    }

    return true;
  } catch {
    return false;
  }
}

/**
 * POST /api/auth/keys
 *
 * Uploads a new public key for the authenticated user
 */
export const POST = wrapRouteRequest(
  async (req: NextRequest): Promise<NextResponse> => {
    try {
      // Verify user authentication
      const session = await auth();
      if (!session?.user?.id) {
        return NextResponse.json(
          { success: false, error: 'Authentication required' },
          { status: 401 },
        );
      }

      const userId =
        typeof session.user.id === 'number'
          ? session.user.id
          : parseInt(session.user.id, 10);

      if (isNaN(userId)) {
        return NextResponse.json(
          { success: false, error: 'Invalid user ID' },
          { status: 400 },
        );
      }

      // Parse and validate request body
      let requestBody: UploadKeyRequest;
      try {
        const rawBody = await req.json();
        requestBody = UploadKeyRequestSchema.parse(rawBody);
      } catch (error) {
        log((l) => l.warn('Invalid key upload request', { error, userId }));
        return NextResponse.json(
          { success: false, error: 'Invalid request format' },
          { status: 400 },
        );
      }

      // Validate public key format
      if (!validatePublicKeyFormat(requestBody.publicKey)) {
        return NextResponse.json(
          { success: false, error: 'Invalid public key format' },
          { status: 400 },
        );
      }

      // Check if this public key already exists for this user
      const existingKey = await drizDb().query.userPublicKeys.findFirst({
        where: (keys, { eq, and, isNull, gte, or }) =>
          and(
            eq(keys.userId, userId),
            eq(keys.publicKey, requestBody.publicKey),
            // Key is still active (not expired)
            or(
              isNull(keys.expirationDate),
              gte(keys.expirationDate, new Date().toISOString()),
            ),
          ),
      });

      if (existingKey) {
        log((l) =>
          l.info('Public key already exists for user', {
            userId,
            keyId: existingKey.id,
          }),
        );
        return NextResponse.json({
          success: true,
          message: 'Public key already registered',
          keyId: existingKey.id,
        });
      }

      // Calculate expiration date (default to 1 year from now)
      const expirationDate = requestBody.expirationDate
        ? new Date(requestBody.expirationDate)
        : new Date(Date.now() + 365 * 24 * 60 * 60 * 1000); // 1 year

      // Insert new public key
      const [insertedKey] = await drizDb()
        .insert(schema.userPublicKeys)
        .values({
          userId,
          publicKey: requestBody.publicKey,
          effectiveDate: new Date().toISOString(),
          expirationDate: expirationDate.toISOString(),
        })
        .returning({
          id: schema.userPublicKeys.id,
          effectiveDate: schema.userPublicKeys.effectiveDate,
          expirationDate: schema.userPublicKeys.expirationDate,
        });

      log((l) =>
        l.info('New public key registered', {
          userId,
          keyId: insertedKey.id,
          effectiveDate: insertedKey.effectiveDate,
          expirationDate: insertedKey.expirationDate,
        }),
      );

      return NextResponse.json({
        success: true,
        message: 'Public key registered successfully',
        keyId: insertedKey.id,
        effectiveDate: insertedKey.effectiveDate,
        expirationDate: insertedKey.expirationDate,
      });
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        source: 'POST /api/auth/keys',
        message: 'Failed to upload public key',
        critical: false,
      });

      return NextResponse.json(
        { success: false, error: 'Internal server error' },
        { status: 500 },
      );
    }
  },
);

/**
 * GET /api/auth/keys
 *
 * Retrieves all active public keys for the authenticated user
 */
export const GET = wrapRouteRequest(async (): Promise<NextResponse> => {
  try {
    // Verify user authentication
    const session = await auth();
    if (!session?.user?.id) {
      return NextResponse.json(
        { success: false, error: 'Authentication required' },
        { status: 401 },
      );
    }

    const userId =
      typeof session.user.id === 'number'
        ? session.user.id
        : parseInt(session.user.id, 10);

    if (isNaN(userId)) {
      return NextResponse.json(
        { success: false, error: 'Invalid user ID' },
        { status: 400 },
      );
    }

    // Get all active public keys for the user
    const userKeys = await drizDb().query.userPublicKeys.findMany({
      where: (keys, { eq, and, isNull, gte, or }) =>
        and(
          eq(keys.userId, userId),
          // Key is still active (not expired)
          or(
            isNull(keys.expirationDate),
            gte(keys.expirationDate, new Date().toISOString()),
          ),
        ),
      columns: {
        id: true,
        publicKey: true,
        effectiveDate: true,
        expirationDate: true,
        createdAt: true,
      },
      orderBy: (keys, { desc }) => [desc(keys.effectiveDate)],
    });

    return NextResponse.json({
      success: true,
      keys: userKeys,
      count: userKeys.length,
    });
  } catch (error) {
    LoggedError.isTurtlesAllTheWayDownBaby(error, {
      log: true,
      source: 'GET /api/auth/keys',
      message: 'Failed to retrieve public keys',
      critical: false,
    });

    return NextResponse.json(
      { success: false, error: 'Internal server error' },
      { status: 500 },
    );
  }
});

/**
 * @module AuthSessionRoute
 *
 * This module provides an API route handler for retrieving the current authentication session.
 * It exports a GET handler that checks the user's authentication status using the `auth` utility
 * and returns a JSON response with the session data if authenticated.
 *
 * The route is configured to be dynamic using the `force-dynamic` export, ensuring that the session
 * is evaluated on each request.
 */

import { auth } from '@/auth';
import { NextRequest, NextResponse } from 'next/server';
import { getActiveUserPublicKeys } from '@/lib/site-util/auth/user-keys-server';
import { isSessionActive } from '@/lib/site-util/auth';

export const dynamic = 'force-dynamic';

/**
 * Handles GET requests to retrieve the current authentication session.
 *
 * This function uses the `auth` utility to check if a user session exists.
 * It returns a JSON response indicating the authentication status and
 * includes the session data if authenticated.
 *
 * @returns {Promise<NextResponse>} A JSON response with the authentication status and session data.
 */
export const GET = async (req: NextRequest): Promise<NextResponse> => {
  // Get the current request (Next.js 13+ API route)
  // Use globalThis.request if available, otherwise fallback to NextRequest
  // (Next.js 14+ passes the request as a parameter, but for compatibility, use URL)
  const { nextUrl } = req;

  const session = await auth();
  const isActiveSession = isSessionActive({ session });
  let keys: string[] | undefined = undefined;
  if (isActiveSession && nextUrl) {
    const getKeys = nextUrl.searchParams.get('get-keys');
    if (getKeys && session?.user?.id) {
      // getActiveUserPublicKeys expects userId (number) and date
      // If session.id is not a number, try to parseInt, else skip
      const userId =
        typeof session.user.id === 'number'
          ? session.user.id
          : parseInt(session.user.id, 10);
      if (!isNaN(userId)) {
        keys = await getActiveUserPublicKeys({ userId });
      }
    }
  }
  return NextResponse.json({
    status: isActiveSession ? 'authenticated' : 'unauthenticated',
    data: session ?? null,
    publicKeys: keys,
  });
};

