import { NextRequest, NextResponse } from 'next/server';
import { log } from '@/lib/logger';
// (normalizeNullableNumeric no longer needed directly; handled in validation module)
import { ValidationError } from '@/lib/react-util/errors/validation-error';

import { EmailService } from '@/lib/api/email/email-service';
import {
  validateCreateEmail,
  validateUpdateEmail,
} from '@/lib/api/email/email-validation';
import {
  buildFallbackGrid,
  wrapRouteRequest,
} from '@/lib/nextjs-util/server/utils';
import { drizDbWithInit, schema } from '@/lib/drizzle-db';
import {
  count_kpi,
  count_attachments,
  count_notes,
  count_responsive_actions,
  count_cta,
} from '@/lib/api/email/drizzle/query-parts';
import { eq } from 'drizzle-orm';
// count_kpi import removed; not used in this route currently
import {
  DrizzleSelectQuery,
  getEmailColumn,
  selectForGrid,
} from '@/lib/components/mui/data-grid/queryHelpers';
import { ContactSummary, EmailMessageSummary } from '@/data-models';

export const dynamic = 'force-dynamic';

/**
 * Handles the GET request to fetch a list of emails with sender and recipient information.
 *
 * This function uses the EmailService to retrieve emails with their sender's details and
 * a list of recipients. The results are returned as a JSON response with pagination support.
 *
 * @param req - The incoming request object
 * @returns {Promise<NextResponse>} A promise that resolves to a JSON response containing the
 * list of emails with sender and recipient information, or an error message if the request fails.
 *
 * @throws {Error} If there is an issue with the service operation or any other error occurs during
 * the execution of the function, an error is logged and a 500 Internal Server Error response is returned.
 */
export const GET = wrapRouteRequest(
  async (req: NextRequest) => {
    const results = await drizDbWithInit(async (db) => {
      // Correlated subquery returning a JSONB array of recipient objects for each email
      const attachments = count_attachments({ db });
      const countKpi = count_kpi({ db });
      const countNotes = count_notes({ db });
      const countRa = count_responsive_actions({ db });
      const countCta = count_cta({ db });

      const getColumn = (columnName: string) => {
        switch (columnName) {
          case 'sentOn':
            return schema.emails.sentTimestamp;
          case 'count_attachments':
            return attachments.countAttachments;
          case 'count_kpi':
            return countKpi.targetCount;
          case 'count_notes':
            return countNotes.targetCount;
          case 'count_cta':
            return countCta.targetCount;
          case 'count_responsive_actions':
            return countRa.targetCount;
          case 'sender':
            return schema.contacts.name;
          default:
            return getEmailColumn({ columnName, table: schema.emails });
        }
      };

      const bq = db
        .select({
          emailId: schema.emails.emailId,
          senderId: schema.emails.senderId,
          senderName: schema.contacts.name,
          senderEmai: schema.contacts.email,
          subject: schema.emails.subject,
          sentOn: schema.emails.sentTimestamp,
          threadId: schema.emails.threadId,
          parentEmailId: schema.emails.parentId,
          importedFromId: schema.emails.importedFromId,
          globalMessageId: schema.emails.globalMessageId,
          count_kpi: countKpi.targetCount,
          count_notes: countNotes.targetCount,
          count_cta: countCta.targetCount,
          count_responsive_actions: countRa.targetCount,
          count_attachments: attachments.countAttachments,
        })
        .from(schema.emails)
        .innerJoin(
          schema.contacts,
          eq(schema.emails.senderId, schema.contacts.contactId),
        )
        .fullJoin(attachments, eq(schema.emails.emailId, attachments.emailId))
        .fullJoin(countKpi, eq(schema.emails.emailId, countKpi.targetId))
        .fullJoin(countNotes, eq(schema.emails.emailId, countNotes.targetId))
        .fullJoin(countCta, eq(schema.emails.emailId, countCta.targetId))
        .fullJoin(countRa, eq(schema.emails.emailId, countRa.targetId));
      return await selectForGrid<EmailMessageSummary>({
        req,
        query: bq as unknown as DrizzleSelectQuery,
        getColumn,
        defaultSort: 'sentOn',
        recordMapper: (emailDomain) => ({
          emailId: String(emailDomain.emailId),
          sender: {
            contactId: Number(emailDomain.senderId),
            name: String(emailDomain.senderName),
            email: String(emailDomain.senderEmail),
          } as ContactSummary,
          subject: String(emailDomain.subject ?? ''),
          sentOn: new Date(
            emailDomain.sentOn
              ? Date.parse(String(emailDomain.sentOn))
              : Date.now(),
          ),
          threadId: emailDomain.threadId
            ? Number(emailDomain.threadId)
            : undefined,
          parentEmailId: emailDomain.parentId
            ? String(emailDomain.parentId)
            : undefined,
          importedFromId: emailDomain.importedFromId
            ? String(emailDomain.importedFromId)
            : undefined,
          globalMessageId: emailDomain.globalMessageId
            ? String(emailDomain.globalMessageId)
            : undefined,
          recipients: emailDomain.recipients as Array<ContactSummary>,
          count_attachments: Number(emailDomain.count_attachments) ?? 0,
          count_kpi: Number(emailDomain.count_kpi) ?? 0,
          count_notes: Number(emailDomain.count_notes) ?? 0,
          count_cta: Number(emailDomain.count_cta) ?? 0,
          count_responsive_actions:
            Number(emailDomain.count_responsive_actions) ?? 0,
        }),
      });
    });

    return Response.json(results);
  },
  { buildFallback: buildFallbackGrid },
);

/**
 * Handles the POST request to create a new email.
 *
 * This function uses the EmailService to create a new email with recipients and
 * automatically creates the associated document unit for content storage.
 *
 * @param {NextRequest} req - The incoming request object.
 * @returns {Promise<NextResponse>} - The response object containing the result of the email creation.
 * @throws {Error} - If there is an issue with the email creation process.
 */
export const POST = wrapRouteRequest(
  async (req: NextRequest): Promise<NextResponse> => {
    try {
      const raw = await req.json();
      const validated = validateCreateEmail(raw);
      if (!validated.success) {
        return NextResponse.json(
          { error: 'Validation failed', details: validated.error.flatten() },
          { status: 400 },
        );
      }
      const emailService = new EmailService();
      const createdEmail = await emailService.createEmail(validated.data);

      return NextResponse.json(
        {
          message: 'Email created successfully',
          email: createdEmail,
        },
        { status: 201 },
      );
    } catch (error) {
      if (ValidationError.isValidationError(error)) {
        return NextResponse.json({ error: error.message }, { status: 400 });
      }
      log((l) =>
        l.error({
          source: 'POST email',
          error,
        }),
      );
      return NextResponse.json(
        { error: 'Internal Server Error' },
        { status: 500 },
      );
    }
  },
);

/**
 * Handles the PUT request to update an existing email.
 *
 * This function uses the EmailService to update an existing email with new data
 * and optionally update recipients.
 *
 * @param {NextRequest} req - The incoming request object.
 * @returns {Promise<NextResponse>} - The response object containing the result of the email update.
 * @throws {Error} - If there is an issue with the email update process.
 */
export const PUT = wrapRouteRequest(
  async (req: NextRequest): Promise<NextResponse> => {
    try {
      const raw = await req.json();
      const validated = validateUpdateEmail(raw);
      if (!validated.success) {
        return NextResponse.json(
          { error: 'Validation failed', details: validated.error.flatten() },
          { status: 400 },
        );
      }
      const emailService = new EmailService();
      const updatedEmail = await emailService.updateEmail(validated.data);

      return NextResponse.json(
        { message: 'Email updated successfully', email: updatedEmail },
        { status: 200 },
      );
    } catch (error) {
      if (ValidationError.isValidationError(error)) {
        return NextResponse.json({ error: error.message }, { status: 400 });
      }
      log((l) => l.error({ source: 'PUT email', error }));
      return NextResponse.json(
        { error: 'Internal Server Error' },
        { status: 500 },
      );
    }
  },
);

/**
 * Handles the DELETE request to remove an email.
 *
 * This function uses the EmailService to delete an email by ID.
 *
 * @param {NextRequest} req - The incoming request object.
 * @returns {Promise<NextResponse>} - The response object containing the result of the email deletion.
 */
export const DELETE = wrapRouteRequest(
  async (req: NextRequest): Promise<NextResponse> => {
    try {
      const { emailId } = await req.json();

      if (!emailId) {
        return NextResponse.json(
          { error: 'Email ID is required' },
          { status: 400 },
        );
      }

      const emailService = new EmailService();
      const deleted = await emailService.deleteEmail(emailId);

      if (!deleted) {
        return NextResponse.json({ error: 'Email not found' }, { status: 404 });
      }

      return NextResponse.json(
        { message: 'Email deleted successfully' },
        { status: 200 },
      );
    } catch (error) {
      log((l) => l.error({ source: 'DELETE email', error }));
      return NextResponse.json(
        { error: 'Internal Server Error' },
        { status: 500 },
      );
    }
  },
);

import { wrapRouteRequest } from '@/lib/nextjs-util/server';
import { NextResponse } from 'next/server';
import {
  ExtendedMemoryClient,
  memoryClientFactory,
} from '@/lib/ai/mem0/memoryclient-factory';
import {
  determineHealthStatus,
  type HealthStatus,
} from '@/lib/ai/mem0/types/health-check';

/**
 * Health Check Route (GET /api/health)
 * -------------------------------------------------------------
 * Provides a lightweight, synchronous snapshot of core platform subsystem status.
 * Designed to be:
 *  - Fast (no outbound network calls here  delegate deeper checks elsewhere)
 *  - Deterministic (always resolves a JSON payload)
 *  - Simple schema (stable keys for external monitoring / k8s / uptime pings)
 *
 * Current Subsystems:
 *  - database: Indicates DB layer reachability (placeholder: assumed OK here)
 *  - chat: Aggregated status for chatrelated infrastructure
 *      - cache: Inprocess / external cache layer (placeholder)
 *      - queue: Background processing / queue subsystem (placeholder)
 *
 * NOTE: This endpoint intentionally does NOT perform real connectivity probes in this
 * implementation to avoid cascading failures or latency amplification. If deeper
 * diagnostics are required, consider a separate /api/health/deep or /api/diagnostics route.
 *
 * Example Successful Response:
 * {
 *   "database": { "status": "ok" },
 *   "chat": {
 *     "status": "ok",
 *     "cache": { "status": "ok" },
 *     "queue": { "status": "ok" }
 *   }
 * }
 *
 * Build / Deployment Considerations:
 *  - Wrapped with wrapRouteRequest, which provides:
 *      * Consistent logging (when enabled)
 *      * Structured error handling returning a JSON errorResponseFactory on failures
 *      * Buildphase fallback shortcircuit (returns a neutral JSON object when
 *        NEXT_PHASE === 'phase-production-build' or IS_BUILDING=1 unless explicitly overridden)
 *
 * Testing:
 *  - See: __tests__/app/api/health/route.test.ts for validation of:
 *      * Successful payload shape
 *      * Build fallback behavior
 *      * Logging invocation path
 *
 * Extending:
 *  - Introduce real checks by replacing static assignments with async probes, e.g.:
 *      const database = await dbPing();
 *      const cache = await cacheClient.ping();
 *    Ensure added calls are timeoutbounded to preserve endpoint responsiveness.
 */

/**
 * Narrow set of status codes surfaced by the health layer.
 * Keep deliberately small to simplify external automation.
 */
type HealthCheckStatusCode = 'ok' | 'warning' | 'error';

/**
 * Enumerates discrete systems (or system groups) represented in the response.
 */
type HealthCheckSystem = 'database' | 'cache' | 'queue' | 'chat' | 'memory';

/**
 * Base shape for any status entry.
 */
type HealthCheckStatusEntryBase = {
  /** Current coarse status value */
  status: HealthCheckStatusCode;
};
type HealthCheckStatusEntry<K extends string = never> =
  HealthCheckStatusEntryBase & {
    [key in K]?: HealthCheckStatusEntryBase;
  };

/**
 * Maps each system to its expected nested structure.
 * chat includes embedded subsystems (cache, queue) to express grouped health.
 * memory includes embedded subsystems (db, vectorStore, graphStore, historyStore, authService) to express grouped health.
 */
type HealthSystemResponseTypeMap = {
  database: HealthCheckStatusEntry<never>;
  chat: HealthCheckStatusEntry<'cache' | 'queue'>;
  cache: HealthCheckStatusEntry<never>;
  queue: HealthCheckStatusEntry<never>;
  memory: HealthCheckStatusEntry<
    'db' | 'vectorStore' | 'graphStore' | 'historyStore' | 'authService'
  >;
};

/**
 * Final JSON response contract. Each top-level key is optional to allow
 * progressive enhancement without breaking consumers (absent key = not reported).
 */
type HealthCheckResponse = {
  [key in HealthCheckSystem]?: HealthSystemResponseTypeMap[key];
};

/**
 * Maps health status to health check status code
 */
function mapHealthStatusToCode(status: HealthStatus): HealthCheckStatusCode {
  switch (status) {
    case 'healthy':
      return 'ok';
    case 'warning':
      return 'warning';
    case 'error':
      return 'error';
    default:
      return 'error';
  }
}

/**
 * Checks memory system health by calling the Mem0 health check endpoint
 * Returns granular subsystem health information
 */
async function checkMemoryHealth(): Promise<
  HealthCheckStatusEntry<
    'db' | 'vectorStore' | 'graphStore' | 'historyStore' | 'authService'
  >
> {
  try {
    const memoryClient = await memoryClientFactory<ExtendedMemoryClient>({});
    const healthResponse = await memoryClient.healthCheck({
      strict: false,
      verbose: 1,
    });

    const { details } = healthResponse;

    // Create granular subsystem status entries
    const subsystems = {
      db: {
        status: details.system_db_available
          ? ('ok' as const)
          : ('error' as const),
      },
      vectorStore: {
        status: details.vector_store_available
          ? ('ok' as const)
          : ('error' as const),
      },
      graphStore: {
        status: details.graph_store_available
          ? ('ok' as const)
          : ('error' as const),
      },
      historyStore: {
        status: details.history_store_available
          ? ('ok' as const)
          : ('error' as const),
      },
      authService: {
        status: details.auth_service.healthy
          ? ('ok' as const)
          : ('error' as const),
      },
    };

    // Determine overall memory system status
    const overallHealthStatus = determineHealthStatus(details);
    const overallStatusCode = mapHealthStatusToCode(overallHealthStatus);

    return {
      status: overallStatusCode,
      ...subsystems,
    };
  } catch (error) {
    // If we can't reach the memory service, consider everything an error
    console.error('Memory health check failed:', error);
    return {
      status: 'error',
      db: { status: 'error' },
      vectorStore: { status: 'error' },
      graphStore: { status: 'error' },
      historyStore: { status: 'error' },
      authService: { status: 'error' },
    };
  }
}

/**
 * GET /api/health
 * Returns a structured snapshot of subsystem statuses.
 * Wrapped for unified logging / error semantics.
 */
export const GET = wrapRouteRequest(async () => {
  // Get memory health asynchronously with timeout
  const memoryHealthPromise = Promise.race([
    checkMemoryHealth(),
    new Promise<
      HealthCheckStatusEntry<
        'db' | 'vectorStore' | 'graphStore' | 'historyStore' | 'authService'
      >
    >(
      (resolve) =>
        setTimeout(
          () =>
            resolve({
              status: 'error',
              db: { status: 'error' },
              vectorStore: { status: 'error' },
              graphStore: { status: 'error' },
              historyStore: { status: 'error' },
              authService: { status: 'error' },
            }),
          15000,
        ), // 15 second timeout
    ),
  ]);

  const [memoryHealth] = await Promise.all([memoryHealthPromise]);

  const healthCheckResponse: HealthCheckResponse = {
    database: { status: 'ok' },
    chat: { status: 'ok', cache: { status: 'ok' }, queue: { status: 'ok' } },
    memory: memoryHealth,
  };

  return NextResponse.json(healthCheckResponse, { status: 200 });
});

import { auth } from '@/auth';
import { setupDefaultTools } from '@/lib/ai/mcp/providers';
import { User } from 'next-auth';
import { wrapRouteRequest } from '@/lib/nextjs-util/server';
import { NextRequest } from 'next/server';

const toolProviderFactory = async ({
  req,
  user,
}: {
  req: NextRequest;
  chatHistoryId: string;
  writeEnabled?: boolean;
  memoryDisabled?: boolean;
  user: User;
  sessionId: string;
}) =>
  setupDefaultTools({
    user,
    writeEnabled: false,
    req,
    chatHistoryId: 'health-check',
    memoryEnabled: true,
  });

/**
 * GET /api/health
 * Returns a structured snapshot of subsystem statuses.
 * Wrapped for unified logging / error semantics.
 */
export const GET = wrapRouteRequest(async (req: NextRequest) => {
  const session = await auth();
  if (!session?.user) {
    return Response.json(
      { status: 401, message: 'Unauthorized' },
      { status: 401 },
    );
  }
  const toolProviders = await toolProviderFactory({
    req,
    chatHistoryId: 'health-check',
    user: session.user,
    sessionId: 'test-session',
  });
  const tools = Object.keys(toolProviders.tools);
  return Response.json({ status: 200, message: 'OK', tools }, { status: 200 });
});

