import * as React from 'react';
import { useEffect } from 'react';
import { errorReporter, ErrorSeverity } from '@/lib/error-monitoring';
import { RenderErrorBoundaryFallback } from '@/components/error-boundaries/renderFallback';
import { FlagProvider } from '@/components/general/flags/flag-provider';

type GlobalErrorProps = {
  error: Error & { digest?: string };
  reset: () => void;
};

/**
 * Global error boundary that catches errors in the root layout
 * This is a last resort fallback for critical application errors
 */
export default function GlobalError({ error, reset }: GlobalErrorProps) {
  useEffect(() => {
    // Report the critical error - this is the last line of defense
    errorReporter.reportBoundaryError(
      error,
      {
        errorBoundary: 'GlobalError',
      },
      ErrorSeverity.CRITICAL,
    );
  }, [error]);

  return (
    <html lang="en">
      <head>
        <title>Application Error - School Case Tracker</title>
        <meta name="viewport" content="width=device-width, initial-scale=1" />
      </head>
      <body style={{ margin: 0, fontFamily: 'system-ui, sans-serif' }}>
        {/* Provide feature flags context so the error UI and any children can call */}
        <FlagProvider>
          <div
            style={{
              minHeight: '100vh',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              backgroundColor: '#fafafa',
            }}
          >
            <RenderErrorBoundaryFallback
              error={error}
              resetErrorBoundary={reset}
            />
          </div>
        </FlagProvider>
      </body>
    </html>
  );
}

import * as React from 'react';
import { Box } from '@mui/material';
import { auth } from '@/auth';
import { EmailDashboardLayout } from '@/components/email-message/dashboard-layout/email-dashboard-layout';
import { ChatHistory } from '@/components/chat/history';
import { extractParams } from '@/lib/nextjs-util/utils';
import { getChatDetails } from '@/lib/ai/chat/history';
import { notFound, unauthorized } from 'next/navigation';

/**
 * Server-rendered inner component that performs async auth/permission checks.
 * Returns either Access Denied UI or the dashboard content.
 */
const ChatDetailPage = async (req: { params: Promise<{ chatId: string }> }) => {
  const props = { session: await auth() };

  if (!props.session) {
    return unauthorized();
  }

  const { user: { id: userIdFromProps } = {} } = props.session;
  const userId = Number(userIdFromProps);
  if (!userId || isNaN(userId)) {
    return notFound();
  }

  const { chatId } = await extractParams(req);
  if (!chatId) {
    return notFound();
  }

  // Try direct id first
  const details = await getChatDetails({ chatId, userId });
  if (!details.ok) {
    return notFound();
  }

  // success
  return (
    <EmailDashboardLayout session={props.session}>
      <Box sx={{ p: 2 }}>
        <ChatHistory chatId={chatId} title={details.title} />
      </Box>
    </EmailDashboardLayout>
  );
};

export default ChatDetailPage;

