 */

'use client';

import React, { useRef, useState, useMemo } from 'react';
import Tooltip from '@mui/material/Tooltip';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import IconButton from '@mui/material/IconButton';
import FileDownloadIcon from '@mui/icons-material/FileDownload';
import {
  exportToCsv,
  exportToMarkdown,
  type SelectedChatItem,
} from '../../lib/ai/chat/export';
import type { ChatTurn } from '@/lib/ai/chat/types';

interface ChatExportMenuProps {
  turns: ChatTurn[];
  selectedItems: SelectedChatItem[];
  chatTitle?: string;
  chatCreatedAt?: string;
  disabled?: boolean;
}

const stableOrigin = {
  bottomRight: { vertical: 'bottom', horizontal: 'right' } as const,
  topRight: { vertical: 'top', horizontal: 'right' } as const,
};

export const ChatExportMenu: React.FC<ChatExportMenuProps> = ({
  turns,
  selectedItems,
  chatTitle,
  chatCreatedAt,
  disabled = false,
}) => {
  const [exportMenuOpen, setExportMenuOpen] = useState(false);
  const exportMenuTriggerRef = useRef<HTMLButtonElement>(null);

  const { onSetExportMenuOpen, onSetExportMenuClosed } = useMemo(() => {
    const onSetExportMenuOpen = () => {
      if (!exportMenuOpen) {
        setExportMenuOpen(true);
      }
    };
    const onSetExportMenuClosed = () => {
      if (exportMenuOpen) {
        setExportMenuOpen(false);
      }
    };
    return {
      onSetExportMenuOpen,
      onSetExportMenuClosed,
    };
  }, [exportMenuOpen]);

  const handleExportCsv = () => {
    try {
      exportToCsv(turns, selectedItems, chatTitle);
      onSetExportMenuClosed();
    } catch (error) {
      console.error('Failed to export CSV:', error);
      // In a real app, you'd want to show a proper error message to the user
      alert(error instanceof Error ? error.message : 'Failed to export CSV');
    }
  };

  const handleExportMarkdown = () => {
    try {
      exportToMarkdown(turns, selectedItems, chatTitle, chatCreatedAt);
      onSetExportMenuClosed();
    } catch (error) {
      console.error('Failed to export Markdown:', error);
      // In a real app, you'd want to show a proper error message to the user
      alert(
        error instanceof Error ? error.message : 'Failed to export Markdown',
      );
    }
  };

  const hasSelection = selectedItems.length > 0;

  return (
    <>
      <Tooltip
        title={
          hasSelection
            ? 'Export selected messages'
            : 'Select messages to export'
        }
      >
        <span>
          <IconButton
            ref={exportMenuTriggerRef}
            id="chat-export-menu-trigger"
            aria-controls="chat-export-menu"
            aria-haspopup="true"
            aria-expanded={exportMenuOpen ? 'true' : undefined}
            onClick={onSetExportMenuOpen}
            disabled={disabled || !hasSelection}
          >
            <FileDownloadIcon fontSize="small" />
          </IconButton>
        </span>
      </Tooltip>
      <Menu
        id="chat-export-menu"
        anchorEl={exportMenuTriggerRef.current}
        open={exportMenuOpen}
        onClose={onSetExportMenuClosed}
        anchorOrigin={stableOrigin.bottomRight}
        transformOrigin={stableOrigin.topRight}
        sx={{
          '& .MuiPaper-root': {
            minWidth: 180,
          },
        }}
      >
        <MenuItem onClick={handleExportCsv}>Export as CSV</MenuItem>
        <MenuItem onClick={handleExportMarkdown}>Export as Markdown</MenuItem>
      </Menu>
    </>
  );
};

/**
 * @module ChatMessageFilters
 * @fileoverview
 * Reusable component for message filtering in chat interfaces.
 *
 * Provides filtering by:
 * - Message type (user, assistant, system, tool)
 * - Content text search across all message fields
 *
 * Used by both global chat-level filtering and per-turn filtering.
 */
'use client';

import * as React from 'react';
import {
  Box,
  Typography,
  FormControlLabel,
  Switch,
  Button,
  Badge,
  Chip,
  TextField,
  InputAdornment,
} from '@mui/material';
import { FilterList, Search } from '@mui/icons-material';
import type { ChatMessage } from '@/lib/ai/chat/types';

// Available message types for filtering
export const MESSAGE_TYPES = ['user', 'assistant', 'system', 'tool'] as const;
export type MessageType = (typeof MESSAGE_TYPES)[number];

export interface ChatMessageFiltersProps {
  // Data
  messages: ChatMessage[];

  // Filter state
  enableFilters: boolean;
  onEnableFiltersChange: (enabled: boolean) => void;
  activeTypeFilters: Set<MessageType>;
  onTypeFiltersChange: (filters: Set<MessageType>) => void;
  contentFilter: string;
  onContentFilterChange: (filter: string) => void;

  // UI customization
  title: string;
  size?: 'small' | 'medium';
  showStatusMessage?: boolean;
}

/**
 * Searches for text across all searchable fields in a message
 */
export const searchMessageContent = (
  message: ChatMessage,
  searchTerm: string,
): boolean => {
  if (!searchTerm.trim()) return true;

  const searchLower = searchTerm.toLowerCase();

  // Search in content fields
  if (message.content && message.content.toLowerCase().includes(searchLower))
    return true;
  if (
    message.optimizedContent &&
    message.optimizedContent.toLowerCase().includes(searchLower)
  )
    return true;
  if (message.toolName && message.toolName.toLowerCase().includes(searchLower))
    return true;

  // Search in structured data
  if (message.functionCall) {
    try {
      const functionCallStr = JSON.stringify(
        message.functionCall,
      ).toLowerCase();
      if (functionCallStr.includes(searchLower)) return true;
    } catch {
      // Ignore JSON stringify errors
    }
  }

  if (message.toolResult) {
    try {
      const toolResultStr = JSON.stringify(message.toolResult).toLowerCase();
      if (toolResultStr.includes(searchLower)) return true;
    } catch {
      // Ignore JSON stringify errors
    }
  }

  return false;
};

/**
 * Get available message types from the provided messages
 * Pure function for better performance
 */
const getAvailableMessageTypes = (messages: ChatMessage[]): MessageType[] => {
  const typesInMessages = new Set<MessageType>();
  messages.forEach((message) => {
    if (MESSAGE_TYPES.includes(message.role as MessageType)) {
      typesInMessages.add(message.role as MessageType);
    }
  });
  return Array.from(typesInMessages).sort();
};

export const ChatMessageFilters: React.FC<ChatMessageFiltersProps> = ({
  messages,
  enableFilters,
  onEnableFiltersChange,
  activeTypeFilters,
  onTypeFiltersChange,
  contentFilter,
  onContentFilterChange,
  title,
  size = 'medium',
  showStatusMessage = true,
}) => {
  // Memoize available message types computation
  const availableTypes = React.useMemo(
    () => getAvailableMessageTypes(messages),
    [messages],
  );

  // Stable filter handling functions
  const toggleFilter = React.useCallback(
    (messageType: MessageType) => {
      const newFilters = new Set(activeTypeFilters);
      if (newFilters.has(messageType)) {
        newFilters.delete(messageType);
      } else {
        newFilters.add(messageType);
      }
      onTypeFiltersChange(newFilters);
    },
    [activeTypeFilters, onTypeFiltersChange],
  );

  const clearAllFilters = React.useCallback(() => {
    onTypeFiltersChange(new Set());
    onContentFilterChange('');
  }, [onTypeFiltersChange, onContentFilterChange]);

  // Stable switch handler
  const handleEnableFiltersChange = React.useCallback(
    (e: React.ChangeEvent<HTMLInputElement>) => {
      onEnableFiltersChange(e.target.checked);
      if (!e.target.checked) {
        clearAllFilters();
      }
    },
    [onEnableFiltersChange, clearAllFilters],
  );

  // Stable content filter handler
  const handleContentFilterChange = React.useCallback(
    (e: React.ChangeEvent<HTMLInputElement>) => {
      onContentFilterChange(e.target.value);
    },
    [onContentFilterChange],
  );

  // Pre-calculate size-dependent values
  const isSmall = size === 'small';

  // Memoize style objects to prevent re-creation
  const containerSx = React.useMemo(
    () => ({
      display: 'flex',
      alignItems: 'center',
      gap: 2,
      mb: isSmall ? 1 : 2,
    }),
    [isSmall],
  );

  const textFieldSx = React.useMemo(
    () => ({
      minWidth: 200,
    }),
    [],
  );

  const typesSectionSx = React.useMemo(
    () => ({
      display: 'flex',
      flexWrap: 'wrap',
      gap: 1,
      alignItems: 'center',
    }),
    [],
  );

  const clearButtonSx = React.useMemo(
    () => ({
      ml: 1,
      fontSize: isSmall ? '0.75rem' : '0.875rem',
      py: isSmall ? 0.5 : undefined,
    }),
    [isSmall],
  );

  const statusMessageSx = React.useMemo(
    () => ({
      mt: isSmall ? 0.5 : 1,
    }),
    [isSmall],
  );

  // Memoize status message text
  const statusMessage = React.useMemo(() => {
    if (
      !showStatusMessage ||
      (activeTypeFilters.size === 0 && !contentFilter.trim())
    ) {
      return null;
    }

    if (contentFilter.trim() && activeTypeFilters.size > 0) {
      return `Filtering by content "${contentFilter}" and ${activeTypeFilters.size} of ${availableTypes.length} message types`;
    } else if (contentFilter.trim()) {
      return `Filtering by content "${contentFilter}"`;
    } else {
      return `Showing ${activeTypeFilters.size} of ${availableTypes.length} message types`;
    }
  }, [
    showStatusMessage,
    activeTypeFilters.size,
    contentFilter,
    availableTypes.length,
  ]);

  return (
    <Box sx={containerSx}>
      <FilterList color="action" fontSize={isSmall ? 'small' : 'medium'} />
      <Typography
        variant={isSmall ? 'body2' : 'h6'}
        sx={{ fontWeight: isSmall ? 'medium' : 'normal' }}
      >
        {title}
      </Typography>
      <FormControlLabel
        control={
          <Switch
            size={isSmall ? 'small' : 'medium'}
            checked={enableFilters}
            onChange={handleEnableFiltersChange}
          />
        }
        label="Enable Filtering"
      />

      {enableFilters && (
        <>
          {/* Content Filter Input */}
          <TextField
            size={isSmall ? 'small' : 'medium'}
            placeholder="Search message content..."
            value={contentFilter}
            onChange={handleContentFilterChange}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <Search fontSize={isSmall ? 'small' : 'medium'} />
                </InputAdornment>
              ),
            }}
            sx={textFieldSx}
          />

          {/* Type Filter Badges */}
          <Box sx={typesSectionSx}>
            <Typography
              variant="body2"
              color="text.secondary"
              sx={{ mr: 1, fontSize: isSmall ? '0.75rem' : '0.875rem' }}
            >
              Types:
            </Typography>
            {availableTypes.map((messageType) => {
              const isActive = activeTypeFilters.has(messageType);
              const messageCount = messages.filter(
                (msg) => msg.role === messageType,
              ).length;

              return (
                <MessageTypeBadge
                  key={messageType}
                  messageType={messageType}
                  messageCount={messageCount}
                  isActive={isActive}
                  isSmall={isSmall}
                  onToggle={toggleFilter}
                />
              );
            })}

            {(activeTypeFilters.size > 0 || contentFilter.trim()) && (
              <Button
                size="small"
                variant="outlined"
                onClick={clearAllFilters}
                sx={clearButtonSx}
              >
                Clear All
              </Button>
            )}
          </Box>

          {statusMessage && (
            <Box sx={statusMessageSx}>
              <Typography
                variant="body2"
                color="text.secondary"
                sx={{ fontSize: isSmall ? '0.75rem' : '0.875rem' }}
              >
                {statusMessage}
              </Typography>
            </Box>
          )}
        </>
      )}
    </Box>
  );
};

// Separate memoized component for message type badges to prevent unnecessary re-renders
const MessageTypeBadge = React.memo<{
  messageType: MessageType;
  messageCount: number;
  isActive: boolean;
  isSmall: boolean;
  onToggle: (messageType: MessageType) => void;
}>(function MessageTypeBadge({
  messageType,
  messageCount,
  isActive,
  isSmall,
  onToggle,
}) {
  const handleClick = React.useCallback(() => {
    onToggle(messageType);
  }, [messageType, onToggle]);

  const badgeSx = React.useMemo(() => ({ cursor: 'pointer' }), []);

  const chipSx = React.useMemo(
    () => ({
      textTransform: 'capitalize' as const,
      '&:hover': {
        backgroundColor: isActive ? 'primary.dark' : 'action.hover',
      },
    }),
    [isActive],
  );

  return (
    <Badge
      badgeContent={messageCount}
      color={isActive ? 'primary' : 'default'}
      sx={badgeSx}
      onClick={handleClick}
    >
      <Chip
        label={messageType}
        variant={isActive ? 'filled' : 'outlined'}
        color={isActive ? 'primary' : 'default'}
        size={isSmall ? 'small' : 'medium'}
        onClick={handleClick}
        sx={chipSx}
      />
    </Badge>
  );
});

'use client';

import React, { useState } from 'react';
import {
  Box,
  Typography,
  Chip,
  Card,
  CardContent,
  IconButton,
  Collapse,
  Paper,
  Grid,
  Divider,
  Alert,
  Checkbox,
  FormControlLabel,
} from '@mui/material';
import {
  Settings as SettingsIcon,
  Schedule as ScheduleIcon,
  Warning as WarningIcon,
  Error as ErrorIcon,
} from '@mui/icons-material';
import { ChatMessageDisplay } from './chat-message-display';
import { ChatTurn } from '@/lib/ai/chat/types';
import type { SelectedChatItem } from '../../lib/ai/chat/export';
import {
  ChatMessageFilters,
  MESSAGE_TYPES,
  type MessageType,
  searchMessageContent,
} from './chat-message-filters';

/**
 * Get available message types in a turn after global filtering
 * Pure function for better performance
 */
const getAvailableMessageTypesInTurn = (
  turn: ChatTurn,
  globalFilters: { typeFilters: Set<MessageType>; contentFilter: string },
): MessageType[] => {
  // First, get messages that pass global filters (or all if no global filters)
  const globallyFilteredMessages =
    globalFilters.typeFilters.size > 0 || globalFilters.contentFilter.trim()
      ? turn.messages.filter((message) => {
          // Type filter
          const passesTypeFilter =
            globalFilters.typeFilters.size === 0 ||
            globalFilters.typeFilters.has(message.role as MessageType);
          // Content filter
          const passesContentFilter = searchMessageContent(
            message,
            globalFilters.contentFilter,
          );

          return passesTypeFilter && passesContentFilter;
        })
      : turn.messages;

  // Then find unique types in those messages
  const typesInTurn = new Set<MessageType>();
  globallyFilteredMessages.forEach((message) => {
    if (MESSAGE_TYPES.includes(message.role as MessageType)) {
      typesInTurn.add(message.role as MessageType);
    }
  });
  return Array.from(typesInTurn).sort();
};

/**
 * Apply both global and per-turn filters to messages
 * Pure function for better performance
 */
const getFilteredMessages = (
  turn: ChatTurn,
  globalFilters: { typeFilters: Set<MessageType>; contentFilter: string },
  enableTurnFilters: boolean,
  activeTurnFilters: Set<MessageType>,
  turnContentFilter: string,
) => {
  let filteredMessages = turn.messages;

  // First apply global filters if active
  if (
    globalFilters.typeFilters.size > 0 ||
    globalFilters.contentFilter.trim()
  ) {
    filteredMessages = filteredMessages.filter((message) => {
      // Type filter
      const passesTypeFilter =
        globalFilters.typeFilters.size === 0 ||
        globalFilters.typeFilters.has(message.role as MessageType);
      // Content filter
      const passesContentFilter = searchMessageContent(
        message,
        globalFilters.contentFilter,
      );

      return passesTypeFilter && passesContentFilter;
    });
  }

  // Then apply per-turn filters if active
  if (
    enableTurnFilters &&
    (activeTurnFilters.size > 0 || turnContentFilter.trim())
  ) {
    filteredMessages = filteredMessages.filter((message) => {
      // Type filter
      const passesTypeFilter =
        activeTurnFilters.size === 0 ||
        activeTurnFilters.has(message.role as MessageType);
      // Content filter
      const passesContentFilter = searchMessageContent(
        message,
        turnContentFilter,
      );

      return passesTypeFilter && passesContentFilter;
    });
  }

  return filteredMessages;
};

/**
 * Get messages available after global filtering for the per-turn filter component
 * Pure function for better performance
 */
const getGloballyFilteredMessages = (
  turn: ChatTurn,
  globalFilters: { typeFilters: Set<MessageType>; contentFilter: string },
) => {
  return globalFilters.typeFilters.size > 0 ||
    globalFilters.contentFilter.trim()
    ? turn.messages.filter((message) => {
        // Type filter
        const passesTypeFilter =
          globalFilters.typeFilters.size === 0 ||
          globalFilters.typeFilters.has(message.role as MessageType);
        // Content filter
        const passesContentFilter = searchMessageContent(
          message,
          globalFilters.contentFilter,
        );

        return passesTypeFilter && passesContentFilter;
      })
    : turn.messages;
};

/**
 * Format duration between start and end times
 * Pure function for better performance
 */
const formatDuration = (startTime: string, endTime: string | null) => {
  if (!endTime) return 'In progress...';
  const start = new Date(startTime);
  const end = new Date(endTime);
  const durationMs = end.getTime() - start.getTime();
  return `${durationMs}ms`;
};

interface ChatTurnDisplayProps {
  turn: ChatTurn;
  showTurnProperties?: boolean;
  showMessageMetadata?: boolean;
  enableSelection?: boolean;
  selectedItems?: SelectedChatItem[];
  onSelectionChange?: (selectedItems: SelectedChatItem[]) => void;
  globalFilters?: {
    typeFilters: Set<MessageType>;
    contentFilter: string;
  };
}

export const ChatTurnDisplay: React.FC<ChatTurnDisplayProps> = ({
  turn,
  showTurnProperties = false,
  showMessageMetadata = false,
  enableSelection = false,
  selectedItems = [],
  onSelectionChange,
  globalFilters = { typeFilters: new Set(), contentFilter: '' },
}) => {
  const [propertiesExpanded, setPropertiesExpanded] = useState(false);

  // Per-turn filtering state
  const [enableTurnFilters, setEnableTurnFilters] = useState(false);
  const [activeTurnFilters, setActiveTurnFilters] = useState<Set<MessageType>>(
    new Set(),
  );
  const [turnContentFilter, setTurnContentFilter] = useState('');

  // Memoize expensive computations
  const availableTypesInTurn = React.useMemo(
    () => getAvailableMessageTypesInTurn(turn, globalFilters),
    [turn, globalFilters],
  );

  const filteredMessages = React.useMemo(
    () =>
      getFilteredMessages(
        turn,
        globalFilters,
        enableTurnFilters,
        activeTurnFilters,
        turnContentFilter,
      ),
    [
      turn,
      globalFilters,
      enableTurnFilters,
      activeTurnFilters,
      turnContentFilter,
    ],
  );

  const globallyFilteredMessages = React.useMemo(
    () => getGloballyFilteredMessages(turn, globalFilters),
    [turn, globalFilters],
  );

  // Memoize selection state
  const isTurnSelected = React.useMemo(
    () =>
      selectedItems.some(
        (item) => item.type === 'turn' && item.turnId === turn.turnId,
      ),
    [selectedItems, turn.turnId],
  );

  const selectedMessages = React.useMemo(
    () =>
      selectedItems.filter(
        (item) => item.type === 'message' && item.turnId === turn.turnId,
      ),
    [selectedItems, turn.turnId],
  );

  const isPartiallySelected = React.useMemo(
    () =>
      selectedMessages.length > 0 &&
      selectedMessages.length < turn.messages.length,
    [selectedMessages.length, turn.messages.length],
  );

  // Stable event handlers
  const handleTurnSelectionChange = React.useCallback(
    (checked: boolean) => {
      if (!onSelectionChange) return;

      let newSelection = [...selectedItems];

      if (checked) {
        // Remove any individual message selections for this turn
        newSelection = newSelection.filter(
          (item) => !(item.type === 'message' && item.turnId === turn.turnId),
        );
        // Add turn selection
        newSelection.push({ type: 'turn', turnId: turn.turnId });
      } else {
        // Remove turn selection
        newSelection = newSelection.filter(
          (item) => !(item.type === 'turn' && item.turnId === turn.turnId),
        );
      }

      onSelectionChange(newSelection);
    },
    [onSelectionChange, selectedItems, turn.turnId],
  );

  const handleMessageSelectionChange = React.useCallback(
    (messageId: number, checked: boolean) => {
      if (!onSelectionChange) return;

      let newSelection = [...selectedItems];

      // Remove turn selection if it exists (since we're selecting individual messages)
      newSelection = newSelection.filter(
        (item) => !(item.type === 'turn' && item.turnId === turn.turnId),
      );

      if (checked) {
        // Add message selection if not already present
        if (
          !newSelection.some(
            (item) =>
              item.type === 'message' &&
              item.turnId === turn.turnId &&
              item.messageId === messageId,
          )
        ) {
          newSelection.push({
            type: 'message',
            turnId: turn.turnId,
            messageId,
          });
        }
      } else {
        // Remove message selection
        newSelection = newSelection.filter(
          (item) =>
            !(
              item.type === 'message' &&
              item.turnId === turn.turnId &&
              item.messageId === messageId
            ),
        );
      }

      onSelectionChange(newSelection);
    },
    [onSelectionChange, selectedItems, turn.turnId],
  );

  const handlePropertiesToggle = React.useCallback(() => {
    setPropertiesExpanded(!propertiesExpanded);
  }, [propertiesExpanded]);

  return (
    <Card sx={{ mb: 2 }}>
      <CardContent>
        {/* Turn Header */}
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2, gap: 1 }}>
          {enableSelection && (
            <FormControlLabel
              control={
                <Checkbox
                  size="small"
                  checked={isTurnSelected}
                  indeterminate={isPartiallySelected && !isTurnSelected}
                  onChange={(e) => handleTurnSelectionChange(e.target.checked)}
                />
              }
              label=""
              sx={{
                mr: 1,
                '& .MuiFormControlLabel-label': { display: 'none' },
              }}
            />
          )}
          <Chip label={`Turn ${turn.turnId}`} variant="outlined" size="small" />
          {turn.modelName && (
            <Chip
              label={turn.modelName}
              variant="outlined"
              size="small"
              color="primary"
            />
          )}
          {turn.latencyMs && (
            <Chip
              label={`${turn.latencyMs}ms`}
              variant="outlined"
              size="small"
              icon={<ScheduleIcon fontSize="small" />}
            />
          )}
          {turn.warnings && turn.warnings.length > 0 && (
            <Chip
              label={`${turn.warnings.length} warning${turn.warnings.length > 1 ? 's' : ''}`}
              variant="outlined"
              size="small"
              icon={<WarningIcon fontSize="small" />}
              color="warning"
            />
          )}
          {turn.errors && turn.errors.length > 0 && (
            <Chip
              label={`${turn.errors.length} error${turn.errors.length > 1 ? 's' : ''}`}
              variant="outlined"
              size="small"
              icon={<ErrorIcon fontSize="small" />}
              color="error"
            />
          )}
          {showTurnProperties && (
            <IconButton
              size="small"
              onClick={handlePropertiesToggle}
              sx={{ ml: 'auto' }}
            >
              <SettingsIcon fontSize="small" />
            </IconButton>
          )}
        </Box>

        {/* Turn Properties (when expanded) */}
        {showTurnProperties && (
          <Collapse in={propertiesExpanded}>
            <Paper variant="outlined" sx={{ p: 2, mb: 2 }} elevation={3}>
              <Typography variant="subtitle2" gutterBottom>
                Turn Properties
              </Typography>
              <Grid container spacing={2}>
                <Grid
                  size={{
                    xs: 6,
                    md: 3,
                  }}
                >
                  <Typography variant="caption" display="block">
                    Status ID: {turn.statusId}
                  </Typography>
                </Grid>
                <Grid
                  size={{
                    xs: 6,
                    md: 3,
                  }}
                >
                  <Typography variant="caption" display="block">
                    Created: {new Date(turn.createdAt).toLocaleString()}
                  </Typography>
                </Grid>
                {turn.completedAt && (
                  <Grid
                    size={{
                      xs: 6,
                      md: 3,
                    }}
                  >
                    <Typography variant="caption" display="block">
                      Completed: {new Date(turn.completedAt).toLocaleString()}
                    </Typography>
                  </Grid>
                )}
                <Grid
                  size={{
                    xs: 6,
                    md: 3,
                  }}
                >
                  <Typography variant="caption" display="block">
                    Duration: {formatDuration(turn.createdAt, turn.completedAt)}
                  </Typography>
                </Grid>
                {turn.temperature !== null && (
                  <Grid
                    size={{
                      xs: 6,
                      md: 3,
                    }}
                  >
                    <Typography variant="caption" display="block">
                      Temperature: {turn.temperature}
                    </Typography>
                  </Grid>
                )}
                {turn.topP !== null && (
                  <Grid
                    size={{
                      xs: 6,
                      md: 3,
                    }}
                  >
                    <Typography variant="caption" display="block">
                      Top P: {turn.topP}
                    </Typography>
                  </Grid>
                )}
                {turn.latencyMs !== null && (
                  <Grid
                    size={{
                      xs: 6,
                      md: 3,
                    }}
                  >
                    <Typography variant="caption" display="block">
                      Latency: {turn.latencyMs}ms
                    </Typography>
                  </Grid>
                )}
              </Grid>

              {/* Warnings */}
              {turn.warnings && turn.warnings.length > 0 && (
                <Box sx={{ mt: 2 }}>
                  <Typography variant="caption" display="block" gutterBottom>
                    Warnings:
                  </Typography>
                  {turn.warnings.map((warning, index) => (
                    <Alert key={index} severity="warning" sx={{ mb: 1 }}>
                      {warning}
                    </Alert>
                  ))}
                </Box>
              )}

              {/* Errors */}
              {turn.errors && turn.errors.length > 0 && (
                <Box sx={{ mt: 2 }}>
                  <Typography variant="caption" display="block" gutterBottom>
                    Errors:
                  </Typography>
                  {turn.errors.map((error, index) => (
                    <Alert key={index} severity="error" sx={{ mb: 1 }}>
                      {error}
                    </Alert>
                  ))}
                </Box>
              )}

              {/* Turn Metadata */}
              {turn.metadata && (
                <Paper sx={{ mt: 2 }} elevation={4}>
                  <Typography variant="caption" display="block" gutterBottom>
                    Turn Metadata:
                  </Typography>
                  <Box
                    component="pre"
                    sx={{
                      fontSize: '0.75rem',
                      // backgroundColor: 'grey.100',
                      p: 1,
                      borderRadius: 1,
                      overflow: 'auto',
                      maxHeight: 200,
                      wordBreak: 'break-all',
                      whiteSpace: 'pre-wrap',
                      maxWidth: '100%',
                    }}
                  >
                    {JSON.stringify(turn.metadata, null, 2)}
                  </Box>
                </Paper>
              )}
            </Paper>
            <Divider sx={{ mb: 2 }} />
          </Collapse>
        )}

        {/* Per-Turn Message Filtering Controls */}
        {availableTypesInTurn.length > 1 && (
          <Paper
            variant="outlined"
            sx={{ p: 2, mb: 2, bgcolor: 'action.hover' }}
          >
            <ChatMessageFilters
              messages={globallyFilteredMessages}
              enableFilters={enableTurnFilters}
              onEnableFiltersChange={setEnableTurnFilters}
              activeTypeFilters={activeTurnFilters}
              onTypeFiltersChange={setActiveTurnFilters}
              contentFilter={turnContentFilter}
              onContentFilterChange={setTurnContentFilter}
              title="Turn Filters"
              size="small"
              showStatusMessage={true}
            />
          </Paper>
        )}

        {/* Messages */}
        {filteredMessages.length === 0 ? (
          <Typography
            color="text.secondary"
            sx={{ textAlign: 'center', py: 2, fontStyle: 'italic' }}
          >
            {(globalFilters.typeFilters.size > 0 ||
              globalFilters.contentFilter.trim()) &&
            enableTurnFilters &&
            (activeTurnFilters.size > 0 || turnContentFilter.trim())
              ? 'No messages match the current global and turn filters.'
              : globalFilters.typeFilters.size > 0 ||
                  globalFilters.contentFilter.trim()
                ? 'No messages match the current global filters.'
                : enableTurnFilters &&
                    (activeTurnFilters.size > 0 || turnContentFilter.trim())
                  ? 'No messages match the current turn filters.'
                  : 'No messages in this turn.'}
          </Typography>
        ) : (
          filteredMessages.map((message) => {
            const isMessageSelected =
              isTurnSelected ||
              selectedItems.some(
                (item) =>
                  item.type === 'message' &&
                  item.turnId === turn.turnId &&
                  item.messageId === message.messageId,
              );

            return (
              <ChatMessageDisplay
                key={`${message.turnId}-${message.messageId}`}
                message={message}
                showMetadata={showMessageMetadata}
                enableSelection={enableSelection}
                isSelected={isMessageSelected}
                onSelectionChange={handleMessageSelectionChange}
              />
            );
          })
        )}
      </CardContent>
    </Card>
  );
};

/**
 * @module ChatHistory
 * @fileoverview
 * ChatHistory component module: provides a full, virtualized, scrollable view of a single chat conversation's message history.
 *
 * - Fetches chat data (turns, title, creation time) by chatId using a custom React Query hook.
 * - Handles loading, error, and empty states with clear user feedback and retry affordance.
 * - Renders chat turns using a high-performance virtualized display for scalability.
 * - Title resolution logic prioritizes explicit prop, then fetched title, then fallback.
 *
 * Usage:
 * ```tsx
 * <ChatHistory chatId="abc123" title="My Chat" />
 * ```
 *
 * Exported Components:
 * - ChatHistory: Main React component for displaying a chat transcript by id.
 */
'use client';

import * as React from 'react';
import {
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  Chip,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  FormControlLabel,
  Switch,
  Button,
  Paper,
} from '@mui/material';
import { ExpandMore } from '@mui/icons-material';
import { VirtualizedChatDisplay } from '@/components/chat/virtualized-chat-display';
import { ChatExportMenu } from '@/components/chat/chat-export-menu';
import {
  ChatMessageFilters,
  type MessageType,
  searchMessageContent,
} from '@/components/chat/chat-message-filters';
import { useChatHistory } from './useChatHistory';
import { Loading } from '@/components/general/loading';
import type { ChatDetails, ChatTurn } from '@/lib/ai/chat/types';
import type { SelectedChatItem } from '@/lib/ai/chat/export';

/**
 * Get all messages from the chat for filter component
 * Pure function for better performance
 */
const getAllMessages = (chatDetails: ChatDetails) => {
  return chatDetails.turns.flatMap((turn) => turn.messages);
};

/**
 * Apply global chat-level filters to the chat data
 * Pure function for better performance
 */
const getFilteredTurns = (
  chatDetails: ChatDetails,
  enableFilters: boolean,
  activeFilters: Set<MessageType>,
  contentFilter: string,
): ChatTurn[] => {
  if (!enableFilters) {
    return chatDetails.turns;
  }

  // Global filtering: only hide entire turns if ALL messages in the turn are filtered out
  return chatDetails.turns.filter((turn) => {
    // Apply global filters to the turn's messages
    const filteredMessages = turn.messages.filter((message) => {
      // Type filter
      const passesTypeFilter =
        activeFilters.size === 0 ||
        activeFilters.has(message.role as MessageType);
      // Content filter
      const passesContentFilter = searchMessageContent(message, contentFilter);

      return passesTypeFilter && passesContentFilter;
    });
    // Only hide the turn if no messages match the filters (all messages filtered out)
    return filteredMessages.length > 0;
  });
};

/**
 * Calculate chat statistics
 * Pure function for better performance
 */
const calculateStats = (chatDetails: ChatDetails) => {
  const turns = chatDetails.turns;
  const totalMessages = turns.reduce(
    (sum, turn) => sum + turn.messages.length,
    0,
  );
  const totalTokens = turns.reduce((sum, turn) => {
    if (turn.metadata?.totalTokens) {
      return sum + (turn.metadata.totalTokens as number);
    }
    return sum;
  }, 0);

  const toolUsage = turns.reduce(
    (acc, turn) => {
      turn.messages.forEach((msg) => {
        if (msg.toolName) {
          acc[msg.toolName] = (acc[msg.toolName] || 0) + 1;
        }
      });
      return acc;
    },
    {} as Record<string, number>,
  );

  return {
    totalTurns: turns.length,
    totalMessages,
    totalTokens,
    toolUsage,
    avgLatency:
      turns
        .filter((t) => t.latencyMs)
        .reduce((sum, t) => sum + (t.latencyMs || 0), 0) /
      Math.max(turns.filter((t) => t.latencyMs).length, 1),
  };
};

/**
 * ChatHistory component: displays the full message history for a given chat, including title, creation time, and all turns/messages.
 *
 * @param chatId - Unique identifier for the chat transcript to load and display.
 * @param title - (Optional) Explicit title to display; if omitted, falls back to fetched title or a generated default.
 *
 * @remarks
 * - Uses `useChatHistory` hook for data fetching and error/loading state management.
 * - Renders a virtualized chat display for performance with large transcripts.
 * - Handles error and empty states with user-friendly messaging and retry option.
 *
 * @example
 * <ChatHistory chatId="abc123" />
 * <ChatHistory chatId="abc123" title="My Custom Title" />
 */

export const ChatHistory = ({
  chatId,
  title: titleFromProps,
}: {
  chatId: string;
  title?: string;
}) => {
  const { data, isLoading, isError, error, refetch } = useChatHistory(chatId);
  const [enableSelection, setEnableSelection] = React.useState(false);
  const [selectedItems, setSelectedItems] = React.useState<SelectedChatItem[]>(
    [],
  );

  // Filter state - Global chat-level filtering
  const [enableFilters, setEnableFilters] = React.useState(false);
  const [activeFilters, setActiveFilters] = React.useState<Set<MessageType>>(
    new Set(),
  );
  const [contentFilter, setContentFilter] = React.useState('');

  // Memoize filtered data computation
  const filteredData = React.useMemo(() => {
    if (!data) return null;
    return {
      ...data,
      turns: getFilteredTurns(
        data,
        enableFilters,
        activeFilters,
        contentFilter,
      ),
    };
  }, [data, enableFilters, activeFilters, contentFilter]);

  // Memoize all messages computation
  const allMessages = React.useMemo(() => {
    return data ? getAllMessages(data) : [];
  }, [data]);

  // Memoize stats computation
  const stats = React.useMemo(() => {
    return filteredData ? calculateStats(filteredData) : null;
  }, [filteredData]);

  // Title resolution rules - memoized to prevent re-computation
  const effectiveTitle = React.useMemo(() => {
    const resolvedTitleFromProps =
      titleFromProps && titleFromProps.trim().length > 0
        ? titleFromProps.trim()
        : null;
    return (
      resolvedTitleFromProps ??
      (data?.title && data.title.trim().length > 0 ? data.title : null)
    );
  }, [titleFromProps, data?.title]);

  // Stable event handlers
  const handleSelectAll = React.useCallback(() => {
    if (!filteredData) return;

    // Select all turns from filtered data
    const allTurns: SelectedChatItem[] = filteredData.turns.map((turn) => ({
      type: 'turn' as const,
      turnId: turn.turnId,
    }));
    setSelectedItems(allTurns);
  }, [filteredData]);

  const handleClearSelection = React.useCallback(() => {
    setSelectedItems([]);
  }, []);

  const handleEnableSelectionChange = React.useCallback(
    (e: React.ChangeEvent<HTMLInputElement>) => {
      setEnableSelection(e.target.checked);
    },
    [],
  );

  // Clear selection when disabling selection mode
  React.useEffect(() => {
    if (!enableSelection) {
      setSelectedItems([]);
    }
  }, [enableSelection]);

  // Clear selection when disabling selection mode
  React.useEffect(() => {
    if (!enableSelection) {
      setSelectedItems([]);
    }
  }, [enableSelection]);

  if (isError) {
    return (
      <Box>
        <Typography color="error" gutterBottom>
          Failed to load chat
        </Typography>
        <Typography variant="body2" gutterBottom>
          {(error as Error).message}
        </Typography>
        <Typography
          variant="body2"
          sx={{ cursor: 'pointer', textDecoration: 'underline' }}
          onClick={() => refetch()}
        >
          Retry
        </Typography>
      </Box>
    );
  }

  return (
    <>
      <Typography variant="h4" gutterBottom>
        {effectiveTitle || `Chat ${chatId.slice(-8)}`}
      </Typography>
      {data && (
        <>
          <Typography variant="body2" color="text.secondary" gutterBottom>
            Created: {new Date(data.createdAt).toLocaleString()}
          </Typography>

          {/* Global Chat-Level Message Filtering Controls */}
          <Paper sx={{ p: 2, mb: 3 }}>
            <ChatMessageFilters
              messages={allMessages}
              enableFilters={enableFilters}
              onEnableFiltersChange={setEnableFilters}
              activeTypeFilters={activeFilters}
              onTypeFiltersChange={setActiveFilters}
              contentFilter={contentFilter}
              onContentFilterChange={setContentFilter}
              title="Global Message Filters"
              size="medium"
              showStatusMessage={true}
            />
          </Paper>

          {/* Chat Statistics */}
          <Accordion sx={{ mb: 3 }}>
            <AccordionSummary expandIcon={<ExpandMore />}>
              <Typography variant="h6">Chat Statistics & Metadata</Typography>
            </AccordionSummary>
            <AccordionDetails>
              <Grid container spacing={2}>
                <Grid gridColumn={{ xs: 12, sm: 6, md: 3 }}>
                  <Card variant="outlined">
                    <CardContent>
                      <Typography variant="h6" color="primary">
                        {stats?.totalTurns || 0}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        Total Turns
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
                <Grid gridColumn={{ xs: 12, sm: 6, md: 3 }}>
                  <Card variant="outlined">
                    <CardContent>
                      <Typography variant="h6" color="primary">
                        {stats?.totalMessages || 0}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        Total Messages
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
                <Grid gridColumn={{ xs: 12, sm: 6, md: 3 }}>
                  <Card variant="outlined">
                    <CardContent>
                      <Typography variant="h6" color="primary">
                        {stats?.totalTokens || 0}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        Total Tokens
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
                <Grid gridColumn={{ xs: 12, sm: 6, md: 3 }}>
                  <Card variant="outlined">
                    <CardContent>
                      <Typography variant="h6" color="primary">
                        {stats?.avgLatency ? Math.round(stats.avgLatency) : 0}ms
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        Avg Latency
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
                {stats?.toolUsage &&
                  Object.keys(stats.toolUsage).length > 0 && (
                    <Grid gridColumn={{ xs: 12 }}>
                      <Typography variant="subtitle1" gutterBottom>
                        Tool Usage
                      </Typography>
                      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
                        {Object.entries(stats.toolUsage).map(
                          ([tool, count]) => (
                            <Chip
                              key={tool}
                              label={`${tool}: ${count}`}
                              size="small"
                              variant="outlined"
                            />
                          ),
                        )}
                      </Box>
                    </Grid>
                  )}
              </Grid>
            </AccordionDetails>
          </Accordion>
        </>
      )}

      {/* Export Controls */}
      {filteredData && (
        <Box
          sx={{ mt: 3, mb: 2, display: 'flex', alignItems: 'center', gap: 2 }}
        >
          <FormControlLabel
            control={
              <Switch
                checked={enableSelection}
                onChange={handleEnableSelectionChange}
              />
            }
            label="Enable Export Selection"
          />
          {enableSelection && (
            <>
              <Button
                size="small"
                variant="outlined"
                onClick={handleSelectAll}
                disabled={selectedItems.length === filteredData.turns.length}
              >
                Select All Turns
              </Button>
              <Button
                size="small"
                variant="outlined"
                onClick={handleClearSelection}
                disabled={selectedItems.length === 0}
              >
                Clear Selection
              </Button>
              <Typography variant="body2" color="text.secondary">
                {selectedItems.length} item
                {selectedItems.length !== 1 ? 's' : ''} selected
              </Typography>
              <ChatExportMenu
                turns={filteredData.turns}
                selectedItems={selectedItems}
                chatTitle={effectiveTitle || undefined}
                chatCreatedAt={data?.createdAt}
              />
            </>
          )}
        </Box>
      )}

      <Box sx={{ mt: 1 }}>
        <Loading loading={isLoading} />
        {!filteredData ? (
          !isLoading && <Typography>No chat found.</Typography>
        ) : filteredData.turns.length === 0 ? (
          <Typography color="text.secondary">
            No messages match the current filters.
          </Typography>
        ) : (
          <VirtualizedChatDisplay
            turns={filteredData.turns}
            height={800}
            enableSelection={enableSelection}
            selectedItems={selectedItems}
            onSelectionChange={setSelectedItems}
            globalFilters={
              enableFilters
                ? { typeFilters: activeFilters, contentFilter }
                : { typeFilters: new Set(), contentFilter: '' }
            }
          />
        )}
      </Box>
    </>
  );
};

/**
 * Chat History Data Hook Module
 * ==================================
 *
 * Purpose:
 *   Provides a single, typed React Query hook (`useChatHistory`) plus its
 *   underlying fetcher (`fetchChatDetails`) for retrieving an entire chat's
 *   conversational history (turns + messages) from the server API route:
 *     GET /api/ai/chat/history/[chatId]
 *
 * Exports:
 *   - fetchChatDetails(chatId): Low-level async function returning `ChatDetails | null`.
 *   - useChatHistory(chatId): High-level React Query hook with loading/error/empty semantics.
 *
 * State Semantics:
 *   - data === undefined  -> Initial load (isLoading true)
 *   - data === null       -> 404 (chat not found) empty state
 *   - isError === true    -> Network / non-404 HTTP failure (error contains detail)
 *
 * Design Decisions:
 *   - Returns `null` for 404 to distinguish "missing resource" from operational failures.
 *   - Uses `cache: 'no-store'` at fetch layer and delegates dedupe / caching to React Query.
 *   - Chooses a modest staleTime (30s) to reduce flicker without drift risk for user-facing chat titles.
 *   - Disables refetchOnWindowFocus to avoid unexpected bandwidth usage and preserve user scroll position.
 *
 * Error Handling Strategy:
 *   - All thrown errors are logged via `LoggedError.isTurtlesAllTheWayDownBaby` with context metadata.
 *   - Consumers should rely on `isError` + `error.message` for UI messaging; avoid parsing thrown error shapes.
 *
 * Performance Considerations:
 *   - Single network round-trip; JSON parsing only transformation.
 *   - Suitable for SSR hydration if integrated higher in tree (currently designed for client usage).
 *
 * Testing Notes:
 *   - Integration tests can mock global `fetch` and assert state transitions driven by React Query.
 *   - For retry flows, sequence `mockRejectedValueOnce` followed by `mockResolvedValueOnce` before triggering refetch.
 *
 * Accessibility / UX:
 *   - Hook supplies granular state flags letting UI implement progressive disclosure (spinner -> retry -> empty -> content).
 *
 * Security:
 *   - Relies on server route authentication (session check); client hook does not embed auth logic.
 *   - Ensure cookies / headers propagate automatically (Next.js fetch wrapper) for protected routes.
 *
 * Usage Example:
 *   const { data, isLoading, isError, error, refetch } = useChatHistory(chatId);
 *   if (isLoading) return <Loading/>;
 *   if (isError) return <ErrorView message={error.message} onRetry={refetch}/>;
 *   if (!data) return <EmptyChat/>; // 404
 *   return <Chat turns={data.turns}/>;
 *
 * Extension Points:
 *   - Add query options (e.g., refetchInterval) by exposing an overload accepting a partial UseQueryOptions.
 *   - Inject suspense boundaries by enabling `suspense: true` (ensure callers prepared for thrown promises).
 *
 * Maintainability Guidelines:
 *   - Keep business logic (title derivation, etc.) out of the data hook; place in presentation component.
 *   - Avoid adding UI concerns (formatting dates) here; return raw canonical data.
 */
import { type ChatDetails } from '@/lib/ai/chat/types';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import { fetch } from '@/lib/nextjs-util/fetch';
import { useQuery, UseQueryResult } from '@tanstack/react-query';

/**
 * Fetch full chat history details for a given chat id.
 *
 * Behavior:
 * - Executes a GET request to `/api/ai/chat/history/{chatId}`.
 * - Returns `null` when a 404 (not found) is received, allowing consumers to distinguish
 *   between an absent chat and an error condition.
 * - Throws for all other non-success HTTP statuses or network faults; React Query (or
 *   direct callers) can then surface error UI / retry affordances.
 *
 * Logging:
 * - All caught errors are passed to `LoggedError.isTurtlesAllTheWayDownBaby` with contextual metadata
 *   before being re-thrown to preserve original stack & semantics.
 *
 * @param chatId Unique identifier of the chat whose history is requested.
 * @returns Parsed `ChatDetails` object or `null` when the chat does not exist.
 * @throws Error on network issues or non-OK, non-404 responses.
 */
async function fetchChatDetails(chatId: string): Promise<ChatDetails | null> {
  try {
    const response = await fetch(
      `/api/ai/chat/history/${encodeURIComponent(chatId)}`,
      {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' },
        cache: 'no-store',
      },
    );

    if (response.status === 404) return null;
    if (!response.ok)
      throw new Error(`API request failed with status ${response.status}`);

    return (await response.json()) as ChatDetails;
  } catch (error) {
    LoggedError.isTurtlesAllTheWayDownBaby(error, {
      log: true,
      context: 'Fetching chat details',
      chatId,
    });
    throw error;
  }
}

/**
 * React Query hook for retrieving a chat's history (turns + messages) with typed
 * loading / error / empty (404) states.
 *
 * Query Key: ['chatDetails', chatId]
 * Cache Strategy:
 *   - `staleTime: 30_000` (30s) prevents jittery refetches after initial load.
 *   - `refetchOnWindowFocus: false` to avoid unexpected network traffic on tab focus.
 *
 * State Semantics:
 *   - `data === undefined` -> initial / loading state (use `isLoading`).
 *   - `data === null` -> chat definitively not found (404).
 *   - `isError` -> fetch failed; `error` contains detail.
 *
 * Usage Pattern:
 *   const { data, isLoading, isError, error, refetch } = useChatHistory(id);
 *   if (isLoading) return <Spinner/>;
 *   if (isError) return <ErrorView message={error.message} onRetry={refetch}/>;
 *   if (!data) return <EmptyChat/>; // 404
 *   return <Chat turns={data.turns}/>;
 *
 * @param chatId Unique chat identifier.
 * @returns React Query result descriptor for the asynchronous fetch.
 */
export function useChatHistory(
  chatId: string,
): UseQueryResult<ChatDetails | null, Error> {
  return useQuery<ChatDetails | null, Error>({
    queryKey: ['chatDetails', chatId],
    queryFn: () => fetchChatDetails(chatId),
    // We want a fresh fetch each mount but still allow quick refetches w/out loading flash.
    staleTime: 30_000,
    refetchOnWindowFocus: false,
  });
}

'use client';
import { JSX, useMemo, useCallback } from 'react';
import { ServerBoundDataGrid } from '@/components/mui/data-grid/server-bound-data-grid';
import siteMap from '@/lib/site-util/url-builder';
import Box from '@mui/material/Box';

import type { MuiEvent } from '@mui/x-internals/types';
import type {
  GridCallbackDetails,
  GridColDef,
  GridRowParams,
} from '@mui/x-data-grid/models';
import { useRouter } from 'next/navigation';
import Link from 'next/link';
import siteBuilder from '@/lib/site-util/url-builder';

/**
 * Chat summary interface matching the API response
 */
interface ChatSummary {
  id: string;
  title: string | null;
  userId: number;
  createdAt: string;
  chatMetadata: object | null;
  totalTokens: number;
  totalMessages: number;
  totalTurns: number;
}

/**
 * Props for the chat grid component
 */
interface ChatGridProps {
  maxHeight?: number | string;
  viewType?: 'user' | 'system';
  onRowDoubleClick?: (
    params: GridRowParams<ChatSummary>,
    event: MuiEvent<React.MouseEvent<HTMLElement, MouseEvent>>,
    details: GridCallbackDetails,
  ) => void;
}

/**
 * Defines the column configuration for the chat history list grid.
 */
const stableColumns: GridColDef<ChatSummary>[] = [
  {
    field: 'title',
    headerName: 'Chat Title',
    editable: false,
    flex: 1,
    renderCell: (params) => {
      const title = params.value || `Chat ${params.row.id.slice(-8)}`;
      return (
        <Link
          href={siteBuilder.messages.chat.page(
            encodeURIComponent(params.row.id),
          )}
          title="Open chat"
          style={{
            // color: '#2563eb',
            textDecoration: 'none',
          }}
          onMouseEnter={(e) =>
            ((e.target as HTMLElement).style.textDecoration = 'underline')
          }
          onMouseLeave={(e) =>
            ((e.target as HTMLElement).style.textDecoration = 'none')
          }
        >
          {title}
        </Link>
      );
    },
  },
  {
    field: 'totalTurns',
    headerName: 'Turns',
    editable: false,
  },
  {
    field: 'totalMessages',
    headerName: 'Messages',
    editable: false,
  },
  {
    field: 'totalTokens',
    headerName: 'Tokens',
    editable: false,
  },
  {
    field: 'createdAt',
    headerName: 'Created',
    editable: false,
    width: 160,
    type: 'date',
    valueGetter: (v: string | Date) => {
      return v ? new Date(v) : v;
    },
    valueFormatter: (v: Date) => {
      if (!v || isNaN(v.getTime())) return '';
      const mm = String(v.getMonth() + 1).padStart(2, '0');
      const dd = String(v.getDate()).padStart(2, '0');
      const yyyy = v.getFullYear();
      return `${mm}/${dd}/${yyyy}`;
    },
  },
];

/**
 * Displays a list of chat histories in a data grid.
 *
 * @param {ChatGridProps} props - Props for configuring the chat data grid.
 * @returns {JSX.Element} The rendered chat list component.
 */
export const ChatList = ({
  maxHeight = undefined,
  viewType = 'user',
  onRowDoubleClick: onRowDoubleClickProps,
  ...props
}: ChatGridProps): JSX.Element => {
  const containerSx = useMemo(
    () => ({
      maxHeight,
    }),
    [maxHeight],
  );
  const { push } = useRouter();

  // Build URL with viewType parameter (only add if not default 'user')
  const gridUrl = useMemo(() => {
    const url = new URL(
      siteMap.api.ai.chat.history().toString(),
      window.location.origin,
    );
    if (viewType !== 'user') {
      url.searchParams.set('viewType', viewType);
    }
    return url.toString();
  }, [viewType]);

  const onRowDoubleClick = useCallback(
    (
      params: GridRowParams<ChatSummary>,
      event: MuiEvent<React.MouseEvent<HTMLElement, MouseEvent>>,
      details: GridCallbackDetails,
    ) => {
      if (onRowDoubleClickProps) {
        onRowDoubleClickProps(params, event, details);
      }
      if (!event.isPropagationStopped()) {
        const chatId = params.row.id;
        if (chatId) {
          push(
            String(
              siteBuilder.messages.chat
                .detail(encodeURIComponent(chatId))
                .toString(),
            ),
          );
        }
      }
    },
    [onRowDoubleClickProps, push],
  );

  return (
    <>
      <Box
        sx={{
          display: 'flex',
          flexDirection: 'column',
          width: '100%',
          ...containerSx,
        }}
      >
        <ServerBoundDataGrid<ChatSummary>
          {...props}
          columns={stableColumns}
          url={gridUrl}
          idColumn="id"
          onRowDoubleClick={onRowDoubleClick}
        />
      </Box>
    </>
  );
};

export default ChatList;

'use client';

/**
 * Module: Virtualized Chat Display
 * -------------------------------------------------------------
 * Highperformance, scrollvirtualized rendering of chat turns/messages.
 *
 * Why virtualization?
 *  - Large chats (hundreds of turns, thousands of messages) can cause DOM bloat,
 *    expensive layout / paint cycles, and sluggish interaction if fully rendered.
 *  - Virtualization keeps only the visible (plus a small overscan buffer) portion
 *    of the chat in the DOM while still preserving accurate scrollbar size and
 *    scroll position semantics.
 *
 * Height Strategy:
 *  - We provide an initial optimistic size estimate per chat turn via the
 *    `estimateSize` callback. This attempts to approximate the total rendered
 *    height of the turn (header, messages, metadata panels, warnings/errors, etc.)
 *    using a markdown + text measurement utility.
 *  - When `ResizeObserver` is available, the virtualization library can refine
 *    the measurement with the actual DOM height (via `measureElement`). This
 *    produces smooth, low-jitter scrolling even for highly variable content.
 *
 * Key Tradeoffs / Assumptions:
 *  - Estimation favors slight overestimation to avoid visual jump when the real
 *    measurement arrives. (Underestimation creates more noticeable layout shifts.)
 *  - We intentionally removed arbitrary maximum height caps so very long AI
 *    responses remain fully accessible.
 *  - Width is derived dynamically from the scroll container (falling back to a
 *    viewport or constant fallback during first render / SSR boundary cases).
 *
 * Accessibility & UX:
 *  - Message and metadata toggles allow users to progressively disclose
 *    diagnostic / system information without penalizing initial load cost.
 *  - Empty state messaging clarifies when a chat has no turns/messages.
 *
 * Performance Notes:
 *  - `overscan` is tuned (currently 3) to balance scroll fluidity and memory
 *    usage. Increase for faster wheels / touchpad momentum; decrease for tight
 *    memory environments.
 *  - Height estimation leverages a shared `textMeasurer` to avoid creating
 *    repeated canvas contexts / DOM nodes.
 *
 * Extension Points:
 *  - Additional permessage adornments (e.g., token usage bars) should factor
 *    their vertical contribution into `estimateSize` for accuracy.
 *  - Alternate rendering modes (collapsing tool / system messages) can be added
 *    behind new toggles  ensure both estimation and actual DOM reflect changes.
 *
 * Error Handling:
 *  - This component assumes `turns` integrity (e.g., arrays present). Defensive
 *    fallbacks (default heights) ensure resilience if partial data arrives.
 */

import React, { useRef, useState, useCallback } from 'react';
import { useVirtualizer } from '@tanstack/react-virtual';
import { Box, Switch, FormControlLabel, FormGroup, Paper } from '@mui/material';
import { ChatTurnDisplay } from './chat-turn-display';
import {
  createTextMeasurer,
  estimateMarkdownHeight,
} from '@/lib/components/ai/height-estimators';
import { ChatTurn } from '@/lib/ai/chat/types';
import type { SelectedChatItem } from '../../lib/ai/chat/export';
import { type MessageType, searchMessageContent } from './chat-message-filters';

/**
 * Fallback container width for virtualized chat display
 */
export const FALLBACK_CONTAINER_WIDTH = 1200;

/**
 * Text measurement utility for estimating heights of chat messages
 */
const textMeasurer = createTextMeasurer();

/**
 * Props for `VirtualizedChatDisplay`.
 */
interface VirtualizedChatDisplayProps {
  /** Ordered collection of turns to render */
  turns: ChatTurn[];
  /** Explicit pixel height of the scroll container (defaults to 600) */
  height?: number;
  /** Enable selection mode for export functionality */
  enableSelection?: boolean;
  /** Currently selected chat items */
  selectedItems?: SelectedChatItem[];
  /** Callback when selection changes */
  onSelectionChange?: (selectedItems: SelectedChatItem[]) => void;
  /** Global message filters to apply to all turns */
  globalFilters?: {
    typeFilters: Set<MessageType>;
    contentFilter: string;
  };
}

/**
 * Virtualized chat transcript component rendering only the visible subset of
 * turns for scalability. Supports optional disclosure of diagnostic metadata
 * (turn properties, message metadata) without compromising baseline performance.
 *
 * Rendering Flow:
 *  1. Derive an estimated height for each turn synchronously (fast path).
 *  2. Virtualizer positions absolute containers within a tall spacer box.
 *  3. Each visible item mounts `ChatTurnDisplay`, after which (when supported)
 *     a `ResizeObserver` refines the actual measured height.
 *
 * Interaction Toggles:
 *  - Show Turn Properties: reveals model parameters + warnings/errors + metadata.
 *  - Show Message Metadata: reveals permessage diagnostic JSON blocks.
 *
 * Performance Considerations:
 *  - Avoid injecting heavy synchronous parsing or large JSON serialization into
 *    the `estimateSize` callback; keep it deterministic and CPUlight.
 *  - For additional data (e.g., token usage charts) remember to adjust both the
 *    estimator and the actual DOM height contributions.
 */
export const VirtualizedChatDisplay: React.FC<VirtualizedChatDisplayProps> = ({
  turns,
  height = 600,
  enableSelection = false,
  selectedItems = [],
  onSelectionChange,
  globalFilters = { typeFilters: new Set(), contentFilter: '' },
}) => {
  const parentRef = useRef<HTMLDivElement>(null);
  const [showTurnProperties, setShowTurnProperties] = useState(false);
  const [showMessageMetadata, setShowMessageMetadata] = useState(false);

  // Estimate size for each turn based on content using improved measurement
  /**
   * Estimate (optimistically) the vertical pixel footprint of a turn.
   *
   * Goals:
   *  - Be fast (no DOM writes, minimal JSON work) to keep scroll perf smooth.
   *  - Slightly overestimate to reduce postmeasurement visual shifts.
   *  - Account for filtering that may hide messages within the turn.
   *
   * Fallback Paths:
   *  - If a turn or container width is unavailable (initial prerender), uses
   *    conservative defaults and a global fallback width.
   *
   * @param index Index of the virtualized row (turn)
   * @returns Estimated height in pixels (never less than the minimum of 150)
   */
  const estimateSize = useCallback(
    (index: number) => {
      const turn = turns[index];
      if (!turn) return 200; // default fallback

      let width = 0;
      // Get container width for accurate measurement
      if (parentRef.current === null) {
        // Handle case where parentRef is not yet available
        if (typeof window !== 'undefined') {
          width = window.innerWidth * 0.9; // Fallback to 90% of viewport width
        } else {
          width = FALLBACK_CONTAINER_WIDTH; // Fallback to a default width
        }
      } else {
        width = parentRef.current.getBoundingClientRect().width;
      }

      // Base size for turn header and card structure
      let totalHeight = 120; // Increased base turn header height to account for chips and spacing

      // Calculate content width accounting for Card padding and margins
      const contentWidth = Math.max(width * 0.85 - 48, 300); // 85% width minus Card padding, min 300px

      // Filter messages based on global filters if active
      const visibleMessages =
        globalFilters.typeFilters.size > 0 || globalFilters.contentFilter.trim()
          ? turn.messages.filter((message) => {
              // Type filter
              const passesTypeFilter =
                globalFilters.typeFilters.size === 0 ||
                globalFilters.typeFilters.has(message.role as MessageType);
              // Content filter
              const passesContentFilter = searchMessageContent(
                message,
                globalFilters.contentFilter,
              );

              return passesTypeFilter && passesContentFilter;
            })
          : turn.messages;

      // If no messages are visible after filtering, return a minimal height
      if (visibleMessages.length === 0) {
        return Math.max(totalHeight + 60, 150); // Just turn header + empty message indicator
      }

      // Measure each visible message content using sophisticated height estimation
      visibleMessages.forEach((message) => {
        if (message.content && message.content.trim()) {
          // Use sophisticated markdown height estimation
          const estimatedHeight = estimateMarkdownHeight(
            message.content,
            contentWidth,
            textMeasurer,
          );

          // Add message container padding and margins
          totalHeight += estimatedHeight + 32; // content height + message container padding
        } else {
          // Base message height for messages without content (tool calls, etc)
          totalHeight += 60; // Increased from 40 to account for message container
        }

        // Add height for optimized content accordion if present and different from main content
        if (
          message.optimizedContent &&
          message.optimizedContent !== message.content
        ) {
          // Add height for accordion header (collapsed state)
          totalHeight += 48; // Accordion header height with padding

          // Add potential expanded content height - we need to be generous here
          // because the accordion can be expanded by the user
          const optimizedContentHeight = estimateMarkdownHeight(
            message.optimizedContent,
            contentWidth,
            textMeasurer,
          );

          // Add space for the optimized content when expanded
          // We estimate for expanded state to avoid content being cut off
          totalHeight += optimizedContentHeight + 24; // content + accordion details padding
        }

        // Add spacing between messages
        totalHeight += 16;
      });

      // Add size if properties are shown
      if (showTurnProperties) {
        totalHeight += 150; // Increased space for model, temperature, latency etc

        // Add space for warnings and errors using proper estimation
        if (turn.warnings?.length) {
          turn.warnings.forEach((warning) => {
            const warningHeight = estimateMarkdownHeight(
              warning,
              contentWidth * 0.9, // Slightly narrower for alerts
              textMeasurer,
            );
            totalHeight += warningHeight + 24; // Alert padding
          });
        }

        if (turn.errors?.length) {
          turn.errors.forEach((error) => {
            const errorHeight = estimateMarkdownHeight(
              error,
              contentWidth * 0.9, // Slightly narrower for alerts
              textMeasurer,
            );
            totalHeight += errorHeight + 24; // Alert padding
          });
        }

        // Add space for metadata if present
        if (turn.metadata) {
          const metadataLines = JSON.stringify(turn.metadata, null, 2).split(
            '\n',
          ).length;
          totalHeight += Math.min(metadataLines * 16, 200); // Cap metadata display at 200px
        }
      }

      // Add space for message metadata display if enabled (only for visible messages)
      if (showMessageMetadata) {
        visibleMessages.forEach((message) => {
          totalHeight += 80; // Base metadata panel height

          // Add space for function call and metadata JSON
          if (message.functionCall) {
            const funcCallLines = JSON.stringify(
              message.functionCall,
              null,
              2,
            ).split('\n').length;
            totalHeight += Math.min(funcCallLines * 16, 200); // Cap at 200px
          }

          if (message.metadata) {
            const msgMetadataLines = JSON.stringify(
              message.metadata,
              null,
              2,
            ).split('\n').length;
            totalHeight += Math.min(msgMetadataLines * 16, 200); // Cap at 200px
          }
        });
      }

      // Add padding for Paper component and margins
      totalHeight += 48; // Card padding and margins

      // Remove artificial cap - let content be as tall as it needs to be
      // Only set a reasonable minimum height
      return Math.max(totalHeight, 150);
    },
    [turns, showTurnProperties, showMessageMetadata, globalFilters],
  );

  /**
   * Virtualization controller from TanStack Virtual handling item measurement,
   * scroll range projection, and overscan buffering.
   */
  const rowVirtualizer = useVirtualizer({
    count: turns.length,
    getScrollElement: () => parentRef.current,
    estimateSize,
    overscan: 3, // Render more items outside visible area for smoother scrolling
    measureElement:
      typeof window !== 'undefined' && window.ResizeObserver
        ? (element) => element?.getBoundingClientRect().height
        : undefined, // Enable dynamic measurement when ResizeObserver is available
  });

  return (
    <Box>
      {/* Controls */}
      <Paper sx={{ p: 2, mb: 2 }}>
        <FormGroup row>
          <FormControlLabel
            control={
              <Switch
                checked={showTurnProperties}
                onChange={(e) => setShowTurnProperties(e.target.checked)}
              />
            }
            label="Show Turn Properties (model, temp, latency, warnings, errors, token usage)"
          />
          <FormControlLabel
            control={
              <Switch
                checked={showMessageMetadata}
                onChange={(e) => setShowMessageMetadata(e.target.checked)}
              />
            }
            label="Show Message Metadata"
          />
        </FormGroup>
      </Paper>

      {/* Virtualized Chat Display */}
      <Box
        ref={parentRef}
        sx={{
          height: `${height}px`,
          overflow: 'auto',
          width: '100%',
        }}
      >
        <Box
          sx={{
            height: `${rowVirtualizer.getTotalSize()}px`,
            width: '100%',
            position: 'relative',
          }}
        >
          {rowVirtualizer.getVirtualItems().map((virtualItem) => {
            const turn = turns[virtualItem.index];

            return (
              <Box
                key={virtualItem.key}
                data-index={virtualItem.index}
                sx={{
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  width: '100%',
                  transform: `translateY(${virtualItem.start}px)`,
                }}
              >
                <ChatTurnDisplay
                  turn={turn}
                  showTurnProperties={showTurnProperties}
                  showMessageMetadata={showMessageMetadata}
                  enableSelection={enableSelection}
                  selectedItems={selectedItems}
                  onSelectionChange={onSelectionChange}
                  globalFilters={globalFilters}
                />
              </Box>
            );
          })}
        </Box>
      </Box>

      {turns.length === 0 && (
        <Paper sx={{ p: 4, textAlign: 'center' }}>
          No messages found in this chat.
        </Paper>
      )}
    </Box>
  );
};

