/**
 * ChunkingLogExporter
 *
 * A LogRecordExporter decorator that inspects log bodies and attributes, splitting
 * oversized string values into multiple synthetic log records to avoid exceeding
 * downstream property limits. Each chunk carries a chunkContextId and ordering
 * information to allow reassembly on the consumer side.
 */
import type {
  ReadableLogRecord,
  LogRecordExporter,
} from '@opentelemetry/sdk-logs';
import type { AnyValueMap } from '@opentelemetry/api-logs';
import type { ExportResult } from '@opentelemetry/core';

export type LogChunkingOptions = {
  /** Maximum characters allowed in any single property or body before chunking */
  maxChunkChars?: number;
  /** If true, keep a truncated preview of original value */
  keepOriginalKey?: boolean;
};

function makeChunkContextId(
  traceId: string | undefined,
  spanId: string | undefined,
  key: string,
): string {
  const seed = `${traceId ?? 'no-trace'}:${spanId ?? 'no-span'}:${key}`;
  let hash = 2166136261;
  for (let i = 0; i < seed.length; i++) {
    hash ^= seed.charCodeAt(i);
    hash +=
      (hash << 1) + (hash << 4) + (hash << 7) + (hash << 8) + (hash << 24);
  }
  return (hash >>> 0).toString(36);
}

export class ChunkingLogExporter implements LogRecordExporter {
  private readonly inner: LogRecordExporter;
  private readonly maxChunkChars: number;
  private readonly keepOriginalKey: boolean;

  constructor(inner: LogRecordExporter, opts: LogChunkingOptions = {}) {
    this.inner = inner;
    this.maxChunkChars = opts.maxChunkChars ?? 8000;
    this.keepOriginalKey = opts.keepOriginalKey ?? false;
  }

  export(
    records: ReadableLogRecord[],
    resultCallback: (result: ExportResult) => void,
  ): void {
    for (const rec of records) {
      const base = rec as unknown as {
        attributes: AnyValueMap;
        body?: unknown;
      };
      const attrs = (base.attributes ?? {}) as Record<string, unknown>;
      const traceId = attrs['trace_id'] as string | undefined;
      const spanId = attrs['span_id'] as string | undefined;

      // Body chunking (add chunk_* properties on attributes)
      if (
        typeof base.body === 'string' &&
        base.body.length > this.maxChunkChars
      ) {
        const s = base.body;
        const totalChunks = Math.ceil(s.length / this.maxChunkChars);
        const ctxId = makeChunkContextId(traceId, spanId, 'body');
        attrs['body_chunked'] = 'true';
        attrs['body_totalChunks'] = String(totalChunks);
        attrs['body_chunkContextId'] = ctxId;
        for (let i = 0; i < totalChunks; i++) {
          const chunk = s.slice(
            i * this.maxChunkChars,
            (i + 1) * this.maxChunkChars,
          );
          attrs[`body_chunk_${i + 1}`] = chunk;
        }
        base.body = this.keepOriginalKey
          ? s.slice(0, this.maxChunkChars)
          : '[chunked]';
      }

      // Prepare a snapshot of original entries to avoid iterating over keys we add below
      const originalEntries = Object.entries(attrs);
      const isChunkMetaKey = (k: string) =>
        k === 'body_chunked' ||
        k === 'body_totalChunks' ||
        k === 'body_chunkContextId' ||
        k.startsWith('body_chunk_') ||
        k.endsWith('_chunked') ||
        k.endsWith('_totalChunks') ||
        k.endsWith('_chunkContextId') ||
        /_chunk_\d+$/.test(k);

      // Attribute chunking (split into key_chunk_* properties and add metadata)
      for (const [key, rawVal] of originalEntries) {
        if (isChunkMetaKey(key)) continue; // don't process metadata keys we add
        if (rawVal == null) continue;
        if (typeof key !== 'string') continue;
        let s: string;
        if (typeof rawVal === 'string') s = rawVal;
        else {
          try {
            s = JSON.stringify(rawVal);
          } catch {
            s = String(rawVal);
          }
        }
        if (s.length > this.maxChunkChars) {
          const totalChunks = Math.ceil(s.length / this.maxChunkChars);
          const ctxId = makeChunkContextId(traceId, spanId, key);
          attrs[`${key}_chunked`] = 'true';
          attrs[`${key}_totalChunks`] = String(totalChunks);
          attrs[`${key}_chunkContextId`] = ctxId;
          for (let i = 0; i < totalChunks; i++) {
            const chunk = s.slice(
              i * this.maxChunkChars,
              (i + 1) * this.maxChunkChars,
            );
            attrs[`${key}_chunk_${i + 1}`] = chunk;
          }
          if (!this.keepOriginalKey) {
            delete (attrs as Record<string, unknown>)[key];
          } else {
            (attrs as Record<string, unknown>)[key] = s.slice(
              0,
              this.maxChunkChars,
            );
          }
        }
      }
      // write back mutated attributes
      (base.attributes as unknown as Record<string, unknown>) = attrs;
    }
    this.inner.export(records, resultCallback);
  }

  async shutdown(): Promise<void> {
    await this.inner.shutdown?.();
  }
}

export default ChunkingLogExporter;

/**
 * ChunkingTraceExporter
 *
 * A SpanExporter decorator that inspects all span attributes and event attributes
 * and splits any oversized string values into multiple chunk events so they can
 * be safely ingested by downstream exporters (e.g., Azure Monitor / App Insights)
 * that enforce strict per-property size limits.
 *
 * The exporter augments the original span/event attributes with metadata:
 * - `${key}_chunked` = 'true' when chunking occurs
 * - `${key}_totalChunks` = total number of chunks
 *
 * For each chunk, an event is appended to the span with attributes:
 * - chunkContextId: stable identifier tying all chunks from the same source together
 * - chunkKey: the original attribute key
 * - chunkIndex: 1-based index of the chunk
 * - totalChunks: total number of chunks
 * - chunk: the chunk text
 *
 * Chunk context id is derived from traceId/spanId/source/key to enable reassembly
 * on the downstream side.
 */
import type {
  ReadableSpan,
  SpanExporter,
  TimedEvent,
} from '@opentelemetry/sdk-trace-base';
import type { ExportResult } from '@opentelemetry/core';
import { hrTime } from '@opentelemetry/core';
import type { Attributes } from '@opentelemetry/api';

export type ChunkingTraceOptions = {
  /** Maximum characters allowed in any single attribute value before chunking */
  maxChunkChars?: number;
  /** Name suffix for generated chunk events */
  eventName?: string;
  /** If true, keep a truncated preview of original value under the same key */
  keepOriginalKey?: boolean;
};

/**
 * Compute a compact, stable chunkContextId from span context and source/key.
 * Uses a small, non-cryptographic hash to avoid heavy dependencies.
 */
function makeChunkContextId(
  traceId: string,
  spanId: string,
  source: string,
  key: string,
): string {
  const seed = `${traceId}:${spanId}:${source}:${key}`;
  let hash = 5381;
  for (let i = 0; i < seed.length; i++) {
    hash = ((hash << 5) + hash) ^ seed.charCodeAt(i);
  }
  return Math.abs(hash).toString(36);
}

type Mutable<T> = { -readonly [P in keyof T]: T[P] };

export class ChunkingTraceExporter implements SpanExporter {
  private readonly inner: SpanExporter;
  private readonly maxChunkChars: number;
  private readonly eventName: string;
  private readonly keepOriginalKey: boolean;

  constructor(inner: SpanExporter, opts: ChunkingTraceOptions = {}) {
    this.inner = inner;
    this.maxChunkChars = opts.maxChunkChars ?? 8000;
    this.eventName = opts.eventName ?? 'chunk';
    this.keepOriginalKey = opts.keepOriginalKey ?? false;
  }

  export(
    spans: ReadableSpan[],
    resultCallback: (result: ExportResult) => void,
  ): void {
    const out: ReadableSpan[] = [];
    for (const span of spans) {
      const mSpan = span as unknown as Mutable<ReadableSpan> & {
        events?: TimedEvent[];
      };
      const sc = span.spanContext();
      const traceId: string = sc?.traceId ?? 'unknown-trace';
      const spanId: string = sc?.spanId ?? 'unknown-span';

      // Snapshot original events and collect chunk events separately to avoid mutation during iteration
      const originalEvents: TimedEvent[] = Array.isArray(mSpan.events)
        ? [...mSpan.events]
        : [];
      const chunkEvents: TimedEvent[] = [];

      // Clone attributes and events to avoid mutating original references
      const newAttrs = this.chunkAttributes(
        (mSpan.attributes ?? {}) as Attributes,
        chunkEvents,
        traceId,
        spanId,
        'attributes',
      );

      const newEvents: TimedEvent[] = [];
      for (const ev of originalEvents) {
        const evCopy: Mutable<TimedEvent> = { ...ev } as Mutable<TimedEvent>;
        evCopy.attributes = this.chunkAttributes(
          (ev.attributes ?? {}) as Attributes,
          chunkEvents,
          traceId,
          spanId,
          ev.name ?? 'event',
        ) as Attributes;
        newEvents.push(evCopy);
      }

      if (chunkEvents.length) {
        newEvents.push(...chunkEvents);
      }

      // Preserve instance methods and internals by using the original span as prototype
      const spanCopy: Mutable<ReadableSpan> = Object.create(
        span,
      ) as Mutable<ReadableSpan>;
      Object.defineProperty(spanCopy, 'attributes', {
        value: newAttrs as Attributes,
        enumerable: true,
        configurable: true,
        writable: true,
      });
      Object.defineProperty(spanCopy, 'events', {
        value: newEvents,
        enumerable: true,
        configurable: true,
        writable: true,
      });
      out.push(spanCopy as ReadableSpan);
    }
    this.inner.export(out, resultCallback);
  }

  private chunkAttributes(
    attrs: Attributes,
    chunkEventsOut: TimedEvent[],
    traceId: string,
    spanId: string,
    sourceName: string,
  ): Attributes {
    const result: Record<string, unknown> = {
      ...(attrs as Record<string, unknown>),
    };
    for (const [key, rawVal] of Object.entries(attrs)) {
      if (rawVal == null) continue;
      let str: string;
      if (typeof rawVal === 'string') {
        str = rawVal;
      } else {
        try {
          str = JSON.stringify(rawVal);
        } catch {
          str = String(rawVal);
        }
      }
      if (str.length > this.maxChunkChars) {
        const totalChunks = Math.ceil(str.length / this.maxChunkChars);
        const chunkContextId = makeChunkContextId(
          traceId,
          spanId,
          sourceName,
          key,
        );
        result[`${key}_chunked`] = 'true';
        result[`${key}_totalChunks`] = String(totalChunks);
        if (!this.keepOriginalKey) {
          delete result[key];
        } else {
          result[key] = str.slice(0, this.maxChunkChars);
        }
        for (let i = 0; i < totalChunks; i++) {
          const chunk = str.slice(
            i * this.maxChunkChars,
            (i + 1) * this.maxChunkChars,
          );
          chunkEventsOut.push({
            name: `${sourceName}/${this.eventName}`,
            time: hrTime(),
            attributes: {
              chunkContextId,
              chunkKey: key,
              chunkIndex: String(i + 1),
              totalChunks: String(totalChunks),
              chunk,
            },
          });
        }
      }
    }
    return result as Attributes;
  }

  async shutdown(): Promise<void> {
    await this.inner.shutdown();
  }

  async forceFlush(): Promise<void> {
    if (
      typeof (this.inner as unknown as { forceFlush?: () => Promise<void> })
        .forceFlush === 'function'
    ) {
      await (
        this.inner as unknown as { forceFlush: () => Promise<void> }
      ).forceFlush();
    }
  }
}

export default ChunkingTraceExporter;

export default class ClickCallbackProcessor {
  /**
   * Function to override the default pageName capturing behavior.
   */
  pageName(): string {
    return window.document.title;
  }
  /**
   * A callback function to augument the default pageTags collected during pageAction event.
  pageActionPageTags?: (element?: Element) => IPageTags;
   */
  /**
   * A callback function to populate customized contentName.
   */
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  contentName(element?: any, useDefaultContentName?: boolean) 
  {
    if (!element) {
      return '';
    }
    let check = element.getAttribute('data-id') ?? element.getAttribute('id') ?? element.getAttribute('name')
      ?? element.getAttribute('aria-label') ?? element.getAttribute('title');
    if (check) {
      return check;
    }
    const labelledBy = element.getAttribute('aria-labelledby');
    if (labelledBy) {
      const labelledElement = document.getElementById(labelledBy);
      if (labelledElement) {
        check = this.contentName(labelledElement, useDefaultContentName);
        if (check) {
          return check;
        } 
      }
    } 
    check = element.getAttribute('data-testid');
    const result = check || element.textContent?.trim() || '';
    return result;
  }
}

import { aiModelFactory } from '@/lib/ai/aiModelFactory';
import { getCaseFileDocument } from '@/lib/ai/tools';
import { ClientTimelineAgent } from './agent';
import {
  TimelineAgentProps,
  DocumentMetadata,
  TimelineSummary,
  ProcessingResult,
  ComplianceRating,
  GlobalMetadata,
  SerializedTimelineAgent,
} from './types';
import { type AiLanguageModelType, generateChatId } from '@/lib/ai/core';
import { drizDb } from '@/lib/drizzle-db';
import { setupDefaultTools } from '@/lib/ai/mcp/providers';
import { NextRequest } from 'next/server';
import {
  type ChatHistoryContext,
  createAgentHistoryContext,
  wrapChatHistoryMiddleware,
} from '@/lib/ai/middleware/chat-history';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import type { ToolProviderSet } from '@/lib/ai/mcp/types';
