import { APICallError } from 'ai';
import { isError } from '@/lib/react-util/utility-methods';
import type { RetryErrorInfo } from './types';

/**
 * Extracts retry-related error information from a given error object.
 *
 * This function inspects the provided error and attempts to determine if it is a retryable API call error,
 * specifically checking for HTTP 429 (Too Many Requests) errors and extracting the `Retry-After` header if present.
 * It recursively traverses nested error properties (`cause`, `lastError`, `error`) to find retryable errors.
 *
 * @param error - The error object to inspect. Can be of any type.
 * @returns A `RetryErrorInfo` object containing details about whether the error is retryable,
 *          the retry delay (if applicable), and the original error. If the input is not an error,
 *          returns an object indicating no error information is available.
 */
export const getRetryErrorInfo = (
  error: unknown,
): RetryErrorInfo | undefined => {
  // Absolutely must be an error to have retry error info
  if (isError(error)) {
    // Also really need to be an APICallError
    if (APICallError.isInstance(error)) {
      if (error.statusCode === 429) {
        if (error.responseHeaders) {
          const retryAfterHeader = parseInt(
            error.responseHeaders['retry-after'] ?? '60',
          );
          const retryAfter = isNaN(retryAfterHeader) ? 60 : retryAfterHeader;
          return {
            isError: true,
            isRetry: true,
            error,
            retryAfter,
          };
        }
      }
    }
    // If not, there's a chance we have an error nested inside us
    if (error.cause) {
      const cause = getRetryErrorInfo(error.cause);
      if (cause?.isRetry) {
        return cause;
      }
    }
    if ('lastError' in error) {
      const lastError = getRetryErrorInfo(error.lastError);
      if (lastError?.isRetry) {
        return lastError;
      }
    }
    if ('error' in error) {
      const errorInfo = getRetryErrorInfo(error.error);
      if (errorInfo?.isRetry) {
        return errorInfo;
      }
    }
    // Otherwise, we're just a boring old error
    return {
      isError: true,
      isRetry: false,
      error,
    };
  }
  // The only way we could possibly be a retry from here is if we are a non-null / undefined value with a child property named 'error'.
  if (error && typeof error === 'object' && 'error' in error) {
    const errorInfo = getRetryErrorInfo(error.error);
    if (errorInfo?.isError || errorInfo?.isRetry) {
      return errorInfo;
    }
  }
  // If we get here, we have no retry error info or error info at all
  return {
    isError: false,
  };
};

/**
 * Chat Export Utilities
 *
 * Provides functions to export selected chat messages to various formats:
 * - CSV: Tabular format with columns for timestamp, role, content, etc.
 * - Markdown: Structured text format with headers and timestamps
 */

import type { ChatMessage, ChatTurn } from '@/lib/ai/chat/types';

export interface SelectedChatItem {
  type: 'turn' | 'message';
  turnId: number;
  messageId?: number; // Only set when type is 'message'
}

/**
 * Export selected chat items to CSV format
 */
export function exportToCsv(
  turns: ChatTurn[],
  selectedItems: SelectedChatItem[],
  chatTitle?: string,
): void {
  const messages = getSelectedMessages(turns, selectedItems);

  if (messages.length === 0) {
    throw new Error('No messages selected for export');
  }

  const csvContent = generateCsvContent(messages);
  const filename = `chat-export-${chatTitle ? sanitizeFilename(chatTitle) : 'untitled'}-${new Date().toISOString().slice(0, 10)}.csv`;

  downloadFile(csvContent, filename, 'text/csv');
}

/**
 * Export selected chat items to Markdown format
 */
export function exportToMarkdown(
  turns: ChatTurn[],
  selectedItems: SelectedChatItem[],
  chatTitle?: string,
  chatCreatedAt?: string,
): void {
  const messages = getSelectedMessages(turns, selectedItems);

  if (messages.length === 0) {
    throw new Error('No messages selected for export');
  }

  const markdownContent = generateMarkdownContent(
    messages,
    chatTitle,
    chatCreatedAt,
  );
  const filename = `chat-export-${chatTitle ? sanitizeFilename(chatTitle) : 'untitled'}-${new Date().toISOString().slice(0, 10)}.md`;

  downloadFile(markdownContent, filename, 'text/markdown');
}

/**
 * Get all messages that match the selected items
 */
const getSelectedMessages = (
  turns: ChatTurn[],
  selectedItems: SelectedChatItem[],
): ChatMessage[] => {
  const messages: ChatMessage[] = [];

  for (const item of selectedItems) {
    const turn = turns.find((t) => t.turnId === item.turnId);
    if (!turn) continue;

    if (item.type === 'turn') {
      // Add all messages from the turn
      messages.push(...turn.messages);
    } else if (item.type === 'message' && item.messageId !== undefined) {
      // Add specific message
      const message = turn.messages.find((m) => m.messageId === item.messageId);
      if (message) {
        messages.push(message);
      }
    }
  }

  // Sort by turn ID and message order for consistent output
  return messages.sort((a, b) => {
    if (a.turnId !== b.turnId) {
      return a.turnId - b.turnId;
    }
    return a.messageOrder - b.messageOrder;
  });
};

/**
 * Generate CSV content from messages
 */
const generateCsvContent = (messages: ChatMessage[]): string => {
  const headers = [
    'Turn ID',
    'Message ID',
    'Role',
    'Content',
    'Tool Name',
    'Message Order',
    'Provider ID',
  ];

  const rows = messages.map((message) => [
    message.turnId.toString(),
    message.messageId.toString(),
    message.role,
    escapeCsvField(message.content || ''),
    message.toolName || '',
    message.messageOrder.toString(),
    message.providerId || '',
  ]);

  const csvLines = [headers.join(','), ...rows.map((row) => row.join(','))];

  return csvLines.join('\n');
};

/**
 * Generate Markdown content from messages
 */
const generateMarkdownContent = (
  messages: ChatMessage[],
  chatTitle?: string,
  chatCreatedAt?: string,
): string => {
  const lines: string[] = [];

  // Header
  lines.push(`# Chat Export: ${chatTitle || 'Untitled Chat'}`);
  lines.push('');

  if (chatCreatedAt) {
    lines.push(`**Created:** ${new Date(chatCreatedAt).toLocaleString()}`);
    lines.push('');
  }

  lines.push(`**Exported:** ${new Date().toLocaleString()}`);
  lines.push(`**Total Messages:** ${messages.length}`);
  lines.push('');
  lines.push('---');
  lines.push('');

  // Group messages by turn for better structure
  const turnGroups = new Map<number, ChatMessage[]>();
  for (const message of messages) {
    if (!turnGroups.has(message.turnId)) {
      turnGroups.set(message.turnId, []);
    }
    turnGroups.get(message.turnId)!.push(message);
  }

  // Generate content for each turn
  for (const [turnId, turnMessages] of Array.from(turnGroups.entries()).sort(
    ([a], [b]) => a - b,
  )) {
    lines.push(`## Turn ${turnId}`);
    lines.push('');

    for (const message of turnMessages.sort(
      (a, b) => a.messageOrder - b.messageOrder,
    )) {
      // Message header
      let messageHeader = `**${capitalizeRole(message.role)}**`;
      if (message.toolName) {
        messageHeader += ` (Tool: ${message.toolName})`;
      }
      lines.push(messageHeader);
      lines.push('');

      // Message content
      if (message.content) {
        // Handle different content types
        if (typeof message.content === 'string') {
          lines.push(message.content);
        } else {
          lines.push('```json');
          lines.push(JSON.stringify(message.content, null, 2));
          lines.push('```');
        }
      } else {
        lines.push('*No content*');
      }

      lines.push('');
      lines.push('---');
      lines.push('');
    }
  }

  return lines.join('\n');
};

/**
 * Escape a field for CSV format
 */
const escapeCsvField = (field: string): string => {
  // If the field contains comma, newline, or quote, wrap in quotes and escape internal quotes
  if (field.includes(',') || field.includes('\n') || field.includes('"')) {
    return `"${field.replace(/"/g, '""')}"`;
  }
  return field;
};

/**
 * Capitalize the first letter of a role
 */
const capitalizeRole = (role: string): string => {
  return role.charAt(0).toUpperCase() + role.slice(1);
};

/**
 * Sanitize filename by removing invalid characters
 */
const sanitizeFilename = (filename: string): string => {
  return filename.replace(/[<>:"/\\|?*]/g, '-').replace(/\s+/g, '-');
};

/**
 * Download a file with the given content
 */
const downloadFile = (
  content: string,
  filename: string,
  mimeType: string,
): void => {
  const blob = new Blob([content], { type: mimeType });
  const url = URL.createObjectURL(blob);

  const link = document.createElement('a');
  link.href = url;
  link.download = filename;
  link.style.display = 'none';

  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);

  URL.revokeObjectURL(url);
};

/**
 * Chat History Utilities
 * ------------------------------------------------------------------
 * Provides a helper for securely retrieving minimal chat metadata while
 * enforcing ownership authorization. The function performs a single
 * database lookup (lazy-initializing the Drizzle connection via
 * `drizDbWithInit`) and then validates that the requesting user is the
 * owner of the chat before returning any information.
 *
 * Returned data is intentionally minimal (currently only the title) to
 * avoid leaking potentially sensitive chat content or internal fields
 * through unauthorized access attempts.
 *
 * SECURITY MODEL
 *  - Authorization is owner-based: a chat is accessible only when
 *    `signedInUserId === ownerUserId` as evaluated by `isUserAuthorized`.
 *  - If the user is not authorized or the chat does not exist the function
 *    returns `{ ok: false }` without throwing, enabling simple branch
 *    handling in calling server components / route handlers.
 *
 * DESIGN NOTES
 *  - Returns a discriminated union-ish shape (`ok: true|false`) instead of
 *    throwing on not-found/unauthorized to keep UI logic straightforward.
 *  - Omits `title` when empty/null by converting to `undefined` so callers
 *    can rely on simple truthiness checks for display logic or fallbacks.
 *
 * EXTENSIBILITY
 *  To expose more public metadata, append fields inside the `columns`
 *  selection and the returned object under the authorized branchpreserving
 *  the guard so unauthorized requests still receive only `{ ok: false }`.
 */
import { drizDbWithInit } from '@/lib/drizzle-db';
import { isUserAuthorized } from '@/lib/site-util/auth';

/**
 * Fetch chat metadata (currently title) if the requesting user owns the chat.
 *
 * @param params.chatId Unique identifier of the chat record.
 * @param params.userId Authenticated (signed-in) user ID performing the request.
 * @returns An object:
 *  - `{ ok: true, title?: string }` when the chat exists and user is authorized.
 *  - `{ ok: false }` when chat missing or unauthorized.
 *
 * @example
 * ```ts
 * const details = await getChatDetails({ chatId: 'abc123', userId: 42 });
 * if (details.ok) {
 *   console.log(details.title ?? '(untitled)');
 * } else {
 *   // handle not-found or unauthorized
 * }
 * ```
 */
export const getChatDetails = async ({
  chatId,
  userId,
}: {
  chatId: string;
  userId: number;
}) => {
  const chat = await drizDbWithInit((db) =>
    db.query.chats.findFirst({
      columns: {
        id: true,
        userId: true,
        title: true,
      },
      where: (chat, { eq }) => eq(chat.id, chatId),
    }),
  );
  return chat &&
    (await isUserAuthorized({
      signedInUserId: userId,
      ownerUserId: chat.userId,
    }))
    ? {
        ok: true,
        title: !chat.title ? undefined : chat.title,
      }
    : {
        ok: false,
      };
};

