/**
 * @fileoverview MCP Tool Cache Management Utilities
 * Provides administrative functions for monitoring and managing the MCP tool cache.
 *
 * @module tool-cache-admin
 * @version 1.0.0
 */

import { getToolCache } from './tool-cache';
import { log } from '@/lib/logger';

/**
 * Cache administration utilities
 */
export class MCPToolCacheAdmin {
  private static toolCache = getToolCache();

  /**
   * Displays comprehensive cache statistics
   */
  static async showStats(): Promise<void> {
    try {
      const stats = await this.toolCache.getStats();

      log((l) =>
        l.info('MCP Tool Cache Statistics:', {
          memoryEntries: stats.memorySize,
          redisKeys: stats.redisKeys,
          hitRate: stats.hitRate
            ? `${(stats.hitRate * 100).toFixed(1)}%`
            : 'N/A',
        }),
      );
    } catch (error) {
      log((l) => l.error('Failed to retrieve cache statistics:', error));
    }
  }

  /**
   * Clears all cached tools (use with caution)
   */
  static async clearCache(): Promise<void> {
    try {
      await this.toolCache.clearAll();
      log((l) => l.info('All MCP tool caches cleared'));
    } catch (error) {
      log((l) => l.error('Failed to clear cache:', error));
    }
  }

  /**
   * Warm up cache by pre-loading tools for common configurations
   * @param commonConfigs Array of frequently used MCP server configurations
   */
  static async warmCache(
    commonConfigs: Array<{ url: string; allowWrite?: boolean }>,
  ): Promise<void> {
    log((l) =>
      l.info(
        `Warming MCP tool cache for ${commonConfigs.length} configurations...`,
      ),
    );

    const { toolProviderFactory } = await import('../providers');

    const warmupPromises = commonConfigs.map(async (config) => {
      try {
        const provider = await toolProviderFactory(config);
        const tools = provider.tools;
        await provider.dispose();

        log((l) =>
          l.debug(`Cache warmed for ${config.url}`, {
            toolCount: Object.keys(tools).length,
          }),
        );
      } catch (error) {
        log((l) => l.warn(`Cache warmup failed for ${config.url}:`, error));
      }
    });

    await Promise.allSettled(warmupPromises);
    log((l) => l.info('MCP tool cache warmup completed'));
  }

  /**
   * Health check for cache system
   */
  static async healthCheck(): Promise<{
    healthy: boolean;
    details: {
      memoryCache: boolean;
      redisCache: boolean;
      stats?: {
        memorySize: number;
        redisKeys: number;
        hitRate?: number;
      };
    };
  }> {
    const details = {
      memoryCache: false,
      redisCache: false,
      stats: undefined as
        | {
            memorySize: number;
            redisKeys: number;
            hitRate?: number;
          }
        | undefined,
    };

    try {
      // Test memory cache
      details.memoryCache = true;

      // Test Redis cache by getting stats
      const stats = await this.toolCache.getStats();
      details.redisCache = stats.redisKeys >= 0; // Redis accessible if we can get key count
      details.stats = stats;

      const healthy = details.memoryCache && details.redisCache;

      return { healthy, details };
    } catch (error) {
      log((l) => l.error('Cache health check failed:', error));
      return { healthy: false, details };
    }
  }
}

/**
 * Environment variable configuration for cache tuning
 */
export const getCacheEnvConfig = () => ({
  MCP_CACHE_TTL: parseInt(process.env.MCP_CACHE_TTL || '86400'), // 24 hours default
  MCP_CACHE_MAX_MEMORY: parseInt(process.env.MCP_CACHE_MAX_MEMORY || '100'), // 100 entries default
  MCP_CACHE_ENABLED: process.env.MCP_CACHE_ENABLED !== 'false', // Enabled by default
  MCP_CACHE_PREFIX: process.env.MCP_CACHE_PREFIX || 'mcp:tools',
});

/**
 * Middleware function to automatically warm cache on startup
 */
export const initializeMCPCache = async () => {
  const config = getCacheEnvConfig();

  if (!config.MCP_CACHE_ENABLED) {
    log((l) => l.info('MCP tool caching disabled via environment variable'));
    return;
  }

  try {
    const healthCheck = await MCPToolCacheAdmin.healthCheck();

    if (healthCheck.healthy) {
      log((l) =>
        l.info(
          'MCP tool cache system initialized successfully',
          healthCheck.details,
        ),
      );
    } else {
      log((l) =>
        l.warn(
          'MCP tool cache system partially unavailable',
          healthCheck.details,
        ),
      );
    }
  } catch (error) {
    log((l) => l.error('MCP tool cache initialization failed:', error));
  }
};

/**
 * @fileoverview MCP Tool Cache System
 * Provides efficient caching of MCP server tool definitions with Redis primary storage
 * and in-memory fallback for high-performance tool discovery.
 *
 * @module tool-cache
 * @version 1.0.0
 */

import { createHash } from 'crypto';
import { ToolSet } from 'ai';
import { getRedisClient } from '@/lib/ai/middleware/cacheWithRedis/redis-client';
import { log } from '@/lib/logger';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import type { ToolProviderFactoryOptions } from '../types';
import z from 'zod';

/**
 * Cache entry structure for MCP tools
 */
type ToolCacheEntry = {
  tools: ToolSet;
  timestamp: number;
  serverCapabilities?: string;
};

type TypedToolCacheEntry<TOOLS extends ToolSet> = ToolCacheEntry & {
  tools: TOOLS;
};

type SchemaFieldEnvelope = {
  __zerialize__schemaField: true;
  serialized: string;
};

/**
 * Configuration for tool caching behavior
 */
interface ToolCacheConfig {
  /** Default TTL in seconds (24 hours) */
  defaultTtl: number;
  /** Maximum in-memory cache size */
  maxMemoryEntries: number;
  /** Key prefix for Redis keys */
  keyPrefix: string;
}

const DEFAULT_CONFIG: ToolCacheConfig = {
  defaultTtl: 24 * 60 * 60, // 24 hours
  maxMemoryEntries: 100,
  keyPrefix: 'mcp:tools',
};

/**
 * In-memory LRU cache for fastest access with TTL awareness
 */
class MemoryToolCache {
  private cache = new Map<string, ToolCacheEntry>();
  private accessOrder = new Map<string, number>();
  private accessCounter = 0;
  private ttlTimers = new Map<string, NodeJS.Timeout>();

  constructor(
    private maxSize: number,
    private defaultTtl: number,
  ) {}

  get(key: string): ToolCacheEntry | null {
    const entry = this.cache.get(key);
    if (entry) {
      this.accessOrder.set(key, ++this.accessCounter);
      return entry;
    }
    return null;
  }

  set(key: string, entry: ToolCacheEntry, ttl?: number): void {
    // Evict oldest if at capacity
    if (this.cache.size >= this.maxSize && !this.cache.has(key)) {
      const oldestKey = this.findOldestKey();
      if (oldestKey) {
        this.removeEntry(oldestKey);
      }
    }

    // Clear existing timer if updating entry
    this.clearTtlTimer(key);

    this.cache.set(key, entry);
    this.accessOrder.set(key, ++this.accessCounter);

    // Set TTL timer for automatic expiration
    const effectiveTtl = ttl || this.defaultTtl;
    const timer = setTimeout(() => {
      this.removeEntry(key);
    }, effectiveTtl * 1000);

    this.ttlTimers.set(key, timer);
  }

  private findOldestKey(): string | null {
    let oldestKey: string | null = null;
    let oldestAccess = Infinity;

    for (const [key, access] of this.accessOrder) {
      if (access < oldestAccess) {
        oldestAccess = access;
        oldestKey = key;
      }
    }

    return oldestKey;
  }

  private removeEntry(key: string): void {
    this.cache.delete(key);
    this.accessOrder.delete(key);
    this.clearTtlTimer(key);
  }

  private clearTtlTimer(key: string): void {
    const timer = this.ttlTimers.get(key);
    if (timer) {
      clearTimeout(timer);
      this.ttlTimers.delete(key);
    }
  }

  invalidateKey(key: string): void {
    this.removeEntry(key);
  }

  clear(): void {
    // Clear all timers first
    for (const timer of this.ttlTimers.values()) {
      clearTimeout(timer);
    }

    this.cache.clear();
    this.accessOrder.clear();
    this.ttlTimers.clear();
    this.accessCounter = 0;
  }

  size(): number {
    return this.cache.size;
  }
}

/**
 * Comprehensive MCP Tool Cache with Redis primary and memory fallback
 */
export class MCPToolCache {
  private memoryCache: MemoryToolCache;
  private config: ToolCacheConfig;
  private redisSubscriber?: Awaited<ReturnType<typeof getRedisClient>>;

  constructor(config: Partial<ToolCacheConfig> = {}) {
    this.config = { ...DEFAULT_CONFIG, ...config };
    this.memoryCache = new MemoryToolCache(
      this.config.maxMemoryEntries,
      this.config.defaultTtl,
    );
    // Setup Redis subscription asynchronously (non-blocking)
    this.setupRedisInvalidationSubscription().catch((error) => {
      log((l) =>
        l.warn('Failed to initialize Redis keyspace notifications:', error),
      );
    });
  }

  /**
   * Creates a cache key from MCP server configuration
   */
  private createCacheKey(options: ToolProviderFactoryOptions): string {
    // Sort headers for consistent hashing
    const headersStr = options.headers
      ? JSON.stringify(Object.entries(options.headers).sort())
      : '';

    const headersHash = createHash('sha256')
      .update(headersStr)
      .digest('hex')
      .substring(0, 16);

    // Create URL hash for shorter keys
    const urlHash = createHash('sha256')
      .update(options.url)
      .digest('hex')
      .substring(0, 16);

    const accessLevel = options.allowWrite ? 'rw' : 'ro';

    return `${this.config.keyPrefix}:${urlHash}:${headersHash}:${accessLevel}`;
  }

  /**
   * Retrieves cached tools with multi-level fallback
   */
  async getCachedTools<TOOLS extends ToolSet = ToolSet>(
    options: ToolProviderFactoryOptions,
  ): Promise<TOOLS | null> {
    const cacheKey = this.createCacheKey(options);

    try {
      // Level 1: Memory cache (fastest)
      const memoryEntry = this.memoryCache.get(cacheKey);
      if (memoryEntry && this.isEntryValid(memoryEntry)) {
        log((l) => l.debug(`MCP tools cache hit (memory): ${cacheKey}`));
        return memoryEntry.tools as TOOLS;
      }

      // Level 2: Redis cache
      const redis = await getRedisClient();
      const cachedData = await redis.get(cacheKey);

      if (cachedData) {
        try {
          const entry = deserializedCacheEntry<TOOLS>(cachedData);
          if (entry && this.isEntryValid(entry)) {
            // Populate memory cache for next access with remaining TTL
            const remainingTtl = Math.max(
              0,
              this.config.defaultTtl -
                Math.floor((Date.now() - entry.timestamp) / 1000),
            );
            this.memoryCache.set(cacheKey, entry, remainingTtl);
            log((l) => l.debug(`MCP tools cache hit (Redis): ${cacheKey}`));
            return entry.tools;
          }
        } catch (parseError) {
          log((l) =>
            l.warn('Failed to parse cached MCP tools', {
              cacheKey,
              error: parseError,
            }),
          );
        }
      }

      log((l) => l.debug(`MCP tools cache miss: ${cacheKey}`));
      return null;
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        source: 'MCPToolCache.getCachedTools',
        message: 'Failed to retrieve cached tools',
        data: { cacheKey, options: { ...options, headers: '[REDACTED]' } },
      });
      return null;
    }
  }

  /**
   * Stores tools in cache with automatic expiration
   */
  async setCachedTools(
    options: ToolProviderFactoryOptions,
    tools: ToolSet,
    ttl?: number,
  ): Promise<void> {
    const cacheKey = this.createCacheKey(options);
    const entry: ToolCacheEntry = {
      tools,
      timestamp: Date.now(),
      serverCapabilities: this.extractServerCapabilities(tools),
    };

    try {
      const cacheTtl = ttl || this.config.defaultTtl;
      // Store in memory cache with same TTL
      this.memoryCache.set(cacheKey, entry, cacheTtl);
      // Store in Redis with same TTL
      const redis = await getRedisClient();
      const serialized = serializeCacheEntry(entry);
      if (!serialized) {
        log((l) =>
          l.warn(
            `Failed to serialize MCP tools for storing in redis; will be available in memory cache only: ${cacheKey}`,
          ),
        );
        return;
      }
      await redis.setEx(cacheKey, cacheTtl, serialized);
      log((l) =>
        l.debug(`MCP tools cached: ${cacheKey}`, {
          toolCount: Object.keys(tools).length,
          ttl: cacheTtl,
        }),
      );
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        source: 'MCPToolCache.setCachedTools',
        message: 'Failed to cache tools',
        data: { cacheKey, toolCount: Object.keys(tools).length },
      });
    }
  }

  /**
   * Invalidates cache for specific MCP server configuration
   */
  async invalidateCache(options: ToolProviderFactoryOptions): Promise<void> {
    const cacheKey = this.createCacheKey(options);

    try {
      // Remove from memory
      this.memoryCache.clear();

      // Remove from Redis
      const redis = await getRedisClient();
      await redis.del(cacheKey);

      log((l) => l.info(`MCP tools cache invalidated: ${cacheKey}`));
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        source: 'MCPToolCache.invalidateCache',
        message: 'Failed to invalidate cache',
        data: { cacheKey },
      });
    }
  }

  /**
   * Clears all cached tools (useful for testing or memory management)
   */
  async clearAll(): Promise<void> {
    try {
      this.memoryCache.clear();

      const redis = await getRedisClient();
      const keys = await redis.keys(`${this.config.keyPrefix}:*`);

      if (keys.length > 0) {
        // Redis del command expects at least one key - batch delete for efficiency
        await redis.del(keys);
      }

      log((l) =>
        l.info('All MCP tools cache cleared', { clearedKeys: keys.length }),
      );
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        source: 'MCPToolCache.clearAll',
        message: 'Failed to clear all cache',
      });
    }
  }

  /**
   * Gets cache statistics for monitoring
   */
  async getStats(): Promise<{
    memorySize: number;
    redisKeys: number;
    hitRate?: number;
  }> {
    try {
      const redis = await getRedisClient();
      const redisKeys = await redis.keys(`${this.config.keyPrefix}:*`);

      return {
        memorySize: this.memoryCache.size(),
        redisKeys: redisKeys.length,
      };
    } catch {
      return {
        memorySize: this.memoryCache.size(),
        redisKeys: 0,
      };
    }
  }

  /**
   * Checks if cache entry is still valid
   */
  private isEntryValid(entry: ToolCacheEntry): boolean {
    const age = Date.now() - entry.timestamp;
    const maxAge = this.config.defaultTtl * 1000;
    return age < maxAge;
  }

  /**
   * Sets up Redis keyspace notification subscription for cache invalidation
   */
  private async setupRedisInvalidationSubscription(): Promise<void> {
    try {
      // Create separate Redis connection for pub/sub
      this.redisSubscriber = await getRedisClient();

      // Enable keyspace notifications for expired events
      await this.redisSubscriber.configSet('notify-keyspace-events', 'Ex');

      // Subscribe to expiration events for our cache keys
      await this.redisSubscriber.pSubscribe(
        `__keyevent@0__:expired`,
        (message: string) => {
          // Extract the expired key and check if it's one of ours
          if (message && message.startsWith(this.config.keyPrefix)) {
            log((l) =>
              l.debug(
                `Redis key expired, invalidating memory cache: ${message}`,
              ),
            );
            this.memoryCache.invalidateKey(message);
          }
        },
      );

      log((l) =>
        l.debug('Redis keyspace notifications enabled for MCP tool cache'),
      );
    } catch (error) {
      // Non-critical failure - cache will still work without notifications
      log((l) =>
        l.warn(
          'Failed to setup Redis keyspace notifications for cache invalidation',
          error,
        ),
      );
    }
  }

  /**
   * Cleanup method for proper resource disposal
   */
  async dispose(): Promise<void> {
    try {
      if (this.redisSubscriber) {
        await this.redisSubscriber.pUnsubscribe();
        await this.redisSubscriber.quit();
      }
      this.memoryCache.clear();
    } catch (error) {
      log((l) => l.warn('Error during cache disposal:', error));
    }
  }

  /**
   * Extracts server capabilities fingerprint from tools
   */
  private extractServerCapabilities(tools: ToolSet): string {
    const toolNames = Object.keys(tools).sort();
    return createHash('md5')
      .update(JSON.stringify(toolNames))
      .digest('hex')
      .substring(0, 8);
  }
}

/**
 * Typeguard to check if a value is a Zod schema.
 * @param value The value to check
 * @return True if the value is a Zod schema, false otherwise
 * @example
 * ```typescript
 * if (isSchema(schema)) {
 *  // schema is typed as z.ZodTypeAny
 *  const shape = schema.shape; // Access object-specific properties
 * }
 */
export const isSchema = (value: unknown): value is z.ZodTypeAny =>
  !!value && value instanceof z.ZodType;

let zodex:
  | undefined
  | {
      zerialize: (schema: z.ZodTypeAny) => { type: unknown };
      dezerialize: (data: unknown) => z.ZodTypeAny;
    } = undefined;

/**
 * Serializes an object, converting any Zod schemas to their serializable forms.
 * Adds metadata to indicate which fields were schemas for proper deserialization.
 * @example
 * ```typescript
 * const json = serializeWithSchema(data);
 * const original = deserializeWithSchema<typeof data>(json);
 * if (isSchema(original.schemaField)) {
 *   // original.schemaField is restored as a Zod schema
 * }
 * ```
 */
export const serializeWithSchema = <T extends object>(data: T): string => {
  return JSON.stringify(data, (_key, value) => {
    if (!isSchema(value)) {
      return value;
    }
    // eslint-disable-next-line @typescript-eslint/no-require-imports
    zodex = zodex ?? require('zodex').Zodex;
    if (!zodex) {
      throw new TypeError('Zodex module is required for schema serialization');
    }
    return {
      __zerialize__schemaField: true,
      serialized: zodex.zerialize(value),
    };
  });
};

const isSerializedSchema = (value: unknown): value is SchemaFieldEnvelope =>
  !!value &&
  typeof value === 'object' &&
  '__zerialize__schemaField' in value &&
  value.__zerialize__schemaField === true &&
  'serialized' in value &&
  typeof value.serialized === 'string';

/**
 * Deserializes a JSON string, restoring any Zod schemas to their original form.
 * @param json The JSON string to deserialize
 * @example
 * ```typescript
 * const json = serializeWithSchema(data);
 * const original = deserializeWithSchema<typeof data>(json);
 * if (isSchema(original.schemaField)) {
 *   // original.schemaField is restored as a Zod schema
 * }
 * ```
 * @returns The deserialized object with restored Zod schemas
 */
export const deserializeWithSchema = <T extends object>(json: string): T => {
  return JSON.parse(json, (_key, value) => {
    // If it's not a serialized schema handle normally
    if (!isSerializedSchema(value)) {
      return value;
    }
    // Otherwise parse and return the value of the serialized field
    try {
      // eslint-disable-next-line @typescript-eslint/no-require-imports
      zodex = zodex ?? require('zodex').Zodex;
      if (!zodex) {
        throw new TypeError(
          'Zodex module is required for schema serialization',
        );
      }
      return zodex.dezerialize(value.serialized);
    } catch (error) {
      const le = LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        critical: false,
        source: 'MCPToolCache.deserializedCacheEntry',
      });
      log((l) =>
        l.warn('Failed to parse cached MCP tools', {
          error: le.toString(),
          json,
        }),
      );
      return value;
    }
  });
};

export const serializeCacheEntry = <TOOLS extends ToolSet>(
  entry: TypedToolCacheEntry<TOOLS>,
): string | undefined => {
  if (!entry || typeof entry !== 'object') {
    return undefined;
  }
  return JSON.stringify(entry, (key, value) => {
    // if this is not the tools property or if its an unexpected type, let default serialization handle it
    // do its thing
    if (key !== 'tools' || !value || typeof value !== 'object') {
      return value;
    }
    // Serialize tools individually to handle inner schemas (and execute callback maybe?)
    const serializedTools: Record<string, unknown> = {};
    for (const [toolName, tool] of Object.entries(value as TOOLS)) {
      serializedTools[toolName] = serializeWithSchema(tool);
    }
    return serializedTools;
  });
};

export const deserializedCacheEntry = <TOOLS extends ToolSet = ToolSet>(
  json: string,
): TypedToolCacheEntry<TOOLS> | undefined => {
  // Handle unexpeced or explicit not values gracefully
  if (
    !json ||
    typeof json !== 'string' ||
    json === 'null' ||
    json === 'undefined'
  ) {
    return undefined;
  }
  try {
    return JSON.parse(json, (key, value) => {
      // if this is not the tools property, let default deserialization handle it
      if (key !== 'tools' || !value || typeof value !== 'object') {
        return value;
      }
      // Deserialize tools individually to handle inner schemas
      return Object.entries(value).reduce(
        (
          acc: Record<keyof TOOLS, TOOLS[keyof TOOLS]>,
          [toolName, toolJson]: [string, unknown],
        ) => {
          if (!toolJson) {
            return acc;
          }
          const tool: TOOLS[typeof toolName] = deserializeWithSchema<
            TOOLS[typeof toolName]
          >(typeof toolJson === 'string' ? toolJson : JSON.stringify(toolJson));
          if (!tool) {
            log((l) =>
              l.warn('Unexpected null tool after deserialization', {
                toolName,
              }),
            );
          } else {
            acc[toolName as keyof TOOLS] = tool;
          }
          return acc;
        },
        {} as TOOLS,
      );
    });
  } catch (error) {
    const le = LoggedError.isTurtlesAllTheWayDownBaby(error, {
      log: true,
      critical: false,
      source: 'MCPToolCache.deserializedCacheEntry',
    });
    log((l) =>
      l.warn('Failed to parse cached MCP tools', {
        error: le.toString(),
        json,
      }),
    );
    return undefined;
  }
};

// Global singleton instance
let globalToolCache: MCPToolCache | null = null;

/**
 * Gets the global MCP tool cache instance
 */
export const getToolCache = (): MCPToolCache => {
  if (!globalToolCache) {
    globalToolCache = new MCPToolCache();
  }
  return globalToolCache;
};

/**
 * Configures the global tool cache
 */
export const configureToolCache = (
  config: Partial<ToolCacheConfig>,
): MCPToolCache => {
  globalToolCache = new MCPToolCache(config);
  return globalToolCache;
};

/**
 * @fileoverview User-scoped tool provider cache for maintaining persistent MCP connections
 *
 * This module provides a cache for maintaining ToolProviderSet instances across HTTP requests
 * while ensuring proper user isolation and cleanup. Each user gets their own tool provider
 * instance that persists for the configured TTL, reducing connection overhead and improving
 * performance.
 */

import type {
  CachedToolProvider,
  ToolProviderSet,
  UserToolProviderCache,
  UserToolProviderCacheConfig,
} from '../types';
import { log } from '@/lib/logger';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import { getFeatureFlag } from '@/lib/site-util/feature-flags/server';
import { globalSingleton } from '@/lib/typescript';

/**
 * Cache for maintaining ToolProviderSet instances per user across HTTP requests.
 *
 * Features:
 * - Per-user isolation with session-based keys
 * - Automatic TTL-based cleanup
 * - LRU eviction when cache limits are exceeded
 * - Proper disposal of tool providers on cleanup
 * - Memory usage monitoring and limits
 *
 * @example
 * ```typescript
 * const cache = UserToolProviderCache.getInstance();
 *
 * // Get or create tool providers for a user
 * const toolProviders = await cache.getOrCreate(
 *   userId,
 *   sessionId,
 *   async () => toolProviderFactory({ ... })
 * );
 *
 * // Cleanup when done (optional - automatic cleanup also occurs)
 * cache.invalidateUser(userId);
 * ```
 */
class UserToolProviderCacheImpl implements UserToolProviderCache {
  private cache = new Map<string, CachedToolProvider>();
  private cleanupTimer: NodeJS.Timeout | null = null;
  private readonly config: UserToolProviderCacheConfig;

  public constructor(config: Partial<UserToolProviderCacheConfig> = {}) {
    this.config = {
      maxEntriesPerUser: 3, // Max 3 different tool provider configs per user
      maxTotalEntries: 100, // Max 100 total entries across all users
      ttl: 30 * 60 * 1000, // 30 minutes
      cleanupInterval: 5 * 60 * 1000, // Cleanup every 5 minutes
      ...config,
    };

    this.startCleanupTimer();
  }

  /**
   * Generate a cache key for a user's tool provider configuration.
   */
  private generateCacheKey(
    userId: string,
    sessionId: string,
    configHash: string,
  ): string {
    return `${userId}:${sessionId}:${configHash}`;
  }

  /**
   * Generate a hash for the tool provider configuration to detect changes.
   */
  private generateConfigHash(config: {
    writeEnabled: boolean;
    memoryDisabled: boolean;
    headers?: Record<string, string>;
  }): string {
    // Create a stable hash of the configuration
    const configString = JSON.stringify({
      writeEnabled: config.writeEnabled,
      memoryDisabled: config.memoryDisabled,
      // Only include non-auth headers in hash to avoid session token changes
      headers: config.headers
        ? Object.fromEntries(
            Object.entries(config.headers).filter(
              ([key]) =>
                !key.toLowerCase().includes('auth') &&
                !key.toLowerCase().includes('cookie') &&
                key !== 'x-chat-history-id',
            ),
          )
        : {},
    });

    // Simple hash function (in production, consider using a crypto hash)
    let hash = 0;
    for (let i = 0; i < configString.length; i++) {
      const char = configString.charCodeAt(i);
      hash = (hash << 5) - hash + char;
      hash = hash & hash; // Convert to 32-bit integer
    }
    return hash.toString(36);
  }

  /**
   * Get or create a tool provider set for a user.
   */
  public async getOrCreate(
    userId: string,
    sessionId: string,
    config: {
      writeEnabled: boolean;
      memoryDisabled: boolean;
      headers?: Record<string, string>;
    },
    factory: () => Promise<ToolProviderSet>,
  ): Promise<ToolProviderSet> {
    const configHash = this.generateConfigHash(config);
    const cacheKey = this.generateCacheKey(userId, sessionId, configHash);

    // Check if we have a valid cached entry
    const cached = this.cache.get(cacheKey);
    if (cached && !this.isExpired(cached)) {
      // Update last accessed time
      cached.lastAccessed = Date.now();
      log((l) => l.debug('Tool provider cache hit', { userId, cacheKey }));
      return cached.toolProviders;
    }

    // Clean up expired entry if it exists
    if (cached && this.isExpired(cached)) {
      this.removeEntry(cacheKey, cached);
    }

    // Create new tool provider set
    log((l) => l.debug('Creating new tool provider set', { userId, cacheKey }));

    try {
      const toolProviders = await factory();

      if (toolProviders.isHealthy) {
        // Check cache size limits before adding
        this.enforceEvictionLimits(userId);

        // Cache the new tool provider
        this.cache.set(cacheKey, {
          toolProviders,
          lastAccessed: Date.now(),
          userId,
          sessionId,
        });

        log((l) =>
          l.debug('Tool provider cached successfully', {
            userId,
            cacheKey,
            cacheSize: this.cache.size,
          }),
        );
      }
      return toolProviders;
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        source: 'UserToolProviderCache.getOrCreate',
        message: 'Failed to create tool provider set',
        data: { userId, cacheKey },
      });
      throw error;
    }
  }

  /**
   * Check if a cached entry is expired.
   */
  private isExpired(entry: CachedToolProvider): boolean {
    return Date.now() - entry.lastAccessed > this.config.ttl;
  }

  /**
   * Remove and properly dispose of a cache entry.
   */
  private removeEntry(cacheKey: string, entry: CachedToolProvider): void {
    try {
      entry.toolProviders.dispose();
      log((l) =>
        l.debug('Tool provider disposed', {
          userId: entry.userId,
          cacheKey,
        }),
      );
    } catch (error) {
      log((l) =>
        l.warn('Error disposing tool provider', {
          userId: entry.userId,
          cacheKey,
          error,
        }),
      );
    }
    this.cache.delete(cacheKey);
  }

  /**
   * Enforce cache size limits with LRU eviction.
   */
  private enforceEvictionLimits(currentUserId: string): void {
    // Check per-user limit
    const userEntries = Array.from(this.cache.entries()).filter(
      ([, entry]) => entry.userId === currentUserId,
    );

    if (userEntries.length >= this.config.maxEntriesPerUser) {
      // Remove oldest entry for this user
      const oldestUserEntry = userEntries.sort(
        ([, a], [, b]) => a.lastAccessed - b.lastAccessed,
      )[0];

      if (oldestUserEntry) {
        this.removeEntry(oldestUserEntry[0], oldestUserEntry[1]);
      }
    }

    // Check total cache limit
    if (this.cache.size >= this.config.maxTotalEntries) {
      // Remove oldest entry globally
      const oldestEntry = Array.from(this.cache.entries()).sort(
        ([, a], [, b]) => a.lastAccessed - b.lastAccessed,
      )[0];

      if (oldestEntry) {
        this.removeEntry(oldestEntry[0], oldestEntry[1]);
      }
    }
  }

  /**
   * Invalidate all cached tool providers for a specific user.
   */
  public invalidateUser(userId: string): void {
    const userEntries = Array.from(this.cache.entries()).filter(
      ([, entry]) => entry.userId === userId,
    );

    for (const [cacheKey, entry] of userEntries) {
      this.removeEntry(cacheKey, entry);
    }

    log((l) =>
      l.debug('Invalidated user tool providers', {
        userId,
        removedCount: userEntries.length,
      }),
    );
  }

  /**
   * Invalidate all cached tool providers for a specific user session.
   */
  public invalidateSession(userId: string, sessionId: string): void {
    const sessionEntries = Array.from(this.cache.entries()).filter(
      ([, entry]) => entry.userId === userId && entry.sessionId === sessionId,
    );

    for (const [cacheKey, entry] of sessionEntries) {
      this.removeEntry(cacheKey, entry);
    }

    log((l) =>
      l.debug('Invalidated session tool providers', {
        userId,
        sessionId,
        removedCount: sessionEntries.length,
      }),
    );
  }

  /**
   * Clean up expired entries.
   */
  private cleanup(): void {
    const expiredEntries: [string, CachedToolProvider][] = [];

    for (const [cacheKey, entry] of this.cache.entries()) {
      if (this.isExpired(entry)) {
        expiredEntries.push([cacheKey, entry]);
      }
    }

    for (const [cacheKey, entry] of expiredEntries) {
      this.removeEntry(cacheKey, entry);
    }

    if (expiredEntries.length > 0) {
      log((l) =>
        l.debug('Cleaned up expired tool providers', {
          removedCount: expiredEntries.length,
          remainingCount: this.cache.size,
        }),
      );
    }
  }

  /**
   * Clear all cached tool providers and dispose them.
   */
  public clear(): void {
    for (const entry of this.cache.values()) {
      try {
        entry.toolProviders.dispose();
      } catch (error) {
        log((l) =>
          l.warn('Error disposing tool provider during clear', { error }),
        );
      }
    }

    this.cache.clear();
    log((l) => l.debug('Cleared all cached tool providers'));
  }

  /**
   * Start the periodic cleanup timer.
   */
  private startCleanupTimer(): void {
    this.cleanupTimer = setInterval(() => {
      this.cleanup();
    }, this.config.cleanupInterval);
  }

  /**
   * Get cache statistics.
   */
  public getStats() {
    const userCounts = new Map<string, number>();
    for (const entry of this.cache.values()) {
      userCounts.set(entry.userId, (userCounts.get(entry.userId) || 0) + 1);
    }

    return {
      totalEntries: this.cache.size,
      userCounts: Object.fromEntries(userCounts),
      config: this.config,
    };
  }

  /**
   * Shutdown the cache and clean up all entries.
   */
  public shutdown(): void {
    if (this.cleanupTimer) {
      clearInterval(this.cleanupTimer);
      this.cleanupTimer = null;
    }

    // Dispose all cached tool providers
    for (const [cacheKey, entry] of this.cache.entries()) {
      this.removeEntry(cacheKey, entry);
    }

    log((l) => l.info('User tool provider cache shutdown complete'));
  }
}
/**
 * Get the singleton instance of the cache.
 */
const getInstanceInternal = async (
  config?: Partial<UserToolProviderCacheConfig>,
): Promise<UserToolProviderCache> => {
  const cachingEnabled = await getFeatureFlag('mcp_cache_tools');
  if (!cachingEnabled) {
    const fnNoOp = () => Promise.resolve();
    return {
      getOrCreate: (
        _userId: string,
        _sessionId: string,
        _config: {
          writeEnabled: boolean;
          memoryDisabled: boolean;
          headers?: Record<string, string>;
        },
        factory: () => Promise<ToolProviderSet>,
      ) => factory(),
      shutdown: fnNoOp,
      clear: fnNoOp,
      invalidateUser: fnNoOp,
      invalidateSession: fnNoOp,
      getStats: () => ({
        totalEntries: 0,
        userCounts: {},
        config: {} as UserToolProviderCacheConfig,
      }),
    };
  }
  return globalSingleton(
    '@seanm/wedontneednoeducation/lib/ai/mcp/user-tool-provider-cache',
    () => new UserToolProviderCacheImpl(config),
  );
};

export const getUserToolProviderCache = getInstanceInternal;

/**
 * @fileoverview Refactored Instrumented SSE MCP Transport Client
 *
 * This is a refactored version of the original InstrumentedSseTransport that
 * delegates specific concerns to dedicated modules while maintaining the
 * same external interface and functionality.
 */

import { Span, SpanStatusCode } from '@opentelemetry/api';
import { SseMCPTransport } from '../ai.sdk';
import type { JSONRPCMessage } from '../ai.sdk';

import { isAbortError, isError } from '@/lib/react-util/utility-methods';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import { log } from '@/lib/logger';

// Import refactored modules
import { tracer, MetricsRecorder, DEBUG_MODE } from './metrics/otel-metrics';
import { CounterManager } from './metrics/counter-manager';
import { SessionManager } from './session/session-manager';
import { TraceContextManager } from './tracing/trace-context';
import {
  SafetyUtils,
  CONNECTION_TIMEOUT_MS,
  SEND_TIMEOUT_MS,
} from './utils/safety-utils';
import { MessageProcessor } from './message/message-processor';
import { ImpersonationService } from '@/lib/auth/impersonation';

type InstrumentedSseTransportOptions = {
  url: string;
  headers?: () => Promise<Record<string, string>>;
  // impersonation?: ImpersonationService;
  onclose?: () => void;
  onmessage?: (message: JSONRPCMessage) => void;
  onerror: ((error: unknown) => void) | ((error: Error) => void);
};

/**
 * Instrumented SSE Transport with comprehensive OpenTelemetry support.
 *
 * This refactored version maintains the same external interface while delegating
 * specific concerns to specialized modules:
 * - MetricsRecorder: OpenTelemetry metrics recording
 * - CounterManager: Active session and tool call tracking
 * - SessionManager: Session lifecycle and timeout management
 * - TraceContextManager: Distributed tracing support
 * - SafetyUtils: Error handling and timeout utilities
 * - MessageProcessor: Message parsing and tool call detection
 *
 * All original functionality is preserved with improved maintainability.
 */
export class InstrumentedSseTransport extends SseMCPTransport {
  // Module dependencies
  #counterManager: CounterManager;
  #sessionManager: SessionManager;
  #safetyUtils: SafetyUtils;
  #messageProcessor: MessageProcessor;
  #impersonation?: ImpersonationService;
  // Core transport state
  #onmessage?: (message: JSONRPCMessage) => void;
  #onerror: (error: unknown) => void;
  #onclose?: () => void;
  #transportSpan?: Span;
  #connectionStartTime: number = 0;
  #isClosing = false;
  #heartbeatTimer?: ReturnType<typeof setInterval>;
  #inactivityTimer?: ReturnType<typeof setTimeout>;
  #lastActivity: number = Date.now();
  #closed = false;
  #getHeaders: () => Promise<Record<string, string>>;

  // Heartbeat / inactivity constants (tunable)
  static readonly HEARTBEAT_INTERVAL_MS = 15_000; // send watchdog ping / check every 15s
  static readonly INACTIVITY_TIMEOUT_MS = 60_000 * 60 * 2; // if no inbound activity for 2 hours -> close
  static readonly POST_ERROR_AUTOCLOSE_DELAY_MS = 2_000; // grace period before forced close after fatal error

  constructor({
    // impersonation,
    headers: getHeaders,
    ...opts
  }: InstrumentedSseTransportOptions) {
    let constructorSpan: Span | undefined;
    try {
      // Start constructor instrumentation as child of current active span
      constructorSpan = tracer.startSpan('mcp.transport.constructor', {
        attributes: {
          'mcp.transport.url': opts.url,
          'mcp.transport.mode': DEBUG_MODE ? 'DEBUG' : 'WARNING',
          'mcp.transport.has_headers': !!getHeaders,
        },
      });

      if (DEBUG_MODE) {
        log((l) =>
          l.debug('Initializing InstrumentedSseTransport', {
            data: {
              url: opts.url,
              mode: DEBUG_MODE ? 'DEBUG' : 'WARNING',
            },
          }),
        );
      }

      // Inject trace context into headers for distributed tracing before calling super
      const headers = TraceContextManager.injectTraceContext({});
      super({ ...opts, headers }); // Call the base constructor with enhanced headers
      // Capture impersonation service if provided
      // this.#impersonation = impersonation;
      this.#getHeaders = getHeaders || (() => Promise.resolve({}));
      if (!opts.onerror) {
        const error = new Error('onerror handler is required');
        constructorSpan?.recordException(error);
        constructorSpan?.setStatus({
          code: SpanStatusCode.ERROR,
          message: error.message,
        });
        throw error;
      }

      // Initialize module dependencies
      this.#counterManager = new CounterManager();
      this.#sessionManager = new SessionManager(opts.url, this.#counterManager);
      this.#safetyUtils = new SafetyUtils(opts.url);
      this.#messageProcessor = new MessageProcessor(
        opts.url,
        this.#sessionManager,
        this.#counterManager,
      );

      // Set up event handlers
      this.#onclose = opts.onclose;
      this.#onmessage = opts.onmessage;
      this.#onerror = this.#safetyUtils.createSafeErrorHandler((e: unknown) => {
        opts.onerror(
          LoggedError.isTurtlesAllTheWayDownBaby(e, {
            log: true,
            message: 'MCP SSE Transport: Error occurred',
          }),
        );
      });

      // Override base callbacks with instrumented versions
      super.onclose = this.#safetyUtils.createSafeAsyncWrapper(
        'handleClose',
        this.handleClose.bind(this),
        this.#onerror,
      );
      super.onerror = this.#safetyUtils.createSafeAsyncWrapper(
        'handleError',
        this.handleError.bind(this),
        this.#onerror,
      );
      super.onmessage = this.#safetyUtils.createSafeAsyncWrapper(
        'handleMessage',
        this.handleMessage.bind(this),
        this.#onerror,
      );

      // Record successful construction
      MetricsRecorder.recordConnection(opts.url, 'constructor', 'success');

      constructorSpan?.setStatus({ code: SpanStatusCode.OK });

      if (DEBUG_MODE) {
        log((l) =>
          l.debug('InstrumentedSseTransport initialized successfully'),
        );
      }
    } catch (error) {
      // Record construction failure
      MetricsRecorder.recordConnection(opts.url, 'constructor', 'error');
      MetricsRecorder.recordError(
        'constructor',
        isError(error) ? error.name : 'unknown',
      );

      constructorSpan?.recordException(error as Error);
      constructorSpan?.setStatus({
        code: SpanStatusCode.ERROR,
        message: isError(error) ? error.message : String(error),
      });

      log((l) =>
        l.error('Failed to initialize InstrumentedSseTransport', {
          data: { error: isError(error) ? error.message : String(error) },
        }),
      );

      throw error;
    } finally {
      constructorSpan?.end();
    }
  }

  // === Property Overrides ===

  override get onmessage(): ((message: JSONRPCMessage) => void) | undefined {
    return this.#onmessage;
  }

  override set onmessage(
    handler: ((message: JSONRPCMessage) => void) | undefined,
  ) {
    this.#onmessage = handler;
  }

  override get onerror(): (error: unknown) => void {
    return this.#onerror;
  }

  override set onerror(handler: (error: unknown) => void) {
    if (!handler) {
      throw new Error('onerror handler is required');
    }
    this.#onerror = this.#safetyUtils.createSafeErrorHandler(handler);
  }

  override get onclose(): (() => void) | undefined {
    return this.#onclose;
  }

  override set onclose(handler: (() => void) | undefined) {
    this.#onclose = handler;
  }

  toString(): string {
    return `InstrumentedSseTransport(${this.url?.toString() || 'unknown'})`;
  }

  // === MCP Client Transport Methods ===

  override async start(): Promise<void> {
    let span: Span | undefined;
    const operationId = this.#safetyUtils.recordOperation('start');

    try {
      this.#connectionStartTime = Date.now();

      // Create span as child of active span if one exists
      span = tracer.startSpan('mcp.transport.start', {
        attributes: {
          'mcp.transport.url': this.url?.toString(),
          'mcp.transport.mode': DEBUG_MODE ? 'DEBUG' : 'WARNING',
        },
      });

      this.#transportSpan = span;

      if (DEBUG_MODE) {
        log((l) =>
          l.debug('Starting MCP Client Transport', {
            data: {
              url: this.url?.toString(),
              mode: DEBUG_MODE ? 'DEBUG' : 'WARNING',
            },
          }),
        );
      }

      // Record connection attempt
      MetricsRecorder.recordConnection(
        this.url?.toString() || 'unknown',
        'start',
        'attempt',
      );

      // Apply connection timeout
      await this.#safetyUtils.withTimeout(
        super.start(),
        CONNECTION_TIMEOUT_MS,
        'MCP connection',
      );

      // Record successful connection
      MetricsRecorder.recordConnection(
        this.url?.toString() || 'unknown',
        'start',
        'success',
      );

      // Initialize heartbeat & inactivity watchdog AFTER successful start
      this.#initializeConnectionWatchdogs();

      span.setStatus({ code: SpanStatusCode.OK });
      this.#safetyUtils.completeOperation(operationId, 'success');

      if (DEBUG_MODE) {
        log((l) => l.debug('MCP Client Transport started successfully'));
      }
    } catch (error) {
      if (isAbortError(error)) {
        // Suppress abort errors; this is a disconnect not a construction failure.
        const isClosing = this.#isClosing;
        log((l) =>
          l.verbose(
            `InstrumentedTransport::MCP Client Transport start() aborted; isClosing=${isClosing}`,
          ),
        );
        return;
      }
      // Record failed connection
      MetricsRecorder.recordConnection(
        this.url?.toString() || 'unknown',
        'start',
        'error',
      );
      MetricsRecorder.recordError(
        'start',
        isError(error) ? error.name : 'unknown',
      );

      span?.recordException(error as Error);
      span?.setStatus({
        code: SpanStatusCode.ERROR,
        message: isError(error) ? error.message : String(error),
      });

      this.#safetyUtils.completeOperation(operationId, 'error');

      log((l) =>
        l.error('Failed to start MCP Client Transport', {
          data: {
            error: isError(error) ? error.message : String(error),
            stack: isError(error) ? error.stack : undefined,
          },
        }),
      );

      throw error;
    } finally {
      // Don't end the transport span here - it should stay active for the connection duration
    }
  }

  protected override async resolveHeaders(): Promise<Headers> {
    const ret = await super.resolveHeaders();
    const dynamicHeaders = await this.#getHeaders();
    Object.entries(dynamicHeaders).forEach(([key, value]) => {
      ret.set(key, value);
    });
    // If impersonation service is provided, add impersonation headers
    if (this.#impersonation) {
      const token = await this.#impersonation.getImpersonatedToken();
      if (token) {
        ret.set('Authorization', `Bearer ${token}`);
      }
    }
    return ret;
  }

  override async close(): Promise<void> {
    let span: Span | undefined;
    const operationId = this.#safetyUtils.recordOperation('close');

    try {
      this.#isClosing = true;

      span = tracer.startSpan('mcp.transport.close', {
        attributes: {
          'mcp.transport.url': this.url?.toString(),
          'mcp.transport.session_count': this.#sessionManager.sessionCount,
        },
      });

      if (DEBUG_MODE) {
        log((l) =>
          l.debug('Closing MCP Client Transport', {
            data: {
              url: this.url?.toString(),
              sessionCount: this.#sessionManager.sessionCount,
              connectionDuration: this.#connectionStartTime
                ? Date.now() - this.#connectionStartTime
                : 0,
            },
          }),
        );
      }

      // Close all active sessions
      this.#sessionManager.closeAllSessions();

      // Stop timers early to avoid late firing while awaiting super.close()
      this.#clearWatchdogs();

      try {
        await super.close();
      } catch (e) {
        // Swallow AbortError that can arise from closing streams mid-flight
        if (isAbortError(e)) {
          log((l) =>
            l.verbose(
              'InstrumentedSseTransport.close: Ignoring AbortError during close()',
            ),
          );
        } else {
          throw e;
        }
      }

      // End the transport span
      if (this.#transportSpan) {
        try {
          const connectionDuration = this.#connectionStartTime
            ? Date.now() - this.#connectionStartTime
            : 0;
          this.#transportSpan.addEvent('transport.closed', {
            'mcp.transport.duration_ms': connectionDuration,
          });
          this.#transportSpan.setStatus({ code: SpanStatusCode.OK });
          this.#transportSpan.end();
        } catch (e) {
          LoggedError.isTurtlesAllTheWayDownBaby(e, {
            log: true,
          });
        }
      }

      span.setStatus({ code: SpanStatusCode.OK });
      this.#safetyUtils.completeOperation(operationId, 'success');

      this.#closed = true;

      if (DEBUG_MODE) {
        log((l) => l.debug('MCP Client Transport closed successfully'));
      }
    } catch (error) {
      MetricsRecorder.recordError(
        'close',
        isError(error) ? error.name : 'unknown',
      );

      span?.recordException(error as Error);
      span?.setStatus({
        code: SpanStatusCode.ERROR,
        message: isError(error) ? error.message : String(error),
      });

      this.#safetyUtils.completeOperation(operationId, 'error');

      log((l) =>
        l.error('Failed to close MCP Client Transport', {
          data: { error: isError(error) ? error.message : String(error) },
        }),
      );
      // throw error;
    } finally {
      span?.end();
    }
  }

  override async send(message: JSONRPCMessage): Promise<void> {
    let span: Span | undefined;
    const messageId = this.#sessionManager.getMessageId(message);
    const messageMethod = this.#sessionManager.getMessageMethod(message);
    const operationId = this.#safetyUtils.recordOperation('send', messageId);

    try {
      // Create span as child of active span if one exists
      span = tracer.startSpan('mcp.transport.send', {
        attributes: {
          'mcp.transport.url': this.url?.toString(),
          'mcp.message.id': String(messageId || 'unknown'),
          'mcp.message.method': messageMethod || 'unknown',
        },
      });

      // Process the outbound message
      this.#messageProcessor.processOutboundMessage(message);

      if (DEBUG_MODE) {
        log((l) =>
          l.debug('Sending MCP Client Message', {
            data: {
              messageId,
              method: messageMethod,
              url: this.url?.toString(),
            },
          }),
        );
      }

      // Apply send timeout
      await this.#safetyUtils.withTimeout(
        super.send(message),
        SEND_TIMEOUT_MS,
        'MCP message send',
      );

      span.setStatus({ code: SpanStatusCode.OK });
      this.#safetyUtils.completeOperation(operationId, 'success');

      if (DEBUG_MODE) {
        log((l) =>
          l.debug('MCP Client Message sent successfully', {
            data: { messageId, method: messageMethod },
          }),
        );
      }
    } catch (error) {
      MetricsRecorder.recordError(
        'send',
        isError(error) ? error.name : 'unknown',
      );

      span?.recordException(error as Error);
      span?.setStatus({
        code: SpanStatusCode.ERROR,
        message: isError(error) ? error.message : String(error),
      });

      this.#safetyUtils.completeOperation(operationId, 'error');

      log((l) =>
        l.error('Failed to send MCP Client Message', {
          data: {
            messageId,
            method: messageMethod,
            error: isError(error) ? error.message : String(error),
          },
        }),
      );

      throw error;
    } finally {
      span?.end();
    }
  }

  // === Public API Methods (Delegated to modules) ===

  /**
   * Gets the current count of active sessions and tool calls
   */
  getActiveCounters(): { sessions: number; toolCalls: number } {
    return this.#counterManager.getActiveCounters();
  }

  /**
   * Manually resets all active counters to zero
   */
  resetActiveCounters(): void {
    this.#counterManager.resetActiveCounters();
  }

  /**
   * Gets current session information for debugging
   */
  getSessionDebugInfo(): Array<{
    sessionId: string;
    messageCount: number;
    duration: number;
    isToolCall: boolean;
    toolCallMethod?: string;
    lastActivity: number;
  }> {
    return this.#sessionManager.getSessionDebugInfo();
  }

  /**
   * Manually completes a stuck tool call session
   */
  forceCompleteToolCall(
    sessionId: string,
    reason: string = 'manual_completion',
  ): boolean {
    return this.#sessionManager.forceCompleteToolCall(sessionId, reason);
  }

  /**
   * Gets enhanced headers with trace context for HTTP requests
   */
  getEnhancedHeaders(
    baseHeaders: Record<string, string> = {},
  ): Record<string, string> {
    return TraceContextManager.getEnhancedHeaders(baseHeaders);
  }

  /**
   * Updates existing headers object with trace context in place
   */
  updateHeadersWithTraceContext(headers: Record<string, string>): boolean {
    return TraceContextManager.updateHeadersWithTraceContext(headers);
  }

  /**
   * Static method to inject trace context into HTTP headers for distributed tracing
   */
  static injectTraceContext(
    headers: Record<string, string> = {},
  ): Record<string, string> {
    return TraceContextManager.injectTraceContext(headers);
  }

  // === Event Handlers ===

  private handleClose() {
    log((l) => l.verbose('MCP Client Transport Closed'));
    try {
      this.#onclose?.(); // pass through to client
    } catch (e) {
      log((l) => l.error('Error handling MCP Client Transport close:', e));
    }
  }

  private handleMessage(message: JSONRPCMessage) {
    log((l) => l.verbose('MCP Client Transport Message Received:', message));

    try {
      // Process the inbound message
      this.#messageProcessor.processInboundMessage(message);

      // Update activity time for watchdog
      this.#lastActivity = Date.now();

      this.#onmessage?.(message); // pass through to client
    } catch (e) {
      log((l) => l.error('Error handling MCP Client Transport message:', e));
    }
  }

  private handleError(error: unknown) {
    const le = LoggedError.isTurtlesAllTheWayDownBaby(error, {
      log: true,
      source: 'MCP Client Transport',
      data: {
        details: 'Error occurred in MCP Client Transport',
      },
    });
    try {
      this.#onerror(le); // pass through to client
    } catch (e) {
      log((l) => l.error('Error handling MCP Client Transport error:', e));
    }

    // If we are not already closing, schedule an automatic close to prevent
    // lingering hung transports after an unrecoverable error. Abort errors
    // are treated as graceful shutdowns upstream, so only close on non-abort.
    if (!this.#isClosing && !isAbortError(error)) {
      this.#schedulePostErrorAutoclose();
    }
  }

  // === Heartbeat & Watchdog Management ===

  /**
   * Initialize heartbeat interval and inactivity timeout watchdog.
   * These guard against silent hung connections where the underlying SSE
   * stream remains open but no messages are received (e.g., network middlebox
   * buffering or server-side stall).
   */
  #initializeConnectionWatchdogs(): void {
    this.#lastActivity = Date.now();
    this.#clearWatchdogs();

    // Heartbeat interval: record metric + (optionally) emit trace event.
    this.#heartbeatTimer = setInterval(() => {
      // Only act if not closing/closed
      if (this.#isClosing || this.#closed) return;
      const now = Date.now();
      MetricsRecorder.recordConnection(
        this.url?.toString() || 'unknown',
        'heartbeat',
        'success',
      );
      // Inactivity check piggybacked here (alternative to separate timeout reset)
      if (
        now - this.#lastActivity >
        InstrumentedSseTransport.INACTIVITY_TIMEOUT_MS
      ) {
        log((l) =>
          l.warn(
            'MCP Client Transport inactivity threshold exceeded; initiating graceful close',
            {
              url: this.url?.toString(),
              idleMs: now - this.#lastActivity,
              threshold: InstrumentedSseTransport.INACTIVITY_TIMEOUT_MS,
            },
          ),
        );
        this.close().catch((err) =>
          log((l) =>
            l.error('Error while closing after inactivity watchdog', {
              error: isError(err) ? err.message : String(err),
            }),
          ),
        );
      }
    }, InstrumentedSseTransport.HEARTBEAT_INTERVAL_MS);
  }

  /** Clear heartbeat and inactivity timers. */
  #clearWatchdogs(): void {
    if (this.#heartbeatTimer) {
      clearInterval(this.#heartbeatTimer);
      this.#heartbeatTimer = undefined;
    }
    if (this.#inactivityTimer) {
      clearTimeout(this.#inactivityTimer);
      this.#inactivityTimer = undefined;
    }
  }

  /** Schedule forced close after fatal error to prevent resource leaks. */
  #schedulePostErrorAutoclose(): void {
    // Avoid multiple schedules
    if (this.#inactivityTimer || this.#isClosing || this.#closed) return;
    this.#inactivityTimer = setTimeout(() => {
      if (this.#isClosing || this.#closed) return;
      log((l) =>
        l.warn('Auto-closing MCP Client Transport after error grace period', {
          url: this.url?.toString(),
          delayMs: InstrumentedSseTransport.POST_ERROR_AUTOCLOSE_DELAY_MS,
        }),
      );
      this.close().catch((err) =>
        log((l) =>
          l.error('Error during post-error autoclose', {
            error: isError(err) ? err.message : String(err),
          }),
        ),
      );
    }, InstrumentedSseTransport.POST_ERROR_AUTOCLOSE_DELAY_MS);
  }
}

/**
 * TransportPlugin interface for MCP Client Transport plugins
 */
export interface TransportPlugin {
  onMessage?(message: JSONRPCMessage): void;
  onError?(error: Error): void;
  onClose?(): void;
}

/**
 * @fileoverview Message Processing Utilities for MCP Transport
 *
 * This module provides utilities for processing and handling MCP messages,
 * including message type detection and tool call identification.
 */

import type { JSONRPCMessage } from '../../ai.sdk';
import { SessionManager } from '../session/session-manager';
import { MetricsRecorder, DEBUG_MODE } from '../metrics/otel-metrics';
import { CounterManager } from '../metrics/counter-manager';
import { log } from '@/lib/logger';
import { LoggedError } from '@/lib/react-util';

/**
 * Handles message processing and tool call lifecycle management
 */
export class MessageProcessor {
  #sessionManager: SessionManager;
  #counterManager: CounterManager;
  #url: string;

  constructor(
    url: string,
    sessionManager: SessionManager,
    counterManager: CounterManager,
  ) {
    this.#url = url;
    this.#sessionManager = sessionManager;
    this.#counterManager = counterManager;
  }

  /**
   * Processes an outbound message and handles session/tool call tracking
   */
  processOutboundMessage(message: JSONRPCMessage): void {
    const messageId = this.#sessionManager.getMessageId(message);
    const messageMethod = this.#sessionManager.getMessageMethod(message);

    // Check if this is a tool call and track it
    const isToolCall =
      messageMethod && this.#sessionManager.isToolCallMethod(messageMethod);

    // Create or update session for this message if it has an ID
    let sessionState = undefined;
    if (messageId) {
      sessionState = this.#sessionManager.getOrCreateSession(message);

      // If this is a new session, increment session counter
      if (sessionState && sessionState.messageCount === 1) {
        this.#counterManager.incrementCounter('sessions');
      }

      // If this is a tool call and session wasn't already marked as tool call
      if (sessionState && isToolCall && !sessionState.isToolCall) {
        sessionState.isToolCall = true;
        sessionState.toolCallMethod = messageMethod;
        this.#counterManager.incrementCounter('toolCalls');

        MetricsRecorder.recordToolCall(this.#url, messageMethod || 'unknown');
      }
    }

    // Record message metrics
    MetricsRecorder.recordMessage(
      this.#url,
      'outbound',
      messageMethod || 'unknown',
    );

    // Record message size
    const messageStr = JSON.stringify(message);
    const messageSize = new TextEncoder().encode(messageStr).length;
    MetricsRecorder.recordMessageSize(
      messageSize,
      'outbound',
      messageMethod || 'unknown',
    );

    if (DEBUG_MODE) {
      log((l) =>
        l.debug('Processing outbound MCP message', {
          data: {
            messageId,
            method: messageMethod,
            size: messageSize,
            url: this.#url,
            isToolCall,
          },
        }),
      );
    }
  }

  /**
   * Processes an inbound message and handles response completion
   */
  processInboundMessage(message: JSONRPCMessage): void {
    let sessionId: string = '';
    try {
      const messageId = this.#sessionManager.getMessageId(message);
      const messageMethod = this.#sessionManager.getMessageMethod(message);

      // Record message metrics
      MetricsRecorder.recordMessage(
        this.#url,
        'inbound',
        messageMethod || 'response',
      );

      // Record message size
      const messageStr = JSON.stringify(message);
      const messageSize = new TextEncoder().encode(messageStr).length;
      MetricsRecorder.recordMessageSize(
        messageSize,
        'inbound',
        messageMethod || 'response',
      );

      // Check if this is a response to a tool call and handle completion
      if (messageId) {
        sessionId = String(messageId);
        const sessionState = this.#sessionManager.getSession(sessionId);

        // If this is a response (has result or error) and the session was a tool call
        if (
          sessionState &&
          sessionState.isToolCall &&
          ('result' in message || 'error' in message)
        ) {
          const reason = 'error' in message ? 'error' : 'success';
          this.#sessionManager.completeSession(sessionId, reason);

          if (DEBUG_MODE) {
            log((l) =>
              l.debug('Tool call completed via response', {
                data: {
                  sessionId,
                  success: reason === 'success',
                  method: sessionState.toolCallMethod,
                },
              }),
            );
          }
        }
      }

      if (DEBUG_MODE) {
        log((l) =>
          l.debug('Processing inbound MCP message', {
            data: {
              messageId,
              method: messageMethod,
              size: messageSize,
              isResponse: 'result' in message || 'error' in message,
            },
          }),
        );
      }
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        source: 'MessageProcessor',
        message: 'Failed to process inbound message',
        critical: true,
      });
      this.#sessionManager.completeSession(sessionId, 'error');
    }
  }
}

/**
 * @fileoverview Counter Management for MCP Transport
 *
 * This module handles tracking of active sessions and tool calls with
 * safe increment/decrement operations and debugging capabilities.
 */

import { activeSessionsGauge, activeToolCallsGauge } from './otel-metrics';
import { log } from '@/lib/logger';
import { DEBUG_MODE } from './otel-metrics';

export interface ActiveCounters {
  sessions: number;
  toolCalls: number;
}

/**
 * Manages active session and tool call counters with safe operations
 */
export class CounterManager {
  #activeCounters: ActiveCounters = { sessions: 0, toolCalls: 0 };

  /**
   * Gets the current count of active sessions and tool calls
   */
  getActiveCounters(): ActiveCounters {
    return { ...this.#activeCounters };
  }

  /**
   * Manually resets all active counters to zero
   * Use this when you suspect counters are out of sync due to errors
   */
  resetActiveCounters(): void {
    if (DEBUG_MODE) {
      log((l) =>
        l.debug('Manually resetting active counters', {
          data: {
            previousSessions: this.#activeCounters.sessions,
            previousToolCalls: this.#activeCounters.toolCalls,
          },
        }),
      );
    }

    // Update metrics to reflect the reset
    activeSessionsGauge.add(-this.#activeCounters.sessions);
    activeToolCallsGauge.add(-this.#activeCounters.toolCalls);

    this.#activeCounters = { sessions: 0, toolCalls: 0 };

    log((l) => l.warn('Active counters have been manually reset to zero'));
  }

  /**
   * Safely increments active counters
   */
  incrementCounter(type: 'sessions' | 'toolCalls', amount: number = 1): void {
    this.#activeCounters[type] = Math.max(
      0,
      this.#activeCounters[type] + amount,
    );

    if (type === 'sessions') {
      activeSessionsGauge.add(amount);
    } else {
      activeToolCallsGauge.add(amount);
    }

    if (DEBUG_MODE) {
      log((l) =>
        l.debug(`Incremented ${type} counter`, {
          data: { amount, newValue: this.#activeCounters[type] },
        }),
      );
    }
  }

  /**
   * Safely decrements active counters (never goes below 0)
   */
  decrementCounter(type: 'sessions' | 'toolCalls', amount: number = 1): void {
    const oldValue = this.#activeCounters[type];
    this.#activeCounters[type] = Math.max(
      0,
      this.#activeCounters[type] - amount,
    );
    const actualDecrement = oldValue - this.#activeCounters[type];

    if (type === 'sessions') {
      activeSessionsGauge.add(-actualDecrement);
    } else {
      activeToolCallsGauge.add(-actualDecrement);
    }

    if (DEBUG_MODE) {
      log((l) =>
        l.debug(`Decremented ${type} counter`, {
          data: {
            requestedAmount: amount,
            actualAmount: actualDecrement,
            newValue: this.#activeCounters[type],
          },
        }),
      );
    }
  }
}

/**
 * @fileoverview OpenTelemetry Metrics Setup for MCP Transport
 * 
 * This module centralizes all OpenTelemetry metric definitions and provides
 * utilities for recording transport-related metrics.
 */

import { trace, metrics } from '@opentelemetry/api';

// OTEL Configuration
export const OTEL_MODE = process.env.MCP_OTEL_MODE?.toUpperCase() || 'WARNING';
export const DEBUG_MODE = OTEL_MODE === 'DEBUG';

// OTEL Instrumentation
export const tracer = trace.getTracer('mcp-client-transport', '1.0.0');
export const meter = metrics.getMeter('mcp-client-transport', '1.0.0');

// Connection Metrics
export const connectionCounter = meter.createCounter('mcp_connections_total', {
  description: 'Total number of MCP connection attempts',
});

// Message Metrics
export const messageCounter = meter.createCounter('mcp_messages_total', {
  description: 'Total number of MCP messages sent/received',
});

export const messageSizeHistogram = meter.createHistogram('mcp_message_size_bytes', {
  description: 'Size of MCP messages in bytes',
});

// Error Metrics
export const errorCounter = meter.createCounter('mcp_errors_total', {
  description: 'Total number of MCP transport errors',
});

// Duration Metrics
export const sessionDurationHistogram = meter.createHistogram(
  'mcp_session_duration_ms',
  {
    description: 'Duration of MCP sessions in milliseconds',
  },
);

export const operationDurationHistogram = meter.createHistogram(
  'mcp_operation_duration_ms',
  {
    description: 'Duration of MCP operations in milliseconds',
  },
);

// Active Counters
export const activeSessionsGauge = meter.createUpDownCounter('mcp_active_sessions', {
  description: 'Number of currently active MCP sessions',
});

export const activeToolCallsGauge = meter.createUpDownCounter(
  'mcp_active_tool_calls',
  {
    description: 'Number of currently active MCP tool calls',
  },
);

// Tool Call Metrics
export const toolCallCounter = meter.createCounter('mcp_tool_calls_total', {
  description: 'Total number of MCP tool calls initiated',
});

export const toolCallCompletionCounter = meter.createCounter(
  'mcp_tool_call_completions_total',
  {
    description: 'Total number of MCP tool call completions',
  },
);

/**
 * Utility class for recording common metric patterns
 */
export class MetricsRecorder {
  static recordConnection(url: string, operation: string, status: string) {
    connectionCounter.add(1, {
      'mcp.transport.url': url,
      'mcp.transport.operation': operation,
      'mcp.transport.status': status,
    });
  }

  static recordMessage(url: string, direction: 'inbound' | 'outbound', method: string) {
    messageCounter.add(1, {
      'mcp.transport.url': url,
      'mcp.transport.direction': direction,
      'mcp.message.method': method,
    });
  }

  static recordMessageSize(size: number, direction: 'inbound' | 'outbound', method: string) {
    messageSizeHistogram.record(size, {
      'mcp.transport.direction': direction,
      'mcp.message.method': method,
    });
  }

  static recordError(operation: string, errorType: string) {
    errorCounter.add(1, {
      'mcp.transport.operation': operation,
      'mcp.transport.error_type': errorType,
    });
  }

  static recordOperationDuration(duration: number, operation: string, status: string) {
    operationDurationHistogram.record(duration, {
      'mcp.transport.operation': operation,
      'mcp.transport.status': status,
    });
  }

  static recordSessionDuration(duration: number, url: string, sessionType: string) {
    sessionDurationHistogram.record(duration, {
      'mcp.transport.url': url,
      'mcp.transport.session_type': sessionType,
    });
  }

  static recordToolCall(url: string, method: string) {
    toolCallCounter.add(1, {
      'mcp.transport.url': url,
      'mcp.tool.method': method,
    });
  }

  static recordToolCallCompletion(url: string, method: string, reason: string) {
    toolCallCompletionCounter.add(1, {
      'mcp.transport.url': url,
      'mcp.tool.method': method,
      'mcp.tool.completion_reason': reason,
    });
  }
}

/**
 * @fileoverview Session Management for MCP Transport
 *
 * This module handles session lifecycle, idle timeouts, and session state tracking.
 */

import { Span, SpanStatusCode } from '@opentelemetry/api';
import { tracer, MetricsRecorder, DEBUG_MODE } from '../metrics/otel-metrics';
import { CounterManager } from '../metrics/counter-manager';
import type { JSONRPCMessage } from '@/lib/ai/mcp/ai.sdk';
import { log } from '@/lib/logger';
import { isError } from '@/lib/react-util/utility-methods';

export interface SpanState {
  span: Span;
  idleTimer: ReturnType<typeof setTimeout>;
  createdAt: number;
  messageCount: number;
  lastActivity: number;
  isToolCall?: boolean;
  toolCallMethod?: string;
}

// Constants
const IDLE_TIMEOUT_MS = 15 * 60 * 1000; // 15 minutes

// Tool call methods that initiate sessions
const TOOL_CALL_METHODS = new Set([
  'tools/call',
  'tools/list',
  'tools/get',
  'prompts/list',
  'prompts/get',
  'resources/list',
  'resources/read',
  'resources/subscribe',
]);

/**
 * Manages MCP session lifecycle and tracking
 */
export class SessionManager {
  #sessions = new Map<string, SpanState>();
  #counterManager: CounterManager;
  #url: string;
  #isClosing = false;

  constructor(url: string, counterManager: CounterManager) {
    this.#url = url;
    this.#counterManager = counterManager;
  }

  /**
   * Creates or updates session state for message tracking
   */
  getOrCreateSession(message: JSONRPCMessage): SpanState | undefined {
    const messageId = this.getMessageId(message);
    if (!messageId) return undefined;

    const sessionId = String(messageId);
    let sessionState = this.#sessions.get(sessionId);

    if (!sessionState) {
      if (DEBUG_MODE) {
        log((l) =>
          l.debug('Creating new MCP session', {
            data: { sessionId, method: this.getMessageMethod(message) },
          }),
        );
      }

      // Create session span as child of current active span
      const span = tracer.startSpan('mcp.session', {
        attributes: {
          'mcp.session.id': sessionId,
          'mcp.session.created_at': Date.now(),
          'mcp.transport.url': this.#url,
          'mcp.session.initiating_method':
            this.getMessageMethod(message) || 'unknown',
        },
      });

      sessionState = {
        span,
        idleTimer: this.setIdleTimer(sessionId),
        createdAt: Date.now(),
        messageCount: 0,
        lastActivity: Date.now(),
      };

      this.#sessions.set(sessionId, sessionState);
    }

    // Update activity
    sessionState.messageCount++;
    sessionState.lastActivity = Date.now();
    clearTimeout(sessionState.idleTimer);
    sessionState.idleTimer = this.setIdleTimer(sessionId);

    return sessionState;
  }

  /**
   * Checks if a message method is a tool call
   */
  isToolCallMethod(method: string): boolean {
    return TOOL_CALL_METHODS.has(method);
  }

  /**
   * Sets up idle timer for session cleanup
   */
  setIdleTimer(sessionId: string): ReturnType<typeof setTimeout> {
    return setTimeout(() => {
      try {
        const sessionState = this.#sessions.get(sessionId);
        if (sessionState && !this.#isClosing) {
          const sessionDuration = Date.now() - sessionState.createdAt;

          MetricsRecorder.recordSessionDuration(
            sessionDuration,
            this.#url,
            'idle_timeout',
          );

          sessionState.span.addEvent('session.idle_timeout', {
            'mcp.session.duration_ms': sessionDuration,
            'mcp.session.message_count': sessionState.messageCount,
          });
          sessionState.span.setStatus({ code: SpanStatusCode.OK });
          sessionState.span.end();

          // Decrement counters before removing session
          this.#counterManager.decrementCounter('sessions');
          if (sessionState.isToolCall) {
            this.#counterManager.decrementCounter('toolCalls');

            // Record tool call completion
            MetricsRecorder.recordToolCallCompletion(
              this.#url,
              sessionState.toolCallMethod || 'unknown',
              'idle_timeout',
            );
          }

          this.#sessions.delete(sessionId);

          if (DEBUG_MODE) {
            log((l) =>
              l.debug('Session idle timeout', {
                data: {
                  sessionId,
                  duration: sessionDuration,
                  messageCount: sessionState.messageCount,
                },
              }),
            );
          }
        }
      } catch (error) {
        log((l) =>
          l.error('Error in session idle timer', {
            data: {
              sessionId,
              error: isError(error) ? error.message : String(error),
            },
          }),
        );
      }
    }, IDLE_TIMEOUT_MS);
  }

  /**
   * Gets session by ID
   */
  getSession(sessionId: string): SpanState | undefined {
    return this.#sessions.get(sessionId);
  }

  /**
   * Closes all active sessions
   */
  closeAllSessions(): void {
    this.#isClosing = true;

    for (const [, sessionState] of this.#sessions.entries()) {
      const sessionDuration = Date.now() - sessionState.createdAt;

      MetricsRecorder.recordSessionDuration(
        sessionDuration,
        this.#url,
        'normal_close',
      );

      sessionState.span.addEvent('session.closing', {
        'mcp.session.duration_ms': sessionDuration,
        'mcp.session.message_count': sessionState.messageCount,
      });
      sessionState.span.setStatus({ code: SpanStatusCode.OK });
      sessionState.span.end();
      clearTimeout(sessionState.idleTimer);

      // Decrement counters safely
      this.#counterManager.decrementCounter('sessions');
      if (sessionState.isToolCall) {
        this.#counterManager.decrementCounter('toolCalls');

        // Record tool call completion
        MetricsRecorder.recordToolCallCompletion(
          this.#url,
          sessionState.toolCallMethod || 'unknown',
          'transport_close',
        );
      }
    }
    this.#sessions.clear();
  }

  /**
   * Completes a specific session
   */
  completeSession(sessionId: string, reason: string = 'completed'): boolean {
    const sessionState = this.#sessions.get(sessionId);
    if (!sessionState) return false;

    const sessionDuration = Date.now() - sessionState.createdAt;

    MetricsRecorder.recordSessionDuration(
      sessionDuration,
      this.#url,
      reason === 'tool_call_response'
        ? 'tool_call_response'
        : 'normal_completion',
    );

    if (sessionState.isToolCall) {
      sessionState.span.addEvent('tool.call.completed', {
        'mcp.session.duration_ms': sessionDuration,
        'mcp.session.message_count': sessionState.messageCount,
        'mcp.tool.success': reason !== 'error',
      });
    }

    sessionState.span.setStatus({ code: SpanStatusCode.OK });
    sessionState.span.end();
    clearTimeout(sessionState.idleTimer);

    // Decrement counters
    this.#counterManager.decrementCounter('sessions');
    if (sessionState.isToolCall) {
      this.#counterManager.decrementCounter('toolCalls');

      // Record tool call completion
      MetricsRecorder.recordToolCallCompletion(
        this.#url,
        sessionState.toolCallMethod || 'unknown',
        reason,
      );
    }

    this.#sessions.delete(sessionId);
    return true;
  }

  /**
   * Gets current session information for debugging
   */
  getSessionDebugInfo(): Array<{
    sessionId: string;
    messageCount: number;
    duration: number;
    isToolCall: boolean;
    toolCallMethod?: string;
    lastActivity: number;
  }> {
    const now = Date.now();
    return Array.from(this.#sessions.entries()).map(([sessionId, state]) => ({
      sessionId,
      messageCount: state.messageCount,
      duration: now - state.createdAt,
      isToolCall: !!state.isToolCall,
      toolCallMethod: state.toolCallMethod,
      lastActivity: now - state.lastActivity,
    }));
  }

  /**
   * Manually completes a stuck tool call session
   */
  forceCompleteToolCall(
    sessionId: string,
    reason: string = 'manual_completion',
  ): boolean {
    const sessionState = this.#sessions.get(sessionId);
    if (!sessionState || !sessionState.isToolCall) {
      return false;
    }

    const sessionDuration = Date.now() - sessionState.createdAt;

    sessionState.span.addEvent('tool.call.force_completed', {
      'mcp.session.duration_ms': sessionDuration,
      'mcp.session.message_count': sessionState.messageCount,
      'mcp.tool.completion_reason': reason,
    });

    sessionState.span.setStatus({ code: SpanStatusCode.OK });
    sessionState.span.end();
    clearTimeout(sessionState.idleTimer);

    // Decrement counters
    this.#counterManager.decrementCounter('sessions');
    this.#counterManager.decrementCounter('toolCalls');

    // Record tool call completion
    MetricsRecorder.recordToolCallCompletion(
      this.#url,
      sessionState.toolCallMethod || 'unknown',
      reason,
    );

    this.#sessions.delete(sessionId);

    log((l) =>
      l.warn('Tool call manually completed', {
        data: {
          sessionId,
          duration: sessionDuration,
          reason,
          method: sessionState.toolCallMethod,
        },
      }),
    );

    return true;
  }

  get sessionCount(): number {
    return this.#sessions.size;
  }

  // Message utility methods
  getMessageId(message: JSONRPCMessage): string | number | undefined {
    return this.hasId(message) ? message.id : undefined;
  }

  getMessageMethod(message: JSONRPCMessage): string | undefined {
    return this.hasMethod(message) ? message.method : undefined;
  }

  private hasId(
    message: JSONRPCMessage,
  ): message is JSONRPCMessage & { id: string | number } {
    return 'id' in message && message.id !== undefined;
  }

  private hasMethod(
    message: JSONRPCMessage,
  ): message is JSONRPCMessage & { method: string } {
    return 'method' in message && typeof message.method === 'string';
  }
}

/**
 * @fileoverview Instrumented SSE MCP Transport Client with OpenTelemetry Support
 *
 * This module provides a comprehensive OpenTelemetry-instrumented wrapper around the base
 * Server-Sent Events (SSE) transport for Model Context Protocol (MCP) client connections.
 *
 * Features:
 * - Comprehensive OTEL metrics (connections, messages, errors, durations, sizes)
 * - Detailed tracing for all transport operations with parent/child span relationships
 * - Two operational modes: WARNING (high-level) and DEBUG (detailed)
 * - Robust error handling with safe async wrappers
 * - Session management with idle timeout handling
 * - Active session and tool call tracking with UpDownCounters
 * - Distributed tracing support with trace context injection
 * - Tool call lifecycle tracking from initiation to completion
 * - Manual counter reset and debugging utilities
 * - Timeout handling for async operations (connect, send)
 * - Type-safe message property access
 *
 * Environment Variables:
 * - MCP_OTEL_MODE: 'WARNING' (default) or 'DEBUG'
 *
 * Counter Management:
 * - Active sessions are tracked and automatically managed
 * - Tool calls are detected and tracked separately
 * - Counters never go negative and can be manually reset
 * - Debugging methods provide insight into current state
 *
 * Distributed Tracing:
 * - All spans created as children of current active span
 * - Trace context injection available for HTTP headers
 * - Session spans track entire tool call lifecycle
 * - Transport span tracks connection lifetime
 *
 * @module InstrumentedSseTransport
 * @version 1.1.0
 * @author NoEducation Project
 *
 * @legal
 * SPDX-License-Identifier: Apache-2.0
