 * WITHOUT WARRANTIES OR CONDITIONS OF ANY KIND, either express or implied.
 * See the License for the specific language governing permissions and
 * limitations under the License.
 */

import { trace, Span, SpanStatusCode, metrics } from '@opentelemetry/api';
import { SseMCPTransport } from '../ai.sdk';
import type { JSONRPCMessage } from '../ai.sdk';

import { isAbortError, isError } from '@/lib/react-util/utility-methods';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import { log } from '@/lib/logger';

// OTEL Configuration
const OTEL_MODE = process.env.MCP_OTEL_MODE?.toUpperCase() || 'WARNING'; // WARNING or DEBUG
const DEBUG_MODE = OTEL_MODE === 'DEBUG';

// OTEL Instrumentation
const tracer = trace.getTracer('mcp-client-transport', '1.0.0');
const meter = metrics.getMeter('mcp-client-transport', '1.0.0');

// Metrics
const connectionCounter = meter.createCounter('mcp_connections_total', {
  description: 'Total number of MCP connection attempts',
});

const messageCounter = meter.createCounter('mcp_messages_total', {
  description: 'Total number of MCP messages sent/received',
});

const errorCounter = meter.createCounter('mcp_errors_total', {
  description: 'Total number of MCP transport errors',
});

const sessionDurationHistogram = meter.createHistogram(
  'mcp_session_duration_ms',
  {
    description: 'Duration of MCP sessions in milliseconds',
  },
);

const messageSizeHistogram = meter.createHistogram('mcp_message_size_bytes', {
  description: 'Size of MCP messages in bytes',
});

const operationDurationHistogram = meter.createHistogram(
  'mcp_operation_duration_ms',
  {
    description: 'Duration of MCP operations in milliseconds',
  },
);

// Active counters for monitoring
const activeSessionsGauge = meter.createUpDownCounter('mcp_active_sessions', {
  description: 'Number of currently active MCP sessions',
});

const activeToolCallsGauge = meter.createUpDownCounter(
  'mcp_active_tool_calls',
  {
    description: 'Number of currently active MCP tool calls',
  },
);

const toolCallCounter = meter.createCounter('mcp_tool_calls_total', {
  description: 'Total number of MCP tool calls initiated',
});

const toolCallCompletionCounter = meter.createCounter(
  'mcp_tool_call_completions_total',
  {
    description: 'Total number of MCP tool call completions',
  },
);

interface SpanState {
  span: Span;
  idleTimer: ReturnType<typeof setTimeout>;
  createdAt: number;
  messageCount: number;
  lastActivity: number;
  isToolCall?: boolean;
  toolCallMethod?: string;
}

interface OperationMetrics {
  startTime: number;
  operation: string;
  messageId?: string | number;
}

interface ActiveCounters {
  sessions: number;
  toolCalls: number;
}

const IDLE_TIMEOUT_MS = 60 * 60 * 1000; // 60 minutes
const CONNECTION_TIMEOUT_MS = 30 * 1000; // 30 seconds for connection
const SEND_TIMEOUT_MS = 10 * 1000; // 10 seconds for sending messages

// Tool call methods that initiate sessions
const TOOL_CALL_METHODS = new Set([
  'tools/call',
  'tools/list',
  'tools/get',
  'prompts/list',
  'prompts/get',
  'resources/list',
  'resources/read',
  'resources/subscribe',
]);

type InstrumentedSseTransportOptions = {
  url: string;
  headers?: Record<string, string>;
  onclose?: () => void;
  onmessage?: (message: JSONRPCMessage) => void;
  onerror: ((error: unknown) => void) | ((error: Error) => void);
};

/**
 * Instrumented SSE Transport with comprehensive OpenTelemetry support.
 *
 * Supports two operational modes:
 * - WARNING: High-level metrics and error reporting
 * - DEBUG: Detailed tracing of all operations and message flows
 *
 * Key Features:
 * - Active session and tool call tracking with UpDownCounters
 * - Distributed tracing with automatic parent/child span relationships
 * - Tool call lifecycle management (initiation to completion)
 * - Trace context injection for HTTP headers
 * - Manual debugging and counter reset capabilities
 * - Timeout handling for all async operations
 * - Safe error handling that never crashes the process
 *
 * Environment Variables:
 * - MCP_OTEL_MODE: 'WARNING' (default) or 'DEBUG'
 *
 * Public Methods:
 * - getActiveCounters(): Get current session and tool call counts
 * - resetActiveCounters(): Manually reset all counters to zero
 * - getSessionDebugInfo(): Get detailed session information
 * - forceCompleteToolCall(): Manually complete stuck tool calls
 * - getEnhancedHeaders(): Get headers with trace context
 * - updateHeadersWithTraceContext(): Update existing headers with trace context
 *
 * Static Methods:
 * - injectTraceContext(): Inject trace context into headers (can be used before construction)
 */
export class InstrumentedSseTransport extends SseMCPTransport {
  #sessions = new Map<string, SpanState>();
  #onmessage?: (message: JSONRPCMessage) => void;
  #onerror: (error: unknown) => void;
  #onclose?: () => void;
  #transportSpan?: Span;
  #connectionStartTime: number = 0;
  #operationMetrics = new Map<string, OperationMetrics>();
  #isClosing = false;
  #activeCounters: ActiveCounters = { sessions: 0, toolCalls: 0 };

  constructor(opts: InstrumentedSseTransportOptions) {
    let constructorSpan: Span | undefined;

    try {
      // Start constructor instrumentation as child of current active span
      constructorSpan = tracer.startSpan('mcp.transport.constructor', {
        attributes: {
          'mcp.transport.url': opts.url,
          'mcp.transport.mode': OTEL_MODE,
          'mcp.transport.has_headers': !!opts.headers,
        },
      });

      if (DEBUG_MODE) {
        log((l) =>
          l.debug('Initializing InstrumentedSseTransport', {
            data: {
              url: opts.url,
              mode: OTEL_MODE,
              headers: opts.headers ? Object.keys(opts.headers) : [],
            },
          }),
        );
      }

      // Inject trace context into headers for distributed tracing before calling super
      const enhancedHeaders = InstrumentedSseTransport.injectTraceContext(
        opts.headers || {},
      );
      super({ ...opts, headers: enhancedHeaders }); // Call the base constructor with enhanced headers

      if (!opts.onerror) {
        const error = new Error('onerror handler is required');
        constructorSpan?.recordException(error);
        constructorSpan?.setStatus({
          code: SpanStatusCode.ERROR,
          message: error.message,
        });
        throw error;
      }

      this.#onclose = opts.onclose;
      this.#onmessage = opts.onmessage;
      this.#onerror = this.#safeErrorHandler((e: unknown) => {
        if (isError(e)) {
          if (isAbortError(e)) {
            log((l) =>
              l.verbose(
                `InstrumentedSseTransport (From error handler...) aborted; isClosing=${this.#isClosing}`,
              ),
            );
          } else {
            opts.onerror(e);
          }
        } else {
          opts.onerror(new Error(String(e)));
        }
      });

      // Override base callbacks with instrumented versions
      super.onclose = this.#safeAsyncWrapper(
        'handleClose',
        this.handleClose.bind(this),
      );
      super.onerror = this.#safeAsyncWrapper(
        'handleError',
        this.handleError.bind(this),
      );
      super.onmessage = this.#safeAsyncWrapper(
        'handleMessage',
        this.handleMessage.bind(this),
      );

      // Record successful construction
      connectionCounter.add(1, {
        'mcp.transport.url': opts.url,
        'mcp.transport.operation': 'constructor',
        'mcp.transport.status': 'success',
      });

      constructorSpan?.setStatus({ code: SpanStatusCode.OK });

      if (DEBUG_MODE) {
        log((l) =>
          l.debug('InstrumentedSseTransport initialized successfully'),
        );
      }
    } catch (error) {
      /*
      if (isAbortError(error)) {
        // Suppress abort errors; this is a disconnect not a construction failure.
        const isClosing = this.#isClosing;
        log((l) =>
          l.verbose(
            `InstrumentedSseTransport construction aborted; isClosing=${isClosing}`,
          ),
        );
      }
      */
      error = LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        source: 'MCP Transport Constructor',
      });
      // Record construction failure
      connectionCounter.add(1, {
        'mcp.transport.url': opts.url,
        'mcp.transport.operation': 'constructor',
        'mcp.transport.status': 'error',
      });

      errorCounter.add(1, {
        'mcp.transport.operation': 'constructor',
        'mcp.transport.error_type': isError(error) ? error.name : 'unknown',
      });

      constructorSpan?.recordException(error as Error);
      constructorSpan?.setStatus({
        code: SpanStatusCode.ERROR,
        message: isError(error) ? error.message : String(error),
      });
      log((l) =>
        l.error('Failed to initialize InstrumentedSseTransport', {
          data: { error: isError(error) ? error.message : String(error) },
        }),
      );

      throw error;
    } finally {
      constructorSpan?.end();
    }
  }

  // === Safety Wrappers ===

  /**
   * Creates a safe wrapper for error handlers that never throws
   */
  #safeErrorHandler = (handler: (error: unknown) => void) => {
    return (error: unknown) => {
      try {
        handler(error);
      } catch (wrapperError) {
        // Last resort logging - error handler itself failed
        log((l) =>
          l.error('Error handler failed', {
            data: {
              originalError: isError(error) ? error.message : String(error),
              wrapperError: isError(wrapperError)
                ? wrapperError.message
                : String(wrapperError),
            },
          }),
        );
      }
    };
  };

  /**
   * Creates a safe async wrapper that catches all exceptions
   */
  #safeAsyncWrapper = <T extends unknown[], R>(
    operationName: string,
    fn: (...args: T) => R | Promise<R>,
  ) => {
    return async (...args: T): Promise<R | void> => {
      const startTime = Date.now();
      let span: Span | undefined;

      try {
        if (DEBUG_MODE) {
          // Create span as child of current active span
          span = tracer.startSpan(`mcp.transport.${operationName}`, {
            attributes: {
              'mcp.transport.operation': operationName,
              'mcp.transport.url': this.url?.toString(),
            },
          });
        }

        const result = await fn(...args);

        if (DEBUG_MODE) {
          const duration = Date.now() - startTime;
          operationDurationHistogram.record(duration, {
            'mcp.transport.operation': operationName,
            'mcp.transport.status': 'success',
          });

          span?.addEvent(`${operationName}.completed`, {
            'mcp.transport.duration_ms': duration,
          });
          span?.setStatus({ code: SpanStatusCode.OK });
        }

        return result;
      } catch (error) {
        const duration = Date.now() - startTime;

        // Record error metrics
        errorCounter.add(1, {
          'mcp.transport.operation': operationName,
          'mcp.transport.error_type': isError(error) ? error.name : 'unknown',
        });

        operationDurationHistogram.record(duration, {
          'mcp.transport.operation': operationName,
          'mcp.transport.status': 'error',
        });

        span?.recordException(error as Error);
        span?.setStatus({
          code: SpanStatusCode.ERROR,
          message: isError(error) ? error.message : String(error),
        });

        log((l) =>
          l.error(`MCP Transport ${operationName} failed`, {
            data: {
              error: isError(error) ? error.message : String(error),
              duration,
              stack: isError(error) ? error.stack : undefined,
            },
          }),
        );

        // Convert to LoggedError and pass to error handler
        const loggedError = LoggedError.isTurtlesAllTheWayDownBaby(error, {
          log: false, // Already logged above
          source: `MCP Transport ${operationName}`,
          data: { operationName, duration },
        });

        this.#onerror(loggedError);
      } finally {
        span?.end();
      }
    };
  };

  /**
   * Records operation metrics for detailed tracking
   */
  #recordOperation = (
    operation: string,
    messageId?: string | number,
  ): string => {
    const operationId = `${operation}-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
    this.#operationMetrics.set(operationId, {
      startTime: Date.now(),
      operation,
      messageId,
    });
    return operationId;
  };

  /**
   * Completes operation metrics tracking
   */
  #completeOperation = (
    operationId: string,
    status: 'success' | 'error' = 'success',
  ) => {
    const metrics = this.#operationMetrics.get(operationId);
    if (metrics) {
      const duration = Date.now() - metrics.startTime;
      operationDurationHistogram.record(duration, {
        'mcp.transport.operation': metrics.operation,
        'mcp.transport.status': status,
      });
      this.#operationMetrics.delete(operationId);

      if (DEBUG_MODE) {
        log((l) =>
          l.debug(`Operation ${metrics.operation} completed`, {
            data: { duration, status, messageId: metrics.messageId },
          }),
        );
      }
    }
  };

  // === Property Overrides ===

  override get onmessage(): ((message: JSONRPCMessage) => void) | undefined {
    return this.#onmessage;
  }

  override set onmessage(
    handler: ((message: JSONRPCMessage) => void) | undefined,
  ) {
    this.#onmessage = handler;
  }

  override get onerror(): (error: unknown) => void {
    return this.#onerror;
  }

  override set onerror(handler: (error: unknown) => void) {
    if (!handler) {
      throw new Error('onerror handler is required');
    }
    this.#onerror = this.#safeErrorHandler(handler);
  }

  override get onclose(): (() => void) | undefined {
    return this.#onclose;
  }

  override set onclose(handler: (() => void) | undefined) {
    this.#onclose = handler;
  }

  toString(): string {
    return `InstrumentedSseTransport(${this.url?.toString() || 'unknown'})`;
  }
  // === MCP Client Transport Methods ===

  override async start(): Promise<void> {
    let span: Span | undefined;
    const operationId = this.#recordOperation('start');

    try {
      this.#connectionStartTime = Date.now();

      // Create span as child of active span if one exists
      span = tracer.startSpan('mcp.transport.start', {
        attributes: {
          'mcp.transport.url': this.url?.toString(),
          'mcp.transport.mode': OTEL_MODE,
        },
      });

      this.#transportSpan = span;

      if (DEBUG_MODE) {
        log((l) =>
          l.debug('Starting MCP Client Transport', {
            data: { url: this.url?.toString(), mode: OTEL_MODE },
          }),
        );
      }

      // Record connection attempt
      connectionCounter.add(1, {
        'mcp.transport.url': this.url?.toString() || 'unknown',
        'mcp.transport.operation': 'start',
        'mcp.transport.status': 'attempt',
      });

      // Apply connection timeout
      await this.#withTimeout(
        super.start(),
        CONNECTION_TIMEOUT_MS,
        'MCP connection',
      );

      // Record successful connection
      connectionCounter.add(1, {
        'mcp.transport.url': this.url?.toString() || 'unknown',
        'mcp.transport.operation': 'start',
        'mcp.transport.status': 'success',
      });

      span.setStatus({ code: SpanStatusCode.OK });
      this.#completeOperation(operationId, 'success');

      if (DEBUG_MODE) {
        log((l) => l.debug('MCP Client Transport started successfully'));
      }
    } catch (error) {
      if (isAbortError(error)) {
        // Suppress abort errors; this is a disconnect not a construction failure.
        const isClosing = this.#isClosing;
        log((l) =>
          l.verbose(
            `InstrumentedSseTransport start() aborted; isClosing=${isClosing}`,
          ),
        );
        return;
      }
      // Record failed connection
      connectionCounter.add(1, {
        'mcp.transport.url': this.url?.toString() || 'unknown',
        'mcp.transport.operation': 'start',
        'mcp.transport.status': 'error',
      });

      errorCounter.add(1, {
        'mcp.transport.operation': 'start',
        'mcp.transport.error_type': isError(error) ? error.name : 'unknown',
      });

      span?.recordException(error as Error);
      span?.setStatus({
        code: SpanStatusCode.ERROR,
        message: isError(error) ? error.message : String(error),
      });

      this.#completeOperation(operationId, 'error');

      log((l) =>
        l.error('Failed to start MCP Client Transport', {
          data: {
            error: isError(error) ? error.message : String(error),
            stack: isError(error) ? error.stack : undefined,
          },
        }),
      );

      throw error;
    } finally {
      // Don't end the transport span here - it should stay active for the connection duration
    }
  }

  override async close(): Promise<void> {
    let span: Span | undefined;
    const operationId = this.#recordOperation('close');

    try {
      this.#isClosing = true;

      span = tracer.startSpan('mcp.transport.close', {
        attributes: {
          'mcp.transport.url': this.url?.toString(),
          'mcp.transport.session_count': this.#sessions.size,
        },
      });

      if (DEBUG_MODE) {
        log((l) =>
          l.debug('Closing MCP Client Transport', {
            data: {
              url: this.url?.toString(),
              sessionCount: this.#sessions.size,
              connectionDuration: this.#connectionStartTime
                ? Date.now() - this.#connectionStartTime
                : 0,
            },
          }),
        );
      }

      // Close all active sessions
      for (const [, sessionState] of this.#sessions.entries()) {
        const sessionDuration = Date.now() - sessionState.createdAt;
        sessionDurationHistogram.record(sessionDuration, {
          'mcp.transport.url': this.url?.toString() || 'unknown',
          'mcp.transport.session_type': 'normal_close',
        });

        sessionState.span.addEvent('session.closing', {
          'mcp.session.duration_ms': sessionDuration,
          'mcp.session.message_count': sessionState.messageCount,
        });
        sessionState.span.setStatus({ code: SpanStatusCode.OK });
        sessionState.span.end();
        clearTimeout(sessionState.idleTimer);

        // Decrement counters safely
        this.#decrementCounter('sessions');
        if (sessionState.isToolCall) {
          this.#decrementCounter('toolCalls');

          // Record tool call completion
          toolCallCompletionCounter.add(1, {
            'mcp.transport.url': this.url?.toString() || 'unknown',
            'mcp.tool.method': sessionState.toolCallMethod || 'unknown',
            'mcp.tool.completion_reason': 'transport_close',
          });
        }
      }
      this.#sessions.clear();

      await super.close();

      // End the transport span
      if (this.#transportSpan) {
        const connectionDuration = this.#connectionStartTime
          ? Date.now() - this.#connectionStartTime
          : 0;
        this.#transportSpan.addEvent('transport.closed', {
          'mcp.transport.duration_ms': connectionDuration,
        });
        this.#transportSpan.setStatus({ code: SpanStatusCode.OK });
        this.#transportSpan.end();
      }

      span.setStatus({ code: SpanStatusCode.OK });
      this.#completeOperation(operationId, 'success');

      if (DEBUG_MODE) {
        log((l) => l.debug('MCP Client Transport closed successfully'));
      }
    } catch (error) {
      errorCounter.add(1, {
        'mcp.transport.operation': 'close',
        'mcp.transport.error_type': isError(error) ? error.name : 'unknown',
      });

      span?.recordException(error as Error);
      span?.setStatus({
        code: SpanStatusCode.ERROR,
        message: isError(error) ? error.message : String(error),
      });

      this.#completeOperation(operationId, 'error');

      log((l) =>
        l.error('Failed to close MCP Client Transport', {
          data: { error: isError(error) ? error.message : String(error) },
        }),
      );

      throw error;
    } finally {
      span?.end();
    }
  }

  override async send(message: JSONRPCMessage): Promise<void> {
    let span: Span | undefined;
    const messageId = this.#getMessageId(message);
    const messageMethod = this.#getMessageMethod(message);
    const operationId = this.#recordOperation('send', messageId);

    try {
      const messageStr = JSON.stringify(message);
      const messageSize = new TextEncoder().encode(messageStr).length;

      // Create span as child of active span if one exists
      span = tracer.startSpan('mcp.transport.send', {
        attributes: {
          'mcp.transport.url': this.url?.toString(),
          'mcp.message.id': String(messageId || 'unknown'),
          'mcp.message.method': messageMethod || 'unknown',
          'mcp.message.size_bytes': messageSize,
        },
      });

      // Check if this is a tool call and track it
      const isToolCall = messageMethod && TOOL_CALL_METHODS.has(messageMethod);

      // Create or update session for this message if it has an ID
      let sessionState: SpanState | undefined;
      if (messageId) {
        sessionState = this.#getOrCreateSession(message);

        // If this is a new session, increment session counter
        if (sessionState && sessionState.messageCount === 1) {
          this.#incrementCounter('sessions');
        }

        // If this is a tool call and session wasn't already marked as tool call
        if (sessionState && isToolCall && !sessionState.isToolCall) {
          sessionState.isToolCall = true;
          sessionState.toolCallMethod = messageMethod;
          this.#incrementCounter('toolCalls');

          toolCallCounter.add(1, {
            'mcp.transport.url': this.url?.toString() || 'unknown',
            'mcp.tool.method': messageMethod || 'unknown',
          });
        }
      }

      // Record message metrics
      messageCounter.add(1, {
        'mcp.transport.url': this.url?.toString() || 'unknown',
        'mcp.transport.direction': 'outbound',
        'mcp.message.method': messageMethod || 'unknown',
      });

      messageSizeHistogram.record(messageSize, {
        'mcp.transport.direction': 'outbound',
        'mcp.message.method': messageMethod || 'unknown',
      });

      if (DEBUG_MODE) {
        log((l) =>
          l.debug('Sending MCP Client Message', {
            data: {
              messageId,
              method: messageMethod,
              size: messageSize,
              url: this.url?.toString(),
              isToolCall,
            },
          }),
        );
      }

      // Apply send timeout
      await this.#withTimeout(
        super.send(message),
        SEND_TIMEOUT_MS,
        'MCP message send',
      );

      span.setStatus({ code: SpanStatusCode.OK });
      this.#completeOperation(operationId, 'success');

      if (DEBUG_MODE) {
        log((l) =>
          l.debug('MCP Client Message sent successfully', {
            data: { messageId, method: messageMethod },
          }),
        );
      }
    } catch (error) {
      errorCounter.add(1, {
        'mcp.transport.operation': 'send',
        'mcp.transport.error_type': isError(error) ? error.name : 'unknown',
        'mcp.message.method': messageMethod || 'unknown',
      });

      span?.recordException(error as Error);
      span?.setStatus({
        code: SpanStatusCode.ERROR,
        message: isError(error) ? error.message : String(error),
      });

      this.#completeOperation(operationId, 'error');

      log((l) =>
        l.error('Failed to send MCP Client Message', {
          data: {
            messageId,
            method: messageMethod,
            error: isError(error) ? error.message : String(error),
          },
        }),
      );

      throw error;
    } finally {
      span?.end();
    }
  }

  // === Helper Functions ===

  /**
   * Type guard to check if message has an id property
   */
  #hasId = (
    message: JSONRPCMessage,
  ): message is JSONRPCMessage & { id: string | number } => {
    return 'id' in message && message.id !== undefined;
  };

  /**
   * Type guard to check if message has a method property
   */
  #hasMethod = (
    message: JSONRPCMessage,
  ): message is JSONRPCMessage & { method: string } => {
    return 'method' in message && typeof message.method === 'string';
  };

  /**
   * Safely gets the message ID
   */
  #getMessageId = (message: JSONRPCMessage): string | number | undefined => {
    return this.#hasId(message) ? message.id : undefined;
  };

  /**
   * Safely gets the message method
   */
  #getMessageMethod = (message: JSONRPCMessage): string | undefined => {
    return this.#hasMethod(message) ? message.method : undefined;
  };

  /**
   * Creates or updates session state for message tracking
   */
  #getOrCreateSession = (message: JSONRPCMessage): SpanState | undefined => {
    const messageId = this.#getMessageId(message);
    if (!messageId) return undefined;

    const sessionId = String(messageId);
    let sessionState = this.#sessions.get(sessionId);

    if (!sessionState) {
      if (DEBUG_MODE) {
        log((l) =>
          l.debug('Creating new MCP session', {
            data: { sessionId, method: this.#getMessageMethod(message) },
          }),
        );
      }

      // Create session span as child of current active span
      const span = tracer.startSpan('mcp.session', {
        attributes: {
          'mcp.session.id': sessionId,
          'mcp.session.created_at': Date.now(),
          'mcp.transport.url': this.url?.toString(),
          'mcp.session.initiating_method':
            this.#getMessageMethod(message) || 'unknown',
        },
      });

      sessionState = {
        span,
        idleTimer: this.#setIdleTimer(sessionId),
        createdAt: Date.now(),
        messageCount: 0,
        lastActivity: Date.now(),
      };

      this.#sessions.set(sessionId, sessionState);
    }

    // Update activity
    sessionState.messageCount++;
    sessionState.lastActivity = Date.now();
    clearTimeout(sessionState.idleTimer);
    sessionState.idleTimer = this.#setIdleTimer(sessionId);

    return sessionState;
  };

  /**
   * Sets up idle timer for session cleanup
   */
  #setIdleTimer = (sessionId: string): ReturnType<typeof setTimeout> => {
    return setTimeout(() => {
      try {
        const sessionState = this.#sessions.get(sessionId);
        if (sessionState && !this.#isClosing) {
          const sessionDuration = Date.now() - sessionState.createdAt;

          sessionDurationHistogram.record(sessionDuration, {
            'mcp.transport.url': this.url?.toString() || 'unknown',
            'mcp.transport.session_type': 'idle_timeout',
          });

          sessionState.span.addEvent('session.idle_timeout', {
            'mcp.session.duration_ms': sessionDuration,
            'mcp.session.message_count': sessionState.messageCount,
          });
          sessionState.span.setStatus({ code: SpanStatusCode.OK });
          sessionState.span.end();

          // Decrement counters before removing session
          this.#decrementCounter('sessions');
          if (sessionState.isToolCall) {
            this.#decrementCounter('toolCalls');

            // Record tool call completion
            toolCallCompletionCounter.add(1, {
              'mcp.transport.url': this.url?.toString() || 'unknown',
              'mcp.tool.method': sessionState.toolCallMethod || 'unknown',
              'mcp.tool.completion_reason': 'idle_timeout',
            });
          }

          this.#sessions.delete(sessionId);

          if (DEBUG_MODE) {
            log((l) =>
              l.debug('Session idle timeout', {
                data: {
                  sessionId,
                  duration: sessionDuration,
                  messageCount: sessionState.messageCount,
                },
              }),
            );
          }
        }
      } catch (error) {
        log((l) =>
          l.error('Error in session idle timer', {
            data: {
              sessionId,
              error: isError(error) ? error.message : String(error),
            },
          }),
        );
      }
    }, IDLE_TIMEOUT_MS);
  };

  /**
   * Gets the current count of active sessions and tool calls
   */
  getActiveCounters(): { sessions: number; toolCalls: number } {
    return { ...this.#activeCounters };
  }

  /**
   * Manually resets all active counters to zero
   * Use this when you suspect counters are out of sync due to errors
   */
  resetActiveCounters(): void {
    if (DEBUG_MODE) {
      log((l) =>
        l.debug('Manually resetting active counters', {
          data: {
            previousSessions: this.#activeCounters.sessions,
            previousToolCalls: this.#activeCounters.toolCalls,
          },
        }),
      );
    }

    // Update metrics to reflect the reset
    activeSessionsGauge.add(-this.#activeCounters.sessions);
    activeToolCallsGauge.add(-this.#activeCounters.toolCalls);

    this.#activeCounters = { sessions: 0, toolCalls: 0 };

    log((l) => l.warn('Active counters have been manually reset to zero'));
  }

  /**
   * Gets current session information for debugging
   */
  getSessionDebugInfo(): Array<{
    sessionId: string;
    messageCount: number;
    duration: number;
    isToolCall: boolean;
    toolCallMethod?: string;
    lastActivity: number;
  }> {
    const now = Date.now();
    return Array.from(this.#sessions.entries()).map(([sessionId, state]) => ({
      sessionId,
      messageCount: state.messageCount,
      duration: now - state.createdAt,
      isToolCall: !!state.isToolCall,
      toolCallMethod: state.toolCallMethod,
      lastActivity: now - state.lastActivity,
    }));
  }

  /**
   * Manually completes a stuck tool call session
   * Use this when a tool call seems to be hanging
   */
  forceCompleteToolCall(
    sessionId: string,
    reason: string = 'manual_completion',
  ): boolean {
    const sessionState = this.#sessions.get(sessionId);
    if (!sessionState || !sessionState.isToolCall) {
      return false;
    }

    const sessionDuration = Date.now() - sessionState.createdAt;

    sessionState.span.addEvent('tool.call.force_completed', {
      'mcp.session.duration_ms': sessionDuration,
      'mcp.session.message_count': sessionState.messageCount,
      'mcp.tool.completion_reason': reason,
    });

    sessionState.span.setStatus({ code: SpanStatusCode.OK });
    sessionState.span.end();
    clearTimeout(sessionState.idleTimer);

    // Decrement counters
    this.#decrementCounter('sessions');
    this.#decrementCounter('toolCalls');

    // Record tool call completion
    toolCallCompletionCounter.add(1, {
      'mcp.transport.url': this.url?.toString() || 'unknown',
      'mcp.tool.method': sessionState.toolCallMethod || 'unknown',
      'mcp.tool.completion_reason': reason,
    });

    this.#sessions.delete(sessionId);

    log((l) =>
      l.warn('Tool call manually completed', {
        data: {
          sessionId,
          duration: sessionDuration,
          reason,
          method: sessionState.toolCallMethod,
        },
      }),
    );

    return true;
  }

  /**
   * Gets enhanced headers with trace context for HTTP requests
   * This method can be used by callers to get headers with trace context included
   */
  getEnhancedHeaders(
    baseHeaders: Record<string, string> = {},
  ): Record<string, string> {
    return this.#injectTraceContext(baseHeaders);
  }

  /**
   * Updates existing headers object with trace context in place
   * Returns true if trace context was injected, false otherwise
   */
  updateHeadersWithTraceContext(headers: Record<string, string>): boolean {
    const originalKeyCount = Object.keys(headers).length;
    const enhancedHeaders = this.#injectTraceContext(headers);

    // Copy new headers to the original object
    Object.assign(headers, enhancedHeaders);

    const wasInjected = Object.keys(headers).length > originalKeyCount;

    if (wasInjected && DEBUG_MODE) {
      log((l) =>
        l.debug('Trace context updated in existing headers', {
          data: {
            originalKeys: originalKeyCount,
            newKeys: Object.keys(headers).length,
          },
        }),
      );
    }

    return wasInjected;
  }

  /**
   * Static method to inject trace context into HTTP headers for distributed tracing
   * This can be called before the instance is created
   */
  static injectTraceContext(
    headers: Record<string, string> = {},
  ): Record<string, string> {
    const activeSpan = trace.getActiveSpan();
    if (activeSpan) {
      const spanContext = activeSpan.spanContext();
      if (spanContext.traceId && spanContext.spanId) {
        headers['traceparent'] =
          `00-${spanContext.traceId}-${spanContext.spanId}-${spanContext.traceFlags?.toString(16).padStart(2, '0') || '01'}`;

        if (DEBUG_MODE) {
          log((l) =>
            l.debug('Injected trace context into headers (static)', {
              data: {
                traceId: spanContext.traceId,
                spanId: spanContext.spanId,
                traceFlags: spanContext.traceFlags,
              },
            }),
          );
        }
      }
    }
    return headers;
  }

  /**
   * Instance method to inject trace context into HTTP headers for distributed tracing
   */
  #injectTraceContext = (
    headers: Record<string, string> = {},
  ): Record<string, string> => {
    return InstrumentedSseTransport.injectTraceContext(headers);
  };

  /**
   * Creates a timeout wrapper for async operations
   */
  #withTimeout = <T>(
    promise: Promise<T>,
    timeoutMs: number,
    operation: string,
  ): Promise<T> => {
    return Promise.race([
      promise,
      new Promise<never>((_, reject) => {
        setTimeout(() => {
          const error = new Error(
            `${operation} timed out after ${timeoutMs}ms`,
          );
          error.name = 'TimeoutError';
          reject(error);
        }, timeoutMs);
      }),
    ]);
  };

  /**
   * Safely increments active counters
   */
  #incrementCounter = (
    type: 'sessions' | 'toolCalls',
    amount: number = 1,
  ): void => {
    this.#activeCounters[type] = Math.max(
      0,
      this.#activeCounters[type] + amount,
    );

    if (type === 'sessions') {
      activeSessionsGauge.add(amount);
    } else {
      activeToolCallsGauge.add(amount);
    }

    if (DEBUG_MODE) {
      log((l) =>
        l.debug(`Incremented ${type} counter`, {
          data: { amount, newValue: this.#activeCounters[type] },
        }),
      );
    }
  };

  /**
   * Safely decrements active counters (never goes below 0)
   */
  #decrementCounter = (
    type: 'sessions' | 'toolCalls',
    amount: number = 1,
  ): void => {
    const oldValue = this.#activeCounters[type];
    this.#activeCounters[type] = Math.max(
      0,
      this.#activeCounters[type] - amount,
    );
    const actualDecrement = oldValue - this.#activeCounters[type];

    if (type === 'sessions') {
      activeSessionsGauge.add(-actualDecrement);
    } else {
      activeToolCallsGauge.add(-actualDecrement);
    }

    if (DEBUG_MODE) {
      log((l) =>
        l.debug(`Decremented ${type} counter`, {
          data: {
            requestedAmount: amount,
            actualAmount: actualDecrement,
            newValue: this.#activeCounters[type],
          },
        }),
      );
    }
  };

  private handleClose() {
    log((l) => l.verbose('MCP Client Transport Closed'));
    try {
      this.#onclose?.(); // pass through to client
    } catch (e) {
      log((l) => l.error('Error handling MCP Client Transport close:', e));
    }
  }

  private handleMessage(message: JSONRPCMessage) {
    log((l) => l.verbose('MCP Client Transport Message Received:', message));

    try {
      // Check if this is a response to a tool call and handle completion
      const messageId = this.#getMessageId(message);
      if (messageId) {
        const sessionId = String(messageId);
        const sessionState = this.#sessions.get(sessionId);

        // If this is a response (has result or error) and the session was a tool call
        if (
          sessionState &&
          sessionState.isToolCall &&
          ('result' in message || 'error' in message)
        ) {
          const sessionDuration = Date.now() - sessionState.createdAt;

          sessionDurationHistogram.record(sessionDuration, {
            'mcp.transport.url': this.url?.toString() || 'unknown',
            'mcp.transport.session_type': 'tool_call_response',
          });

          sessionState.span.addEvent('tool.call.completed', {
            'mcp.session.duration_ms': sessionDuration,
            'mcp.session.message_count': sessionState.messageCount,
            'mcp.tool.success': !('error' in message),
          });

          sessionState.span.setStatus({ code: SpanStatusCode.OK });
          sessionState.span.end();
          clearTimeout(sessionState.idleTimer);

          // Decrement counters
          this.#decrementCounter('sessions');
          this.#decrementCounter('toolCalls');

          // Record tool call completion
          toolCallCompletionCounter.add(1, {
            'mcp.transport.url': this.url?.toString() || 'unknown',
            'mcp.tool.method': sessionState.toolCallMethod || 'unknown',
            'mcp.tool.completion_reason':
              'error' in message ? 'error' : 'success',
          });

          this.#sessions.delete(sessionId);

          if (DEBUG_MODE) {
            log((l) =>
              l.debug('Tool call completed', {
                data: {
                  sessionId,
                  duration: sessionDuration,
                  success: !('error' in message),
                  method: sessionState.toolCallMethod,
                },
              }),
            );
          }
        }
      }

      this.#onmessage?.(message); // pass through to client
    } catch (e) {
      log((l) => l.error('Error handling MCP Client Transport message:', e));
    }
  }

  private handleError(error: unknown) {
    const le = LoggedError.isTurtlesAllTheWayDownBaby(error, {
      log: true,
      source: 'MCP Client Transport',
      data: {
        details: 'Error occurred in MCP Client Transport',
      },
    });
    try {
      this.#onerror(le); // pass through to client
    } catch (e) {
      log((l) => l.error('Error handling MCP Client Transport error:', e));
    }
  }
}

/**
 * TransportPlugin interface for MCP Client Transport plugins
 */
export interface TransportPlugin {
  onMessage?(message: JSONRPCMessage): void;
  onError?(error: Error): void;
  onClose?(): void;
}

/**
 * @fileoverview Trace Context Management for MCP Transport
 *
 * This module handles distributed tracing support with trace context injection
 * for HTTP headers.
 */

import { trace } from '@opentelemetry/api';
import { log } from '@/lib/logger';
import { DEBUG_MODE } from '../metrics/otel-metrics';

/**
 * Manages trace context injection for distributed tracing
 */
export class TraceContextManager {
  /**
   * Static method to inject trace context into HTTP headers for distributed tracing
   * This can be called before the instance is created
   */
  static injectTraceContext(
    headers: Record<string, string> = {},
  ): Record<string, string> {
    const activeSpan = trace.getActiveSpan();
    if (activeSpan) {
      const spanContext = activeSpan.spanContext();
      if (spanContext.traceId && spanContext.spanId) {
        headers['traceparent'] =
          `00-${spanContext.traceId}-${spanContext.spanId}-${spanContext.traceFlags?.toString(16).padStart(2, '0') || '01'}`;

        if (DEBUG_MODE) {
          log((l) =>
            l.debug('Injected trace context into headers (static)', {
              data: {
                traceId: spanContext.traceId,
                spanId: spanContext.spanId,
                traceFlags: spanContext.traceFlags,
              },
            }),
          );
        }
      }
    }
    return headers;
  }

  /**
   * Gets enhanced headers with trace context for HTTP requests
   * This method can be used by callers to get headers with trace context included
   */
  static getEnhancedHeaders(
    baseHeaders: Record<string, string> = {},
  ): Record<string, string> {
    return TraceContextManager.injectTraceContext(baseHeaders);
  }

  /**
   * Updates existing headers object with trace context in place
   * Returns true if trace context was injected, false otherwise
   */
  static updateHeadersWithTraceContext(
    headers: Record<string, string>,
  ): boolean {
    const originalKeyCount = Object.keys(headers).length;
    const enhancedHeaders = TraceContextManager.injectTraceContext(headers);

    // Copy new headers to the original object
    Object.assign(headers, enhancedHeaders);

    const wasInjected = Object.keys(headers).length > originalKeyCount;

    if (wasInjected && DEBUG_MODE) {
      log((l) =>
        l.debug('Trace context updated in existing headers', {
          data: {
            originalKeys: originalKeyCount,
            newKeys: Object.keys(headers).length,
          },
        }),
      );
    }

    return wasInjected;
  }
}

/**
 * @fileoverview Safety Utilities for MCP Transport
 *
 * This module provides error handling wrappers, timeout utilities, and
 * safe operation patterns.
 */

import { Span, SpanStatusCode } from '@opentelemetry/api';
import { tracer, MetricsRecorder, DEBUG_MODE } from '../metrics/otel-metrics';
import { isError } from '@/lib/react-util/utility-methods';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import { log } from '@/lib/logger';

// Timeout constants
export const CONNECTION_TIMEOUT_MS = 30 * 1000; // 30 seconds for connection
export const SEND_TIMEOUT_MS = 5 * 60 * 1000; // 5 minutes for sending messages

export interface OperationMetrics {
  startTime: number;
  operation: string;
  messageId?: string | number;
}

/**
 * Provides safety utilities for async operations and error handling
 */
export class SafetyUtils {
  #operationMetrics = new Map<string, OperationMetrics>();
  #url: string;

  constructor(url: string) {
    this.#url = url;
  }

  /**
   * Creates a safe wrapper for error handlers that never throws
   */
  createSafeErrorHandler(
    handler: (error: unknown) => void,
  ): (error: unknown) => void {
    return (error: unknown) => {
      try {
        handler(error);
      } catch (wrapperError) {
        // Last resort logging - error handler itself failed
        log((l) =>
          l.error('Error handler failed', {
            data: {
              originalError: isError(error) ? error.message : String(error),
              wrapperError: isError(wrapperError)
                ? wrapperError.message
                : String(wrapperError),
            },
          }),
        );
      }
    };
  }

  /**
   * Creates a safe async wrapper that catches all exceptions
   */
  createSafeAsyncWrapper<T extends unknown[], R>(
    operationName: string,
    fn: (...args: T) => R | Promise<R>,
    errorHandler: (error: unknown) => void,
  ): (...args: T) => Promise<R | void> {
    return async (...args: T): Promise<R | void> => {
      const startTime = Date.now();
      let span: Span | undefined;

      try {
        if (DEBUG_MODE) {
          // Create span as child of current active span
          span = tracer.startSpan(`mcp.transport.${operationName}`, {
            attributes: {
              'mcp.transport.operation': operationName,
              'mcp.transport.url': this.#url,
            },
          });
        }

        const result = await fn(...args);

        if (DEBUG_MODE) {
          const duration = Date.now() - startTime;
          MetricsRecorder.recordOperationDuration(
            duration,
            operationName,
            'success',
          );

          span?.addEvent(`${operationName}.completed`, {
            'mcp.transport.duration_ms': duration,
          });
          span?.setStatus({ code: SpanStatusCode.OK });
        }

        return result;
      } catch (error) {
        const duration = Date.now() - startTime;

        // Record error metrics
        MetricsRecorder.recordError(
          operationName,
          isError(error) ? error.name : 'unknown',
        );
        MetricsRecorder.recordOperationDuration(
          duration,
          operationName,
          'error',
        );

        span?.recordException(error as Error);
        span?.setStatus({
          code: SpanStatusCode.ERROR,
          message: isError(error) ? error.message : String(error),
        });

        log((l) =>
          l.error(`MCP Transport ${operationName} failed`, {
            data: {
              error: isError(error) ? error.message : String(error),
              duration,
              stack: isError(error) ? error.stack : undefined,
            },
          }),
        );

        // Convert to LoggedError and pass to error handler
        const loggedError = LoggedError.isTurtlesAllTheWayDownBaby(error, {
          log: false, // Already logged above
          source: `MCP Transport ${operationName}`,
          data: { operationName, duration },
        });

        errorHandler(loggedError);
      } finally {
        span?.end();
      }
    };
  }

  /**
   * Records operation metrics for detailed tracking
   */
  recordOperation(operation: string, messageId?: string | number): string {
    const operationId = `${operation}-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
    this.#operationMetrics.set(operationId, {
      startTime: Date.now(),
      operation,
      messageId,
    });
    return operationId;
  }

  /**
   * Completes operation metrics tracking
   */
  completeOperation(
    operationId: string,
    status: 'success' | 'error' = 'success',
  ) {
    const metrics = this.#operationMetrics.get(operationId);
    if (metrics) {
      const duration = Date.now() - metrics.startTime;
      MetricsRecorder.recordOperationDuration(
        duration,
        metrics.operation,
        status,
      );
      this.#operationMetrics.delete(operationId);

      if (DEBUG_MODE) {
        log((l) =>
          l.debug(`Operation ${metrics.operation} completed`, {
            data: { duration, status, messageId: metrics.messageId },
          }),
        );
      }
    }
  }

  /**
   * Creates a timeout wrapper for async operations
   */
  withTimeout<T>(
    promise: Promise<T>,
    timeoutMs: number,
    operation: string,
  ): Promise<T> {
    return Promise.race([
      promise,
      new Promise<never>((_, reject) => {
        setTimeout(() => {
          const error = new Error(
            `${operation} timed out after ${timeoutMs}ms`,
          );
          error.name = 'TimeoutError';
          reject(error);
        }, timeoutMs);
      }),
    ]);
  }
}

/**
 * MCP Default Tool Setup Utilities
 * ------------------------------------------------------------------
 * This module centralizes construction of a standard set of Model Context
 * Protocol (MCP) tool providers used by the AI orchestration layer. It
 * produces a composable tool provider collection based on runtime options
 * such as request context, write permissions, and optional memory tooling.
 *
 * EXPORTS
 *  - {@link getMcpClientHeaders}: Extracts / synthesizes HTTP headers (cookies,
 *    correlation IDs) required by remote MCP tool endpoints.
 *  - {@link setupDefaultTools}: High-level factory that assembles an array of
 *    provider configurations and delegates to `toolProviderSetFactory`.
 *
 * DESIGN PRINCIPLES
 *  1. Isolation: HTTP header extraction is separate from provider assembly.
 *  2. Extensibility: Add new provider sources by pushing onto the `options`
 *     array before invocation of `toolProviderSetFactory`.
 *  3. Safety: Only forwards the session cookie when present; omits entirely
 *     if absent to avoid sending empty tokens.
 *  4. Determinism: Absent `req` or when `memoryEnabled` is false, those
 *     providers are simply not includedno partial objects.
 *
 * ENVIRONMENT VARIABLES (via env())
 *  - NEXT_PUBLIC_HOSTNAME: Base host used to build first-party tool SSE URL.
 *  - MEM0_API_HOST, MEM0_USERNAME: Configure optional memory provider path.
 *
 * NOTE: The underlying `toolProviderSetFactory` is expected to merge/instantiate
 * the specified provider configurations and return an aggregated interface the
 * caller can hand to higher-level AI tooling.
 */
import { toolProviderSetFactory } from './toolProviderFactory';
import { env } from '@/lib/site-util/env';
import { ToolProviderFactoryOptions, ToolProviderSet } from '../types';
import { NextRequest } from 'next/server';
import { fromUserId as fromUser } from '@/lib/auth/impersonation/impersonation-factory';
import { User } from 'next-auth';

/**
 * Builds a minimal header map for MCP client connections.
 *
 * Responsibilities:
 *  - Conditionally attaches a chat history correlation header.
 *  - Forwards a session cookie (Auth.js) when present so server-originating
 *    tool invocations can perform authenticated actions.
 *
 * Cookie Forwarding Logic:
 *  If `authjs.session-token` exists in the request cookies, sets a `Cookie`
 *  header with only that token (scope-limited forwarding instead of sending
 *  the entire cookie jar).
 *
 * @param params.req Incoming Next.js request object (server context).
 * @param params.chatHistoryId Optional chat history identifier for correlation.
 * @returns A record of header key/value pairs safe for upstream MCP requests.
 */
export const getMcpClientHeaders = ({
  req,
  chatHistoryId,
}: {
  req: NextRequest;
  chatHistoryId?: string;
}): Record<string, string> => {
  const ret: { [key: string]: string } = {
    ...(chatHistoryId ? { 'x-chat-history-id': chatHistoryId } : {}),
  };
  const sessionCookie = req.cookies?.get('authjs.session-token')?.value ?? '';
  if (sessionCookie.length > 0) {
    ret.Cookie = `authjs.session-token=${sessionCookie}`;
  }
  return ret;
};

/**
 * Constructs and returns the default set of MCP tool providers based on
 * runtime configuration flags.
 *
 * Provider Sources:
 *  1. First-party tools (if `req` is supplied)  Configured with SSE endpoint
 *     at `/api/ai/tools/sse` relative to the public hostname. Write capability
 *     is governed by `writeEnabled`.
 *  2. Memory provider (if `memoryEnabled` true)  An auxiliary tool namespace
 *     for persistent or contextual memory operations (OpenMemory / MEM0).
 *
 * Header Strategy:
 *  Uses {@link getMcpClientHeaders} for request-scoped headers, and attaches
 *  caching directives for the memory provider to avoid unintended intermediaries.
 *
 * @param params.writeEnabled Optional flag enabling write operations for the
 *        first-party tool provider. Undefined treated as falsey (read-only).
 * @param params.req Optional Next.js request; absence skips first-party tools.
 * @param params.chatHistoryId Optional identifier passed to headers for context.
 * @param params.memoryEnabled Defaults to true; toggles memory provider inclusion.
 * @returns A promise resolving to the aggregated provider set returned by
 *          `toolProviderSetFactory`.
 */
export const setupDefaultTools = async ({
  writeEnabled,
  req,
  chatHistoryId,
  user,
  memoryEnabled = true,
}: {
  writeEnabled?: boolean;
  req?: NextRequest;
  chatHistoryId?: string;
  memoryEnabled?: boolean;
  user: User | undefined;
}): Promise<ToolProviderSet> => {
  const options: Array<ToolProviderFactoryOptions> = [];
  const defaultHeaders = {
    ...(chatHistoryId ? { 'x-chat-history-id': chatHistoryId } : {}),
  };
  if (req) {
    const sessionToken = req.cookies?.get('authjs.session-token')?.value ?? '';
    options.push({
      allowWrite: writeEnabled,
      url: new URL('/api/ai/tools/sse', env('NEXT_PUBLIC_HOSTNAME')).toString(),
      headers: async () => ({
        ...defaultHeaders,
        ...(sessionToken
          ? { Cookie: `authjs.session-token=${sessionToken}` }
          : {}),
      }),
    });
  }
  if (memoryEnabled && !env('MEM0_DISABLED')) {
    const impersonation = await fromUser({ user });
    options.push({
      allowWrite: true,
      headers: async () => ({
        ...defaultHeaders,
        'cache-control': 'no-cache, no-transform',
        'content-encoding': 'none',
        Authorization: impersonation
          ? `Bearer ${impersonation ? await impersonation.getImpersonatedToken() : ''}`
          : `APIKey ${env('MEM0_API_KEY')}`,
      }),
      url: `${env('MEM0_API_HOST')}/mcp/${env('MEM0_PROJECT_ID')}/sse`,
    });
  }
  return await toolProviderSetFactory(options);
};

/**
 * @fileoverview Tool Provider Factory for Model Context Protocol (MCP) client connections.
 * Handles connection timeouts, error recovery, and resource cleanup for MCP servers.
 *
 * @module toolProviderFactory
 * @version 1.0.0
 * @author NoEducation Team
 */

import { log } from '@/lib/logger';
import type {
  ConnectableToolProvider,
  ToolProviderFactoryOptions,
  ToolProviderSet,
  MCPClient,
} from '../types';
import { toolProxyFactory, attachProxyToTool } from '../tools';
import {
  experimental_createMCPClient as createMCPClient,
  Tool,
  ToolSet,
} from 'ai';
import {
  getResolvedPromises,
  isAbortError,
  isError,
} from '@/lib/react-util/utility-methods';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import { InstrumentedSseTransport } from '../instrumented-sse-transport';
import { FirstParameter } from '@/lib/typescript';
import { clientToolProviderFactory } from './client-tool-provider';
import { getToolCache } from '../cache';
import { getAllFeatureFlags } from '@/lib/site-util/feature-flags/server';

export const toolProviderFactory = async ({
  impersonation,
  ...options
}: ToolProviderFactoryOptions): Promise<ConnectableToolProvider> => {
  const onerror = ((error: unknown) => {
    const le = LoggedError.isTurtlesAllTheWayDownBaby(error, {
      log: true,
      source: 'MCPClientMessageHandler',
      relog: true,
    });
    return {
      role: 'assistant',
      content: `An error occurred while connecting to the MCP server: ${le.message}. Please try again later.`,
    };
  }) as unknown as (error: unknown) => void;

  try {
    const user = impersonation ? impersonation.getUserContext() : undefined;
    const userId = user ? String(user.userId) : undefined;
    const features = await getAllFeatureFlags(userId);

    type MCPClientConfig = FirstParameter<typeof createMCPClient>;
    const tx: Omit<MCPClientConfig['transport'], 'headers'> = {
      type: 'sse',
      url: options.url,
      headers: options.headers,
      /**
       * Handles SSE connection errors and returns user-friendly error messages.
       * @param {unknown} error - The error that occurred during SSE connection
       * @returns {object} Assistant message with error information
       */
      onerror,
      /**
       * Handles SSE connection close events with error recovery.
       */
      onclose: () => {
        try {
          log((l) => l.warn('MCP Client SSE Connection Closed'));
        } catch (e) {
          LoggedError.isTurtlesAllTheWayDownBaby(e, {
            log: true,
            source: 'MCPClientMessageHandler',
            message: 'MCP Client SSE Close Error',
            critical: true,
          });
        }
      },
      /**
       * Handles incoming SSE messages with error protection.
       * @param {unknown} message - The received SSE message
       */
      onmessage(message: unknown) {
        try {
          // Handle incoming messages if needed for debugging/monitoring
          log((l) =>
            l.info({ message: 'MCP Client SSE Message:', data: message }),
          );
        } catch (e) {
          LoggedError.isTurtlesAllTheWayDownBaby(e, {
            log: true,
            source: 'MCPClientMessageHandler',
            message: 'MCP Client SSE Message Error',
            critical: true,
            data: {
              message,
            },
          });
        }
      },
    };

    // Create instrumented SSE transport with comprehensive error handling
    const transport = new InstrumentedSseTransport({
      url: options.url,
      onerror,
      ...tx,
    });

    // Create MCP client with transport and error handling
    let mcpClient = await createMCPClient({
      transport,
      /**
       * Handles uncaught errors from the MCP client with nested error protection.
       * @param {unknown} error - The uncaught error from the MCP client
       * @returns {object} Assistant message with error information
       */
      onUncaughtError: (error: unknown): object => {
        try {
          LoggedError.isTurtlesAllTheWayDownBaby(error, {
            log: true,
            source: 'MCPClientMessageHandler',
            message: 'MCP Client SSE Uncaught Error',
            critical: true,
          });
          return {
            role: 'assistant',
            content: [
              {
                type: 'text',
                text: `An error occurred while processing your request: ${isError(error) ? error.message : String(error)}. Please try again later.`,
              },
            ],
          };
        } catch (e) {
          // Fallback error handling if logging itself fails. This prevents the
          // app from crashing, but swallows the error.  If you are seeing this
          // message in logs, it indicates a deeper issue is at play.
          log((l) => l.error('MCP Client Uncaught Error Handler Error:', e));
          return {
            role: 'assistant',
            content: [
              {
                type: 'text',
                text: `A critical error occurred while processing your request. Please try again later.`,
              },
            ],
          };
        }
      },
    });

    // Check cache first for faster tool discovery
    const toolCache = features?.mcp_cache_tools
      ? getToolCache()
      : {
          getCachedTools: async () => Promise.resolve(null),
          setCachedTools: async () => Promise.resolve(),
          invalidateCache: async () => Promise.resolve(),
        };
    const cachedTools: ToolSet | null = await toolCache.getCachedTools(options);
    let tools: ToolSet;

    if (!cachedTools) {
      // Cache miss: retrieve and filter tools based on access permissions
      const allTools = await mcpClient.tools();
      const filteredTools = options.allowWrite
        ? allTools
        : Object.entries(allTools).reduce((acc, [toolName, tool]) => {
            // Filter out tools that require write access when in read-only mode
            if ((tool.description?.indexOf('Write access') ?? -1) === -1) {
              acc[toolName] = tool;
            }
            return acc;
          }, {} as ToolSet);

      // Cache the filtered tools for future requests
      await toolCache.setCachedTools(options, filteredTools);

      // Use the live tools directly (they have valid function context)
      tools = filteredTools;
    } else {
      // Cache hit: wrap cached tools with proxies to restore function context
      tools = Object.entries(cachedTools).reduce(
        (acc, [toolName, cachedTool]) => {
          acc[toolName] = toolProxyFactory<unknown, unknown>({
            mcpClient: async (name: string) => {
              const liveTools = await mcpClient.tools();
              Object.entries(liveTools).forEach(([liveName, liveTool]) => {
                const cachedTool = acc[liveName];
                if (cachedTool) {
                  attachProxyToTool(liveTool);
                }
              });
              return liveTools[name] as Tool<unknown, unknown> | undefined;
            },
            name: toolName,
            tool: cachedTool as Tool<unknown, unknown>,
          });
          return acc;
        },
        {} as ToolSet,
      );
    }

    let isConnected = true;

    // Return the connected tool provider interface
    return {
      /**
       * Gets the underlying MCP client instance.
       * @returns {MCPClient} The MCP client instance
       */
      get_mcpClient: (): MCPClient => mcpClient,

      /**
       * Checks if the provider is currently connected.
       * @returns {boolean} True if connected, false otherwise
       */
      get_isConnected: (): boolean => isConnected,

      /**
       * Gets the filtered tool set based on access permissions.
       * @returns {ToolSet} The available tools for this provider
       */
      get tools(): ToolSet {
        return tools;
      },

      /**
       * Disposes of the MCP client and cleans up resources.
       * @async
       * @returns {Promise<void>} Promise that resolves when disposal is complete
       */
      dispose: async (): Promise<void> => {
        try {
          await mcpClient.close();
        } catch (e) {
          // Downgrade AbortError noise on shutdown
          if (isAbortError(e)) {
            log((l) =>
              l.verbose('toolProviderFactory.dispose: Ignoring AbortError'),
            );
          } else {
            LoggedError.isTurtlesAllTheWayDownBaby(e, {
              log: true,
              source: 'toolProviderFactory dispose',
              severity: 'error',
              data: {
                message: 'Error disposing MCP client',
                options,
              },
            });
          }
        }
      },

      /**
       * Reconnects the provider with new access permissions.
       * @async
       * @param {object} options - Reconnection options
       * @param {boolean} [options.allowWrite=false] - Whether to allow write access
       * @returns {Promise<ConnectableToolProvider>} New provider instance with updated permissions
       */
      connect: async ({
        allowWrite = false,
      }: {
        allowWrite?: boolean;
      }): Promise<ConnectableToolProvider> => {
        const disconnect = isConnected
          ? await mcpClient.close()
          : Promise.resolve();

        // Invalidate cache when reconnecting with different permissions
        if (allowWrite !== options.allowWrite) {
          await toolCache.invalidateCache({ ...options, allowWrite });
        }

        const newTool = await toolProviderFactory({
          ...options,
          allowWrite,
          impersonation,
        });
        mcpClient = newTool.get_mcpClient();
        await disconnect;
        isConnected = true;
        return newTool;
      },
    };
  } catch (error) {
    // Graceful degradation: return stub provider on connection failure
    LoggedError.isTurtlesAllTheWayDownBaby(error, {
      log: true,
      source: 'MCPClientMessageHandler',
      message: `A critical failure occurred connecting to MCP server at [${options?.url}] - tools from this resource will not be available.`,
      url: options?.url,
    });
    return {
      /**
       * Stub implementation - returns undefined as no client is available.
       * @returns {MCPClient} Undefined cast to MCPClient type
       */
      get_mcpClient: (): MCPClient => undefined as unknown as MCPClient,

      /**
       * Stub implementation - always returns false for failed connections.
       * @returns {boolean} Always false
       */
      get_isConnected: (): boolean => false as boolean,

      /**
       * Stub implementation - returns empty tool set for failed connections.
       * @returns {ToolSet} Empty tool set
       */
      get tools(): ToolSet {
        return {} as ToolSet;
      },

      /**
       * Stub implementation - no-op disposal for failed connections.
       * @async
       * @returns {Promise<void>} Resolved promise
       */
      dispose: async (): Promise<void> => {},

      /**
       * Stub implementation - attempts to reconnect by creating a new provider.
       * @async
       * @param {object} options - Reconnection options
       * @param {boolean} [options.allowWrite=false] - Whether to allow write access
       * @returns {Promise<ConnectableToolProvider>} New provider instance
       */
      connect: async ({
        allowWrite = false,
      }: {
        allowWrite?: boolean;
      }): Promise<ConnectableToolProvider> => {
        const newTool = await toolProviderFactory({
          ...options,
          allowWrite,
          impersonation,
        });
        return newTool;
      },
    } as ConnectableToolProvider;
  }
};

const getResolvedProvidersWithCleanup = async (
  promises: Promise<ConnectableToolProvider>[],
  timeoutMs: number = 60 * 1000,
): Promise<ConnectableToolProvider[]> => {
  const categorized = await getResolvedPromises(promises, timeoutMs);

  // Setup cleanup hooks for any MCP clients that are still pending after timeout
  categorized.pending.forEach((p) => {
    p.then((provider) => {
      // Dispose of providers that resolve after timeout to prevent resource leaks
      if (provider && typeof provider.dispose === 'function') {
        provider.dispose().catch((e) => {
          log((l) => l.error('Error disposing provider after rejection:', e));
        });
      }
    }).catch((e) => {
      // Log cleanup errors but continue execution
      LoggedError.isTurtlesAllTheWayDownBaby(e, {
        log: true,
        relog: true,
        source: 'toolProviderFactory::getResolvedProvidersWithCleanup',
        severity: 'error',
        message: 'Error during provider cleanup after rejection',
      });
      return Promise.resolve();
    });
  });

  // Log aggregate error information for rejected promises
  if (categorized.rejected.length > 0) {
    LoggedError.isTurtlesAllTheWayDownBaby(
      new AggregateError(
        categorized.rejected,
        'Some MCP clients failed to connect or returned errors',
      ),
      {
        log: true,
        source: 'getResolvedProvidersWithCleanup',
        severity: 'error',
        data: {
          numberOfFailures: categorized.rejected.length,
          timeoutMs,
        },
      },
    );
  }

  // Log successful connection statistics for monitoring
  log((l) =>
    l.debug(
      `MCP toolProviderFactory resolved; ${categorized.fulfilled.length} connections established.`,
    ),
  );
  return categorized.fulfilled;
};

/**
 * Type guard to check if an object conforms to the ConnectableToolProvider interface.
 *
 * This function verifies that the provided object has all required methods of the
 * ConnectableToolProvider interface, ensuring type safety when working with dynamic
 * or unknown objects.
 *
 * @function isToolProvider
 * @param {unknown} check - The object to check
 * @returns {boolean} True if the object is a ConnectableToolProvider, false otherwise
 *
 * @example
 * ```typescript
 * const obj: unknown = getSomeObject();
 * if (isToolProvider(obj)) {
 *   // Now TypeScript knows obj is a ConnectableToolProvider
 *   const tools = obj.tools;
 * } else {
 *   console.error('Object is not a valid tool provider');
 * }
 * ```
 *
 * @see {@link ConnectableToolProvider} For the interface being checked
 *
 * @since 1.0.0
 */
export const isToolProvider = (
  check: unknown,
): check is ConnectableToolProvider => {
  return (
    typeof check === 'object' &&
    !!check &&
    'get_mcpClient' in check &&
    'get_isConnected' in check &&
    'tools' in check &&
    'dispose' in check &&
    'connect' in check &&
    typeof (check as ConnectableToolProvider).get_mcpClient === 'function' &&
    typeof (check as ConnectableToolProvider).get_isConnected === 'function' &&
    typeof (check as ConnectableToolProvider).tools === 'function' &&
    typeof (check as ConnectableToolProvider).tools === 'object' &&
    typeof (check as ConnectableToolProvider).dispose === 'function' &&
    typeof (check as ConnectableToolProvider).connect === 'function'
  );
};

/**
 * Creates and manages multiple Model Context Protocol (MCP) client connections concurrently.
 *
 * This factory function establishes connections to multiple MCP servers simultaneously,
 * handling timeouts, failures, and resource cleanup automatically. It provides a unified
 * interface for working with tools from multiple providers.
 *
 * @async
 * @function toolProviderSetFactory
 * @param {ToolProviderFactoryOptions[]} providers - Array of provider configurations
 * @param {number} [timeoutMs=60000] - Timeout in milliseconds for each connection attempt
 * @returns {Promise<ToolProviderSet>} A promise that resolves to a collection of connected providers
 *
 * @description
 * **Features:**
 * - **Concurrent Connections**: All providers connect simultaneously for optimal performance
 * - **Fault Tolerance**: Failed connections don't prevent successful ones from working
 * - **Resource Management**: Automatic cleanup of failed or timed-out connections
 * - **Unified Tool Access**: Aggregates tools from all successful providers
 * - **Graceful Disposal**: Coordinated cleanup with timeout protection
 *
 * @example
 * ```typescript
 * // Connect to multiple MCP servers
 * const providerSet = await toolProviderSetFactory([
 *   {
 *     url: 'https://file-server.example.com/api',
 *     allowWrite: false,
 *     headers: { 'Authorization': 'Bearer token1' }
 *   },
 *   {
 *     url: 'https://email-server.example.com/api',
 *     allowWrite: true,
 *     headers: { 'Authorization': 'Bearer token2' }
 *   },
 *   {
 *     url: 'https://calendar-server.example.com/api',
 *     allowWrite: false
 *   }
 * ], 30000); // 30 second timeout per connection
 *
 * // Get all available tools from all connected providers
 * const allTools = providerSet.tools();
 * console.log(`Total tools available: ${Object.keys(allTools).length}`);
 *
 * // Clean up all providers when done
 * await providerSet.dispose();
 * ```
 *
 * @example
 * ```typescript
 * // Handle partial connection failures gracefully
 * const providerSet = await toolProviderSetFactory([
 *   { url: 'https://working-server.com/api' },
 *   { url: 'https://broken-server.com/api' },  // This may fail
 *   { url: 'https://slow-server.com/api' }     // This may timeout
 * ]);
 *
 * // Still get tools from successful connections
 * const availableTools = providerSet.tools();
 * console.log(`Connected to ${providerSet.providers.length} providers`);
 * ```
 *
 * @see {@link ToolProviderSet} For the returned provider set interface
 * @see {@link toolProviderFactory} For single provider connections
 * @see {@link getResolvedProvidersWithCleanup} For the internal connection management logic
 *
 * @since 1.0.0
 */
export const toolProviderSetFactory = async (
  providers: Array<ToolProviderFactoryOptions | ConnectableToolProvider>,
  timeoutMs: number = 180 * 1000,
): Promise<ToolProviderSet> => {
  // Create provider promises and wait for resolution with cleanup
  const resolvedProviders = await getResolvedProvidersWithCleanup(
    providers.map((options) =>
      isToolProvider(options)
        ? Promise.resolve(options)
        : toolProviderFactory(options),
    ),
    timeoutMs,
  );
  const allProviders = [clientToolProviderFactory(), ...resolvedProviders];
  const isHealthy = allProviders.length === providers.length;
  return {
    /** @type {ConnectableToolProvider[]} Array of successfully connected providers */
    providers: allProviders,
    get isHealthy(): boolean {
      return isHealthy;
    },
    /**
     * Aggregates tools from all connected providers into a single ToolSet.
     *
     * @returns {ToolSet} Combined tool set from all providers
     * @description
     * Merges tools from all successful provider connections. If multiple providers
     * offer tools with the same name, the last provider's tool will take precedence.
     */
    get tools(): ToolSet {
      return allProviders.reduce((acc, provider) => {
        return { ...acc, ...provider.tools };
      }, {} as ToolSet);
    },

    /**
     * Disposes of all providers with timeout protection.
     *
     * @async
     * @returns {Promise<void>} Promise that resolves when disposal is complete or times out
     * @description
     * Attempts to dispose all providers gracefully within a 30-second timeout.
     * Uses `Promise.any` to ensure the function doesn't hang indefinitely if
     * some providers fail to dispose properly. Suppresses AbortErrors that occur
     * during disposal as these are expected during cleanup.
     */
    dispose: async (): Promise<void> => {
      await Promise.any([
        Promise.all(
          allProviders.map(async (provider) => {
            try {
              await provider.dispose();
            } catch (e) {
              // Suppress AbortErrors during disposal as they're expected
              if (isAbortError(e)) {
                log((l) =>
                  l.verbose(
                    'toolProviderFactory.dispose: Ignoring AbortError from provider disposal',
                  ),
                );
              } else {
                // Re-throw non-AbortErrors so they can be properly logged
                throw e;
              }
            }
          }),
        ),
        new Promise((resolve) => setTimeout(resolve, 30 * 1000)), // Wait 30 seconds max for disposal
      ]);
    },
  };
};

import { MemoryClient } from './lib/client/mem0';
import type { MemoryOptions, Message } from './lib/client/types';
import type {
  MemoryHealthCheckResponse,
  HealthCheckParams,
} from './types/health-check';
import { fromRequest, ImpersonationService } from '@/lib/auth/impersonation';
import { env } from '@/lib/site-util/env';
import { log } from '@/lib/logger';

/**
 * Options for configuring the MemoryClient instance.
 *
 * @property projectId - The ID of the project to associate with the client.
 * @property orgId - The ID of the organization to associate with the client.
 * @property defaults - Default memory options to use for the client.
 * @property impersonation - Optional impersonation instance for authenticated calls.
 */
type ClientOptions = {
  projectId?: string;
  orgId?: string;
  defaults?: MemoryOptions;
  impersonation?: ImpersonationService;
};

export type ExtendedMemoryClient = MemoryClient & {
  healthCheck: (
    params?: HealthCheckParams,
  ) => Promise<MemoryHealthCheckResponse>;
};

/**
 * A specialized MemoryClient for SchoolLawyer use cases.
 *
 * @class SchoolLawyerMemoryClient
 * @extends MemoryClient
 * @private defaultOptions - Default memory options for the client.
 * @private #impersonation - Optional impersonation instance for authenticated calls.
 */
class SchoolLawyerMemoryClient
  extends MemoryClient
  implements ExtendedMemoryClient
{
  readonly defaultOptions: MemoryOptions;

  /**
   * Constructs a new SchoolLawyerMemoryClient instance.
   *
   * @param defaults - Default memory options.
   * @param projectId - The ID of the project.
   * @param orgId - The ID of the organization.
   * @param impersonation - Optional impersonation instance.
   * @param ops - Additional client options.
   */
  constructor({ defaults, projectId, orgId, ...ops }: ClientOptions) {
    super({
      ...ops,
      projectId: (projectId ? projectId : env('MEM0_PROJECT_ID')) ?? undefined,
      organizationId: (orgId ? orgId : env('MEM0_ORG_ID')) ?? undefined,
      host: env('MEM0_API_HOST'),
    });

    this.defaultOptions = {
      ...(defaults ?? {}),
    };
  }

  /**
   * Prepares the payload for memory operations.
   *
   * @param messages - Array of messages to include in the payload.
   * @param options - Memory options to include in the payload.
   * @returns The prepared payload object.
   */
  override _preparePayload(
    messages: Array<Message>,
    options: MemoryOptions,
  ): object {
    return super._preparePayload(messages, {
      ...this.defaultOptions,
      ...options,
    });
  }

  /**
   * Prepares the parameters for memory operations.
   *
   * @param options - Memory options to include in the parameters.
   * @returns The prepared parameters object.
   */
  override _prepareParams(options: MemoryOptions): Record<string, string> {
    return super._prepareParams({
      ...this.defaultOptions,
      ...options,
    });
  }

  /**
   * Performs a health check on the memory service.
   *
   * @param params - Optional parameters for the health check.
   * @returns Promise resolving to the health check response.
   */
  async healthCheck(
    params: HealthCheckParams = {},
  ): Promise<MemoryHealthCheckResponse> {
    const { strict = false, verbose = 1 } = params;
    const searchParams = new URLSearchParams({
      strict: strict.toString(),
      verbose: verbose.toString(),
    });

    const url = `api/v1/stats/health-check?${searchParams.toString()}`;

    try {
      const response = await this._fetchWithErrorHandling(url, {
        method: 'GET',
        headers: this.headers,
      });

      return response as MemoryHealthCheckResponse;
    } catch (error) {
      log((l) => l.error('Memory health check failed', { error, url }));
      throw error;
    }
  }
}

/**
 * Factory function for creating a MemoryClient instance.
 *
 * @param options - Configuration options for the MemoryClient.
 * @returns A new MemoryClient instance.
 *
 * @example
 * ```typescript
 * // Create with impersonation
 * const impersonation = await Impersonation.fromRequest(request);
 * const client = await memoryClientFactory({ impersonation });
 *
 * // Create with API key (fallback)
 * const client = await memoryClientFactory({});
 * ```
 */
export const memoryClientFactory = async <
  TClient extends MemoryClient = MemoryClient,
>(
  options: ClientOptions,
): Promise<TClient> => {
  const clientOps = {
    ...options,
  };
  if (!options.impersonation) {
    const impersonateService = await fromRequest({});
    if (impersonateService) {
      clientOps.impersonation = impersonateService;
    }
  }
  const ret = new SchoolLawyerMemoryClient(clientOps) as unknown as TClient;
  return ret;
};

/**
 * Memory Health Check Types
 * ========================
 * 
 * Type definitions for the Mem0 API health check response structure.
 * Based on the sample JSON structure from /api/v1/stats/health-check endpoint.
 */

/**
 * Mem0 version and build information
 */
export interface Mem0Info {
  version: string;
  build_type: string;
  build_info: string;
  verbose: {
    mem0_version: string;
    build_details: {
      type: string;
      info: string;
      path: string;
    };
    build_stamp: string;
  };
}

/**
