import type { CacheableResponse, JailEntry } from './types';
import { getCacheConfig } from './config';
import { metricsCollector } from './metrics';
import { createJailKey } from './cacheKeys';
import type { getRedisClient } from './redis-client';
import { log } from '@/lib/logger';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import { LanguageModelV2Content } from '@ai-sdk/provider';

const config = getCacheConfig();

/**
 * Handles caching of a successful response
 *
 * @param redis - Redis client instance
 * @param cacheKey - The cache key to store under
 * @param response - The response to cache
 * @param context - Optional context string for logging
 */
export const cacheSuccessfulResponse = async (
  redis: Awaited<ReturnType<typeof getRedisClient>>,
  cacheKey: string,
  response: CacheableResponse,
  context: string = '',
): Promise<void> => {
  const extractText = (content: Array<LanguageModelV2Content>) => {
    return content
      .map((item) => (item.type === 'text' ? item.text : ''))
      .filter((text) => text.length > 0)
      .join(' ');
  };
  try {
    const extractedText = extractText(response.content);

    await redis.setEx(
      cacheKey,
      config.cacheTtl,
      JSON.stringify({
        text: extractedText,
        finishReason: response.finishReason,
        usage: response.usage,
        warnings: response.warnings,
        rawCall: response.rawCall,
        rawResponse: response.rawResponse,
        response: response.response,
      }),
    );

    const responseSize = extractedText?.length || 0;

    // Record metrics
    if (config.enableMetrics) {
      metricsCollector.recordStore(cacheKey, responseSize);
    }

    if (config.enableLogging) {
      log((l) =>
        l.verbose(
          ` Cached successful ${context}response for key: ${cacheKey.substring(0, config.maxKeyLogLength)}...`,
        ),
      );
    }
  } catch (cacheStoreError) {
    if (config.enableMetrics) {
      metricsCollector.recordError(cacheKey, String(cacheStoreError));
    }
    if (config.enableLogging) {
      LoggedError.isTurtlesAllTheWayDownBaby(cacheStoreError, {
        message: `Error storing ${context}response in cache`,
        data: {
          cacheKey,
        },
        source: 'cacheWithRedis',
        log: true,
      });
    }
  }
};

/**
 * Handles cache jail logic for problematic responses
 *
 * @param redis - Redis client instance
 * @param cacheKey - The cache key for the response
 * @param response - The problematic response
 * @param context - Optional context string for logging
 */
export const handleCacheJail = async (
  redis: Awaited<ReturnType<typeof getRedisClient>>,
  cacheKey: string,
  response: CacheableResponse,
  context: string = '',
): Promise<void> => {
  const jailKey = createJailKey(cacheKey);

  try {
    // Get current jail data
    const jailData = await redis.get(jailKey);
    const jailEntry: JailEntry = jailData
      ? JSON.parse(jailData)
      : { count: 0, firstSeen: Date.now() };

    // Increment count
    jailEntry.count += 1;
    jailEntry.lastSeen = Date.now();
    jailEntry.lastResponse = {
      finishReason: response.finishReason || 'unknown',
      hasWarnings: !!(response.warnings && response.warnings.length > 0),
      textLength:
        response.content?.reduce(
          (acc, part) => acc + (part.type === 'text' ? part.text.length : 0),
          0,
        ) || 0,
    };

    // Store updated jail entry
    await redis.setEx(jailKey, config.jailTtl, JSON.stringify(jailEntry));

    // Record metrics
    if (config.enableMetrics) {
      metricsCollector.recordJailUpdate(
        cacheKey,
        jailEntry.count,
        config.jailThreshold,
      );
    }

    if (config.enableLogging) {
      log((l) =>
        l.verbose(
          ` ${context}cache jail updated for key ${cacheKey.substring(0, config.maxKeyLogLength)}... (count: ${jailEntry.count}/${config.jailThreshold})`,
        ),
      );
    }

    // Check if we've hit the threshold
    if (jailEntry.count >= config.jailThreshold) {
      if (config.enableLogging) {
        log((l) =>
          l.verbose(
            ` ${context}cache jail threshold reached for key ${cacheKey.substring(0, config.maxKeyLogLength)}... - promoting to cache`,
          ),
        );
      }

      // Promote to cache
      await cacheSuccessfulResponse(
        redis,
        cacheKey,
        response,
        `${context}problematic `,
      );

      // Record promotion metrics
      if (config.enableMetrics) {
        const responseSize =
          response.content?.reduce(
            (acc, part) => acc + (part.type === 'text' ? part.text.length : 0),
            0,
          ) || 0;
        metricsCollector.recordJailPromotion(cacheKey, responseSize);
      }
    }
  } catch (jailError) {
    if (config.enableMetrics) {
      metricsCollector.recordError(cacheKey, String(jailError));
    }
    if (config.enableLogging) {
      console.error(`Error managing ${context}cache jail:`, jailError);
    }
  }
};

/**
 * @fileoverview Main cache strategy orchestrator
 */

import type { CacheableResponse } from './types';
import {
  isSuccessfulResponse,
  isProblematicResponse,
} from './responseClassifiers';
import { cacheSuccessfulResponse, handleCacheJail } from './cacheOperations';
import { getCacheConfig } from './config';
import type { getRedisClient } from './redis-client';
import { log } from '@/lib/logger';

const config = getCacheConfig();

/**
 * Handles the caching strategy for any response based on its classification
 *
 * @param redis - Redis client instance
 * @param cacheKey - The cache key for the response
 * @param response - The AI response to process
 * @param context - Optional context string for logging
 */
export const handleResponseCaching = async (
  redis: Awaited<ReturnType<typeof getRedisClient>>,
  cacheKey: string,
  response: CacheableResponse,
  context: string = '',
): Promise<void> => {
  const isSuccessful = isSuccessfulResponse(response);
  const isProblematic = isProblematicResponse(response);

  if (isSuccessful) {
    await cacheSuccessfulResponse(redis, cacheKey, response, context);
  } else if (isProblematic) {
    await handleCacheJail(redis, cacheKey, response, context);
  } else {
    // Log why we're not caching
    if (config.enableLogging) {
      log((l) =>
        l.warn(
          ` Not caching ${context}response (finishReason: ${response.finishReason}, hasText: ${!!(response.content && response.content.length > 0)}) for key: ${cacheKey.substring(0, config.maxKeyLogLength)}...`,
        ),
      );
    }
  }
};

import type {
  LanguageModelV2Middleware,
  LanguageModelV2StreamPart,
} from '@ai-sdk/provider';
import { getRedisClient } from './redis-client';
import { getCacheConfig, validateCacheConfig } from './config';
import { metricsCollector } from './metrics';
import { createCacheKey } from './cacheKeys';
import type { CacheableResponse } from './types';
import { handleCacheHit, handleCacheMiss } from './cacheEventHandlers';
import { createStreamFromCachedText } from './streamUtils';
import { handleResponseCaching } from './cacheStrategy';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import { newUuid } from '@/lib/typescript/_record-decorators';
import { MiddlewareStateManager } from '../state-management';

// Enterprise configuration and metrics
const config = getCacheConfig();
validateCacheConfig(config);

/**
 * Enterprise-grade Redis caching middleware for AI language models (Original Implementation)
 * - Configurable via environment variables
 * - Comprehensive metrics collection
 * - Caches successful responses immediately
 * - Uses "cache jail" for problematic responses (content-filter, other, warnings)
 * - Promotes jailed responses to cache after configurable threshold
 * - Never caches error responses
 */
const originalCacheWithRedis: LanguageModelV2Middleware = {
  wrapGenerate: async ({ doGenerate, params, model }) => {
    const cacheKey = createCacheKey(params, model?.modelId);

    try {
      if (cacheKey.length === 0) {
        throw new Error('Cache key is empty, cannot proceed with caching.');
      }
      const redis = await getRedisClient();

      // Try to get cached response
      const cachedResponse = await redis.get(cacheKey);

      if (cachedResponse) {
        const parsed = JSON.parse(cachedResponse);
        handleCacheHit(cacheKey, parsed);
        return parsed as Awaited<ReturnType<typeof doGenerate>>;
      }

      handleCacheMiss(cacheKey);

      // Generate new response
      const result = await doGenerate();

      // Handle caching strategy
      await handleResponseCaching(redis, cacheKey, {
        ...result,
        id: newUuid(),
      });

      return result;
    } catch (error) {
      if (config.enableMetrics) {
        metricsCollector.recordError(cacheKey, String(error));
      }
      if (config.enableLogging) {
        LoggedError.isTurtlesAllTheWayDownBaby(error, {
          log: true,
        });
      }
      return await doGenerate();
    }
  },

  wrapStream: async ({ doStream, params, model }) => {
    const cacheKey = createCacheKey(params, model?.modelId);

    try {
      if (cacheKey.length === 0) {
        throw new Error('Cache key is empty, cannot proceed with caching.');
      }
      const redis = await getRedisClient();

      // Try to get cached response
      const cachedResponse = await redis.get(cacheKey);

      if (cachedResponse) {
        const parsed = JSON.parse(cachedResponse);
        handleCacheHit(cacheKey, parsed, 'Stream ');

        // Convert cached text back to a stream
        const cachedStream = createStreamFromCachedText(parsed);

        return {
          stream: cachedStream,
          warnings: parsed.warnings,
          rawCall: parsed.rawCall,
          rawResponse: parsed.rawResponse,
        };
      }

      handleCacheMiss(cacheKey, 'Stream ');

      // Generate new stream
      const { stream, ...rest } = await doStream();

      let generatedText = '';
      let finishReason = 'stop';
      let usage: Record<string, unknown> | undefined = undefined;

      const cacheStream = new TransformStream<
        LanguageModelV2StreamPart,
        LanguageModelV2StreamPart
      >({
        transform(chunk, controller) {
          if (chunk.type === 'text-delta') {
            generatedText += chunk.delta;
          } else if ('text' in chunk) {
            generatedText += chunk.text;
          } else if (chunk.type === 'finish') {
            finishReason = chunk.finishReason;
            usage = chunk.usage;
          }

          controller.enqueue(chunk);
        },

        async flush() {
          // Create response object for caching logic
          const streamResponse: CacheableResponse = {
            id: newUuid(),
            content: [{ type: 'text', text: generatedText }],
            finishReason,
            usage,
            rawResponse: (rest.response as Record<string, unknown>).body,
          };

          // Handle caching strategy for streaming response
          await handleResponseCaching(
            redis,
            cacheKey,
            streamResponse,
            'stream ',
          );
        },
      });

      return {
        stream: stream.pipeThrough(cacheStream),
        ...rest,
      };
    } catch (error) {
      if (config.enableMetrics) {
        metricsCollector.recordError(cacheKey, String(error));
      }
      if (config.enableLogging) {
        console.error('Redis cache error in wrapStream:', error);
      }
      return await doStream();
    }
  },
};

/**
 * Cache with Redis Middleware with State Management Support
 *
 * This middleware supports the state management protocol and can participate
 * in state collection and restoration operations.
 */
export const cacheWithRedis =
  MiddlewareStateManager.Instance.basicMiddlewareWrapper({
    middlewareId: 'cache-with-redis',
    middleware: originalCacheWithRedis,
  });

/**
 * Enterprise-grade configuration for AI caching middleware
 * Supports environment variables with sensible defaults
 */

export interface CacheConfig {
  /** Cache TTL in seconds (default: 24 hours) */
  cacheTtl: number;
  /** Cache jail threshold - number of occurrences before promotion (default: 3) */
  jailThreshold: number;
  /** Cache jail TTL in seconds (default: 24 hours) */
  jailTtl: number;
  /** Stream chunk size for cached responses (default: 5 characters) */
  streamChunkSize: number;
  /** Enable detailed logging (default: true) */
  enableLogging: boolean;
  /** Enable metrics collection (default: true) */
  enableMetrics: boolean;
  /** Cache key prefix (default: 'ai-cache') */
  cacheKeyPrefix: string;
  /** Jail key prefix (default: 'ai-jail') */
  jailKeyPrefix: string;
  /** Maximum cache key length for logging (default: 20) */
  maxKeyLogLength: number;
}

/**
 * Parse environment variable as integer with fallback
 */
function parseIntEnv(envVar: string | undefined, defaultValue: number): number {
  if (!envVar) return defaultValue;
  const parsed = parseInt(envVar, 10);
  return isNaN(parsed) ? defaultValue : parsed;
}

/**
 * Parse environment variable as boolean with fallback
 */
function parseBoolEnv(
  envVar: string | undefined,
  defaultValue: boolean,
): boolean {
  if (!envVar) return defaultValue;
  return envVar.toLowerCase() === 'true' || envVar === '1';
}

/**
 * Get cache configuration from environment variables with defaults
 */
export function getCacheConfig(): CacheConfig {
  return {
    cacheTtl: parseIntEnv(process.env.AI_CACHE_TTL, 86400), // 24 hours
    jailThreshold: parseIntEnv(process.env.AI_CACHE_JAIL_THRESHOLD, 3),
    jailTtl: parseIntEnv(process.env.AI_CACHE_JAIL_TTL, 86400), // 24 hours
    streamChunkSize: parseIntEnv(process.env.AI_CACHE_STREAM_CHUNK_SIZE, 5),
    enableLogging: parseBoolEnv(process.env.AI_CACHE_ENABLE_LOGGING, true),
    enableMetrics: parseBoolEnv(process.env.AI_CACHE_ENABLE_METRICS, true),
    cacheKeyPrefix: process.env.AI_CACHE_KEY_PREFIX || 'ai-cache',
    jailKeyPrefix: process.env.AI_CACHE_JAIL_KEY_PREFIX || 'ai-jail',
    maxKeyLogLength: parseIntEnv(process.env.AI_CACHE_MAX_KEY_LOG_LENGTH, 20),
  };
}

/**
 * Validate cache configuration
 */
export function validateCacheConfig(config: CacheConfig): void {
  if (config.cacheTtl <= 0) {
    throw new Error('Cache TTL must be positive');
  }
  if (config.jailThreshold <= 0) {
    throw new Error('Jail threshold must be positive');
  }
  if (config.jailTtl <= 0) {
    throw new Error('Jail TTL must be positive');
  }
  if (config.streamChunkSize <= 0) {
    throw new Error('Stream chunk size must be positive');
  }
  if (config.maxKeyLogLength <= 0) {
    throw new Error('Max key log length must be positive');
  }
}

/**
 * Enterprise-grade metrics collection for AI caching middleware
 * Provides hooks for monitoring cache performance and jail behavior
 *
 * Features:
 * - OpenTelemetry integration for enterprise observability
 * - Prometheus-compatible metrics export
 * - Real-time event streaming and hooks
 * - Performance timing and response size tracking
 * - Error categorization and jail behavior monitoring
 */

import { log } from '@/lib/logger';
import { appMeters } from '@/lib/site-util/metrics';

// OpenTelemetry Metrics - Enterprise observability
const cacheHitsCounter = appMeters.createCounter('ai_cache_hits_total', {
  description: 'Total number of AI cache hits',
  unit: '1',
});

const cacheMissesCounter = appMeters.createCounter('ai_cache_misses_total', {
  description: 'Total number of AI cache misses',
  unit: '1',
});

const cacheStoresCounter = appMeters.createCounter('ai_cache_stores_total', {
  description: 'Total number of successful cache stores',
  unit: '1',
});

const jailPromotionsCounter = appMeters.createCounter(
  'ai_cache_jail_promotions_total',
  {
    description:
      'Total number of jail promotions (problematic responses cached)',
    unit: '1',
  },
);

const cacheErrorsCounter = appMeters.createCounter('ai_cache_errors_total', {
  description: 'Total number of cache operation errors',
  unit: '1',
});

const hitRateGauge = appMeters.createUpDownCounter('ai_cache_hit_rate', {
  description: 'Current cache hit rate (0-1)',
  unit: '1',
});

const avgResponseSizeGauge = appMeters.createUpDownCounter(
  'ai_cache_avg_response_size',
  {
    description: 'Average response size in characters',
    unit: 'By',
  },
);

const responseSizeHistogram = appMeters.createHistogram(
  'ai_cache_response_size_bytes',
  {
    description: 'Distribution of AI response sizes',
    unit: 'By',
  },
);

const cacheOperationDuration = appMeters.createHistogram(
  'ai_cache_operation_duration_ms',
  {
    description: 'Duration of cache operations in milliseconds',
    unit: 'ms',
  },
);

export interface CacheMetrics {
  /** Total cache hits */
  cacheHits: number;
  /** Total cache misses */
  cacheMisses: number;
  /** Total successful caches */
  successfulCaches: number;
  /** Total problematic responses */
  problematicResponses: number;
  /** Total jail promotions */
  jailPromotions: number;
  /** Total cache errors */
  cacheErrors: number;
  /** Cache hit rate (0-1) */
  hitRate: number;
  /** Average response size in characters */
  avgResponseSize: number;
  /** Total responses processed */
  totalResponses: number;
}

export interface CacheEvent {
  type: 'hit' | 'miss' | 'store' | 'jail_update' | 'jail_promotion' | 'error';
  cacheKey: string;
  timestamp: number;
  metadata?: Record<string, unknown>;
}

/**
 * Metrics collector with thread-safe operations
 */
class MetricsCollector {
  private metrics: CacheMetrics = {
    cacheHits: 0,
    cacheMisses: 0,
    successfulCaches: 0,
    problematicResponses: 0,
    jailPromotions: 0,
    cacheErrors: 0,
    hitRate: 0,
    avgResponseSize: 0,
    totalResponses: 0,
  };

  private totalResponseSize = 0;
  private events: CacheEvent[] = [];
  private maxEvents = 1000; // Keep last 1000 events
  private metricsHooks: Array<(metrics: CacheMetrics) => void> = [];
  private eventHooks: Array<(event: CacheEvent) => void> = [];

  /**
   * Record a cache hit
   */
  recordHit(cacheKey: string, responseSize?: number): void {
    const startTime = performance.now();

    this.metrics.cacheHits++;
    this.metrics.totalResponses++;

    if (responseSize !== undefined) {
      this.totalResponseSize += responseSize;
      this.metrics.avgResponseSize =
        this.totalResponseSize / this.metrics.totalResponses;

      // OpenTelemetry metrics
      responseSizeHistogram.record(responseSize, {
        operation: 'hit',
        cache_key: this.hashCacheKey(cacheKey),
      });
    }

    this.updateHitRate();

    // OpenTelemetry metrics
    cacheHitsCounter.add(1, {
      cache_key: this.hashCacheKey(cacheKey),
    });

    // Update hit rate gauge to current value
    hitRateGauge.add(this.metrics.hitRate, {
      cache_key: this.hashCacheKey(cacheKey),
    });

    // Update average response size gauge to current value
    if (responseSize !== undefined) {
      avgResponseSizeGauge.add(this.metrics.avgResponseSize, {
        cache_key: this.hashCacheKey(cacheKey),
      });
    }

    cacheOperationDuration.record(performance.now() - startTime, {
      operation: 'hit',
      cache_key: this.hashCacheKey(cacheKey),
    });

    this.addEvent({
      type: 'hit',
      cacheKey,
      timestamp: Date.now(),
      metadata: { responseSize },
    });

    this.notifyMetricsHooks();
  }

  /**
   * Record a cache miss
   */
  recordMiss(cacheKey: string): void {
    const startTime = performance.now();

    this.metrics.cacheMisses++;
    this.metrics.totalResponses++;
    this.updateHitRate();

    // OpenTelemetry metrics
    cacheMissesCounter.add(1, {
      cache_key: this.hashCacheKey(cacheKey),
    });

    hitRateGauge.add(this.metrics.hitRate, {
      cache_key: this.hashCacheKey(cacheKey),
    });

    cacheOperationDuration.record(performance.now() - startTime, {
      operation: 'miss',
      cache_key: this.hashCacheKey(cacheKey),
    });

    this.addEvent({
      type: 'miss',
      cacheKey,
      timestamp: Date.now(),
    });

    this.notifyMetricsHooks();
  }

  /**
   * Record a successful cache store
   */
  recordStore(cacheKey: string, responseSize: number): void {
    const startTime = performance.now();

    this.metrics.successfulCaches++;

    // OpenTelemetry metrics
    cacheStoresCounter.add(1, {
      cache_key: this.hashCacheKey(cacheKey),
    });

    responseSizeHistogram.record(responseSize, {
      operation: 'store',
      cache_key: this.hashCacheKey(cacheKey),
    });

    cacheOperationDuration.record(performance.now() - startTime, {
      operation: 'store',
      cache_key: this.hashCacheKey(cacheKey),
    });

    this.addEvent({
      type: 'store',
      cacheKey,
      timestamp: Date.now(),
      metadata: { responseSize },
    });

    this.notifyMetricsHooks();
  }

  /**
   * Record a jail update
   */
  recordJailUpdate(cacheKey: string, count: number, threshold: number): void {
    const startTime = performance.now();

    this.metrics.problematicResponses++;

    // OpenTelemetry metrics - track jail updates
    cacheOperationDuration.record(performance.now() - startTime, {
      operation: 'jail_update',
      cache_key: this.hashCacheKey(cacheKey),
      count: count.toString(),
      threshold: threshold.toString(),
    });

    this.addEvent({
      type: 'jail_update',
      cacheKey,
      timestamp: Date.now(),
      metadata: { count, threshold },
    });

    this.notifyMetricsHooks();
  }

  /**
   * Record a jail promotion
   */
  recordJailPromotion(cacheKey: string, responseSize: number): void {
    const startTime = performance.now();

    this.metrics.jailPromotions++;

    // OpenTelemetry metrics
    jailPromotionsCounter.add(1, {
      cache_key: this.hashCacheKey(cacheKey),
    });

    responseSizeHistogram.record(responseSize, {
      operation: 'jail_promotion',
      cache_key: this.hashCacheKey(cacheKey),
    });

    cacheOperationDuration.record(performance.now() - startTime, {
      operation: 'jail_promotion',
      cache_key: this.hashCacheKey(cacheKey),
    });

    this.addEvent({
      type: 'jail_promotion',
      cacheKey,
      timestamp: Date.now(),
      metadata: { responseSize },
    });

    this.notifyMetricsHooks();
  }

  /**
   * Record a cache error
   */
  recordError(cacheKey: string, error: string): void {
    const startTime = performance.now();

    this.metrics.cacheErrors++;

    // OpenTelemetry metrics
    cacheErrorsCounter.add(1, {
      cache_key: this.hashCacheKey(cacheKey),
      error_type: this.categorizeError(error),
    });

    cacheOperationDuration.record(performance.now() - startTime, {
      operation: 'error',
      cache_key: this.hashCacheKey(cacheKey),
      error_type: this.categorizeError(error),
    });

    this.addEvent({
      type: 'error',
      cacheKey,
      timestamp: Date.now(),
      metadata: { error },
    });

    this.notifyMetricsHooks();
  }

  /**
   * Get current metrics snapshot
   */
  getMetrics(): CacheMetrics {
    return { ...this.metrics };
  }

  /**
   * Get recent events
   */
  getEvents(limit?: number): CacheEvent[] {
    const events = [...this.events];
    return limit ? events.slice(-limit) : events;
  }

  /**
   * Subscribe to metrics updates
   */
  onMetricsUpdate(callback: (metrics: CacheMetrics) => void): () => void {
    this.metricsHooks.push(callback);
    return () => {
      const index = this.metricsHooks.indexOf(callback);
      if (index > -1) {
        this.metricsHooks.splice(index, 1);
      }
    };
  }

  /**
   * Subscribe to events
   */
  onEvent(callback: (event: CacheEvent) => void): () => void {
    this.eventHooks.push(callback);
    return () => {
      const index = this.eventHooks.indexOf(callback);
      if (index > -1) {
        this.eventHooks.splice(index, 1);
      }
    };
  }

  /**
   * Reset all metrics
   */
  reset(): void {
    this.metrics = {
      cacheHits: 0,
      cacheMisses: 0,
      successfulCaches: 0,
      problematicResponses: 0,
      jailPromotions: 0,
      cacheErrors: 0,
      hitRate: 0,
      avgResponseSize: 0,
      totalResponses: 0,
    };
    this.totalResponseSize = 0;
    this.events = [];
  }

  private updateHitRate(): void {
    const total = this.metrics.cacheHits + this.metrics.cacheMisses;
    this.metrics.hitRate = total > 0 ? this.metrics.cacheHits / total : 0;
  }

  /**
   * Hash cache key for telemetry (avoid exposing sensitive data)
   */
  private hashCacheKey(cacheKey: string): string {
    // Simple hash to avoid exposing sensitive cache keys in telemetry
    let hash = 0;
    for (let i = 0; i < cacheKey.length; i++) {
      const char = cacheKey.charCodeAt(i);
      hash = (hash << 5) - hash + char;
      hash = hash & hash; // Convert to 32-bit integer
    }
    return Math.abs(hash).toString(16).substring(0, 8);
  }

  /**
   * Categorize error types for telemetry
   */
  private categorizeError(error: string): string {
    const errorLower = error.toLowerCase();
    if (errorLower.includes('timeout') || errorLower.includes('ttl')) {
      return 'timeout';
    }
    if (errorLower.includes('connection') || errorLower.includes('network')) {
      return 'connection';
    }
    if (errorLower.includes('redis') || errorLower.includes('database')) {
      return 'redis';
    }
    if (errorLower.includes('parse') || errorLower.includes('json')) {
      return 'parse';
    }
    if (errorLower.includes('permission') || errorLower.includes('auth')) {
      return 'auth';
    }
    return 'unknown';
  }

  private addEvent(event: CacheEvent): void {
    this.events.push(event);
    if (this.events.length > this.maxEvents) {
      this.events.shift();
    }
    this.notifyEventHooks(event);
  }

  private notifyMetricsHooks(): void {
    const metrics = this.getMetrics();
    this.metricsHooks.forEach((hook) => {
      try {
        hook(metrics);
      } catch (error) {
        console.error('Error in metrics hook:', error);
      }
    });
  }

  private notifyEventHooks(event: CacheEvent): void {
    this.eventHooks.forEach((hook) => {
      try {
        hook(event);
      } catch (error) {
        console.error('Error in event hook:', error);
      }
    });
  }
}

// Global metrics collector instance
const metricsCollector = new MetricsCollector();

export { metricsCollector };

/**
 * Helper function to set up basic console logging for metrics
 */
export function setupConsoleMetrics(): () => void {
  return metricsCollector.onMetricsUpdate((metrics) => {
    if (metrics.totalResponses % 10 === 0 && metrics.totalResponses > 0) {
      log((l) =>
        l.info(
          ` Cache Metrics - Hit Rate: ${(metrics.hitRate * 100).toFixed(1)}%, ` +
            `Hits: ${metrics.cacheHits}, Misses: ${metrics.cacheMisses}, ` +
            `Jail Promotions: ${metrics.jailPromotions}, Errors: ${metrics.cacheErrors}`,
        ),
      );
    }
  });
}

/**
 * Helper function to set up Prometheus-style metrics export
 */
export function getPrometheusMetrics(): string {
  const metrics = metricsCollector.getMetrics();
  return [
    `# HELP ai_cache_hits_total Total number of cache hits`,
    `# TYPE ai_cache_hits_total counter`,
    `ai_cache_hits_total ${metrics.cacheHits}`,
    ``,
    `# HELP ai_cache_misses_total Total number of cache misses`,
    `# TYPE ai_cache_misses_total counter`,
    `ai_cache_misses_total ${metrics.cacheMisses}`,
    ``,
    `# HELP ai_cache_hit_rate Current cache hit rate`,
    `# TYPE ai_cache_hit_rate gauge`,
    `ai_cache_hit_rate ${metrics.hitRate}`,
    ``,
    `# HELP ai_cache_jail_promotions_total Total number of jail promotions`,
    `# TYPE ai_cache_jail_promotions_total counter`,
    `ai_cache_jail_promotions_total ${metrics.jailPromotions}`,
    ``,
    `# HELP ai_cache_errors_total Total number of cache errors`,
    `# TYPE ai_cache_errors_total counter`,
    `ai_cache_errors_total ${metrics.cacheErrors}`,
    ``,
    `# HELP ai_cache_avg_response_size Average response size in characters`,
    `# TYPE ai_cache_avg_response_size gauge`,
    `ai_cache_avg_response_size ${metrics.avgResponseSize}`,
  ].join('\n');
}

/**
 * Helper function to update OpenTelemetry gauge metrics with current values
 * Call this periodically to ensure gauges reflect current state
 */
export function updateOpenTelemetryGauges(): void {
  const metrics = metricsCollector.getMetrics();

  // Update gauges with current values (reset and set)
  hitRateGauge.add(metrics.hitRate, { source: 'periodic_update' });
  avgResponseSizeGauge.add(metrics.avgResponseSize, {
    source: 'periodic_update',
  });
}

/**
 * Helper function to get OpenTelemetry metrics summary for debugging
 */
export function getOpenTelemetryMetricsSummary(): Record<string, unknown> {
  const metrics = metricsCollector.getMetrics();
  return {
    opentelemetry_metrics: {
      counters: {
        ai_cache_hits_total: metrics.cacheHits,
        ai_cache_misses_total: metrics.cacheMisses,
        ai_cache_stores_total: metrics.successfulCaches,
        ai_cache_jail_promotions_total: metrics.jailPromotions,
        ai_cache_errors_total: metrics.cacheErrors,
      },
      gauges: {
        ai_cache_hit_rate: metrics.hitRate,
        ai_cache_avg_response_size: metrics.avgResponseSize,
      },
      histograms: {
        ai_cache_response_size_bytes: 'tracked',
        ai_cache_operation_duration_ms: 'tracked',
      },
    },
    collection_info: {
      total_events: metricsCollector.getEvents().length,
      last_updated: new Date().toISOString(),
    },
  };
}

import { createClient, RedisClientType } from 'redis';
import { env } from '@/lib/site-util/env';
import { isError } from '@/lib/react-util/utility-methods';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import { log } from '@/lib/logger';

export type { RedisClientType } from 'redis';

/**
 * Redis client singleton manager using global symbol registry
 */
class RedisClientManager {
  /** Symbol-based global registry key for Redis client singleton. */
  static readonly #REGISTRY_KEY = Symbol.for('@noeducation/redis:RedisClient');

  /** Global singleton Redis client via symbol registry. */
  private static get client(): RedisClientType | null {
    type GlobalReg = { [k: symbol]: RedisClientType | null };
    const g = globalThis as unknown as GlobalReg;
    return g[this.#REGISTRY_KEY] ?? null;
  }
  private static set client(value: RedisClientType | null) {
    type GlobalReg = { [k: symbol]: RedisClientType | null };
    const g = globalThis as unknown as GlobalReg;
    g[this.#REGISTRY_KEY] = value;
  }

  /**
   * Get or create a Redis client instance
   * Uses singleton pattern to ensure we reuse the same connection
   */
  static async getClient(): Promise<RedisClientType> {
    if (!this.client) {
      this.client = createClient({
        url: env('REDIS_URL'),
        password: env('REDIS_PASSWORD'),
      });

      this.client.on('error', (err) => {
        LoggedError.isTurtlesAllTheWayDownBaby(err, {
          message: 'Redis client error',
          data: {
            redisUrl: env('REDIS_URL'),
          },
          source: 'redis-client',
          log: true,
        });
      });

      this.client.on('connect', () => {
        log((l) => l.info('Redis client connected'));
      });

      this.client.on('reconnecting', () => {
        log((l) => l.warn('Redis client reconnecting'));
      });

      this.client.on('ready', () => {
        log((l) => l.info('Redis client ready'));
      });

      await this.client.connect();
    }

    return this.client;
  }

  /**
   * Close the Redis connection
   * Should be called when the application shuts down
   */
  static async closeClient(): Promise<void> {
    if (this.client) {
      try {
        await this.client.quit();
      } catch (error) {
        if (
          !isError(error) ||
          !error.message.includes('The client is closed')
        ) {
          console.error('Error closing Redis client:', error);
          throw error; // Re-throw to ensure we handle this in the application shutdown
        }
      }
      this.client = null;
    }
  }
}

/**
 * Get or create a Redis client instance
 * Uses singleton pattern to ensure we reuse the same connection
 */
export async function getRedisClient(): Promise<RedisClientType> {
  return RedisClientManager.getClient();
}

/**
 * Close the Redis connection
 * Should be called when the application shuts down
 */
export async function closeRedisClient(): Promise<void> {
  return RedisClientManager.closeClient();
}

/**
 * @fileoverview Response classification utilities for cache middleware
 */

import type { CacheableResponse } from './types';

/**
 * Determines if a response should be cached immediately (successful response)
 *
 * @param response - The AI response to evaluate
 * @returns true if the response is successful and should be cached immediately
 */
export const isSuccessfulResponse = (response: CacheableResponse): boolean => {
  return !!(
    response &&
    response.finishReason !== 'error' &&
    !!response.content &&
    Array.isArray(response.content) &&
    response.content.some((x) => x.type === 'text' && !!x.text) &&
    response.finishReason !== 'other' &&
    response.finishReason !== 'content-filter' &&
    (!response.warnings || response.warnings.length === 0)
  );
};

/**
 * Determines if a response should be put in cache jail (problematic but not error)
 *
 * @param response - The AI response to evaluate
 * @returns true if the response is problematic and should go to cache jail
 */
export const isProblematicResponse = (response: CacheableResponse): boolean => {
  return !!(
    response &&
    !!response.content &&
    Array.isArray(response.content) &&
    response.content.length > 0 &&
    response.finishReason !== 'error' && // Never jail errors
    (response.finishReason === 'other' ||
      response.finishReason === 'content-filter' ||
      (response.warnings && response.warnings.length > 0))
  );
};

/**
 * @fileoverview Stream utilities for converting cached responses to streams
 */

import type { LanguageModelV2StreamPart } from '@ai-sdk/provider';
import type { CacheableResponse } from './types';
import { getCacheConfig } from './config';

const config = getCacheConfig();

/**
 * Converts cached text response to a ReadableStream for streaming APIs
 *
 * @param parsed - The cached response to convert to a stream
 * @returns A ReadableStream that emits the cached content as streaming chunks
 */
export const createStreamFromCachedText = (
  parsed: CacheableResponse,
): ReadableStream<LanguageModelV2StreamPart> => {
  return new ReadableStream<LanguageModelV2StreamPart>({
    start(controller) {
      // Emit text deltas to simulate streaming
      const text = parsed.content?.reduce((acc, part) => acc + (part.type === 'text' ? part.text : ''), '') || '';

      for (let i = 0; i < text.length; i += config.streamChunkSize) {
        const chunk = text.slice(i, i + config.streamChunkSize);
        controller.enqueue({
          type: 'text-delta',
          id: parsed.id,
          delta: chunk,
        });
      }

      // Emit finish event with proper type casting
      const finishReason = (parsed.finishReason || 'stop') as
        | 'stop'
        | 'length'
        | 'content-filter'
        | 'tool-calls'
        | 'error'
        | 'other'
        | 'unknown';

      if (parsed.usage) {
        controller.enqueue({
          type: 'finish',
          finishReason,
          usage: parsed.usage as {
            inputTokens: number;
            outputTokens: number;
            totalTokens: number;
          },
        });
      } else {
        controller.enqueue({
          type: 'finish',
          finishReason,
          usage: { inputTokens: 0, outputTokens: 0, totalTokens: 0 },
        });
      }

      controller.close();
    },
  });
};

import type { LanguageModelV2Content } from '@ai-sdk/provider';

/**
 * @fileoverview Type definitions for Redis cache middleware
 */

/**
 * Represents a cacheable AI response with all relevant metadata
 */
export interface CacheableResponse {
  id: string;
  content: Array<LanguageModelV2Content>;
  finishReason?: string;
  usage?: Record<string, unknown>;
  warnings?: unknown[];
  rawCall?: unknown;
  rawResponse?: unknown;
  response?: unknown;
}

/**
 * Represents an entry in the cache jail system for problematic responses
 */
export interface JailEntry {
