// Check if model is available and get fallback if needed
export function getAvailableModel(
  provider: 'azure' | 'google',
  classification: ModelClassification,
): string | null {
  const modelKey = `${provider}:${classification}`;
  return isModelAvailable(modelKey) ? modelKey : null;
}

/**
 * Retry delay for model requests.
 */
export const CHAT_RETRY_DELAY_MS = 90 * 1000; // 90 seconds

/**
 * Checks model availability and handles fallback logic.
 * If no models are available, enqueues the request for retry.
 *
 * @param currentModelKey - The current model key being checked
 * @param modelClassification - The model classification
 * @param failoverConfig - The failover configuration
 * @param params - The request parameters
 * @returns The available model key or throws an error if none available
 */
export async function checkModelAvailabilityAndFallback(
  currentModelKey: string,
  modelClassification: ModelClassification,
  failoverConfig: ModelFailoverConfig | undefined,
  params: Record<string, unknown> & {
    prompt?: unknown[];
    chatId: string;
    turnId: string;
  },
): Promise<string | void> {
  if (!isModelAvailable(currentModelKey)) {
    log((l) =>
      l.warn(`Model ${currentModelKey} is disabled, attempting fallback`),
    );

    if (failoverConfig) {
      const fallbackModelKey = getAvailableModel(
        failoverConfig.fallbackProvider,
        modelClassification,
      );

      if (fallbackModelKey) {
        log((l) => l.info(`Using fallback model: ${fallbackModelKey}`));
        return fallbackModelKey;
        // Note: In a real implementation, we'd need to modify the params to use the fallback model
        // This would require integration with the model factory to switch providers
      } else {
        // No fallback available, enqueue for retry
        const requestId = await enqueueRequestForRetry(
          modelClassification,
          params,
          'no_models_available',
        );

        throw new RateRetryError({
          chatId: params.chatId,
          turnId: params.turnId,
          retryId: requestId,
          retryAfter: new Date(Date.now() + 90000), // Retry after 1.5 minute
        });
      }
    } else {
      // No failover config, enqueue for retry
      const retryId = await enqueueRequestForRetry(
        modelClassification,
        params,
        'no_models_available',
      );

      throw new RateRetryError({
        chatId: params.chatId,
        turnId: params.turnId,
        retryId,
        retryAfter: new Date(Date.now() + 90000), // Retry after 1.5 minute
      });
    }
  }
}

/**
 * Enqueues a request for retry processing when models are unavailable or rate limited.
 *
 * @param modelClassification - The model classification
 * @param params - The request parameters
 * @param errorType - The type of error causing the enqueue
 * @returns The generated request ID
 */
export const enqueueRequestForRetry = async (
  modelClassification: ModelClassification,
  {
    prompt,
    ...params
  }: Record<string, unknown> & {
    prompt?: unknown[];
    chatId: string;
    turnId: string;
  },
  errorType: string,
): Promise<string> => {
  if (!prompt || prompt.length === 0) {
    throw new TypeError(
      'Unable to locate prompt; this request does not support delayed run enqueuing.',
    );
  }
  const requestId = uuidv4();
  const rateLimitedRequest: RateLimitedRequest = {
    id: requestId,
    modelClassification,
    request: { params, messages: prompt },
    metadata: {
      submittedAt: new Date().toISOString(),
      chatTurnId: String(params.chatTurnId ?? '1'),
      chatHistoryId: String(params.chatHistoryId ?? 'unassigned'),
      retryAfter: new Date(Date.now() + CHAT_RETRY_DELAY_MS).valueOf(), // Retry after 90 seconds
      generation: 1,
    },
  };

  await rateLimitQueueManager.enqueueRequest(rateLimitedRequest);
  rateLimitMetrics.recordError(errorType, modelClassification);

  return requestId;
};

import { getRedisClient } from '../cacheWithRedis/redis-client';
import type { RateLimitedRequest, ProcessedResponse } from './types';

const REDIS_PREFIX = 'rate-limit';
const EXPIRATION_HOURS = 6;
const EXPIRATION_SECONDS = EXPIRATION_HOURS * 60 * 60;

export class RateLimitQueueManager {
  /**
   * Returns a singleton instance using a Symbol-backed global registry.
   * This avoids duplicate instances across HMR, SSR, or multi-bundle scenarios.
   */
  static getInstance(): RateLimitQueueManager {
    const GLOBAL_KEY = Symbol.for('@noeducation/key-rate-limiter:QueueManager');
    const globalRegistry = globalThis as unknown as {
      [key: symbol]: RateLimitQueueManager | undefined;
    };
    if (!globalRegistry[GLOBAL_KEY]) {
      globalRegistry[GLOBAL_KEY] = new RateLimitQueueManager();
    }
    return globalRegistry[GLOBAL_KEY]!;
  }

  private getQueueKey(generation: 1 | 2, modelClassification: string): string {
    return `${REDIS_PREFIX}:queue:gen${generation}:${modelClassification}`;
  }

  private getResponseKey(requestId: string): string {
    return `${REDIS_PREFIX}:response:${requestId}`;
  }

  async enqueueRequest(request: RateLimitedRequest): Promise<void> {
    const redis = await getRedisClient();
    const queueKey = this.getQueueKey(
      request.metadata.generation,
      request.modelClassification,
    );

    await redis
      .multi()
      .lPush(queueKey, JSON.stringify(request))
      .expire(queueKey, EXPIRATION_SECONDS)
      .exec();
  }

  async dequeueRequests(
    generation: 1 | 2,
    modelClassification: string,
    maxCount: number = 10,
  ): Promise<RateLimitedRequest[]> {
    const redis = await getRedisClient();
    const queueKey = this.getQueueKey(generation, modelClassification);

    const requests = await redis.lRange(queueKey, 0, maxCount - 1);
    if (requests.length > 0) {
      await redis.lTrim(queueKey, requests.length, -1);
    }

    return requests.map((req) => JSON.parse(req) as RateLimitedRequest);
  }

  async getQueueSize(
    generation: 1 | 2,
    modelClassification: string,
  ): Promise<number> {
    const redis = await getRedisClient();
    const queueKey = this.getQueueKey(generation, modelClassification);
    return await redis.lLen(queueKey);
  }

  async storeResponse(response: ProcessedResponse): Promise<void> {
    const redis = await getRedisClient();
    const responseKey = this.getResponseKey(response.id);

    await redis
      .multi()
      .set(responseKey, JSON.stringify(response))
      .expire(responseKey, EXPIRATION_SECONDS)
      .exec();
  }

  async getResponse(requestId: string): Promise<ProcessedResponse | null> {
    const redis = await getRedisClient();
    const responseKey = this.getResponseKey(requestId);

    const response = await redis.get(responseKey);
    return response ? (JSON.parse(response) as ProcessedResponse) : null;
  }

  async removeResponse(requestId: string): Promise<void> {
    const redis = await getRedisClient();
    const responseKey = this.getResponseKey(requestId);
    await redis.del(responseKey);
  }

  async checkIfRequestExists(requestId: string): Promise<boolean> {
    const redis = await getRedisClient();
    const responseKey = this.getResponseKey(requestId);

    // Check both queue and response storage
    const responseExists = await redis.exists(responseKey);
    if (responseExists) {
      return true;
    }

    // Check all queues for the request
    const modelClassifications = ['hifi', 'lofi', 'completions', 'embedding'];
    for (const classification of modelClassifications) {
      for (const generation of [1, 2] as const) {
        const queueKey = this.getQueueKey(generation, classification);
        const requests = await redis.lRange(queueKey, 0, -1);
        const requestExists = requests.some((req) => {
          try {
            const parsed = JSON.parse(req) as RateLimitedRequest;
            return parsed.id === requestId;
          } catch {
            return false;
          }
        });
        if (requestExists) {
          return true;
        }
      }
    }

    return false;
  }
}

export const rateLimitQueueManager = RateLimitQueueManager.getInstance();

import { getRetryErrorInfo } from '@/lib/ai/chat';
import { log } from '@/lib/logger';
import { temporarilyDisableModel } from '@/lib/ai/aiModelFactory';
import { rateLimitMetrics } from './metrics';
import type { ModelClassification, ModelFailoverConfig } from './types';
import {
  enqueueRequestForRetry,
  getAvailableModel,
  CHAT_RETRY_DELAY_MS,
} from './model-availability';
import { RateRetryError } from '@/lib/react-util/errors/rate-retry-error';

/**
 * Disables a model based on rate limit headers.
 *
 * @param modelKey - The model key to disable
 * @param retryAfter - The retry after duration in seconds
 */
export function disableModelFromRateLimit(
  modelKey: string,
  retryAfter: number,
): void {
  const disableDurationMs = Math.max(retryAfter * 1000, 60000); // At least 1 minute
  console.warn(
    `Rate limit detected for ${modelKey}, disabling for ${disableDurationMs}ms`,
  );
  temporarilyDisableModel(modelKey, disableDurationMs);
  rateLimitMetrics.recordError('rate_limit_disable', modelKey);
}

/**
 * Handles rate limit errors by disabling the current model, attempting fallback,
 * and enqueueing the request for retry if needed.
 *
 * @param error - The error that occurred
 * @param currentModelKey - The current model key that hit the rate limit
 * @param modelClassification - The model classification
 * @param failoverConfig - The failover configuration
 * @param params - The request parameters
 * @param errorContext - Additional context for error types ('generate', 'stream', 'stream_setup')
 * @returns The request ID if enqueued, otherwise rethrows the original error
 */
export async function handleRateLimitError(
  error: unknown,
  currentModelKey: string,
  modelClassification: ModelClassification,
  failoverConfig: ModelFailoverConfig | undefined,
  params: Record<string, unknown>,
  errorContext: 'generate' | 'stream' | 'stream_setup' = 'generate',
): Promise<never> {
  const rateLimitErrorInfo = getRetryErrorInfo(error);

  if (rateLimitErrorInfo?.isRetry && rateLimitErrorInfo.retryAfter) {
    log((l) =>
      l.warn(`Rate limit detected: ${rateLimitErrorInfo.retryAfter}s`),
    );

    // Disable the current model
    disableModelFromRateLimit(currentModelKey, rateLimitErrorInfo.retryAfter);

    // Try fallback if available
    if (failoverConfig) {
      const fallbackModelKey = getAvailableModel(
        failoverConfig.fallbackProvider,
        modelClassification,
      );
      if (fallbackModelKey && fallbackModelKey !== currentModelKey) {
        console.log(`Attempting fallback to: ${fallbackModelKey}`);
        // Note: Would need to retry with fallback model here
        // For now, enqueue for later processing
      }
    }

    // Enqueue for retry processing
    const errorType =
      errorContext === 'generate'
        ? 'rate_limit_enqueue'
        : errorContext === 'stream'
          ? 'stream_rate_limit'
          : 'stream_rate_limit_enqueue';

    const requestId = await enqueueRequestForRetry(
      modelClassification,
      {
        ...params,
        ...{
          chatId: 'unassigned',
          turnId: '1',
          ...((params?.providerMetadata as Record<string, unknown>)
            ?.backoffice ?? {}),
        },
      },
      errorType,
    );

    // Create context-specific error message
    throw new RateRetryError({
      chatId: String(params.chatId ?? 'unassigned'),
      turnId: String(params.turnId ?? '1'),
      retryId: String(requestId),
      retryAfter: new Date(Date.now() + CHAT_RETRY_DELAY_MS),
    });
  }

  // Not a rate limit error, record as other error and rethrow
  const errorType =
    errorContext === 'generate'
      ? 'other_error'
      : errorContext === 'stream'
        ? 'stream_rate_limit'
        : 'stream_other_error';

  rateLimitMetrics.recordError(errorType, modelClassification);
  throw error;
}

