/**
 * @fileoverview FIFO rate-aware language model queue implementation
 *
 * This class provides a queue-based system for processing language model requests
 * while respecting rate limits and providing intelligent request scheduling.
 */

import { LanguageModelV2 } from '@ai-sdk/provider';
import { v4 as uuidv4 } from 'uuid';
import { getRedisClient } from '../../../middleware/cacheWithRedis/redis-client';
import { countTokens } from '../../../core/count-tokens';
import { auth } from '@/auth';
import { log } from '@/lib/logger';
import type {
  LanguageModelQueueOptions,
  QueuedRequest,
  LanguageModelMethod,
  ModelCapacity,
  RateLimitInfo,
  QueueMetrics,
} from './types';
import { MessageTooLargeForQueueError } from '../errors';
import { ModelMap } from '../../model-stats/model-map';

const REDIS_PREFIX = 'language-model-queue';
const FIFO_CHATQUEUE_QUEUE_EXPIRATION_HOURS = 6;
const FIFO_CHATQUEUE_QUEUE_EXPIRATION_SECONDS =
  FIFO_CHATQUEUE_QUEUE_EXPIRATION_HOURS * 60 * 60;
const FIFO_CHATQUEUE_MESSAGE_STALE_TIMEOUT = 5 * 60 * 1000;
const FIFO_CHATQUEUE_OUTPUT_TOKEN_BUFFER = 1500; // Reserved tokens for response

/**
 * FIFO rate-aware language model queue
 *
 * Manages queued requests to language models with intelligent rate limiting,
 * FIFO processing with capacity-aware skipping, and comprehensive error handling.
 */
export class LanguageModelQueue {
  private readonly model: LanguageModelV2;
  private readonly maxConcurrentRequests: number;
  private readonly _queueInstanceId: string;
  private readonly modelType: string;
  private processingInterval?: NodeJS.Timeout;
  private abortControllers = new Map<string, AbortController>();

  constructor(options: LanguageModelQueueOptions) {
    this.model = options.model;
    this.maxConcurrentRequests = options.maxConcurrentRequests;
    this._queueInstanceId = uuidv4();
    this.modelType = this.extractModelType(options.model);

    log((l) =>
      l.info('LanguageModelQueue initialized', {
        modelType: this.modelType,
        maxConcurrentRequests: this.maxConcurrentRequests,
        queueInstanceId: this._queueInstanceId,
      }),
    );

    // Start processing loop
    this.startProcessing();
  }

  /**
   * Get the readonly queue instance ID
   */
  get queueInstanceId(): string {
    return this._queueInstanceId;
  }

  /**
   * Extract model type from LanguageModel instance
   */
  private extractModelType(model: LanguageModelV2): string {
    // Try to extract from the model's provider and model ID
    if ('provider' in model && 'modelId' in model) {
      const modelWithProps = model as { provider: string; modelId: string };
      return `${modelWithProps.provider}:${modelWithProps.modelId}`;
    }
    // Fallback to a generic identifier
    return 'unknown-model';
  }

  /**
   * Generate Redis keys for queue operations
   */
  private getQueueKey(): string {
    return `${REDIS_PREFIX}:queue:${this.modelType}`;
  }

  private getCapacityKey(): string {
    return `${REDIS_PREFIX}:capacity:${this.modelType}`;
  }

  private getProcessingKey(): string {
    return `${REDIS_PREFIX}:processing:${this.modelType}`;
  }

  /**
   * Get current user ID from auth
   */
  private async getCurrentUserId(): Promise<string> {
    try {
      const session = await auth();
      return session?.user?.id || 'anonymous';
    } catch (error) {
      log((l) => l.warn('Failed to get user ID from auth', { error }));
      return 'anonymous';
    }
  }

  /**
   * Get model token per minute limit from the models table
   */
  private async getModelTokenLimit(): Promise<number> {
    try {
      const quotaRecord = await (
        await ModelMap.getInstance()
      ).getModelFromLanguageModel(this.model);
      // This would need to be implemented to query the models table
      // For now, return a reasonable default
      // TODO: Implement actual database query to get model limits
      return quotaRecord?.quota?.maxTokensPerMinute || 50000; // Default limit
    } catch (error) {
      log((l) => l.warn('Failed to get model token limit', { error }));
      return 10000; // Conservative fallback
    }
  }

  /**
   * Validate that a message isn't too large for the queue
   */
  private async validateMessageSize(tokenCount: number): Promise<void> {
    const maxTokensPerMinute = await this.getModelTokenLimit();
    const maxAllowedTokens =
      maxTokensPerMinute - FIFO_CHATQUEUE_OUTPUT_TOKEN_BUFFER;

    if (tokenCount > maxAllowedTokens) {
      throw new MessageTooLargeForQueueError(
        tokenCount,
        maxAllowedTokens,
        this.modelType,
      );
    }
  }

  /**
   * Create a queued request object
   */
  private async createQueuedRequest(
    method: LanguageModelMethod,
    params: unknown,
    tokenCount: number,
  ): Promise<QueuedRequest> {
    const userId = await this.getCurrentUserId();

    return {
      id: uuidv4(),
      modelType: this.modelType,
      method,
      params,
      tokenCount,
      userId,
      status: 'pending',
      queuedAt: new Date().toISOString(),
    };
  }

  /**
   * Add a request to the Redis queue
   */
  private async enqueueRequest(request: QueuedRequest): Promise<void> {
    const redis = await getRedisClient();
    const queueKey = this.getQueueKey();

    await redis
      .multi()
      .lPush(queueKey, JSON.stringify(request))
      .expire(queueKey, FIFO_CHATQUEUE_QUEUE_EXPIRATION_SECONDS)
      .exec();

    log((l) =>
      l.info('Request enqueued', {
        requestId: request.id,
        method: request.method,
        tokenCount: request.tokenCount,
        queueInstanceId: this._queueInstanceId,
      }),
    );
  }

  /**
   * Get the current model capacity from Redis
   */
  private async getModelCapacity(): Promise<ModelCapacity | null> {
    try {
      const redis = await getRedisClient();
      const capacityKey = this.getCapacityKey();
      const data = await redis.get(capacityKey);

      if (!data) {
        return null;
      }

      return JSON.parse(data) as ModelCapacity;
    } catch (error) {
      log((l) => l.warn('Failed to get model capacity', { error }));
      return null;
    }
  }

  /**
   * Update model capacity in Redis
   */
  private async updateModelCapacity(capacity: ModelCapacity): Promise<void> {
    try {
      const redis = await getRedisClient();
      const capacityKey = this.getCapacityKey();

      await redis
        .multi()
        .set(capacityKey, JSON.stringify(capacity))
        .expire(capacityKey, FIFO_CHATQUEUE_QUEUE_EXPIRATION_SECONDS)
        .exec();
    } catch (error) {
      log((l) => l.warn('Failed to update model capacity', { error }));
    }
  }

  /**
   * Extract rate limit information from response headers
   */
  private extractRateLimitInfo(
    headers: Record<string, string | string[]>,
  ): RateLimitInfo {
    const info: RateLimitInfo = {};

    const remainingTokens = headers['x-ratelimit-remaining-tokens'];
    if (remainingTokens) {
      info.remainingTokens = parseInt(
        Array.isArray(remainingTokens) ? remainingTokens[0] : remainingTokens,
      );
    }

    const remainingRequests = headers['x-ratelimit-remaining-requests'];
    if (remainingRequests) {
      info.remainingRequests = parseInt(
        Array.isArray(remainingRequests)
          ? remainingRequests[0]
          : remainingRequests,
      );
    }

    const retryAfter = headers['x-retry-after'];
    if (retryAfter) {
      info.retryAfter = Array.isArray(retryAfter) ? retryAfter[0] : retryAfter;
    }

    return info;
  }

  /**
   * Check if the model has capacity to process a request
   */
  private async hasCapacity(tokenCount: number): Promise<boolean> {
    const capacity = await this.getModelCapacity();

    if (!capacity) {
      // No capacity data available, assume we have capacity
      return true;
    }

    // Check if we're still in a rate limit period
    if (capacity.resetAt) {
      const resetTime = new Date(capacity.resetAt);
      if (new Date() < resetTime) {
        return false;
      }
    }

    // Check token capacity
    const requiredTokens = tokenCount + FIFO_CHATQUEUE_OUTPUT_TOKEN_BUFFER;
    return capacity.tokensPerMinute >= requiredTokens;
  }

  /**
   * Generate text using the underlying model
   */
  async generateText(params: unknown, signal?: AbortSignal): Promise<unknown> {
    return this.processRequest('generateText', params, signal);
  }

  /**
   * Generate object using the underlying model
   */
  async generateObject(
    params: unknown,
    signal?: AbortSignal,
  ): Promise<unknown> {
    return this.processRequest('generateObject', params, signal);
  }

  /**
   * Stream text using the underlying model
   */
  async streamText(params: unknown, signal?: AbortSignal): Promise<unknown> {
    return this.processRequest('streamText', params, signal);
  }

  /**
   * Stream object using the underlying model
   */
  async streamObject(params: unknown, signal?: AbortSignal): Promise<unknown> {
    return this.processRequest('streamObject', params, signal);
  }

  /**
   * Process a request by adding it to the queue and waiting for completion
   */
  private async processRequest(
    method: LanguageModelMethod,
    params: unknown,
    signal?: AbortSignal,
  ): Promise<unknown> {
    // Count tokens in the request
    const tokenCount = this.estimateTokenCount(params);

    // Validate message size
    await this.validateMessageSize(tokenCount);

    // Create queued request
    const queuedRequest = await this.createQueuedRequest(
      method,
      params,
      tokenCount,
    );

    // Set up abort handling
    if (signal) {
      this.abortControllers.set(queuedRequest.id, new AbortController());

      signal.addEventListener('abort', () => {
        this.handleAbort(queuedRequest.id);
      });
    }

    // Enqueue the request
    await this.enqueueRequest(queuedRequest);

    // Return a promise that resolves when the request is processed
    return new Promise((resolve, reject) => {
      this.waitForRequestCompletion(queuedRequest.id, resolve, reject);
    });
  }

  /**
   * Estimate token count for request parameters
   */
  private estimateTokenCount(params: unknown): number {
    try {
      // Try to extract messages from params for token counting
      if (params && typeof params === 'object' && 'messages' in params) {
        const messages = (params as { messages: unknown }).messages;
        if (Array.isArray(messages)) {
          return countTokens({ prompt: messages });
        }
      }

      // Fallback estimation based on string length
      const str = JSON.stringify(params);
      return Math.ceil(str.length / 4); // Rough estimation: 4 chars per token
    } catch (error) {
      log((l) => l.warn('Failed to count tokens, using fallback', { error }));
      return 1000; // Conservative fallback
    }
  }

  /**
   * Handle request abortion
   */
  private async handleAbort(requestId: string): Promise<void> {
    try {
      const redis = await getRedisClient();
      const queueKey = this.getQueueKey();

      // Remove the request from the queue
      const requests = await redis.lRange(queueKey, 0, -1);
      const filteredRequests = requests.filter((req) => {
        try {
          const parsed = JSON.parse(req) as QueuedRequest;
          return parsed.id !== requestId;
        } catch {
          return true; // Keep unparseable requests
        }
      });

      // Update the queue
      await redis.del(queueKey);
      if (filteredRequests.length > 0) {
        await redis.lPush(queueKey, filteredRequests);
        await redis.expire(queueKey, FIFO_CHATQUEUE_QUEUE_EXPIRATION_SECONDS);
      }

      // Clean up abort controller
      this.abortControllers.delete(requestId);

      log((l) => l.info('Request aborted', { requestId }));
    } catch (error) {
      log((l) => l.warn('Failed to handle abort', { requestId, error }));
    }
  }

  /**
   * Wait for a request to complete processing
   */
  private async waitForRequestCompletion(
    requestId: string,
    resolve: (value: unknown) => void,
    reject: (reason: unknown) => void,
  ): Promise<void> {
    // This would be implemented to poll for completion or use pub/sub
    // For now, this is a placeholder
    setTimeout(() => {
      reject(
        new Error(
          `Request processing not yet implemented for request ${requestId}`,
        ),
      );
    }, 1000);
  }

  /**
   * Start the processing loop
   */
  private startProcessing(): void {
    this.processingInterval = setInterval(() => {
      this.processQueue().catch((error) => {
        log((l) => l.error('Error in processing loop', { error }));
      });
    }, 1000); // Process every second
  }

  /**
   * Main queue processing logic
   */
  private async processQueue(): Promise<void> {
    try {
      // Get current queue state
      const redis = await getRedisClient();
      const queueKey = this.getQueueKey();
      const requests = await redis.lRange(queueKey, 0, -1);

      if (requests.length === 0) {
        return; // Nothing to process
      }

      // Parse requests
      const parsedRequests = requests
        .map((req) => {
          try {
            return JSON.parse(req) as QueuedRequest;
          } catch {
            return null;
          }
        })
        .filter((req) => req !== null) as QueuedRequest[];

      // Filter to pending requests only
      const pendingRequests = parsedRequests.filter(
        (req) => req.status === 'pending',
      );

      if (pendingRequests.length === 0) {
        return;
      }

      // Check how many are currently processing
      const processingCount = parsedRequests.filter(
        (req) => req.status === 'processing',
      ).length;
      const availableSlots = this.maxConcurrentRequests - processingCount;

      if (availableSlots <= 0) {
        return; // No available processing slots
      }

      // Process requests with FIFO + capacity-aware logic
      await this.selectAndProcessRequests(pendingRequests, availableSlots);
    } catch (error) {
      log((l) => l.error('Error processing queue', { error }));
    }
  }

  /**
   * Select and process requests using FIFO with capacity-aware skipping
   */
  private async selectAndProcessRequests(
    pendingRequests: QueuedRequest[],
    availableSlots: number,
  ): Promise<void> {
    const requestsToProcess: QueuedRequest[] = [];
    const now = new Date();

    for (const request of pendingRequests) {
      if (requestsToProcess.length >= availableSlots) {
        break;
      }

      const hasCapacityForRequest = await this.hasCapacity(request.tokenCount);

      if (hasCapacityForRequest) {
        requestsToProcess.push(request);
      } else {
        // Check if we should wait for capacity to process this request due to the stale-message rule
        const queuedTime = new Date(request.queuedAt);
        const timeDiff = now.getTime() - queuedTime.getTime();

        if (timeDiff > FIFO_CHATQUEUE_MESSAGE_STALE_TIMEOUT) {
          // Don't skip requests older than 5 minutes
          requestsToProcess.push(request);
        }
        // Otherwise skip this request and continue to the next
      }
    }

    // Process selected requests
    for (const request of requestsToProcess) {
      await this.executeRequest(request);
    }
  }

  /**
   * Execute a single request
   */
  private async executeRequest(request: QueuedRequest): Promise<void> {
    try {
      // Mark as processing
      await this.markRequestAsProcessing(request);

      // Execute the actual model call
      const result = await this.callModel(request);

      // Handle successful completion
      await this.handleRequestSuccess(request, result);
    } catch (error) {
      // Handle errors
      await this.handleRequestError(request, error);
    }
  }

  /**
   * Mark a request as processing
   */
  private async markRequestAsProcessing(request: QueuedRequest): Promise<void> {
    const updatedRequest: QueuedRequest = {
      ...request,
      status: 'processing',
      processingStartedAt: new Date().toISOString(),
      processingQueueInstanceId: this._queueInstanceId,
    };

    await this.updateRequestInQueue(request.id, updatedRequest);
  }

  /**
   * Update a request in the queue
   */
  private async updateRequestInQueue(
    requestId: string,
    updatedRequest: QueuedRequest,
  ): Promise<void> {
    const redis = await getRedisClient();
    const queueKey = this.getQueueKey();

    // Get all requests
    const requests = await redis.lRange(queueKey, 0, -1);

    // Update the specific request
    const updatedRequests = requests.map((req) => {
      try {
        const parsed = JSON.parse(req) as QueuedRequest;
        if (parsed.id === requestId) {
          return JSON.stringify(updatedRequest);
        }
        return req;
      } catch {
        return req;
      }
    });

    // Replace the queue
    await redis.del(queueKey);
    if (updatedRequests.length > 0) {
      await redis.lPush(queueKey, updatedRequests);
      await redis.expire(queueKey, FIFO_CHATQUEUE_QUEUE_EXPIRATION_SECONDS);
    }
  }

  /**
   * Call the underlying language model
   */
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  private async callModel(request: QueuedRequest): Promise<unknown> {
    // This would implement the actual model calls
    // For now, this is a placeholder
    throw new Error('Model calling not yet implemented');
  }

  /**
   * Handle successful request completion
   */
  private async handleRequestSuccess(
    request: QueuedRequest,
    result: unknown,
  ): Promise<void> {
    // Remove from queue
    await this.removeRequestFromQueue(request.id);

    // Update capacity based on response headers if available
    if (result && typeof result === 'object' && 'rawResponse' in result) {
      const rawResponse = (
        result as {
          rawResponse?: { headers?: Record<string, string | string[]> };
        }
      ).rawResponse;
      if (rawResponse && rawResponse.headers) {
        await this.updateCapacityFromHeaders(rawResponse.headers);
      }
    }

    // Report metrics
    await this.reportMetrics();

    log((l) =>
      l.info('Request completed successfully', { requestId: request.id }),
    );
  }

  /**
   * Handle request errors
   */
  private async handleRequestError(
    request: QueuedRequest,
    error: unknown,
  ): Promise<void> {
    const isRateLimitError = this.isRateLimitError(error);

    if (isRateLimitError) {
      // Reset request to pending and update capacity
      const resetRequest: QueuedRequest = {
        ...request,
        status: 'pending',
        processingStartedAt: undefined,
        processingQueueInstanceId: undefined,
      };

      await this.updateRequestInQueue(request.id, resetRequest);
      await this.handleRateLimitError(error);
    } else {
      // Remove from queue for non-rate-limit errors
      await this.removeRequestFromQueue(request.id);
    }

    log((l) =>
      l.error('Request failed', {
        requestId: request.id,
        error,
        isRateLimitError,
      }),
    );
  }

  /**
   * Remove a request from the queue
   */
  private async removeRequestFromQueue(requestId: string): Promise<void> {
    const redis = await getRedisClient();
    const queueKey = this.getQueueKey();

    const requests = await redis.lRange(queueKey, 0, -1);
    const filteredRequests = requests.filter((req) => {
      try {
        const parsed = JSON.parse(req) as QueuedRequest;
        return parsed.id !== requestId;
      } catch {
        return true;
      }
    });

    await redis.del(queueKey);
    if (filteredRequests.length > 0) {
      await redis.lPush(queueKey, filteredRequests);
      await redis.expire(queueKey, FIFO_CHATQUEUE_QUEUE_EXPIRATION_SECONDS);
    }
  }

  /**
   * Check if an error is a rate limit error
   */
  private isRateLimitError(error: unknown): boolean {
    if (error && typeof error === 'object') {
      const errorObj = error as {
        code?: string;
        status?: number;
        message?: string;
        headers?: Record<string, string | string[]>;
      };

      // Check for common rate limit indicators
      if (
        errorObj.code === 'rate_limit_exceeded' ||
        errorObj.status === 429 ||
        (errorObj.message && errorObj.message.includes('rate limit'))
      ) {
        return true;
      }

      // Check for x-retry-after header presence
      if (errorObj.headers && errorObj.headers['x-retry-after']) {
        return true;
      }
    }

    return false;
  }

  /**
   * Handle rate limit errors
   */
  private async handleRateLimitError(error: unknown): Promise<void> {
    // Extract retry-after information
    let retryAfter: string | undefined;

    if (error && typeof error === 'object') {
      const errorObj = error as { headers?: Record<string, string | string[]> };
      if (errorObj.headers && errorObj.headers['x-retry-after']) {
        retryAfter = Array.isArray(errorObj.headers['x-retry-after'])
          ? errorObj.headers['x-retry-after'][0]
          : errorObj.headers['x-retry-after'];
      }
    }

    // Update capacity to indicate no tokens available
    const capacity: ModelCapacity = {
      tokensPerMinute: 0,
      lastUpdated: new Date().toISOString(),
      resetAt: retryAfter || new Date(Date.now() + 60000).toISOString(), // Default 1 minute
    };

    await this.updateModelCapacity(capacity);
  }

  /**
   * Update capacity from response headers
   */
  private async updateCapacityFromHeaders(
    headers: Record<string, string | string[]>,
  ): Promise<void> {
    const rateLimitInfo = this.extractRateLimitInfo(headers);

    if (rateLimitInfo.remainingTokens !== undefined) {
      const capacity: ModelCapacity = {
        tokensPerMinute: rateLimitInfo.remainingTokens,
        requestsPerMinute: rateLimitInfo.remainingRequests,
        lastUpdated: new Date().toISOString(),
        resetAt: rateLimitInfo.retryAfter,
      };

      await this.updateModelCapacity(capacity);
    }
  }

  /**
   * Report metrics to Application Insights
   */
  private async reportMetrics(): Promise<void> {
    try {
      const redis = await getRedisClient();
      const queueKey = this.getQueueKey();
      const requests = await redis.lRange(queueKey, 0, -1);

      const parsedRequests = requests
        .map((req) => {
          try {
            return JSON.parse(req) as QueuedRequest;
          } catch {
            return null;
          }
        })
        .filter((req) => req !== null) as QueuedRequest[];

      const activeRequests = parsedRequests.filter(
        (req) => req.status === 'processing',
      ).length;
      const queueSize = parsedRequests.length;

      const capacity = await this.getModelCapacity();
      const availableTokens = capacity?.tokensPerMinute || 0;

      const metrics: QueueMetrics = {
        activeRequests,
        queueSize,
        availableTokens,
        queueInstanceId: this._queueInstanceId,
        modelType: this.modelType,
      };

      // Report to Application Insights
      log((l) => l.info('Queue metrics', metrics));
    } catch (error) {
      log((l) => l.warn('Failed to report metrics', { error }));
    }
  }

  /**
   * Clean up resources
   */
  dispose(): void {
    if (this.processingInterval) {
      clearInterval(this.processingInterval);
      this.processingInterval = undefined;
    }

    // Abort any pending requests
    this.abortControllers.forEach((controller) => {
      controller.abort();
    });
    this.abortControllers.clear();

    log((l) =>
      l.info('LanguageModelQueue disposed', {
        queueInstanceId: this._queueInstanceId,
      }),
    );
  }
}

/**
 * @fileoverview Types and error classes for the FIFO rate-aware model queue system
 */

import { LanguageModelV2 } from '@ai-sdk/provider';

/**
 * Status of a queued request
 */
export type QueuedRequestStatus = 'pending' | 'processing';

/**
 * Method type for language model operations
 */
export type LanguageModelMethod =
  | 'generateText'
  | 'generateObject'
  | 'streamText'
  | 'streamObject';

/**
 * Configuration options for LanguageModelQueue constructor
 */
export interface LanguageModelQueueOptions {
  /** The language model the queue is attached to */
  model: LanguageModelV2;
  /** Maximum number of concurrent requests the queue will run */
  maxConcurrentRequests: number;
}

/**
 * A queued request record stored in Redis
 */
export interface QueuedRequest {
  /** Unique request identifier */
  id: string;
  /** Model type from the LanguageModelV2 */
  modelType: string;
  /** Method used to queue the request */
  method: LanguageModelMethod;
  /** Request parameters */
  params: unknown;
  /** Number of tokens needed to process the request */
  tokenCount: number;
  /** User ID making the request */
  userId: string;
  /** Status of the request */
  status: QueuedRequestStatus;
  /** Datetime the request was added to the queue */
  queuedAt: string;
  /** Datetime processing began (only set when status is 'processing') */
  processingStartedAt?: string;
  /** Queue instance ID of the queue processing the request */
  processingQueueInstanceId?: string;
}

/**
 * Model capacity tracking information
 */
export interface ModelCapacity {
  /** Available tokens per minute */
  tokensPerMinute: number;
  /** Available requests per minute */
  requestsPerMinute?: number;
  /** Timestamp when capacity was last updated */
  lastUpdated: string;
  /** Timestamp when capacity will reset (for rate limit scenarios) */
  resetAt?: string;
}

/**
 * Rate limit information from response headers
 */
export interface RateLimitInfo {
  /** Remaining tokens from x-ratelimit-remaining-tokens header */
  remainingTokens?: number;
  /** Remaining requests from x-ratelimit-remaining-requests header */
  remainingRequests?: number;
  /** Retry after datetime from x-retry-after header */
  retryAfter?: string;
}

/**
 * Queue processing metrics for Application Insights
 */
export interface QueueMetrics {
  /** Number of requests currently being processed */
  activeRequests: number;
  /** Total number of requests in the queue */
  queueSize: number;
  /** Available model token capacity */
  availableTokens: number;
  /** Queue instance ID */
  queueInstanceId: string;
  /** Model type */
  modelType: string;
}

import { EmbeddingModelV2 } from '@ai-sdk/provider';
import { createEmbeddingModel } from '../../aiModelFactory';
import { embed } from 'ai';
import { IEmbeddingService } from './types';

export class EmbeddingService implements IEmbeddingService {
  /**
   * Global embedding model stored in a Symbol-backed registry to avoid
   * duplication across HMR/SSR/multi-bundle environments.
   */
  private static get globalEmbeddingModel(): EmbeddingModelV2<string> {
    const GLOBAL_KEY = Symbol.for('@noeducation/embedding:Model');
    const registry = globalThis as unknown as {
      [key: symbol]: EmbeddingModelV2<string> | undefined;
    };
    if (!registry[GLOBAL_KEY]) {
      registry[GLOBAL_KEY] = createEmbeddingModel();
    }
    return registry[GLOBAL_KEY]!;
  }
  private static set globalEmbeddingModel(model: EmbeddingModelV2<string>) {
    const GLOBAL_KEY = Symbol.for('@noeducation/embedding:Model');
    const registry = globalThis as unknown as {
      [key: symbol]: EmbeddingModelV2<string> | undefined;
    };
    registry[GLOBAL_KEY] = model;
  }

  private openAiClient: EmbeddingModelV2<string>;
  private cacheEmbeddings = true;
  private embeddingCache: Map<string, number[]> = new Map();

  constructor(openAiClient?: EmbeddingModelV2<string>) {
    this.openAiClient = openAiClient ?? EmbeddingService.globalEmbeddingModel;
  }

  public setCacheEmbeddings(cache: boolean): this {
    this.cacheEmbeddings = cache;
    return this;
  }

  private async getEmbedding(query: string): Promise<number[]> {
    const ret = await embed({
      model: this.openAiClient,
      value: query,
    });
    return ret.embedding;
  }

  public async embed(query: string): Promise<number[]> {
    if (this.cacheEmbeddings && this.embeddingCache.has(query)) {
      return this.embeddingCache.get(query)!;
    }
    const vector = await this.getEmbedding(query);
    this.embeddingCache.set(query, vector);
    return vector;
  }
}

/**
 * A very thin embedding-service interface. Your real service
 * must implement .embed(text): Promise<number[]>
 */
export interface IEmbeddingService {
  embed(text: string): Promise<number[]>;
}

import { ResourceNotFoundError } from '@/lib/ai/services/chat/errors/resource-not-found-error';
/**
 * @module lib/ai/services/model-stats/model-map
 * @fileoverview
 * ModelMap provides centralized management of model configurations, quotas, and metadata.
 * It follows the singleton pattern with local caching and database persistence.
 * Supports normalization of provider/model names and direct lookup from LanguageModelV1 instances.
 *
 * @author NoEducation Team
 * @version 1.0.0
 * @since 2025-08-23
 */

import { drizDbWithInit, type DbDatabaseType } from '@/lib/drizzle-db';
import { schema } from '@/lib/drizzle-db/schema';
import { eq, and } from 'drizzle-orm';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import {
  ProviderMap,
  ProviderPrimaryNameType,
  ProviderPrimaryNameTypeValues,
} from './provider-map';
import { log } from '@/lib/logger';
import { LanguageModel } from 'ai';
import { ModelClassification } from '../../middleware/key-rate-limiter/types';
import { isKeyOf, newUuid } from '@/lib/typescript';

/**
 * Type representing a complete model record with provider information.
 */
export type ModelRecord = {
  id: string;
  providerId: string;
  providerName?: string;
  modelName: string;
  displayName?: string;
  description?: string;
  isActive: boolean;
  createdAt: string;
  updatedAt: string;
};

/**
 * Type representing optional fields when creating or updating a model quota.
 */
type UpdateOptionalQuotaFields = 'id' | 'createdAt' | 'updatedAt' | 'isActive';

/**
 * Type representing a model quota configuration.
 */
export type ModelQuotaRecord = {
  id: string;
  modelId: string;
  maxTokensPerMessage?: number;
  maxTokensPerMinute?: number;
  maxTokensPerDay?: number;
  isActive: boolean;
  createdAt: string | null;
  updatedAt: string | null;
};

/**
 * Type representing a complete model with its quota configuration.
 */
export type ModelWithQuota = ModelRecord & {
  quota?: ModelQuotaRecord;
};

/**
 * Type for provider/model name normalization results.
 */
export type ProviderModelNormalization = {
  provider: string;
  modelName: string;
  modelId: string;
  providerId: string;
  rethrow: () => void;
  get classification(): ModelClassification;
};

/**
 * List of aliased model names.
 */
export const ModelAliasNameValues = ['hifi', 'lofi', 'embedding'] as const;

export type ModelAliasNameType = (typeof ModelAliasNameValues)[number];

/**
 * Maps alias names to environment variables per provider.
 */
export const EnvironmentAliasMap: Record<
  ProviderPrimaryNameType,
  Record<ModelAliasNameType, string>
> = {
  azure: {
    hifi: 'AZURE_OPENAI_DEPLOYMENT_HIFI',
    lofi: 'AZURE_OPENAI_DEPLOYMENT_LOFI',
    embedding: 'AZURE_OPENAI_DEPLOYMENT_EMBEDDING',
  },
  google: {
    hifi: 'GOOGLE_GENERATIVE_HIFI',
    lofi: 'GOOGLE_GENERATIVE_LOFI',
    embedding: 'GOOGLE_GENERATIVE_EMBEDDING',
  },
  openai: {
    hifi: 'OPENAI_HIFI',
    lofi: 'OPENAI_LOFI',
    embedding: 'OPENAI_EMBEDDING',
  },
} as const;

/**
 * Apparently I was just waiting for typescript to catch up?
 * keeping around in case we wind up needing it
type ModelRowExt = {
  modelId: string;
  providerId: string;
  modelName: string;
  displayName: string;
  description: string;
  isActive: string;
  createdAt: string;
  updatedAt: string;
  providerName: string;
};
 */

/**
 * ModelMap provides centralized management of AI model configurations and quotas.
 * Uses singleton pattern with local caching and database persistence.
 *
 * Key features:
 * - Singleton instance with lazy initialization
 * - Local caching with TTL for performance
 * - Provider/model name normalization
 * - Direct lookup from LanguageModelV1 instances
 * - Comprehensive quota management
 *
 * @example
 * ```typescript
 * const modelMap = await ModelMap.getInstance();
 * const model = await modelMap.getModelByProviderAndName('azure-openai.chat', 'gpt-4');
 * const quota = await modelMap.getQuotaByModel(model);
 * ```
 */
export class ModelMap {
  /** Global symbol key to access init resolvers across module copies/HMR. */
  static readonly #INIT_KEY = Symbol.for(
    '@noeducation/model-stats:ModelMap:init',
  );
  /** Symbol-based global registry key for ModelMap singleton. */
  static readonly #REGISTRY_KEY = Symbol.for(
    '@noeducation/model-stats:ModelMap',
  );
  /** Local cached reference to the global singleton via global symbol registry. */
  static get #instance(): ModelMap | undefined {
    type GlobalReg = { [k: symbol]: ModelMap | undefined };
    const g = globalThis as unknown as GlobalReg;
    return g[this.#REGISTRY_KEY];
  }
  static set #instance(value: ModelMap | undefined) {
    type GlobalReg = { [k: symbol]: ModelMap | undefined };
    const g = globalThis as unknown as GlobalReg;
    g[this.#REGISTRY_KEY] = value;
  }

  /** In-memory cache for model records, keyed by `${providerId}:${modelName}`. */
  readonly #providerModelToRecord: Map<string, ModelRecord>;

  /** In-memory cache for model records, keyed by model ID. */
  readonly #idToRecord: Map<string, ModelRecord>;

  /** In-memory cache for quota records, keyed by model ID. */
  readonly #modelIdToQuota: Map<string, ModelQuotaRecord>;

  /** Promise that resolves when the instance is initialized. */
  #whenInitialized: PromiseWithResolvers<boolean>;

  /** Whether the instance has been initialized. */
  #initialized: boolean = false;

  /** Timestamp of last cache update (ms since epoch). */
  #lastCacheUpdate = 0;

  /** Cache time-to-live in milliseconds (default: 5 minutes). */
  readonly #CACHE_TTL = 5 * 60 * 1000;

  /**
   * Private constructor for singleton pattern.
   * @param {(readonly [string, ModelRecord])[] | DbDatabaseType} [modelsOrDb] - Initial models or database instance.
   */
  private constructor(
    modelsOrDb?: (readonly [string, ModelRecord])[] | DbDatabaseType,
  ) {
    this.#providerModelToRecord = new Map();
    this.#idToRecord = new Map();
    this.#modelIdToQuota = new Map();
    this.#initialized = false;
    this.#whenInitialized = Promise.withResolvers<boolean>();
    (this as unknown as Record<symbol, PromiseWithResolvers<boolean>>)[
      ModelMap.#INIT_KEY
    ] = this.#whenInitialized;
    if (Array.isArray(modelsOrDb)) {
      for (const [key, record] of modelsOrDb) {
        this.#providerModelToRecord.set(key, record);
        this.#idToRecord.set(record.id, record);
      }
      this.#initialized = true;
      this.#lastCacheUpdate = Date.now();
      this.#whenInitialized.resolve(true);
    }
  }

  /**
   * Get the singleton instance of ModelMap.
   * @returns {ModelMap} The singleton instance.
   */
  static get Instance(): ModelMap {
    type GlobalReg = { [k: symbol]: ModelMap | undefined };
    const g = globalThis as unknown as GlobalReg;
    if (!g[this.#REGISTRY_KEY]) {
      // Create without DB here; callers who need DB-backed init should call getInstance(db)
      g[this.#REGISTRY_KEY] = new ModelMap();
    }
    this.#instance = g[this.#REGISTRY_KEY]!;
    return this.#instance;
  }

  /**
   * Get the singleton instance with initialization guarantee.
   * @param {DbDatabaseType} [db] - Optional database instance.
   * @returns {Promise<ModelMap>} Promise that resolves to the initialized instance.
   */
  static async getInstance(db?: DbDatabaseType): Promise<ModelMap> {
    type GlobalReg = { [k: symbol]: ModelMap | undefined };
    const g = globalThis as unknown as GlobalReg;
    let inst = g[this.#REGISTRY_KEY];
    if (!inst) {
      inst = new ModelMap(db);
      g[this.#REGISTRY_KEY] = inst;
      this.#instance = inst;
      const init = (
        inst as unknown as Record<
          symbol,
          PromiseWithResolvers<boolean> | undefined
        >
      )[ModelMap.#INIT_KEY];
      const p = (init?.promise ?? Promise.resolve(true))
        .then((x) => {
          log((l) => l.silly('ModelMap initialized successfully'));
          return x;
        })
        .catch((e) => {
          this.#instance = undefined;
          g[this.#REGISTRY_KEY] = undefined;
          LoggedError.isTurtlesAllTheWayDownBaby(e, {
            log: true,
            message: 'Uncaught error during ModelMap initialization',
            source: 'ModelMap.Instance',
          });
        });
      inst.refresh(db);
      await p;
      return inst;
    }
    this.#instance = inst;
    const init2 = (
      inst as unknown as Record<
        symbol,
        PromiseWithResolvers<boolean> | undefined
      >
    )[ModelMap.#INIT_KEY];
    await (init2?.promise ?? Promise.resolve(true)).catch(() => {});
    return inst;
  }

  /**
   * Setup a mock instance of ModelMap for testing.
   * @param records - The model records to initialize the map with.
   * @returns The initialized ModelMap instance.
   */
  static setupMockInstance(
    records: (readonly [string, ModelRecord])[],
    quotas: (readonly [string, ModelQuotaRecord])[],
  ): ModelMap {
    type GlobalReg = { [k: symbol]: ModelMap | undefined };
    const g = globalThis as unknown as GlobalReg;
    g[this.#REGISTRY_KEY] = new ModelMap(records);
    this.#instance = g[this.#REGISTRY_KEY]!;
    for (const [key, record] of quotas) {
      this.#instance.#modelIdToQuota.set(key, record);
    }
    return this.#instance;
  }

  /**
   * Reset the singleton instance (for testing or reinitialization).
   */
  static reset(): void {
    type GlobalReg = { [k: symbol]: ModelMap | undefined };
    const g = globalThis as unknown as GlobalReg;
    g[this.#REGISTRY_KEY] = undefined;
    ModelMap.#instance = undefined;
  }

  /**
   * Refresh the model and quota data from the database.
   *
   * @param {DbDatabaseType} [db] - Optional database instance.
   * @returns {Promise<boolean>} Promise that resolves to true when refresh is complete.
   */
  async refresh(db?: DbDatabaseType): Promise<boolean> {
    this.#providerModelToRecord.clear();
    this.#idToRecord.clear();
    this.#modelIdToQuota.clear();
    if (this.#initialized) {
      this.#initialized = false;
      this.#whenInitialized = Promise.withResolvers<boolean>();
    }

    try {
      const database = db || (await drizDbWithInit());
      // Load models with provider information
      const modelsWithProviders = database
        .select({
          modelId: schema.models.id,
          providerId: schema.models.providerId,
          modelName: schema.models.modelName,
          displayName: schema.models.displayName,
          description: schema.models.description,
          isActive: schema.models.isActive,
          createdAt: schema.models.createdAt,
          updatedAt: schema.models.updatedAt,
          providerName: schema.providers.name,
        })
        .from(schema.models)
        .innerJoin(
          schema.providers,
          eq(schema.models.providerId, schema.providers.id),
        )
        .where(eq(schema.models.isActive, true))
        .execute();

      // Load all quotas
      const quotas = database
        .select()
        .from(schema.modelQuotas)
        .where(eq(schema.modelQuotas.isActive, true))
        .execute();

      // Populate model caches - ensure we have an array to iterate over
      const now = new Date(Date.now());
      Array.from((await modelsWithProviders) ?? []).forEach((row) => {
        const record: ModelRecord = {
          id: row.modelId,
          providerId: row.providerId,
          providerName: row.providerName,
          modelName: row.modelName,
          displayName: row.displayName || undefined,
          description: row.description || undefined,
          isActive: row.isActive,
          createdAt: row.createdAt ?? now.toISOString(),
          updatedAt: row.updatedAt ?? now.toISOString(),
        };
        this.#providerModelToRecord.set(
          `${row.providerId}:${row.modelName}`,
          record,
        );
        this.#idToRecord.set(row.modelId, record);
      });

      // Populate quota cache - ensure we have an array to iterate over
      Array.from((await quotas) ?? []).forEach((quota) => {
        const quotaRecord: ModelQuotaRecord = {
          id: quota.id,
          modelId: quota.modelId,
          maxTokensPerMessage: quota.maxTokensPerMessage || undefined,
          maxTokensPerMinute: quota.maxTokensPerMinute || undefined,
          maxTokensPerDay: quota.maxTokensPerDay || undefined,
          isActive: quota.isActive,
          createdAt: quota.createdAt ?? now.toISOString(),
          updatedAt: quota.updatedAt ?? now.toISOString(),
        };
        this.#modelIdToQuota.set(quota.modelId, quotaRecord);
      });
      this.#initialized = true;
      this.#lastCacheUpdate = Date.now();
      this.#whenInitialized.resolve(true);
      return true;
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        message: 'Failed to refresh ModelMap from database',
        source: 'ModelMap.refresh',
      });
      this.#whenInitialized.reject(error);
      throw error;
    }
  }

  /**
   * Get all model records as an iterable.
   * @returns {IterableIterator<[string, ModelRecord]>} Iterator over provider:model key-value pairs.
   */
  get entries(): IterableIterator<[string, ModelRecord]> {
    return this.#providerModelToRecord.entries();
  }

  /**
   * Get all model IDs.
   * @returns {string[]} Array of all model IDs.
   */
  get allIds(): string[] {
    return Array.from(this.#idToRecord.keys());
  }

  /**
   * Get all provider:model keys.
   * @returns {string[]} Array of all provider:model combinations.
   */
  get allProviderModelKeys(): string[] {
    return Array.from(this.#providerModelToRecord.keys());
  }

  /**
   * Check if the instance is initialized.
   * @returns {boolean} True if initialized.
   */
  get initialized(): boolean {
    return this.#initialized;
  }

  /**
   * Get a promise that resolves when the instance is initialized.
   * @returns {Promise<boolean>} Promise that resolves to true when initialized.
   */
  get whenInitialized(): Promise<boolean> {
    return this.#whenInitialized.promise;
  }

  /**
   * Normalize provider and model names for consistent storage and lookup.
   * Handles both separate and 'provider:model' formats.
   *
   * @param {string} providerOrModel - Provider name or 'provider:model' string.
   * @param {string} [modelName] - Optional model name.
   * @returns {Promise<ProviderModelNormalization>} Normalized provider and model information.
   *
   * @example
   * ```typescript
   * const norm1 = await modelMap.normalizeProviderModel('azure-openai.chat', 'gpt-4');
   * const norm2 = await modelMap.normalizeProviderModel('azure-openai.chat:gpt-4');
   * ```
   */
  async normalizeProviderModel(
    providerOrModel: LanguageModel,
    modelName?: string,
  ): Promise<ProviderModelNormalization> {
    if (typeof providerOrModel === 'object' && providerOrModel) {
      return await this.normalizeProviderModel(
        providerOrModel.provider,
        providerOrModel.modelId,
      );
    }
    // Parse provider:model format if present
    let provider: string;
    let parsedModelName: string;

    if (providerOrModel.includes(':')) {
      const [providerPart, modelPart] = providerOrModel.split(':', 2);
      provider = providerPart.trim();
      parsedModelName = modelPart.trim() || (modelName?.trim() ?? '');
    } else {
      provider = providerOrModel?.trim() ?? '';
      parsedModelName = modelName?.trim() ?? '';
      if (!parsedModelName) {
        // Sometimes we get the model name and only the model name - usually when dealing with an alias (eg 'hifi') or unique model name.
        // Apply common-sense provider detection rules to try and flesh this out.
        // If it has 'gemini' or 'google' as a prefix it's a google model
        if (
          ['gemini-', 'google-'].some((prefix) => provider.startsWith(prefix))
        ) {
          parsedModelName = provider;
          provider = 'google';
        } else {
          // Everything else defaults to azure
          parsedModelName = provider;
          provider = 'azure';
        }
      }
    }
    // Get provider ID from ProviderMap
    try {
      // Give maps a chance to finish initialization
      const [providerMap] = await Promise.all([
        ProviderMap.getInstance(),
        this.whenInitialized,
      ]);
      // Get provider ID from ProviderMap - note providermap.id is a synchronous function, so no await is necessary.
      const providerId = providerMap.id(provider);
      const providerCanonicalName = providerId
        ? isKeyOf(provider, ProviderPrimaryNameTypeValues)
          ? provider
          : providerMap.name(providerId)!
        : undefined;
      // If provider ID was found then use it pull the model, otherwise set id to undefined
      const record = providerId
        ? this.#providerModelToRecord.get(`${providerId}:${parsedModelName}`)
        : undefined;
      const modelId = record?.id;
      // Return normalized results with an error rethrow callback
      return {
        provider: providerCanonicalName!,
        modelName: parsedModelName,
        modelId: modelId!,
        providerId: providerId!,
        get classification(): ModelClassification {
          if (!modelId) {
            // HACK: undefined does not a modelclassification make,
            // however I should never have a null /undefined modelId
            // either, so good for the goose good for the gander.
            return undefined as unknown as ModelClassification;
          }
          if (
            modelId.includes('hifi') ||
            modelId.includes('gpt-4') ||
            (modelId.includes('gemini') && modelId.includes('pro'))
          ) {
            return 'hifi';
          }
          if (
            modelId.includes('lofi') ||
            modelId.includes('gpt-3.5') ||
            (modelId.includes('gemini') && modelId.includes('flash'))
          ) {
            return 'lofi';
          }
          if (modelId.includes('embedding')) {
            return 'embedding';
          }
          if (modelId.includes('completions')) {
            return 'completions';
          }

          return 'hifi'; // default fallback
        },
        rethrow: () => {
          if (!providerId) {
            throw new ResourceNotFoundError({
              resourceType: 'provider',
              normalized: provider,
              inputRaw: providerOrModel,
              message: `Provider not found: ${provider}`,
            });
          }
          if (!modelId) {
            throw new ResourceNotFoundError({
              resourceType: 'model',
              normalized: `${providerId}:${parsedModelName}`,
              inputRaw: { providerOrModel, modelName },
              message: `Model not found for provider ${provider}: ${parsedModelName}`,
            });
          }
        },
      };
    } catch (error) {
      // Log the failure
      const loggedError = LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        message: 'Error normalizing provider/model',
        extra: { providerOrModel, modelName },
        source: 'ModelMap.normalizeProviderModel',
      });
      // Yeah, i know...undefined as unknown as string is evil...but thats
      // what the rethrow callback is for ;)
      return {
        provider,
        modelId: undefined as unknown as string,
        modelName: parsedModelName,
        providerId: undefined as unknown as string,
        classification: undefined as unknown as ModelClassification,
        rethrow: () => {
          throw loggedError;
        },
      };
    }
  }

  /** Helper that throws ResourceNotFoundError when provider/model cant be normalized */
  async normalizeProviderModelOrThrow(
    providerOrModel: string,
    modelName?: string,
  ): Promise<
    Required<
      Pick<
        ProviderModelNormalization,
        'provider' | 'modelName' | 'providerId' | 'modelId'
      >
    >
  > {
    const norm = await this.normalizeProviderModel(providerOrModel, modelName);
    norm.rethrow();
    return {
      provider: norm.provider,
      modelName: norm.modelName,
      providerId: norm.providerId!,
      modelId: norm.modelId!,
    };
  }

  /**
   * Get a model record by provider and model name.
   *
   * @param {string} provider - Provider name or ID.
   * @param {string} modelName - Model name.
   * @returns {Promise<ModelRecord | null>} Model record or null if not found.
   *
   * @example
   * ```typescript
   * const model = await modelMap.getModelByProviderAndName('azure-openai.chat', 'gpt-4');
   * ```
   */
  async getModelByProviderAndName(
    provider: LanguageModel,
    modelName: string,
  ): Promise<ModelRecord | null> {
    await this.#ensureFreshCache();

    const {
      modelName: normalizedModelName,
      rethrow,
      providerId: normalizedProviderId,
    } = await this.normalizeProviderModel(provider, modelName);

    try {
      rethrow();
      const key = `${normalizedProviderId}:${normalizedModelName}`;
      return this.#providerModelToRecord.get(key) || null;
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        message: 'Error getting model by provider and name',
        extra: { provider, modelName },
        source: 'ModelMap.getModelByProviderAndName',
      });
      return null;
    }
  }

  /**
   * Get a model record by model ID.
   *
   * @param {string} modelId - Model ID.
   * @returns {Promise<ModelRecord | null>} Model record or null if not found.
   */
  async getModelById(modelId: string): Promise<ModelRecord | null> {
    await this.#ensureFreshCache();
    return this.#idToRecord.get(modelId) || null;
  }

  /**
   * Get a quota record by model ID.
   *
   * @param {string} modelId - Model ID.
   * @returns {Promise<ModelQuotaRecord | null>} Quota record or null if not found.
   */
  async getQuotaByModelId(modelId: string): Promise<ModelQuotaRecord | null> {
    await this.#ensureFreshCache();
    return this.#modelIdToQuota.get(modelId) || null;
  }
  /**
   * Get a quota record by model ID.
   *
   * @param {string} modelId - Model ID.
   * @returns {Promise<ModelQuotaRecord | null>} Quota record or null if not found.
   */
  async addQuotaToModel(
    record: Omit<ModelQuotaRecord, UpdateOptionalQuotaFields> &
      Partial<Pick<ModelQuotaRecord, UpdateOptionalQuotaFields>>,
  ): Promise<ModelQuotaRecord> {
    if (!record.modelId) {
      throw new TypeError('modelId is required when adding a quota record.');
    }
    await this.#ensureFreshCache();
    let current = this.#modelIdToQuota.get(record.modelId);
    const now = new Date().toISOString();
    if (current) {
      current.maxTokensPerDay = record.maxTokensPerDay;
      current.maxTokensPerMinute = record.maxTokensPerMinute;
      current.maxTokensPerMessage = record.maxTokensPerMessage;
      current.isActive = record.isActive ?? true;
      current.updatedAt = now;
    } else {
      current = {
        ...record,
        id: record.id ?? newUuid(),
        createdAt: now,
        updatedAt: now,
        isActive: record.isActive ?? true,
      };
    }
    this.#modelIdToQuota.set(record.modelId, current);
    return current;
  }

  /**
   * Get a quota record by model record.
   *
   * @param {ModelRecord} model - Model record.
   * @returns {Promise<ModelQuotaRecord | null>} Quota record or null if not found.
   */
  async getQuotaByModel(model: ModelRecord): Promise<ModelQuotaRecord | null> {
    return this.getQuotaByModelId(model.id);
  }

  /**
   * Get a complete model with quota by provider and model name.
   *
   * @param {string} provider - Provider name or ID.
   * @param {string} modelName - Model name.
   * @returns {Promise<ModelWithQuota | null>} Model with quota or null if not found.
   */
  async getModelWithQuota(
    provider: string,
    modelName: string,
  ): Promise<ModelWithQuota | null> {
    const model = await this.getModelByProviderAndName(provider, modelName);
    if (!model) return null;

    const quota = await this.getQuotaByModelId(model.id);
    return {
      ...model,
      quota: quota || undefined,
    };
  }

  /**
   * Get model and quota information from a LanguageModelV1 instance.
   * This method extracts provider and model information from the model's metadata
   * and performs a lookup in the ModelMap.
   *
   * @param {LanguageModelV1} languageModel - The LanguageModelV1 instance.
   * @returns {Promise<ModelWithQuota | null>} Model with quota or null if not found.
   *
   * @example
   * ```typescript
   * const model = aiModelFactory('hifi');
   * const modelInfo = await modelMap.getModelFromLanguageModelV1(model);
   * ```
   */
  async getModelFromLanguageModel(
    languageModel: LanguageModel,
  ): Promise<ModelWithQuota | null> {
    try {
      // Extract provider and model information from the LanguageModelV1 instance
      // LanguageModelV1 instances have provider and modelId properties
      const provider = (languageModel as { provider?: string }).provider;
      const modelId = (languageModel as { modelId?: string }).modelId;

      if (!provider || !modelId) {
        LoggedError.isTurtlesAllTheWayDownBaby(
          new Error('Missing provider or modelId in LanguageModelV1 instance'),
          {
            log: true,
            message: 'Unable to extract provider/model from LanguageModelV1',
            extra: {
              hasProvider: !!provider,
              hasModelId: !!modelId,
              keys: Object.keys(languageModel),
            },
            source: 'ModelMap.getModelFromLanguageModelV1',
          },
        );
        return null;
      }

      // Some models may have provider prefixes in the modelId, normalize them
      let normalizedModelName = modelId;

      // Handle Azure OpenAI format (e.g., "gpt-4" or "azure:gpt-4")
      if (provider.includes('azure') && normalizedModelName.includes(':')) {
        normalizedModelName =
          normalizedModelName.split(':').pop() || normalizedModelName;
      }

      // Handle Google format (e.g., "models/gemini-pro")
      if (
        provider.includes('google') &&
        normalizedModelName.startsWith('models/')
      ) {
        normalizedModelName = normalizedModelName.replace('models/', '');
      }

      return await this.getModelWithQuota(provider, normalizedModelName);
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        message: 'Error getting model from LanguageModelV1',
        extra: {
          languageModelType: typeof languageModel,
          languageModelKeys: Object.keys(languageModel || {}),
        },
        source: 'ModelMap.getModelFromLanguageModelV1',
      });
      return null;
    }
  }

  /**
   * Load quota configuration from PostgreSQL database.
   * This method is extracted from TokenStatsService to centralize model/quota management.
   *
   * @param {string} provider - Provider ID.
   * @param {string} modelName - Model name.
   * @returns {Promise<ModelQuotaRecord | null>} Quota configuration or null if not found.
   *
   * @example
   * ```typescript
   * const quota = await modelMap.loadQuotaFromDatabase('azure-provider-id', 'gpt-4');
   * ```
   */
  async loadQuotaFromDatabase(
    provider: string,
    modelName: string,
  ): Promise<ModelQuotaRecord | null> {
    try {
      return await drizDbWithInit(async (db) => {
        const row = await db
          .select({
            id: schema.modelQuotas.id,
            modelId: schema.modelQuotas.modelId,
            maxTokensPerMessage: schema.modelQuotas.maxTokensPerMessage,
            maxTokensPerMinute: schema.modelQuotas.maxTokensPerMinute,
            maxTokensPerDay: schema.modelQuotas.maxTokensPerDay,
            isActive: schema.modelQuotas.isActive,
            createdAt: schema.modelQuotas.createdAt,
            updatedAt: schema.modelQuotas.updatedAt,
          })
          .from(schema.modelQuotas)
          .innerJoin(
            schema.models,
            eq(schema.modelQuotas.modelId, schema.models.id),
          )
          .where(
            and(
              eq(schema.models.providerId, provider),
              eq(schema.models.modelName, modelName),
              eq(schema.modelQuotas.isActive, true),
            ),
          )
          .limit(1)
          .execute()
          .then((r) => r.at(0));

        if (!row) {
          return null;
        }
        const now =
          row.createdAt ?? row.updatedAt ?? new Date(Date.now()).toISOString();
        return {
          id: row.id,
          modelId: row.modelId,
          maxTokensPerMessage: row.maxTokensPerMessage || undefined,
          maxTokensPerMinute: row.maxTokensPerMinute || undefined,
          maxTokensPerDay: row.maxTokensPerDay || undefined,
          isActive: row.isActive,
          createdAt: now,
          updatedAt: now,
        };
      });
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        message: 'Error loading quota from database',
        extra: { provider, modelName },
        source: 'ModelMap.loadQuotaFromDatabase',
      });
      return null;
    }
  }

  /**
   * Check if the cache needs to be refreshed and refresh if necessary.
   * @private
   */
  async #ensureFreshCache(): Promise<void> {
    if (
      !this.#initialized ||
      Date.now() - this.#lastCacheUpdate > this.#CACHE_TTL
    ) {
      await this.refresh();
    }
  }

  /**
   * Get the model ID for a given provider and model name.
   * Similar to ProviderMap.id() but requires both provider and model name.
   *
   * @param {string} provider - Provider name or ID.
   * @param {string} modelName - Model name.
   * @returns {Promise<string | undefined>} Model ID or undefined if not found.
   *
   * @example
   * ```typescript
   * const modelId = await modelMap.id('azure-openai.chat', 'gpt-4');
   * ```
   */
  async id(provider: string, modelName: string): Promise<string | undefined> {
    const model = await this.getModelByProviderAndName(provider, modelName);
    return model?.id;
  }

  /**
   * Get the model name for a given model ID.
   *
   * @param {string} modelId - Model ID.
   * @returns {Promise<string | undefined>} Model name or undefined if not found.
   */
  async modelName(modelId: string): Promise<string | undefined> {
    const model = await this.getModelById(modelId);
    return model?.modelName;
  }

  /**
   * Get the provider ID for a given model ID.
   *
   * @param {string} modelId - Model ID.
   * @returns {Promise<string | undefined>} Provider ID or undefined if not found.
   */
  async providerId(modelId: string): Promise<string | undefined> {
    const model = await this.getModelById(modelId);
    return model?.providerId;
  }

  /**
   * Get the provider name for a given model ID.
   *
   * @param {string} modelId - Model ID.
   * @returns {Promise<string | undefined>} Provider name or undefined if not found.
   */
  async providerName(modelId: string): Promise<string | undefined> {
    const model = await this.getModelById(modelId);
    return model?.providerName;
  }

  /**
   * Get a model record by provider and model name or IDs.
   * Similar to ProviderMap.record() but requires both provider and model identifiers.
   *
   * @param {string} provider - Provider name or ID.
   * @param {string} modelName - Model name or ID.
   * @returns {Promise<ModelRecord | undefined>} Model record or undefined if not found.
   */
  async record(
    provider: string,
    modelName: string,
  ): Promise<ModelRecord | undefined> {
    const model = await this.getModelByProviderAndName(provider, modelName);
    return model || undefined;
  }

  /**
   * Check if the ModelMap contains a model with the given provider and name.
   *
   * @param {string} provider - Provider name or ID.
   * @param {string} modelName - Model name.
   * @returns {Promise<boolean>} True if the model exists.
   */
  async contains(provider: string, modelName: string): Promise<boolean> {
    const model = await this.getModelByProviderAndName(provider, modelName);
    return model !== null;
  }

  /**
   * Get all active models for a specific provider.
   *
   * @param {string} provider - Provider name or ID.
   * @returns {Promise<ModelRecord[]>} Array of model records for the provider.
   */
  async getModelsForProvider(provider: string): Promise<ModelRecord[]> {
    await this.#ensureFreshCache();

    const { providerId, rethrow } = await this.normalizeProviderModel(
      provider,
      '',
    );

    try {
      rethrow();
      const models: ModelRecord[] = [];

      for (const [key, record] of this.#providerModelToRecord.entries()) {
        if (key.startsWith(`${providerId}:`)) {
          models.push(record);
        }
      }

      return models;
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        message: 'Error getting models for provider',
        extra: { provider },
        source: 'ModelMap.getModelsForProvider',
      });
      return [];
    }
  }
}

/**
 * @module lib/ai/services/model-stats/provider-map
 *
 * ProviderMap manages provider metadata (IDs, names, aliases) used throughout
 * the model/quotas subsystem. It exposes a singleton instance which can be
 * initialized from the database or seeded with a mock set for tests.
 *
 * Responsibilities:
 * - Load and cache provider rows from the DB (id, name, displayName, aliases).
 * - Provide stable lookup helpers (by id or by name/alias).
 * - Offer defensive helpers that throw typed errors when lookups fail.
 *
 * The module exports small typed constants and guards to make provider names
 * ergonomic and well-typed across the codebase.
 */
import {
  drizDbWithInit,
  type ProvidersType,
  type DbDatabaseType,
  schema,
} from '@/lib/drizzle-db';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import { ResourceNotFoundError } from '@/lib/ai/services/chat/errors/resource-not-found-error';
import { isKeyOf } from '@/lib/typescript';
import { log } from '@/lib/logger';

/**
 * Primary provider canonical names.
 *
 * These are the short, canonical names used internally to represent the
 * capabilities of a provider.
 */
export const ProviderPrimaryNameTypeValues = [
  'azure',
  'google',
  'openai',
] as const;

/**
 * Common provider alias values used by external libraries or legacy configs.
 *
 * Aliases are additional names that should be treated as equivalent to the
 * canonical provider name when normalizing provider inputs.
 */
export const ProviderAliasTypeValues = [
  'google.generative-ai',
  'azure.chat',
  'azure-openai.chat',
] as const;

/**
 * Type representing a primary provider name (one of the canonical names).
 */
export type ProviderPrimaryNameType =
  (typeof ProviderPrimaryNameTypeValues)[number];

/**
 * Type representing a provider alias name.
 */
export type ProviderAliasType = (typeof ProviderAliasTypeValues)[number];

/**
 * Union of all supported provider name variants (primary and aliases).
 */
export type ProviderNameType = ProviderPrimaryNameType | ProviderAliasType;

