            aliases,
          }) => {
            this.#idToRecord.set(id, {
              name,
              displayName,
              description,
              baseUrl,
              isActive,
              aliases,
            });
          },
        );
        return Promise.resolve();
      })
      .then(() => this.#initializeNameToIdMap());
    // Log and suppress failure
    initDb.catch((err: unknown) => {
      LoggedError.isTurtlesAllTheWayDownBaby(err, {
        log: true,
        message: 'Failed to load provider map from database',
      });
      this.#whenInitialized.reject();
    });
    return this.#whenInitialized.promise;
  }

  #initializeNameToIdMap(): Promise<boolean> {
    this.#nameToId.clear();
    this.#idToRecord.forEach((rec, id) => {
      const thisName = rec[ProviderMap.#ProviderNameKey];
      if (isProviderName(thisName)) {
        this.#nameToId.set(thisName, id);
      } else {
        log((l) => l.warn(`Invalid provider name for id ${id}: ${thisName}`));
        // Force it, but keep in mind it probably won't work very well until we
        // the the code updated...
        this.#nameToId.set(thisName as ProviderNameType, id);
      }
      const aliases = rec[ProviderMap.#ProviderAliasesKey] || [];
      aliases.forEach((alias) => {
        if (isProviderName(alias)) {
          this.#nameToId.set(alias, id);
        } else {
          log((l) =>
            l.warn(
              `Invalid provider alias for provider ${thisName} (${id}): ${alias}`,
            ),
          );
          // Force it, but keep in mind it probably won't work very well until we
          // the the code updated...
          this.#nameToId.set(alias as ProviderNameType, id);
        }
      });
    });
    this.#initialized = true;
    this.#whenInitialized.resolve(true);
    return this.#whenInitialized.promise;
  }
}

/**
 * @module lib/ai/middleware/tokenStatsTracking/tokenStatsService
 * @fileoverview
 * TokenStatsService provides centralized logic for tracking AI token consumption, enforcing quotas, and reporting usage statistics.
 * It integrates Redis for fast, sliding-window statistics and PostgreSQL for persistent system-of-record storage.
 * This module is used by AI middleware, model factories, and quota enforcement logic throughout the application.
 *
 * @author NoEducation Team
 * @version 1.0.0
 * @since 2025-01-01
 */

import { getRedisClient } from '@/lib/ai/middleware/cacheWithRedis/redis-client';
import { drizDbWithInit, schema, sql } from '@/lib/drizzle-db';
import { log } from '@/lib/logger';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import {
  ModelQuota,
  QuotaCheckResult,
  TokenStats,
  TokenStatsServiceType,
  TokenUsageData,
} from '../../middleware/tokenStatsTracking/types';
import { ModelMap } from './model-map';

/**
 * Service for tracking token consumption statistics and enforcing quotas.
 * Uses Redis for fast access and PostgreSQL as system of record.
 *
 * @implements {TokenStatsServiceType}
 */
class TokenStatsService implements TokenStatsServiceType {
  /** Symbol-based global registry key for TokenStatsService singleton. */
  static readonly #REGISTRY_KEY = Symbol.for(
    '@noeducation/model-stats:TokenStatsService',
  );

  /** Global singleton instance via symbol registry. */
  private static get instance(): TokenStatsService | undefined {
    type GlobalReg = { [k: symbol]: TokenStatsService | undefined };
    const g = globalThis as unknown as GlobalReg;
    return g[this.#REGISTRY_KEY];
  }
  private static set instance(value: TokenStatsService | undefined) {
    type GlobalReg = { [k: symbol]: TokenStatsService | undefined };
    const g = globalThis as unknown as GlobalReg;
    g[this.#REGISTRY_KEY] = value;
  }

  /** Quota cache time-to-live in milliseconds (default: 5 minutes). */
  private readonly QUOTA_CACHE_TTL = 5 * 60 * 1000;

  /** Private constructor for singleton pattern. */
  private constructor() {}

  /**
   * Reset the singleton instance (for testing or reinitialization).
   * @function
   */
  static reset(): void {
    this.instance = undefined;
  }

  /**
   * Get the singleton instance of TokenStatsService.
   * @returns {TokenStatsService} The singleton instance.
   * @function
   */
  static get Instance(): TokenStatsService {
    if (!TokenStatsService.instance) {
      TokenStatsService.instance = new TokenStatsService();
    }
    return TokenStatsService.instance;
  }

  /**
   * Get the Redis key for token statistics for a given provider/model and window type.
   * @param {string} provider - Provider ID (e.g., 'azure-openai.chat').
   * @param {string} modelName - Model name (e.g., 'gpt-4').
   * @param {string} windowType - Time window ('minute', 'hour', 'day').
   * @returns {string} Redis key for token stats.
   * @private
   */
  private getRedisStatsKey(
    provider: string,
    modelName: string,
    windowType: string,
  ): string {
    return `token_stats:${provider}:${modelName}:${windowType}`;
  }

  /**
   * Get quota configuration for a provider/model from cache, Redis, or database.
   * @param {string} provider - Provider name or ID.
   * @param {string} modelName - Model name.
   * @returns {Promise<ModelQuota|null>} Quota configuration or null if not found.
   */
  async getQuota(
    provider: string,
    modelName: string,
  ): Promise<ModelQuota | null> {
    const {
      providerId: normalizedProvider,
      modelName: normalizedModel,
      modelId: normalizedModelId,
      rethrow,
    } = await ModelMap.getInstance().then((x) =>
      x.normalizeProviderModel(provider, modelName),
    );
    try {
      rethrow();
      let quotaFromMap = await ModelMap.getInstance().then((x) =>
        x.getQuotaByModelId(normalizedModelId!),
      );
      if (!quotaFromMap) {
        quotaFromMap = await ModelMap.Instance.addQuotaToModel({
          modelId: normalizedModelId!,
          maxTokensPerMessage: undefined,
          maxTokensPerMinute: undefined,
          maxTokensPerDay: undefined,
        });
      }
      return {
        ...quotaFromMap,
        provider: normalizedProvider!,
        modelName: normalizedModel,
      };
    } catch (error) {
      log((l) =>
        l.error('Error getting quota', {
          provider: provider,
          modelName: normalizedModel,
          error,
        }),
      );
      return null;
    }
  }

  /**
   * Get current token usage statistics for a provider/model from Redis.
   * @param {string} provider - Provider name or ID.
   * @param {string} modelName - Model name.
   * @returns {Promise<TokenStats>} Aggregated token usage statistics.
   */
  async getTokenStats(
    provider: string,
    modelName: string,
  ): Promise<TokenStats> {
    const {
      providerId: normalizedProvider,
      modelName: normalizedModel,
      rethrow,
    } = await ModelMap.getInstance().then((x) =>
      x.normalizeProviderModel(provider, modelName),
    );
    try {
      rethrow();

      const redis = await getRedisClient();

      // Get current stats from Redis sliding windows
      const currentMinuteKey = this.getRedisStatsKey(
        normalizedProvider,
        normalizedModel,
        'minute',
      );
      const lastHourKey = this.getRedisStatsKey(
        normalizedProvider,
        normalizedModel,
        'hour',
      );
      const last24HoursKey = this.getRedisStatsKey(
        normalizedProvider,
        normalizedModel,
        'day',
      );

      const [minuteData, hourData, dayData] = await Promise.all([
        redis.get(currentMinuteKey),
        redis.get(lastHourKey),
        redis.get(last24HoursKey),
      ]);

      return {
        currentMinuteTokens: minuteData
          ? JSON.parse(minuteData).totalTokens || 0
          : 0,
        lastHourTokens: hourData ? JSON.parse(hourData).totalTokens || 0 : 0,
        last24HoursTokens: dayData ? JSON.parse(dayData).totalTokens || 0 : 0,
        requestCount: minuteData ? JSON.parse(minuteData).requestCount || 0 : 0,
      };
    } catch (error) {
      log((l) =>
        l.error('Error getting token stats', {
          provider: normalizedProvider,
          modelName: normalizedModel,
          error,
        }),
      );
      return {
        currentMinuteTokens: 0,
        lastHourTokens: 0,
        last24HoursTokens: 0,
        requestCount: 0,
      };
    }
  }

  /**
   * Check if a token usage request would exceed quotas for a provider/model.
   * Returns a result indicating allowance, reason, and current usage.
   * @param {string} provider - Provider name or ID.
   * @param {string} modelName - Model name.
   * @param {number} requestedTokens - Number of tokens requested.
   * @returns {Promise<QuotaCheckResult>} Quota check result.
   */
  async checkQuota(
    provider: string,
    modelName: string,
    requestedTokens: number,
  ): Promise<QuotaCheckResult> {
    const {
      providerId: normalizedProvider,
      modelName: normalizedModel,
      rethrow,
    } = await ModelMap.getInstance().then((x) =>
      x.normalizeProviderModel(provider, modelName),
    );

    try {
      rethrow();
      const [quota, currentStats] = await Promise.all([
        this.getQuota(normalizedProvider, normalizedModel),
        this.getTokenStats(normalizedProvider, normalizedModel),
      ]);

      // If no quota is configured, allow the request
      if (!quota) {
        return { allowed: true, currentUsage: currentStats };
      }

      // Check per-message limit
      if (
        quota.maxTokensPerMessage &&
        requestedTokens > quota.maxTokensPerMessage
      ) {
        return {
          allowed: false,
          reason: `Request tokens (${requestedTokens}) exceed per-message limit (${quota.maxTokensPerMessage})`,
          currentUsage: currentStats,
          quota,
        };
      }

      // Check per-minute limit
      if (
        quota.maxTokensPerMinute &&
        currentStats.currentMinuteTokens + requestedTokens >
          quota.maxTokensPerMinute
      ) {
        return {
          allowed: false,
          reason: `Request would exceed per-minute limit (${quota.maxTokensPerMinute})`,
          currentUsage: currentStats,
          quota,
        };
      }

      // Check daily limit
      if (
        quota.maxTokensPerDay &&
        currentStats.last24HoursTokens + requestedTokens > quota.maxTokensPerDay
      ) {
        return {
          allowed: false,
          reason: `Request would exceed daily limit (${quota.maxTokensPerDay})`,
          currentUsage: currentStats,
          quota,
        };
      }

      return { allowed: true, currentUsage: currentStats, quota };
    } catch (error) {
      log((l) =>
        l.error('Error checking quota', {
          provider: normalizedProvider,
          modelName: normalizedModel,
          error,
        }),
      );
      // On error, allow the request to avoid blocking legitimate usage
      return { allowed: true };
    }
  }

  /**
   * Safely record token usage for a provider/model.
   * Updates both Redis and PostgreSQL with sliding window statistics.
   * This method is guaranteed not to reject and can be safely ignored.
   *
   * @param {string} provider - Provider ID (e.g., 'azure-openai.chat').
   * @param {string} modelName - Model name (e.g., 'gpt-4').
   * @param {TokenUsageData} usage - Token usage data to record.
   * @returns {Promise<void>} Resolves when usage is recorded.
   * @throws Never - errors are handled internally.
   *
   * @example
   * await tokenStatsService.safeRecordTokenUsage('azure-openai.chat', 'gpt-4', {
   *   promptTokens: 100,
   *   completionTokens: 200,
   *   totalTokens: 300
   * });
   */
  async safeRecordTokenUsage(
    provider: string,
    modelName: string,
    usage: TokenUsageData,
  ): Promise<void> {
    const {
      providerId: normalizedProvider,
      modelName: normalizedModel,
      rethrow,
    } = await ModelMap.getInstance().then((x) =>
      x.normalizeProviderModel(provider, modelName),
    );

    try {
      rethrow();
      await Promise.all([
        this.updateRedisStats(normalizedProvider, normalizedModel, usage),
        this.updateDatabaseStats(normalizedProvider, normalizedModel, usage),
      ]);
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        message: 'Error recording token usage',
        extra: {
          provider: normalizedProvider,
          modelName: normalizedModel,
          usage,
        },
        source: 'TokenStatsService.safeRecordTokenUsage',
      });
    }
  }

  /**
   * Update Redis statistics for a provider/model with sliding windows.
   * @param {string} provider - Provider ID.
   * @param {string} modelName - Model name.
   * @param {TokenUsageData} usage - Token usage data.
   * @returns {Promise<void>} Resolves when Redis stats are updated.
   * @private
   */
  private async updateRedisStats(
    provider: string,
    modelName: string,
    usage: TokenUsageData,
  ): Promise<void> {
    const {
      providerId: normalizedProvider,
      modelName: normalizedModel,
      rethrow,
    } = await ModelMap.getInstance().then((x) =>
      x.normalizeProviderModel(provider, modelName),
    );
    try {
      rethrow();
      const redis = await getRedisClient();
      const now = new Date();

      // Update each time window
      const windows = [
        {
          type: 'minute',
          duration: 60,
          start: new Date(Math.floor(now.getTime() / 60000) * 60000),
        },
        {
          type: 'hour',
          duration: 3600,
          start: new Date(Math.floor(now.getTime() / 3600000) * 3600000),
        },
        {
          type: 'day',
          duration: 86400,
          start: new Date(Math.floor(now.getTime() / 86400000) * 86400000),
        },
      ];
      // Use a Lua EVAL script to atomically read-modify-write and set TTL
      const lua = `
        local raw = redis.call('GET', KEYS[1])
        local obj = nil
        if raw then
          local ok, parsed = pcall(cjson.decode, raw)
          if ok and parsed then
            obj = parsed
          else
            obj = {promptTokens=0, completionTokens=0, totalTokens=0, requestCount=0, windowStart=ARGV[5]}
          end
        else
          obj = {promptTokens=0, completionTokens=0, totalTokens=0, requestCount=0, windowStart=ARGV[5]}
        end

        obj['promptTokens'] = (obj['promptTokens'] or 0) + tonumber(ARGV[1])
        obj['completionTokens'] = (obj['completionTokens'] or 0) + tonumber(ARGV[2])
        obj['totalTokens'] = (obj['totalTokens'] or 0) + tonumber(ARGV[3])
        obj['requestCount'] = (obj['requestCount'] or 0) + tonumber(ARGV[4])
        obj['windowStart'] = ARGV[5]
        obj['lastUpdated'] = ARGV[7]

        redis.call('SETEX', KEYS[1], tonumber(ARGV[6]), cjson.encode(obj))
        return cjson.encode(obj)
      `;

      for (const window of windows) {
        const key = this.getRedisStatsKey(
          normalizedProvider,
          normalizedModel,
          window.type,
        );

        // Execute Lua script to atomically update the JSON blob and set TTL.
        // ARGV: promptDelta, completionDelta, totalDelta, requestDelta, windowStart, ttl, nowIso
        await redis.eval(lua, {
          keys: [key],
          arguments: [
            String(usage.promptTokens),
            String(usage.completionTokens),
            String(usage.totalTokens),
            '1',
            window.start.toISOString(),
            String(window.duration + 300),
            now.toISOString(),
          ],
        });
      }
    } catch (error) {
      throw LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        message: 'Failed to update Redis stats',
        extra: {
          provider: normalizedProvider,
          modelName: normalizedModel,
          usage,
        },
        source: 'TokenStatsService.updateRedisStats',
      });
    }
  }

  /**
   * Update PostgreSQL statistics for a provider/model for persistence.
   * @param {string} provider - Provider ID.
   * @param {string} modelName - Model name.
   * @param {TokenUsageData} usage - Token usage data.
   * @returns {Promise<void>} Resolves when database stats are updated.
   * @private
   */
  private async updateDatabaseStats(
    provider: string,
    modelName: string,
    usage: TokenUsageData,
  ): Promise<void> {
    try {
      const model = await ModelMap.Instance.getModelByProviderAndName(
        provider,
        modelName,
      );
      if (!model) {
        throw new Error(`Model not found: ${provider}:${modelName}`);
      }
      await drizDbWithInit(async (db) => {
        const now = new Date();
        // Update each time window in the database
        const windows = [
          {
            type: 'minute',
            start: new Date(Math.floor(now.getTime() / 60000) * 60000),
          },
          {
            type: 'hour',
            start: new Date(Math.floor(now.getTime() / 3600000) * 3600000),
          },
          {
            type: 'day',
            start: new Date(Math.floor(now.getTime() / 86400000) * 86400000),
          },
        ];

        for (const window of windows) {
          const windowEnd = new Date(window.start.getTime());
          if (window.type === 'minute')
            windowEnd.setMinutes(windowEnd.getMinutes() + 1);
          else if (window.type === 'hour')
            windowEnd.setHours(windowEnd.getHours() + 1);
          else windowEnd.setDate(windowEnd.getDate() + 1);

          const conflictTarget = [
            schema.tokenConsumptionStats.modelId,
            schema.tokenConsumptionStats.windowStart,
            schema.tokenConsumptionStats.windowType,
          ];

          // Use upsert to update or insert stats
          await db
            .insert(schema.tokenConsumptionStats)
            .values({
              modelId: model.id,
              windowStart: window.start.toISOString(),
              windowEnd: windowEnd.toISOString(),
              windowType: window.type,
              promptTokens: usage.promptTokens,
              completionTokens: usage.completionTokens,
              totalTokens: usage.totalTokens,
              requestCount: 1,
            })
            .onConflictDoUpdate({
              target: conflictTarget,
              set: {
                promptTokens: sql`${schema.tokenConsumptionStats.promptTokens} + ${usage.promptTokens}`,
                completionTokens: sql`${schema.tokenConsumptionStats.completionTokens} + ${usage.completionTokens}`,
                totalTokens: sql`${schema.tokenConsumptionStats.totalTokens} + ${usage.totalTokens}`,
                requestCount: sql`${schema.tokenConsumptionStats.requestCount} + 1`,
                lastUpdated: new Date().toISOString(),
              },
            });
        }
      });
    } catch (error) {
      throw LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        message: 'Failed to update database stats.',
        data: { provider, modelName, usage },
        source: 'TokenStatsService.updateDatabaseStats',
      });
    }
  }

  /**
   * Get a comprehensive token usage report for a provider/model.
   * Includes quota, current stats, and quota check result.
   * @param {string} provider - Provider name or ID.
   * @param {string} modelName - Model name.
   * @returns {Promise<{quota: ModelQuota|null, currentStats: TokenStats, quotaCheckResult: QuotaCheckResult}>}
   *   Usage report object.
   */
  async getUsageReport(
    provider: string,
    modelName: string,
  ): Promise<{
    quota: ModelQuota | null;
    currentStats: TokenStats;
    quotaCheckResult: QuotaCheckResult;
  }> {
    const {
      providerId: normalizedProvider,
      modelName: normalizedModel,
      rethrow,
    } = await ModelMap.getInstance().then((x) =>
      x.normalizeProviderModel(provider, modelName),
    );
    try {
      rethrow();
      const [quota, currentStats] = await Promise.all([
        this.getQuota(normalizedProvider, normalizedModel),
        this.getTokenStats(normalizedProvider, normalizedModel),
      ]);

      const quotaCheckResult = await this.checkQuota(
        normalizedProvider,
        normalizedModel,
        0,
      );

      return { quota, currentStats, quotaCheckResult };
    } catch (error) {
      throw error;
    }
  }
}

/**
 * Get the singleton instance of TokenStatsService as TokenStatsServiceType.
 * @returns {TokenStatsServiceType} The singleton instance.
 */
export const getInstance = (): TokenStatsServiceType =>
  TokenStatsService.Instance;

/**
 * Reset the singleton instance of TokenStatsService.
 * @returns {void}
 */
export const reset = (): void => TokenStatsService.reset();

import { drizDbWithInit, type DbDatabaseType, schema } from '@/lib/drizzle-db';
import type { ChatToolType } from '@/lib/drizzle-db/drizzle-types';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import { ResourceNotFoundError } from '@/lib/ai/services/chat/errors/resource-not-found-error';
import { log } from '@/lib/logger';
import type {
  LanguageModelV2ProviderDefinedTool,
  LanguageModelV2FunctionTool,
} from '@ai-sdk/provider';

type ToolMapEntry = ChatToolType;
type ToolIdType = ChatToolType['chatToolId'];
type ToolNameType = ChatToolType['toolName'];
type ToolNameOrIdType = ToolIdType | ToolNameType;

/**
 * ToolMap manages chat tool metadata from the `chat_tool` table.
 * It mirrors ProviderMap's lifecycle and lookup utilities.
 */
export class ToolMap {
  /** Global symbol key to access the init resolvers across module copies */
  static readonly #INIT_KEY = Symbol.for(
    '@noeducation/model-stats:ToolMap:init',
  );
  /** Symbol-based global registry key for singleton ToolMap. */
  static readonly #REGISTRY_KEY = Symbol.for(
    '@noeducation/model-stats:ToolMap',
  );
  /** Local cached reference to the global singleton via global symbol registry. */
  static get #instance(): ToolMap | undefined {
    type GlobalReg = { [k: symbol]: ToolMap | undefined };
    const g = globalThis as unknown as GlobalReg;
    return g[this.#REGISTRY_KEY];
  }
  static set #instance(value: ToolMap | undefined) {
    type GlobalReg = { [k: symbol]: ToolMap | undefined };
    const g = globalThis as unknown as GlobalReg;
    g[this.#REGISTRY_KEY] = value;
  }

  /** In-memory cache for tool records, keyed by record ID. */
  readonly #idToRecord: Map<ToolIdType, ToolMapEntry>;

  /** In-memory mapping of tool name to record ID. */
  readonly #nameToId: Map<ToolNameType, ToolIdType>;

  /** Promise that resolves when the instance is initialized. */
  #whenInitialized: PromiseWithResolvers<boolean>;

  /** Whether the instance has been initialized. */
  #initialized: boolean = false;

  constructor(
    entriesOrDb?: (readonly [ToolIdType, ToolMapEntry])[] | DbDatabaseType,
  ) {
    this.#idToRecord = new Map();
    this.#nameToId = new Map();
    this.#whenInitialized = Promise.withResolvers<boolean>();
    // Expose init resolvers via a global symbol so static methods don't touch private slots
    (this as unknown as Record<symbol, PromiseWithResolvers<boolean>>)[
      ToolMap.#INIT_KEY
    ] = this.#whenInitialized;
    this.#initialized = false;

    if (Array.isArray(entriesOrDb)) {
      for (const [id, record] of entriesOrDb) {
        this.#idToRecord.set(id, record);
      }
      this.#initializeNameToIdMap();
    } else {
      this.refresh(entriesOrDb);
    }
  }

  /**
   * Synchronous singleton getter. Prefer getInstance() in async flows.
   */
  static get Instance(): ToolMap {
    type GlobalReg = { [k: symbol]: ToolMap | undefined };
    const g = globalThis as unknown as GlobalReg;
    if (!g[this.#REGISTRY_KEY]) {
      g[this.#REGISTRY_KEY] = new ToolMap();
    }
    this.#instance = g[this.#REGISTRY_KEY]!;
    return this.#instance;
  }

  /**
   * Return the singleton ToolMap instance, initializing from DB if needed.
   */
  static getInstance(db?: DbDatabaseType): Promise<ToolMap> {
    type GlobalReg = { [k: symbol]: ToolMap | undefined };
    const g = globalThis as unknown as GlobalReg;
    if (!g[this.#REGISTRY_KEY]) {
      g[this.#REGISTRY_KEY] = new ToolMap(db);
    }
    this.#instance = g[this.#REGISTRY_KEY]!;
    const init = (
      this.#instance as unknown as Record<
        symbol,
        PromiseWithResolvers<boolean> | undefined
      >
    )[ToolMap.#INIT_KEY];
    const promise =
      init?.promise ??
      (this.#instance.initialized
        ? Promise.resolve(true)
        : Promise.resolve(true));
    return promise.then(() => this.#instance!);
  }

  /** Setup a mock instance for tests using in-memory entries. */
  static setupMockInstance(
    records: (readonly [ToolIdType, ToolMapEntry])[],
  ): ToolMap {
    type GlobalReg = { [k: symbol]: ToolMap | undefined };
    const g = globalThis as unknown as GlobalReg;
    g[this.#REGISTRY_KEY] = new ToolMap(records);
    this.#instance = g[this.#REGISTRY_KEY]!;
    return this.#instance;
  }

  /** Reset the singleton (tests/reinit). */
  static reset(): void {
    type GlobalReg = { [k: symbol]: ToolMap | undefined };
    const g = globalThis as unknown as GlobalReg;
    g[this.#REGISTRY_KEY] = undefined;
    ToolMap.#instance = undefined;
  }

  /** Whether initialized. */
  get initialized(): boolean {
    return this.#initialized;
  }

  /** Promise that resolves when initialized. */
  get whenInitialized(): Promise<boolean> {
    return this.#whenInitialized.promise;
  }

  /** Iterate entries as [id, record]. */
  get entries(): IterableIterator<[ToolIdType, ToolMapEntry]> {
    return this.#idToRecord.entries();
  }

  /** Return all tool IDs. */
  get allIds(): ToolIdType[] {
    return Array.from(this.#idToRecord.keys());
  }

  /** Return all tool names. */
  get allNames(): ToolNameType[] {
    return Array.from(this.#nameToId.keys());
  }

  /** Lookup a tool record by id or name. */
  record(idOrName: ToolNameOrIdType): ToolMapEntry | undefined {
    // First, try by id
    if (this.#idToRecord.has(idOrName as ToolIdType)) {
      return this.#idToRecord.get(idOrName as ToolIdType);
    }
    // Then, try by name
    const id = this.#nameToId.get(idOrName as ToolNameType);
    return id ? this.#idToRecord.get(id) : undefined;
  }

  /** Lookup a tool record or throw when missing. */
  recordOrThrow(idOrName: ToolNameOrIdType): ToolMapEntry {
    const rec = this.record(idOrName);
    if (rec) return rec;
    throw new ResourceNotFoundError({
      resourceType: 'tool',
      normalized: idOrName,
      inputRaw: idOrName,
      message: `Tool not found: ${String(idOrName)}`,
    });
  }

  /** Return the tool name for an id or name. */
  name(idOrName: ToolNameOrIdType): ToolNameType | undefined {
    const rec = this.record(idOrName);
    return rec?.toolName;
  }

  /** Like name(), but throws if missing. */
  nameOrThrow(idOrName: ToolNameOrIdType): ToolNameType {
    const n = this.name(idOrName);
    if (n) return n;
    throw new ResourceNotFoundError({
      resourceType: 'tool',
      normalized: idOrName,
      inputRaw: idOrName,
      message: `Tool name not found for: ${String(idOrName)}`,
    });
  }

  /** Return the tool id for an id or name. */
  id(idOrName: ToolNameOrIdType): ToolIdType | undefined {
    if (this.#idToRecord.has(idOrName as ToolIdType)) {
      return idOrName as ToolIdType;
    }
    const name = idOrName as ToolNameType;
    return this.#nameToId.get(name);
  }

  /** Like id(), but throws if missing. */
  idOrThrow(idOrName: ToolNameOrIdType): ToolIdType {
    const val = this.id(idOrName);
    if (val) return val;
    throw new ResourceNotFoundError({
      resourceType: 'tool',
      normalized: idOrName,
      inputRaw: idOrName,
      message: `Tool id not found: ${String(idOrName)}`,
    });
  }

  /** Whether a tool exists for id or name. */
  contains(idOrName: ToolNameOrIdType): boolean {
    return !!this.record(idOrName);
  }

  async scanForTools(
    tools:
      | Array<LanguageModelV2FunctionTool | LanguageModelV2ProviderDefinedTool>
      | (LanguageModelV2FunctionTool | LanguageModelV2ProviderDefinedTool),
  ): Promise<number> {
    if (!Array.isArray(tools)) {
      tools = [tools];
    }
    const newEntries = tools
      .filter((tool) => {
        try {
          const id = tool && tool.name && this.name(tool.name);
          return !id;
        } catch (error) {
          LoggedError.isTurtlesAllTheWayDownBaby(error, {
            log: true,
            message: 'Error checking tool existence in ToolMap',
          });
          return false;
        }
      })
      .map((tool) => {
        let chatTool: ChatToolType | null = null;
        switch (tool.type) {
          case 'function':
            chatTool = {
              chatToolId: crypto.randomUUID(),
              toolName: tool.name,
              inputSchema: JSON.stringify(
                tool.inputSchema || { type: 'object' },
              ),
              outputSchema: null,
              providerOptions: tool.providerOptions
                ? JSON.stringify(tool.providerOptions)
                : null,
              description: tool.description || '',
            };
            break;
          case 'provider-defined':
            chatTool = {
              chatToolId: crypto.randomUUID(),
              toolName: tool.name,
              inputSchema: JSON.stringify(tool.args ?? { type: 'object' }),
              outputSchema: null,
              providerOptions: null,
              description: `provider-defined tool: ${tool.id || ''}`,
            };
            break;
          default:
            log((l) =>
              l.warn(
                `Unknown tool type in scanForTools: ${(tool as { type: string })?.type}`,
              ),
            );
        }
        return chatTool;
      })
      .filter(Boolean);
    if (newEntries.length === 0) {
      return 0;
    }
    const db = await drizDbWithInit();
    let processed = 0;
    for (const entry of newEntries) {
      try {
        await db.insert(schema.chatTool).values(entry!).execute();
        this.#idToRecord.set(entry!.chatToolId, entry!);
        this.#nameToId.set(entry!.toolName, entry!.chatToolId);
        processed++;
      } catch (error) {
        LoggedError.isTurtlesAllTheWayDownBaby(error, {
          log: true,
          message: 'Error inserting new tool in scanForTools',
          data: { toolName: entry!.toolName },
        });
      }
    }
    return processed;
  }

  /** Refresh caches from database. */
  refresh(db?: DbDatabaseType): Promise<boolean> {
    this.#idToRecord.clear();
    this.#nameToId.clear();
    this.#initialized = false;
    this.#whenInitialized = Promise.withResolvers<boolean>();
    // Re-expose fresh resolvers under the global INIT symbol
    (this as unknown as Record<symbol, PromiseWithResolvers<boolean>>)[
      ToolMap.#INIT_KEY
    ] = this.#whenInitialized;

    const initDb = (!!db ? Promise.resolve(db) : drizDbWithInit())
      .then((database) => database.select().from(schema.chatTool))
      .then((rows) => {
        (rows as ToolMapEntry[]).forEach((row) => {
          this.#idToRecord.set(row.chatToolId, row);
        });
      })
      .then(() => this.#initializeNameToIdMap());

    initDb.catch((err: unknown) => {
      LoggedError.isTurtlesAllTheWayDownBaby(err, {
        log: true,
        message: 'Failed to load tool map from database',
      });
      this.#whenInitialized.reject();
    });

    return this.#whenInitialized.promise;
  }

  #initializeNameToIdMap(): Promise<boolean> {
    this.#nameToId.clear();
    this.#idToRecord.forEach((rec, id) => {
      if (!rec.toolName) {
        log((l) => l.warn(`Invalid tool name for id ${id}`));
        return;
      }
      this.#nameToId.set(rec.toolName, id);
    });
    this.#initialized = true;
    this.#whenInitialized.resolve(true);
    return this.#whenInitialized.promise;
  }
}

import { env } from '@/lib/site-util/env';
import { CaseFileSearchOptions } from '../../tools/types';
import { CaseFileSearchScopeType } from '../../tools/unions';
import { HybridSearchClient } from './HybridSearchBase';
import { HybridSearchPayload } from './types';
import { IEmbeddingService } from '../embedding';

/**
 * Concrete hybrid search client specializing in case file (email + attachment + derived
 * document unit) content. Provides filtering semantics for different logical document
 * types as well as crossdocument relational identifiers (threads, replies, relationships).
 */
export class HybridDocumentSearch extends HybridSearchClient<CaseFileSearchOptions> {
  /**
   * Index name for document corpus (emails, attachments, key points, CTAs, etc.).
   * Derived from environment so deployments can swap indexes without code changes.
   */
  protected getSearchIndexName(): string {
    return env('AZURE_AISEARCH_DOCUMENTS_INDEX_NAME');
  }

  /**
   * Applies domain specific filters based on provided {@link CaseFileSearchOptions}.
   *
   * Supported filter dimensions:
   *  - scope: Accepts singular or array of logical types which are mapped to concrete
   *           `document_type` values stored in metadata attributes.
   *  - emailId, threadId, attachmentId, documentId: Direct entity scoping.
   *  - replyToDocumentId: Parent / reply chain scoping.
   *  - relatedToDocumentId: Custom relation tag (stored as composite key in metadata).
   *
   * Filtering Strategy:
   *  - Each dimension becomes an OData filter snippet targeting the `metadata/attributes`
   *    collection using `any()` semantics to match the desired attribute key/value pair.
   *  - Multiple dimensions are ANDcombined; multiple document types inside `scope` are ORcombined.
   *
   * NOTE: This mutation is additive  existing payload properties are preserved.
   */
  protected appendScopeFilter(
    payload: HybridSearchPayload,
    options: CaseFileSearchOptions,
  ): void {
    const {
      scope: policyType,
      emailId,
      threadId,
      attachmentId,
      documentId,
      replyToDocumentId,
      relatedToDocumentId,
    } = options ?? {};
    let filterValues: string[] = [];
    const validTypes: Record<CaseFileSearchScopeType, Array<string>> = {
      email: ['email'],
      attachment: ['attachment'],
      'key-point': ['key_point'],
      'call-to-action': ['cta'],
      'responsive-action': ['cta_response'],
      note: ['note'],
      'core-document': ['email', 'attachment'],
    };
    const filters: Array<string> = [];

    if (Array.isArray(policyType)) {
      filterValues = policyType
        .flatMap((type) => validTypes[type])
        .filter(Boolean);
    } else if (typeof policyType === 'string') {
      const mapped = validTypes[policyType];
      if (mapped) {
        filterValues = [mapped];
      }
    }

    if (filterValues.length > 0) {
      const orFilters = filterValues
        .map(
          (val) =>
            `metadata/attributes/any(a: a/key eq 'document_type' and a/value eq '${val}')`,
        )
        .join(' or ');
      filters.push(`(${orFilters})`);
    }

    if (emailId) {
      filters.push(
        `metadata/attributes/any(a: a/key eq 'email_id' and a/value eq '${emailId}')`,
      );
    }
    if (threadId) {
      filters.push(
        `metadata/attributes/any(a: a/key eq 'thread_id' and a/value eq '${threadId}')`,
      );
    }
    if (attachmentId) {
      filters.push(
        `metadata/attributes/any(a: a/key eq 'attachment_id' and a/value eq '${attachmentId}')`,
      );
    }
    if (documentId) {
      filters.push(
        `metadata/attributes/any(a: a/key eq 'id' and a/value eq '${documentId}')`,
      );
    }
    if (replyToDocumentId) {
      filters.push(
        `metadata/attributes/any(a: a/key eq 'parent_email_id' and a/value eq '${replyToDocumentId}')`,
      );
    }
    if (relatedToDocumentId) {
      filters.push(
        `metadata/attributes/any(a: a/key eq 'relatedEmailId:${relatedToDocumentId}')`,
      );
    }
    if (filters.length > 0) {
      payload.filter = filters.join(' and ');
    }
  }
}
/**
 * Factory helper for creating a {@link HybridDocumentSearch} instance while optionally
 * injecting a custom embedding service (for testing or alternate vector providers).
 *
 * @param options Optional configuration containing an `embeddingService` override.
 */
export const hybridDocumentSearchFactory = (options?: {
  embeddingService?: IEmbeddingService;
}) => new HybridDocumentSearch(options);

