import z, { ZodRawShape } from 'zod';
import {
  CaseFileRequestProps,
  ToolCallbackResult,
  ValidCaseFileRequestProps,
} from './types';
import { isError } from '@/lib/react-util/utility-methods';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import { drizDb, drizDbWithInit } from '@/lib/drizzle-db';

interface ToolCallbackResultOverloads {
  <T>(result: T): ToolCallbackResult<T>;
  <T>(error: Error, message?: string): ToolCallbackResult<T>;
}

export const toolCallbackResultFactory: ToolCallbackResultOverloads = <T>(
  result: T | Error,
  message?: string,
): ToolCallbackResult<T> => {
  if (isError(result)) {
    return {
      content: [{ type: 'text', text: message ?? result.message }],
      structuredContent: {
        result: {
          isError: true,
          message: message ?? result.message,
          cause: result.cause,
        },
      },
      isError: true,
    };
  }
  return Array.isArray(result)
    ? {
        content: [{ type: 'text', text: 'tool success' }],
        structuredContent: {
          result: {
            isError: false,
            items: result as T extends Array<infer U> ? Array<U> : never,
          },
        },
      }
    : {
        content: [{ type: 'text', text: 'tool success' }],
        structuredContent: {
          result: {
            isError: false,
            // eslint-disable-next-line @typescript-eslint/no-explicit-any
            value: result as T extends Array<any> ? never : T,
          },
        },
      };
};

export const toolCallbackResultSchemaFactory = <T extends ZodRawShape>(
  resultSchema:
    | z.ZodObject<T>
    | z.ZodString
    | z.ZodUnion<[z.ZodString, z.ZodObject<T>]>,
) => {
  const error = z.object({
    isError: z.literal(true),
    message: z.string().optional(),
    cause: z.any().optional(),
  });

  const success = z.object({
    isError: z.literal(false).optional(),
    value: resultSchema.optional(),
  });

  const result = z.discriminatedUnion('isError', [error, success]);

  return {
    result: result,
    //.describe('The returned value.')
  };
};
export const toolCallbackArrayResultSchemaFactory = <T extends ZodRawShape>(
  resultSchema:
    | z.ZodObject<T>
    | z.ZodString
    | z.ZodUnion<[z.ZodString, z.ZodObject<T>]>,
) => {
  const error = z.object({
    isError: z.literal(true),
    message: z.string().optional(),
    cause: z.any().optional(),
  });

  const success = z.object({
    isError: z.literal(false).optional(),
    items: z.array(resultSchema).optional(),
  });

  const result = z.discriminatedUnion('isError', [error, success]);

  return {
    result,
  };
};

/**
 * Checks if the provided string is a valid version 4 UUID.
 *
 * A valid UUID v4 is in the format: xxxxxxxx-xxxx-4xxx-[8|9|A|B]xxx-xxxxxxxxxxxx,
 * where 'x' is a hexadecimal digit.
 *
 * @param id - The string to validate as a UUID v4.
 * @returns `true` if the string is a valid UUID v4, otherwise `false`.
 */
export const isValidUuid = (id: string): boolean =>
  /[0-9a-z]{8}-[0-9a-z]{4}-4[0-9a-z]{3}-[89ABab][0-9a-z]{3}-[0-9a-z]{12}/i.test(
    id,
  );

/**
 * Resolves a case file's unit ID from a given document identifier.
 *
 * The function attempts to parse the provided `documentId` as a number.
 * If the `documentId` is a string that does not represent a number (e.g., a GUID or other non-numeric string),
 * it queries the database to find a matching document unit by `emailId` (with `documentType` 'email')
 * or by `documentPropertyId`. If a match is found, the corresponding `unitId` is returned.
 *
 * @param documentId - The identifier of the document, which can be a number or a string.
 * @returns A promise that resolves to the unit ID as a number, or `undefined` if not found.
 */
export const resolveCaseFileId = async (
  documentId: number | string,
): Promise<number | undefined> => {
  let parsedId: number | undefined;
  if (typeof documentId === 'string') {
    const isUuid = isValidUuid(documentId);
    if (isUuid) {
      parsedId = await drizDb()
        .query.documentUnits.findFirst({
          where: (du, { eq, and, or }) =>
            or(
              and(eq(du.emailId, documentId), eq(du.documentType, 'email')),
              eq(du.documentPropertyId, documentId),
            ),
          columns: {
            unitId: true,
          },
        })
        .then((result) => result?.unitId)
        .catch((err) => {
          LoggedError.isTurtlesAllTheWayDownBaby(err, {
            log: true,
            source: 'resolveCaseFileId',
            message:
              'Error querying for case file ID - validate document ID format',
            include: { documentId },
          });
          return undefined;
        });
    } else {
      parsedId = parseInt(documentId, 10);
      if (isNaN(parsedId)) {
        parsedId = undefined;
      }
    }
  } else if (typeof documentId === 'number') {
    parsedId = documentId;
  } else {
    parsedId = undefined;
  }
  return parsedId;
};

/**
 * Resolves a batch of case file identifiers to their corresponding numeric IDs.
 *
 * This function processes an array of requests, each containing a `caseFileId` that may be:
 * - A number (already resolved)
 * - A string representing a UUID (pending resolution)
 * - A string that can be parsed as a number (resolved)
 * - An invalid value (ignored)
 *
 * For UUIDs, the function queries the database to find matching records and resolves them to their numeric IDs.
 * Returns an array of objects, each with a resolved numeric `caseFileId`.
 *
 * @param requests - An array of objects containing a `caseFileId` property, which may be a number or string.
 * @returns A promise that resolves to an array of objects with a numeric `caseFileId`.
 */
export const resolveCaseFileIdBatch = async (
  requests: Array<CaseFileRequestProps>,
): Promise<Array<ValidCaseFileRequestProps>> => {
  // First, split up into valid and pending sets, dropping anything so invalid we wont even try
  const { valid, pending } = requests.reduce(
    (acc, request) => {
      // If input is a number then it is valid
      if (typeof request.caseFileId === 'number') {
        acc.valid.push({ caseFileId: request.caseFileId });
        return acc;
      }
      // If input is a string, check if it's a UUID or numeric string
      if (typeof request.caseFileId === 'string') {
        // First check if it's a valid UUID
        if (isValidUuid(request.caseFileId)) {
          acc.pending.push(request);
          return acc;
        }

        // Then check if it's a valid numeric string
        const check = request.caseFileId.trim();
        if (/^-?\d+$/.test(check)) {
          const parsedId = parseInt(request.caseFileId, 10);
          if (!isNaN(parsedId)) {
            acc.valid.push({
              ...request,
              caseFileId: parsedId,
            });
          }
        }
        return acc;
      }
      // All other values are so hosed we just drop them
      return acc;
    },
    {
      valid: [] as Array<ValidCaseFileRequestProps>,
      pending: [] as Array<CaseFileRequestProps>,
    },
  );
  // Now lets try and look up these GUIDs
  const guids = pending.map((r) => r.caseFileId as string);
  if (!guids.length) {
    return valid;
  }
  const records = await drizDbWithInit((db) => {
    return db.query.documentUnits.findMany({
      where: (du, { and, or, eq, inArray }) =>
        or(
          and(inArray(du.emailId, guids), eq(du.documentType, 'email')),
          inArray(du.documentPropertyId, guids),
        ),
      columns: {
        unitId: true,
        documentPropertyId: true,
        emailId: true,
      },
    });
  });
  // Now use records to translate pending into valid
  const { resolved } = pending.reduce(
    (acc, request) => {
      const record = records.find(
        (r) =>
          r.documentPropertyId === request.caseFileId ||
          r.emailId === request.caseFileId,
      );
      if (record) {
        request.caseFileId = record.unitId;
        acc.resolved.push({
          ...request,
          caseFileId: record.unitId,
        });
      }
      return acc;
    },
    { resolved: valid },
  );
  return resolved;
};

/**
 * @fileoverview Strongly-typed interface for message structure preservation
 *
 * This module defines a type-safe alternative to the '__preserveStructure' magic property,
 * providing compile-time type checking and better developer experience for message
 * structure preservation logic.
 *
 * @module lib/ai/types/message-structure-preservation
 */

import type { UIMessage, UIMessagePart, UIDataTypes, UITools } from 'ai';

/**
 * Preservation strategy types for different use cases
 */
export type PreservationStrategy =
  | 'full' // Preserve complete message structure including metadata
  | 'content-only' // Preserve only text content, strip metadata
  | 'semantic' // Preserve semantic meaning while allowing format changes
  | 'minimal' // Preserve only essential fields for functionality
  | 'custom'; // Use custom preservation rules

/**
 * Configuration for preserving message parts based on type
 */
export interface MessagePartPreservationRules {
  /** Whether to preserve text parts */
  text?: boolean;
  /** Whether to preserve tool call parts */
  toolCall?: boolean;
  /** Whether to preserve tool result parts */
  toolResult?: boolean;
  /** Whether to preserve file attachment parts */
  file?: boolean;
  /** Whether to preserve image parts */
  image?: boolean;
  /** Whether to preserve custom/dynamic parts */
  dynamic?: boolean;
}

/**
 * Metadata preservation configuration
 */
export interface MetadataPreservationOptions {
  /** Preserve timestamps */
  timestamps?: boolean;
  /** Preserve user identifiers */
  userIds?: boolean;
  /** Preserve message IDs */
  messageIds?: boolean;
  /** Preserve tool execution metadata */
  toolMetadata?: boolean;
  /** Preserve custom metadata fields */
  customFields?: string[];
}

/**
 * Content transformation options for preserved messages
 */
export interface ContentTransformationOptions {
  /** Maximum length for preserved content */
  maxContentLength?: number;
  /** Whether to truncate long content */
  truncateContent?: boolean;
  /** Truncation suffix (e.g., "...") */
  truncationSuffix?: string;
  /** Whether to summarize long content instead of truncating */
  summarizeContent?: boolean;
  /** Custom content transformer function */
  contentTransformer?: (content: string) => string;
}

/**
 * Tool-specific preservation rules
 */
export interface ToolPreservationRules {
  /** Tools to always preserve */
  alwaysPreserve?: string[];
  /** Tools to never preserve */
  neverPreserve?: string[];
  /** Custom preservation logic for specific tools */
  customRules?: Record<
    string,
    (part: UIMessagePart<UIDataTypes, UITools>) => boolean
  >;
}

/**
 * Context-aware preservation options
 */
export interface ContextualPreservationOptions {
  /** Preserve messages within this many recent interactions */
  recentInteractionCount?: number;
  /** Preserve messages containing these keywords */
  preserveKeywords?: string[];
  /** Preserve messages matching these patterns */
  preservePatterns?: RegExp[];
  /** Custom context evaluation function */
  contextEvaluator?: (
    message: UIMessage,
    index: number,
    messages: UIMessage[],
  ) => boolean;
}

/**
 * Performance optimization options
 */
export interface PerformanceOptions {
  /** Enable caching of preservation decisions */
  enableCaching?: boolean;
  /** Cache TTL in milliseconds */
  cacheTtlMs?: number;
  /** Maximum number of items to cache */
  maxCacheSize?: number;
  /** Enable async processing for large message sets */
  enableAsyncProcessing?: boolean;
}

/**
 * Main interface for message structure preservation configuration
 *
 * This interface replaces the '__preserveStructure' magic property with
 * a strongly-typed, comprehensive configuration system.
 *
 * @example
 * ```typescript
 * const preservationConfig: MessageStructureOptions = {
 *   strategy: 'semantic',
 *   partRules: {
 *     text: true,
 *     toolCall: true,
 *     toolResult: false
 *   },
 *   metadata: {
 *     timestamps: true,
 *     messageIds: true
 *   },
 *   performance: {
 *     enableCaching: true,
 *     cacheTtlMs: 300000
 *   }
 * };
 * ```
 */
export interface MessageStructureOptions {
  /** Whether preservation is enabled */
  enabled?: boolean;

  /** Overall preservation strategy */
  strategy?: PreservationStrategy;

  /** Rules for preserving different message part types */
  partRules?: MessagePartPreservationRules;

  /** Metadata preservation configuration */
  metadata?: MetadataPreservationOptions;

  /** Content transformation options */
  contentTransformation?: ContentTransformationOptions;

  /** Tool-specific preservation rules */
  toolRules?: ToolPreservationRules;

  /** Context-aware preservation options */
  contextual?: ContextualPreservationOptions;

  /** Performance optimization settings */
  performance?: PerformanceOptions;

  /** Custom validation function for preserved messages */
  validator?: (preservedMessage: UIMessage) => boolean;

  /** Debug mode for logging preservation decisions */
  debug?: boolean;
}

/**
 * Result of message structure preservation operation
 */
export interface MessagePreservationResult {
  /** Successfully preserved messages */
  preserved: UIMessage[];

  /** Messages that were filtered out */
  filtered: UIMessage[];

  /** Preservation statistics */
  stats: {
    originalCount: number;
    preservedCount: number;
    filteredCount: number;
    processingTimeMs: number;
    cacheHits?: number;
    cacheMisses?: number;
  };

  /** Any warnings or issues during preservation */
  warnings?: string[];

  /** Debug information (if debug mode enabled) */
  debugInfo?: {
    decisions: Array<{
      messageId: string;
      preserved: boolean;
      reason: string;
      strategy: PreservationStrategy;
    }>;
  };
}

/**
 * Type guard to check if an object has message structure options
 */
export function hasMessageStructureOptions(
  obj: unknown,
): obj is { messageStructure: MessageStructureOptions } {
  return (
    typeof obj === 'object' &&
    obj !== null &&
    'messageStructure' in obj &&
    typeof obj.messageStructure === 'object'
  );
}

/**
 * Type guard to check if preservation is enabled in options
 */
export function isPreservationEnabled(
  options: MessageStructureOptions,
): boolean {
  return options.enabled !== false; // Default to true if not explicitly disabled
}

/**
 * Default preservation configuration
 */
export const DEFAULT_MESSAGE_STRUCTURE_OPTIONS: MessageStructureOptions = {
  enabled: true,
  strategy: 'semantic',
  partRules: {
    text: true,
    toolCall: true,
    toolResult: true,
    file: true,
    image: true,
    dynamic: false,
  },
  metadata: {
    timestamps: true,
    userIds: true,
    messageIds: true,
    toolMetadata: true,
    customFields: [],
  },
  contentTransformation: {
    maxContentLength: 2000,
    truncateContent: true,
    truncationSuffix: '...',
    summarizeContent: false,
  },
  contextual: {
    recentInteractionCount: 5,
  },
  performance: {
    enableCaching: true,
    cacheTtlMs: 300000, // 5 minutes
    maxCacheSize: 1000,
    enableAsyncProcessing: false,
  },
  debug: false,
} as const;

/**
 * Utility type to create partial preservation options with type checking
 */
export type PartialMessageStructureOptions = Partial<MessageStructureOptions>;

/**
 * Factory function to create preservation options with defaults
 */
export function createMessageStructureOptions(
  options: PartialMessageStructureOptions = {},
): MessageStructureOptions {
  return {
    ...DEFAULT_MESSAGE_STRUCTURE_OPTIONS,
    ...options,
    partRules: {
      ...DEFAULT_MESSAGE_STRUCTURE_OPTIONS.partRules,
      ...options.partRules,
    },
    metadata: {
      ...DEFAULT_MESSAGE_STRUCTURE_OPTIONS.metadata,
      ...options.metadata,
    },
    contentTransformation: {
      ...DEFAULT_MESSAGE_STRUCTURE_OPTIONS.contentTransformation,
      ...options.contentTransformation,
    },
    contextual: {
      ...DEFAULT_MESSAGE_STRUCTURE_OPTIONS.contextual,
      ...options.contextual,
    },
    performance: {
      ...DEFAULT_MESSAGE_STRUCTURE_OPTIONS.performance,
      ...options.performance,
    },
  };
}

/**
 * @fileoverview Utility functions for message structure preservation
 const shouldPreservePart = (
  part: UIMessagePart<UIDataTypes, UITools>,
  rules: MessagePartPreservationRules
): boolean => { This module provides utility functions for working with the MessageStructureOptions
 * interface, including message filtering, preservation logic, and performance optimizations.
 *
 * @module lib/ai/utils/message-structure-preservation
 */

import type { UIMessage, UIMessagePart, UIDataTypes, UITools } from 'ai';
import type {
  MessageStructureOptions,
  MessagePreservationResult,
  PreservationStrategy,
  MessagePartPreservationRules,
} from '@/lib/ai/types/message-structure-preservation';
import {
  createMessageStructureOptions,
  isPreservationEnabled,
} from '@/lib/ai/types/message-structure-preservation';
import { log } from '@/lib/logger';

/**
 * Cache for preservation decisions to improve performance
 */
const preservationCache = new Map<string, boolean>();
const cacheStats = {
  hits: 0,
  misses: 0,
  created: Date.now(),
};

/**
 * Generate a cache key for a message preservation decision
 */
const generateCacheKey = (
  message: UIMessage,
  options: MessageStructureOptions,
): string => {
  const messageHash = JSON.stringify({
    id: message.id,
    role: message.role,
    partsLength: message.parts.length,
    strategy: options.strategy,
  });
  return `preserve_${Buffer.from(messageHash).toString('base64').slice(0, 16)}`;
};

/**
 * Clear the preservation cache
 */
export function clearPreservationCache(): void {
  preservationCache.clear();
  cacheStats.hits = 0;
  cacheStats.misses = 0;
  cacheStats.created = Date.now();
}

/**
 * Get cache statistics
 */
export function getPreservationCacheStats() {
  const total = cacheStats.hits + cacheStats.misses;
  return {
    size: preservationCache.size,
    hits: cacheStats.hits,
    misses: cacheStats.misses,
    hitRate: total > 0 ? cacheStats.hits / total : 0,
    ageMs: Date.now() - cacheStats.created,
  };
}

/**
 * Evaluate whether a message part should be preserved based on rules
 */
function shouldPreservePart(
  part: UIMessagePart<UIDataTypes, UITools>,
  rules: MessagePartPreservationRules,
): boolean {
  switch (part.type) {
    case 'text':
      return rules.text ?? true;
    case 'tool-call':
      return rules.toolCall ?? true;
    case 'tool-result':
      return rules.toolResult ?? true;
    case 'file':
      return rules.file ?? true;
    default:
      // Handle dynamic/unknown parts including image and others
      return rules.dynamic ?? false;
  }
}

/**
 * Apply content transformation to a message part
 */
function transformMessageContent(
  part: UIMessagePart<UIDataTypes, UITools>,
  options: MessageStructureOptions,
): UIMessagePart<UIDataTypes, UITools> {
  const transformation = options.contentTransformation;

  if (!transformation || part.type !== 'text') {
    return part;
  }

  const textPart = part as { type: 'text'; text: string };
  let content = textPart.text;

  // Apply custom transformer first
  if (transformation.contentTransformer) {
    content = transformation.contentTransformer(content);
  }

  // Apply length limits
  if (
    transformation.maxContentLength &&
    content.length > transformation.maxContentLength
  ) {
    if (transformation.summarizeContent) {
      // In a real implementation, you might call a summarization service here
      content =
        content.substring(0, transformation.maxContentLength / 2) +
        (transformation.truncationSuffix || '...');
    } else if (transformation.truncateContent) {
      content =
        content.substring(0, transformation.maxContentLength) +
        (transformation.truncationSuffix || '...');
    }
  }

  return {
    ...part,
    text: content,
  } as UIMessagePart<UIDataTypes, UITools>;
}

/**
 * Check if a message should be preserved based on contextual rules
 */
function evaluateContextualPreservation(
  message: UIMessage,
  index: number,
  messages: UIMessage[],
  options: MessageStructureOptions,
): { preserve: boolean; reason: string } {
  const contextual = options.contextual;

  if (!contextual) {
    return { preserve: true, reason: 'No contextual rules' };
  }

  // Apply custom context evaluator first (highest priority)
  if (contextual.contextEvaluator) {
    const shouldPreserve = contextual.contextEvaluator(
      message,
      index,
      messages,
    );
    return {
      preserve: shouldPreserve,
      reason: shouldPreserve
        ? 'Custom evaluator: preserve'
        : 'Custom evaluator: filter',
    };
  }

  // Check recent interaction count
  if (
    contextual.recentInteractionCount &&
    index >= messages.length - contextual.recentInteractionCount
  ) {
    return { preserve: true, reason: 'Within recent interaction count' };
  }

  // Check for preserve keywords
  if (contextual.preserveKeywords?.length) {
    const hasKeyword = message.parts.some((part) => {
      if (part.type === 'text') {
        const textPart = part as { type: 'text'; text: string };
        return contextual.preserveKeywords!.some((keyword) =>
          textPart.text.toLowerCase().includes(keyword.toLowerCase()),
        );
      }
      return false;
    });

    if (hasKeyword) {
      return { preserve: true, reason: 'Contains preserve keyword' };
    }
  }

  // Check for preserve patterns
  if (contextual.preservePatterns?.length) {
    const matchesPattern = message.parts.some((part) => {
      if (part.type === 'text') {
        const textPart = part as { type: 'text'; text: string };
        return contextual.preservePatterns!.some((pattern) =>
          pattern.test(textPart.text),
        );
      }
      return false;
    });

    if (matchesPattern) {
      return { preserve: true, reason: 'Matches preserve pattern' };
    }
  }

  return {
    preserve: false,
    reason: 'No contextual preservation rules matched',
  };
}

/**
 * Evaluate whether a message should be preserved based on all rules
 */
function evaluateMessagePreservation(
  message: UIMessage,
  index: number,
  messages: UIMessage[],
  options: MessageStructureOptions,
): { preserve: boolean; reason: string; strategy: PreservationStrategy } {
  const strategy = options.strategy || 'semantic';

  // Check cache first
  const cacheKey = generateCacheKey(message, options);
  if (options.performance?.enableCaching) {
    const cached = preservationCache.get(cacheKey);
    if (cached !== undefined) {
      cacheStats.hits++;
      return {
        preserve: cached,
        reason: 'Cached decision',
        strategy,
      };
    }
    cacheStats.misses++;
  }

  let preserve = true;
  let reason = 'Default preserve';

  // Strategy-based evaluation
  switch (strategy) {
    case 'full':
      preserve = true;
      reason = 'Full preservation strategy';
      break;

    case 'minimal':
      // Only preserve if it has essential content
      preserve = message.parts.some(
        (part) => part.type === 'text' || part.type === 'tool-call',
      );
      reason = preserve ? 'Has essential content' : 'No essential content';
      break;

    case 'content-only':
      // Preserve if it has text content
      preserve = message.parts.some((part) => part.type === 'text');
      reason = preserve ? 'Has text content' : 'No text content';
      break;

    case 'semantic':
      // Contextual evaluation for semantic preservation
      const contextResult = evaluateContextualPreservation(
        message,
        index,
        messages,
        options,
      );
      preserve = contextResult.preserve;
      reason = contextResult.reason;
      break;

    case 'custom':
      // Apply custom validator if provided
      if (options.validator) {
        preserve = options.validator(message);
        reason = preserve
          ? 'Custom validator: preserve'
          : 'Custom validator: filter';
      }
      break;
  }

  // Cache the decision
  if (options.performance?.enableCaching) {
    preservationCache.set(cacheKey, preserve);

    // Cleanup cache if it gets too large
    if (preservationCache.size > (options.performance.maxCacheSize || 1000)) {
      const keysToDelete = Array.from(preservationCache.keys()).slice(0, 100);
      keysToDelete.forEach((key) => preservationCache.delete(key));
    }
  }

  return { preserve, reason, strategy };
}

/**
 * Process and filter messages based on preservation options
 */
export function preserveMessageStructure(
  messages: UIMessage[],
  options: MessageStructureOptions = {},
): MessagePreservationResult {
  const startTime = Date.now();
  const fullOptions = createMessageStructureOptions(options);

  // Early return if preservation is disabled
  if (!isPreservationEnabled(fullOptions)) {
    return {
      preserved: messages,
      filtered: [],
      stats: {
        originalCount: messages.length,
        preservedCount: messages.length,
        filteredCount: 0,
        processingTimeMs: Date.now() - startTime,
      },
      warnings: ['Preservation is disabled - returning all messages'],
    };
  }

  const preserved: UIMessage[] = [];
  const filtered: UIMessage[] = [];
  const warnings: string[] = [];
  const debugDecisions: Array<{
    messageId: string;
    preserved: boolean;
    reason: string;
    strategy: PreservationStrategy;
  }> = [];

  // Process each message
  for (let i = 0; i < messages.length; i++) {
    const message = messages[i];

    try {
      const evaluation = evaluateMessagePreservation(
        message,
        i,
        messages,
        fullOptions,
      );

      if (fullOptions.debug) {
        debugDecisions.push({
          messageId: message.id,
          preserved: evaluation.preserve,
          reason: evaluation.reason,
          strategy: evaluation.strategy,
        });
      }

      if (evaluation.preserve) {
        // Transform message content if needed
        const transformedParts = message.parts
          .filter((part) =>
            shouldPreservePart(part, fullOptions.partRules || {}),
          )
          .map((part) => transformMessageContent(part, fullOptions));

        if (transformedParts.length > 0) {
          preserved.push({
            ...message,
            parts: transformedParts,
          });
        } else {
          filtered.push(message);
          warnings.push(
            `Message ${message.id} preserved but has no valid parts after filtering`,
          );
        }
      } else {
        filtered.push(message);
      }
    } catch (error) {
      filtered.push(message);
      warnings.push(`Error processing message ${message.id}: ${error}`);

      if (fullOptions.debug) {
        log((l) =>
          l.warn('Message preservation error', {
            messageId: message.id,
            error: error instanceof Error ? error.message : String(error),
          }),
        );
      }
    }
  }

  const result: MessagePreservationResult = {
    preserved,
    filtered,
    stats: {
      originalCount: messages.length,
      preservedCount: preserved.length,
      filteredCount: filtered.length,
      processingTimeMs: Date.now() - startTime,
      ...(fullOptions.performance?.enableCaching && {
        cacheHits: cacheStats.hits,
        cacheMisses: cacheStats.misses,
      }),
    },
    warnings: warnings.length > 0 ? warnings : undefined,
    ...(fullOptions.debug && {
      debugInfo: { decisions: debugDecisions },
    }),
  };

  // Log performance information if debug is enabled
  if (fullOptions.debug) {
    log((l) =>
      l.debug('Message structure preservation completed', {
        originalCount: messages.length,
        preservedCount: preserved.length,
        filteredCount: filtered.length,
        processingTimeMs: result.stats.processingTimeMs,
        strategy: fullOptions.strategy,
      }),
    );
  }

  return result;
}

/**
 * Validate message structure options
 */
export function validateMessageStructureOptions(
  options: MessageStructureOptions,
): { valid: boolean; errors: string[] } {
  const errors: string[] = [];

  // Validate strategy
  const validStrategies: PreservationStrategy[] = [
    'full',
    'content-only',
    'semantic',
    'minimal',
    'custom',
  ];
  if (options.strategy && !validStrategies.includes(options.strategy)) {
    errors.push(
      `Invalid strategy: ${options.strategy}. Must be one of: ${validStrategies.join(', ')}`,
    );
  }

  // Validate performance options
  if (options.performance) {
    const perf = options.performance;
    if (perf.cacheTtlMs !== undefined && perf.cacheTtlMs <= 0) {
      errors.push('cacheTtlMs must be positive');
    }
    if (perf.maxCacheSize !== undefined && perf.maxCacheSize <= 0) {
      errors.push('maxCacheSize must be positive');
    }
  }

  // Validate content transformation
  if (options.contentTransformation) {
    const content = options.contentTransformation;
    if (
      content.maxContentLength !== undefined &&
      content.maxContentLength <= 0
    ) {
      errors.push('maxContentLength must be positive');
    }
  }

  // Validate contextual options
  if (options.contextual) {
    const contextual = options.contextual;
    if (
      contextual.recentInteractionCount !== undefined &&
      contextual.recentInteractionCount <= 0
    ) {
      errors.push('recentInteractionCount must be positive');
    }
  }

  return {
    valid: errors.length === 0,
    errors,
  };
}

/**
 * Create a preset configuration for common use cases
 */
export function createPresetConfiguration(
  preset: 'minimal' | 'balanced' | 'comprehensive' | 'performance',
): MessageStructureOptions {
  switch (preset) {
    case 'minimal':
      return createMessageStructureOptions({
        strategy: 'minimal',
        partRules: {
          text: true,
          toolCall: false,
          toolResult: false,
          file: false,
          image: false,
          dynamic: false,
        },
        performance: {
          enableCaching: false,
        },
      });

    case 'balanced':
      return createMessageStructureOptions({
        strategy: 'semantic',
        contextual: {
          recentInteractionCount: 3,
        },
        contentTransformation: {
          maxContentLength: 1000,
          truncateContent: true,
        },
      });

    case 'comprehensive':
      return createMessageStructureOptions({
        strategy: 'full',
        debug: true,
        performance: {
          enableCaching: true,
          enableAsyncProcessing: true,
        },
      });

    case 'performance':
      return createMessageStructureOptions({
        strategy: 'content-only',
        performance: {
          enableCaching: true,
          cacheTtlMs: 600000, // 10 minutes
          maxCacheSize: 5000,
        },
        contentTransformation: {
          maxContentLength: 500,
          truncateContent: true,
        },
      });

    default:
      return createMessageStructureOptions();
  }
}

import { drizDbWithInit } from '@/lib/drizzle-db';
import {
  ObjectRepository,
  DrizzleRepositoryConfig,
  IObjectRepositoryExt,
} from './_types';
import { PaginatedResultset, PaginationStats } from '@/data-models/_types';
import { PartialExceptFor } from '@/lib/typescript';
import { eq, count, SQL } from 'drizzle-orm';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import { log } from '@/lib/logger';
import { getTableConfig } from 'drizzle-orm/pg-core';
import { PgColumn } from 'drizzle-orm/pg-core';

/**
 * Helper function to detect primary key column and field name from a Drizzle table schema
 */
function detectPrimaryKey<T extends object, KId extends keyof T>(
  config: DrizzleRepositoryConfig<T, KId>,
): { idColumn: PgColumn; idField: KId } {
  try {
    const tableConfig = getTableConfig(config.table);
    const tableName = config.tableName || tableConfig.name;

    // Find the primary key column
    const primaryKeyColumns = tableConfig.columns.filter((col) => col.primary);

    if (primaryKeyColumns.length === 0) {
      throw new Error(`No primary key found in table ${tableName}`);
    }

    if (primaryKeyColumns.length > 1) {
      throw new Error(
        `Multiple primary keys found in table ${tableName}. Please specify idColumn and idField manually.`,
      );
    }

    const primaryKeyColumn = primaryKeyColumns[0];

    // Convert snake_case database column name to camelCase field name
    const databaseColumnName = primaryKeyColumn.name;
    const camelCaseFieldName = databaseColumnName.replace(
      /_([a-z])/g,
      (_, letter) => letter.toUpperCase(),
    );

    return {
      idColumn: primaryKeyColumn,
      idField: camelCaseFieldName as KId,
    };
  } catch (error) {
    const tableName = config.tableName || getTableConfig(config.table).name;
    throw new Error(
      `Unable to auto-detect primary key for table ${tableName}. ` +
        `Please provide idColumn and idField explicitly in the config. ` +
        `Error: ${error instanceof Error ? error.message : String(error)}`,
    );
  }
}

/**
 * BaseDrizzleRepository is a generic class that provides basic CRUD operations for objects of type T
 * using Drizzle ORM. It implements the ObjectRepository interface and provides methods for listing,
 * getting, creating, updating, and deleting records in a database.
 *
 * @template T - The type of the objects managed by this repository.
 * @template KId - The type of the key field in the objects managed by this repository.
 *
 * @implements {ObjectRepository<T, KId>}
 */
export abstract class BaseDrizzleRepository<
  T extends object,
  KId extends keyof T,
> implements ObjectRepository<T, KId>
{
  protected readonly config: DrizzleRepositoryConfig<T, KId>;
  protected readonly idColumn: PgColumn;
  protected readonly idField: KId;
  protected readonly tableName: string;

  constructor(config: DrizzleRepositoryConfig<T, KId>) {
    this.config = config;

    // Auto-detect table name if not provided
    this.tableName = config.tableName || getTableConfig(config.table).name;

    // Auto-detect primary key if not provided
    if (!config.idColumn || !config.idField) {
      const detected = detectPrimaryKey(config);
      this.idColumn = config.idColumn || detected.idColumn;
      this.idField = config.idField || detected.idField;
    } else {
      this.idColumn = config.idColumn;
      this.idField = config.idField;
    }
  }

  /**
   * Validates the input for a specific repository method.
   * Override this method in subclasses to provide custom validation.
   */
  protected validate<TMethod extends keyof ObjectRepository<T, KId>>(
    method: TMethod,
    obj: Record<string, unknown>,
  ): void {
    // NO-OP by default, can be overridden
    log((l) =>
      l.silly(`Validating ${String(method)} operation`, {
        obj,
        tableName: this.tableName,
      }),
    );
  }

  /**
   * Retrieves a paginated list of objects.
   */
  async list(
    pagination?: PaginationStats,
  ): Promise<PaginatedResultset<Partial<T>>> {
    try {
      const page = pagination?.page ?? 1;
      const num = pagination?.num ?? 10;
      const offset = (page - 1) * num;

      // Get the query conditions from subclasses (if any)
      const queryConditions = this.buildQueryConditions();

      const validDb = await drizDbWithInit();
      // Build count query with the same conditions
      const countQueryBase = validDb
        .select({ count: count() })
        .from(this.config.table);
      const countQuery = queryConditions
        ? countQueryBase.where(queryConditions)
        : countQueryBase;

      // Build data query with the same conditions
      const dataQueryBase = validDb.select().from(this.config.table);
      const dataQuery = queryConditions
        ? dataQueryBase.where(queryConditions)
        : dataQueryBase;

      // Execute both queries
      const [countRecords, results] = await Promise.all([
        countQuery.execute(),
        dataQuery
          .offset(offset)
          .limit(num)
          .execute()
          .then((x) => x.map(this.config.summaryMapper)),
      ]);
      const [{ count: totalCount }] = countRecords;

      log((l) =>
        l.verbose({
          message: `[[AUDIT]] - ${this.tableName} list retrieved ${results.length} of ${totalCount} records.`,
          data: {
            results,
            totalCount,
            page,
            num,
          },
        }),
      );

      return {
        results,
        pageStats: {
          total: totalCount,
          page,
          num,
        },
      };
    } catch (error) {
      throw this.logDatabaseError('list', error);
    }
  }

  /**
   * Retrieves a single object by its unique identifier.
   */
  async get(recordId: T[KId]): Promise<T | null> {
    try {
      this.validate('get', { [this.idField]: recordId });

      const records = await drizDbWithInit((db) =>
        db
          .select()
          .from(this.config.table)
          .where(eq(this.idColumn, recordId as string | number))
          .execute(),
      );

      if (records.length === 0) {
        return null;
      }

      if (records.length > 1) {
        throw new Error(
          `Multiple records found for ${String(this.idField)}: ${recordId}`,
        );
      }

      const result = this.config.recordMapper(records[0]);

      log((l) =>
        l.verbose({
          message: `[[AUDIT]] - ${this.tableName} record retrieved:`,
          resultset: result,
        }),
      );

      return result;
    } catch (error) {
      const le = this.logDatabaseError('get', error);
      throw le;
    }
  }

  /**
   * Creates a new object.
   */
  async create(model: Omit<T, KId>): Promise<T> {
    try {
      this.validate('create', model);

      const insertData = this.prepareInsertData(model);
      const records = await drizDbWithInit((db) =>
        db.insert(this.config.table).values(insertData).returning(),
      );

      if (records.length !== 1) {
        throw new Error(`Failed to create ${this.tableName} record`);
      }

      const result = this.config.recordMapper(records[0]);

      log((l) =>
        l.verbose({
          message: `[[AUDIT]] - ${this.tableName} record created:`,
          resultset: result,
        }),
      );

      return result;
    } catch (error) {
      throw this.logDatabaseError('create', error);
    }
  }

  /**
   * Updates an existing object.
   */
  async update(
    model: PartialExceptFor<T, KId> & Required<Pick<T, KId>>,
  ): Promise<T> {
    try {
      this.validate('update', model);

      const updateData = this.prepareUpdateData(model);
      const records = await drizDbWithInit((db) =>
        db
          .update(this.config.table)
          .set(updateData)
          .where(eq(this.idColumn, model[this.idField] as string | number))
          .returning(),
      );

      if (records.length === 0) {
        throw new Error(`${this.tableName} record not found for update`);
      }

      if (records.length > 1) {
        throw new Error(`Multiple ${this.tableName} records updated`);
      }

      const result = this.config.recordMapper(records[0]);

      log((l) =>
        l.verbose({
          message: `[[AUDIT]] - ${this.tableName} record updated:`,
          resultset: result,
        }),
      );

      return result;
    } catch (error) {
      throw this.logDatabaseError('update', error);
    }
  }

  /**
   * Deletes an object by its unique identifier.
   */
  async delete(recordId: T[KId]): Promise<boolean> {
    try {
      this.validate('delete', { [this.idField]: recordId });

      const record = await drizDbWithInit((db) =>
        db
          .delete(this.config.table)
          .where(eq(this.idColumn, recordId as string | number))
          .returning()
          .then((records) => records.at(0)),
      );
      if (!record) {
