            values.push(value);
          }
        });
        values.push(props[this.objectIdField]);
        const ret = queryExt(
          (sql) =>
            sql<false, true>(
              `UPDATE ${this.tableName} SET ${updateFields.join(
                ', ',
              )} WHERE "${String(
                this.tableIdField,
              )}" = $${paramIndex} RETURNING *`.toString(),
