  }: {
    props: PartialExceptFor<T, KId> & Required<Pick<T, KId>>;
    updateQuery: Promise<TransformedFullQueryResults<T>>;
  }): Promise<TransformedFullQueryResults<T>> {
    return updateQuery;
  }

