      [
        emailId,
        attachmentId,
        emailPropertyId,
        content,
        documentType,
        embeddingModel,
      ],
    ];
  }

  /**
   * Retrieves the query properties for updating an existing `DocumentUnit` record.
   *
   * @param documentUnit - The `DocumentUnit` object to update.
   * @returns An array containing the updated fields and their values.
   */
  protected getUpdateQueryProperties({
    content,
    documentType,
    embeddingModel,
    embeddedOn,
  }: DocumentUnit): [Record<string, unknown>] {
    return [
      {
        content,
        document_type: documentType,
        embedding_model: embeddingModel,
        embedded_on: embeddedOn,
      },
    ];
  }

  /**
   * Maps a record object to a `DocumentUnitSummary` object.
   *
   * @param record - A record object containing key-value pairs representing the document unit data.
   * @returns A `DocumentUnitSummary` object with the mapped properties.
   *
   * - `unitId`: The unit ID, converted to a number.
   * - `emailId`: The email ID, converted to a string if present, otherwise `null`.
   * - `attachmentId`: The attachment ID, converted to a number if present, otherwise `null`.
   * - `emailPropertyId`: The email property ID, converted to a string if present, otherwise `null`.
   * - `documentType`: The document type, cast to the `DocumentUnit['documentType']` type.
   * - `createdOn`: The creation date, converted to a `Date` object.
   */
  mapToDocumentUnitSummary = (
    record: Record<string, unknown>,
  ): DocumentUnitSummary => {
    const ret: DocumentUnitSummary = {
      unitId: Number(record.unit_id),
      emailId: record.email_id ? String(record.email_id) : null,
      attachmentId: record.attachment_id ? Number(record.attachment_id) : null,
      emailPropertyId: record.email_property_id
        ? String(record.email_property_id)
        : null,
      threadId: record.thread_id ? Number(record.thread_id) : 0,
      relatedEmailIds: record.related_email_ids
        ? (record.related_email_ids as string[])
        : [],
      documentType: String(
        record.document_type,
      ) as DocumentUnit['documentType'],
      createdOn: new Date(String(record.created_on)),
      parentEmailId: record.parent_email_id
        ? String(record.parent_email_id)
        : null,
      embeddingModel: record.embedding_model
        ? String(record.embedding_model)
        : null,
      embeddedOn: new Date(String(record.embedded_on)),
    };
    switch (ret.documentType) {
      case 'email':
        ret.hrefDocument = `${env('NEXT_PUBLIC_HOSTNAME')}/api/email/${ret.emailId}`;
        ret.hrefApi = `${env('NEXT_PUBLIC_HOSTNAME')}/api/email/${ret.emailId}`;
        break;
      case 'attachment':
        ret.hrefDocument = record.file_path
          ? `${record.file_path}${this.SasKey}`
          : undefined;
        ret.hrefApi = `${env('NEXT_PUBLIC_HOSTNAME')}/api/attachment/${ret.attachmentId}`;
        break;
      case 'note':
        ret.hrefDocument = `${env('NEXT_PUBLIC_HOSTNAME')}/api/email/${ret.emailId}/properties/${ret.emailPropertyId}`;
        ret.hrefApi = `${env('NEXT_PUBLIC_HOSTNAME')}/api/email/${ret.emailId}/properties/${ret.emailPropertyId}`;
        break;
      case 'key_point':
        ret.hrefDocument = `${env('NEXT_PUBLIC_HOSTNAME')}/api/email/${ret.emailId}/properties/key-points/${ret.emailPropertyId}`;
        ret.hrefApi = `${env('NEXT_PUBLIC_HOSTNAME')}/api/email/${ret.emailId}/properties/key-points/${ret.emailPropertyId}`;
        break;
      case 'cta':
        ret.hrefDocument = `${env('NEXT_PUBLIC_HOSTNAME')}/api/email/${ret.emailId}/properties/call-to-action/${ret.emailPropertyId}`;
        ret.hrefApi = `${env('NEXT_PUBLIC_HOSTNAME')}/api/email/${ret.emailId}/properties/call-to-action/${ret.emailPropertyId}`;
        break;
      case 'sentiment':
        ret.hrefDocument = `${env('NEXT_PUBLIC_HOSTNAME')}/api/email/${ret.emailId}/properties/sentiment-analysis/${ret.emailPropertyId}`;
        ret.hrefApi = `${env('NEXT_PUBLIC_HOSTNAME')}/api/email/${ret.emailId}/properties/sentiment-analysis/${ret.emailPropertyId}`;
        break;
      case 'compliance':
        ret.hrefDocument = `${env('NEXT_PUBLIC_HOSTNAME')}/api/email/${ret.emailId}/properties/compliance-scores/${ret.emailPropertyId}`;
        ret.hrefApi = `${env('NEXT_PUBLIC_HOSTNAME')}/api/email/${ret.emailId}/properties/compliance-scores/${ret.emailPropertyId}`;
        break;
      default:
        break;
    }
    return ret;
  };

  /**
   * Maps a record object to a `DocumentUnit` object.
   *
   * @param record - A record object containing key-value pairs representing the document unit data.
   * @returns A `DocumentUnit` object with the mapped properties.
   */
  mapToDocumentUnit = (record: Record<string, unknown>): DocumentUnit => {
    const ret = this.mapToDocumentUnitSummary(record);
    return {
      ...ret,
      content: String(record.content),
    };
  };
}

import { NextRequest, NextResponse } from 'next/server';
import { BaseDrizzleRepository } from './_baseDrizzleRepository';
import { PickField } from '@/lib/typescript';
import { LikeNextRequest } from '@/lib/nextjs-util/types';
import { isRequestOrApiRequest } from '@/lib/nextjs-util/guards';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import { PaginatedResultset } from '@/data-models/_types';
import type { PaginationStats } from '@/data-models/_types';
import { GridSortModel, GridFilterModel } from '@mui/x-data-grid-pro';
import { PaginatedGridListRequest } from '../components/mui/data-grid';
import { parsePaginationStats } from '@/lib/components/mui/data-grid/queryHelpers/utility';

type KeysFromModel<
  TRepositoryModel,
  TRepositoryKey extends keyof TRepositoryModel,
> = {
  [key in TRepositoryKey]: PickField<TRepositoryModel, key>;
};

/**
 * DrizzleCrudRepositoryController provides REST API endpoints for any BaseDrizzleRepository implementation.
 * It handles HTTP requests and delegates to the repository for data operations while providing
 * consistent error handling and response formatting.
 *
 * @template TRepository - The specific DrizzleRepository implementation
 * @template TRepositoryModel - The domain model type managed by the repository
 * @template TRepositoryKey - The primary key field of the domain model
 */
export class DrizzleCrudRepositoryController<
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  TRepository extends BaseDrizzleRepository<any, keyof any>,
  TRepositoryModel extends TRepository extends BaseDrizzleRepository<
    infer TInferModel,
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    any
  >
    ? TInferModel
    : // eslint-disable-next-line @typescript-eslint/no-explicit-any
      any = TRepository extends BaseDrizzleRepository<infer TInferModel, any>
    ? TInferModel
    : // eslint-disable-next-line @typescript-eslint/no-explicit-any
      any,
  TRepositoryKey extends TRepository extends BaseDrizzleRepository<
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    any,
    infer TInferKey
  >
    ? TInferKey
    : // eslint-disable-next-line @typescript-eslint/no-explicit-any
      any = TRepository extends BaseDrizzleRepository<any, infer TInferKey>
    ? TInferKey
    : // eslint-disable-next-line @typescript-eslint/no-explicit-any
      any,
> {
  constructor(private repository: TRepository) {}

  /**
   * Handles GET requests for listing resources with optional pagination
   */
  async list(): Promise<
    NextResponse<
      PaginatedResultset<Partial<TRepositoryModel>> | { error: string }
    >
  >;
  async list(
    ops: PaginationStats,
    sort?: GridSortModel,
    filter?: GridFilterModel,
  ): Promise<
    NextResponse<
      PaginatedResultset<Partial<TRepositoryModel>> | { error: string }
    >
  >;
  async list(
    req: LikeNextRequest,
  ): Promise<
    NextResponse<
      PaginatedResultset<Partial<TRepositoryModel>> | { error: string }
    >
  >;

  async list(
    ops?: LikeNextRequest | PaginatedGridListRequest,
  ): Promise<
    NextResponse<
      PaginatedResultset<Partial<TRepositoryModel>> | { error: string }
    >
  > {
    let pagination: PaginatedGridListRequest & { offset?: number };
    if (isRequestOrApiRequest(ops)) {
      const thisUrl = new URL(ops.url!);
      pagination = ops.url
        ? parsePaginationStats(thisUrl)
        : { page: 1, num: 10, total: 0 };
    } else if (ops) {
      pagination = {
        page: ops.page ?? 1,
        num: ops.num ?? 10,
        filter: ops.filter,
        sort: ops.sort,
        total: ops.total ?? 0,
      };
    } else {
      pagination = { page: 1, num: 10, total: 0 };
    }

    try {
      const result = await this.repository.list(pagination);
      return NextResponse.json(result, { status: 200 });
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        source: 'DrizzleCrudRepositoryController::list',
      });
      return NextResponse.json(
        { error: 'Internal Server Error' },
        { status: 500 },
      );
    }
  }

  /**
   * Handles GET requests for retrieving a single resource by ID
   */
  async get(
    req: NextRequest,
    args: {
      params: Promise<KeysFromModel<TRepositoryModel, TRepositoryKey>>;
    },
  ): Promise<
    NextResponse<TRepositoryModel | { success: false; error: string }>
  > {
    try {
      const params = await args.params;
      const keys = Object.keys(params);
      if (keys.length !== 1) {
        return NextResponse.json(
          { success: false, error: 'Invalid parameters' },
          { status: 400 },
        );
      }

      const idValue = params[keys[0] as TRepositoryKey];
      const record = await this.repository.get(idValue);

      if (!record) {
        return NextResponse.json(
          { success: false, error: 'Record not found' },
          { status: 404 },
        );
      }

      return NextResponse.json(record, { status: 200 });
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        source: 'DrizzleCrudRepositoryController::get',
      });
      return NextResponse.json(
        { success: false, error: 'Internal Server Error' },
        { status: 500 },
      );
    }
  }

  /**
   * Handles POST requests for creating new resources
   */
  async create(
    req: NextRequest,
  ): Promise<
    NextResponse<TRepositoryModel | { success: false; error: string }>
  > {
    try {
      const body = await req.json();
      const newRecord = await this.repository.create(body);
      return NextResponse.json(newRecord, { status: 201 });
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        source: 'DrizzleCrudRepositoryController::create',
      });
      return NextResponse.json(
        { success: false, error: 'Internal Server Error' },
        { status: 500 },
      );
    }
  }

  /**
   * Handles PUT requests for updating existing resources
   */
  async update(
    req: NextRequest,
    args: {
      params: Promise<KeysFromModel<TRepositoryModel, TRepositoryKey>>;
    },
  ): Promise<
    NextResponse<TRepositoryModel | { success: false; error: string }>
  > {
    try {
      const params = await args.params;
      const body = await req.json();

      const keys = Object.keys(params);
      if (keys.length !== 1) {
        return NextResponse.json(
          { success: false, error: 'Invalid parameters' },
          { status: 400 },
        );
      }

      const idKey = keys[0] as TRepositoryKey;
      const idValue = params[idKey];

      // Ensure the ID is included in the update data
      const updateData = { ...body, [idKey]: idValue };

      const updatedRecord = await this.repository.update(updateData);
      return NextResponse.json(updatedRecord, { status: 200 });
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        source: 'DrizzleCrudRepositoryController::update',
      });
      return NextResponse.json(
        { success: false, error: 'Internal Server Error' },
        { status: 500 },
      );
    }
  }

  /**
   * Handles DELETE requests for removing resources
   */
  async delete(
    req: NextRequest,
    args: {
      params: Promise<KeysFromModel<TRepositoryModel, TRepositoryKey>>;
    },
  ): Promise<NextResponse<{ success: boolean; error?: string }>> {
    try {
      const params = await args.params;
      const keys = Object.keys(params);
      if (keys.length !== 1) {
        return NextResponse.json(
          { success: false, error: 'Invalid parameters' },
          { status: 400 },
        );
      }

      const idValue = params[keys[0] as TRepositoryKey];
      const deleted = await this.repository.delete(idValue);

      if (!deleted) {
        return NextResponse.json(
          { success: false, error: 'Record not found' },
          { status: 404 },
        );
      }

      return NextResponse.json({ success: true }, { status: 200 });
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        source: 'DrizzleCrudRepositoryController::delete',
      });
      return NextResponse.json(
        { success: false, error: 'Internal Server Error' },
        { status: 500 },
      );
    }
  }

  /**
   * Executes a custom operation using the repository
   */
  async updateFromRepository(
    callback: (repository: TRepository) => Promise<TRepositoryModel>,
  ): Promise<
    NextResponse<TRepositoryModel | { success: false; error: string }>
  > {
    try {
      const result = await callback(this.repository);
      return NextResponse.json(result, { status: 200 });
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        source: 'DrizzleCrudRepositoryController::updateFromRepository',
      });
      return NextResponse.json(
        { success: false, error: 'Internal Server Error' },
        { status: 500 },
      );
    }
  }
}

import type {
  EmailMessageSummary,
  EmailMessage,
  EmailMessageStats,
} from '@/data-models/api/email-message';
import type { PaginatedResultset, PaginationStats } from '@/data-models/_types';
import type { EmailSearchApiParams } from './types';
import siteMap from '@/lib/site-util/url-builder';
import {
  apiRequestHelperFactory,
  ApiRequestHelper,
} from '@/lib/send-api-request';
import { ICancellablePromiseExt } from '@/lib/typescript';

const apiRequest = <TResult>(
  cb: (api: ApiRequestHelper, builder: typeof siteMap.api.email) => TResult,
): TResult => {
  const apiHelper = apiRequestHelperFactory({ area: 'email' });
  const builder = siteMap.api.email;
  return cb(apiHelper, builder);
};

/**
 * Fetches a list of email messages.
 *
 * @returns {ICancellablePromiseExt<ReadonlyArray<EmailMessageSummary>>} A ICancellablePromiseExt that resolves to an array of email message summaries.
 */
export const getEmailList = ({
  page,
  num,
}: Omit<PaginationStats, 'total'>): ICancellablePromiseExt<
  ReadonlyArray<EmailMessageSummary>
> =>
  apiRequest((apiHelper, builder) =>
    apiHelper.get<ReadonlyArray<EmailMessageSummary>>({
      url: builder.page({ page, num }),
      action: 'list',
    }),
  );

/**
 * Fetches a specific email message by its ID.
 *
 * @param {number} id - The ID of the email message to fetch.
 * @returns {ICancellablePromiseExt<ReadonlyArray<EmailMessage>>} A ICancellablePromiseExt that resolves to the email message.
 */
export const getEmail = (id: string): ICancellablePromiseExt<EmailMessage> =>
  apiRequest((apiHelper, builder) =>
    apiHelper.get<EmailMessage>({
      url: builder.page(id),
      action: 'load',
    }),
  );

/**
 * Creates a new email record.
 *
 * @param {Omit<EmailMessage, 'emailId'>} email - The email message data to create.
 * @returns {ICancellablePromiseExt<EmailMessage>} A ICancellablePromiseExt that resolves to the created email message.
 */
export const createEmailRecord = (
  email: Omit<EmailMessage, 'emailId'>,
): ICancellablePromiseExt<EmailMessage> =>
  apiRequest((apiHelper, builder) =>
    apiHelper.post<EmailMessage>({
      url: builder.page(),
      action: 'create',
      input: email,
    }),
  );

/**
 * Updates an existing email record.
 *
 * @param {EmailMessage} email - The email message data to update.
 * @returns {ICancellablePromiseExt<EmailMessage>} A ICancellablePromiseExt that resolves to the updated email message.
 */
export const updateEmailRecord = (
  email: EmailMessage,
): ICancellablePromiseExt<EmailMessage> =>
  apiRequest((apiHelper, builder) =>
    apiHelper.put<EmailMessage>({
      url: builder.page(),
      action: 'update',
      input: email,
    }),
  );

/**
 * Writes an email record. If the email has an `emailId` greater than 0, it updates the existing record.
 * Otherwise, it creates a new email record.
 *
 * @param email - The email message object. It omits the `emailId` property but allows it to be partially included.
 * @returns A cancellable promise that resolves to the email message.
 */
export const writeEmailRecord = (
  email: Omit<EmailMessage, 'emailId'> & Partial<Pick<EmailMessage, 'emailId'>>,
): ICancellablePromiseExt<EmailMessage> =>
  (email.emailId ?? 0 > 0)
    ? updateEmailRecord(email as EmailMessage)
    : createEmailRecord(email);

/**
 * Deletes an email record by its ID.
 *
 * @param {number} id - The ID of the email message to delete.
 * @returns {ICancellablePromiseExt<EmailMessageSummary>} A ICancellablePromiseExt that resolves to the summary of the deleted email message.
 */
export const deleteEmailRecord = (
  id: number,
): ICancellablePromiseExt<EmailMessageSummary> =>
  apiRequest((apiHelper, builder) =>
    apiHelper.delete<EmailMessageSummary>({
      url: builder.page(id),
      action: 'delete',
    }),
  );

/**
 * Fetches email statistics.
 *
 * @returns {ICancellablePromiseExt<EmailMessageStats>} A ICancellablePromiseExt that resolves to the email message statistics.
 */
export const getEmailStats = (): ICancellablePromiseExt<EmailMessageStats> =>
  apiRequest((apiHelper, builder) =>
    apiHelper.get<EmailMessageStats>({
      url: builder.stats(),
      action: 'stats',
    }),
  );

/**
 * Fetches email search results based on the provided search parameters.
 *
 * @param {EmailSearchApiParams} ops - The search parameters.
 * @returns {ICancellablePromiseExt<PaginatedResultset<EmailMessageSummary>>} A ICancellablePromiseExt that resolves to a paginated result set of email message summaries.
 */
export const getEmailSearchResults = (
  ops: EmailSearchApiParams,
): ICancellablePromiseExt<PaginatedResultset<EmailMessageSummary>> =>
  apiRequest((apiHelper, builder) =>
    apiHelper.get<PaginatedResultset<EmailMessageSummary>>({
      url: builder.search(ops),
      action: 'search',
    }),
  );

import { ValidationError } from '@/lib/react-util/errors';
import { FirstParameter, newUuid } from '@/lib/typescript';
import { ObjectRepository } from '../_types';
import {
  EmailMessageAttachment,
  EmailMessageAttachmentSummary,
  EmailMessageSummary,
} from '@/data-models/api/email-message';
import { BaseObjectRepository } from '../_baseObjectRepository';
import { query } from '@/lib/neondb';
import { AbstractObjectRepository } from '../abstractObjectRepository';
import { db } from '@/lib/neondb';

type RepositoryEmailSummary = Omit<
  EmailMessageSummary,
  'sender' | 'recipients'
> & { senderId: number };

type RepositoryEmail = RepositoryEmailSummary & {
  emailContents: string;
  documentId?: number;
};

const mapRecordToSummary = (
  record: Record<string, unknown>,
): RepositoryEmailSummary => ({
  emailId: record.email_id as string,
  subject: record.subject as string,
  sentOn: record.sent_timestamp as Date,
  senderId: Number(record.sender_id),
  globalMessageId: (record.global_message_id as string) ?? undefined,
  parentEmailId: record.parent_email_id as string | null,
  importedFromId: record.imported_from_id
    ? (record.imported_from_id as string)
    : null,
  threadId: record.thread_id ? (record.thread_id as number) : undefined,
});

const mapRecordToObject = (record: Record<string, unknown>) => ({
  ...mapRecordToSummary(record),
  senderId: Number(record.sender_id),
  emailContents: record.email_contents as string,
});

export class EmailRepository extends BaseObjectRepository<
  RepositoryEmail,
  'emailId'
> {
  constructor() {
    super({
      tableName: 'emails',
      idField: 'emailId',
      objectMap: mapRecordToObject,
      summaryMap: mapRecordToSummary,
    });
  }

  public async create(
    props: Omit<RepositoryEmail, 'emailId'>,
  ): Promise<RepositoryEmail> {
    const ret = await super.create(props);
    if (ret && ret.emailId) {
      const importDate = props.sentOn ? new Date(props.sentOn) : new Date();
      const res = await db(
        (
          sql,
        ) => sql`INSERT INTO document_units (email_id, content, created_on, document_type)
        VALUES (${ret.emailId}, ${props.emailContents}, ${importDate.toISOString()}, 'email')
        returning unit_id`,
      );
      if (res.length < 1) {
        throw new Error('Failed to create document unit');
      }
      ret.documentId = res[0].unit_id;
    }
    return ret;
  }
  /**
   * Validates the input for a specific method.
   *
   * @template TMethod
   * @param {TMethod} method - The method to validate.
   * @param {FirstParameter<ObjectRepository<T, KId>[TMethod]>} obj - The input to validate.
   */
  validate<TMethod extends keyof ObjectRepository<RepositoryEmail, 'emailId'>>(
    method: TMethod,
    obj: FirstParameter<
      Pick<ObjectRepository<RepositoryEmail, 'emailId'>, TMethod>[TMethod]
    >,
  ): void {
    const asModel = obj as RepositoryEmail;
    switch (method) {
      case 'create':
        if (!asModel.senderId || !asModel.subject || !asModel.emailContents) {
          throw new ValidationError({
            field: 'senderId||subject||emailContents',
            source: 'EmailRepository',
          });
        }
        break;
      case 'update':
        if (
          !asModel.emailId ||
          (!asModel.senderId &&
            !asModel.subject &&
            !asModel.globalMessageId &&
            !asModel.importedFromId &&
            !asModel.emailContents &&
            !asModel.sentOn)
        ) {
          throw new ValidationError({
            field: 'emailId||At least one field is required for update',
            source: 'EmailRepository',
          });
        }
        break;
      default:
        break;
    }
  }
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  protected getListQueryProperties(): [string, Array<any>, string] {
    return [
      `SELECT * FROM emails ORDER BY sent_timestamp`,
      [],
      `SELECT COUNT(*) as records FROM emails`,
    ];
  }
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  protected getQueryProperties(emailId: string): [string, Array<any>] {
    return ['SELECT * FROM emails WHERE email_id = $1', [emailId]];
  }
  protected getCreateQueryProperties(
    obj: RepositoryEmail,
  ): [
    string,
    [
      string,
      number,
      number | null,
      string | null,
      string,
      string,
      string | null,
      string | null,
      string | Date | null,
    ],
  ] {
    if (!obj.emailId) {
      obj.emailId = newUuid();
    }
    return [
      'INSERT INTO emails (email_id, sender_id, thread_id, parent_id, subject, email_contents, global_message_id, imported_from_id, sent_timestamp) VALUES ($1, $2, $3, $4, $5, $6, $7, $8, $9) RETURNING email_id',
      [
        obj.emailId,
        obj.senderId,
        obj.threadId ?? null,
        obj.parentEmailId ?? null,
        obj.subject,
        obj.emailContents,
        obj.globalMessageId ?? null,
        obj.importedFromId ?? null,
        !obj.sentOn
          ? null
          : typeof obj.sentOn === 'string'
            ? obj.sentOn
            : obj.sentOn.toISOString(),
      ],
    ];
  }
  protected getUpdateQueryProperties(
    obj: RepositoryEmail,
  ): [Record<string, unknown>] {
    return [
      {
        sender_id: obj.senderId,
        thread_id: obj.threadId,
        parent_email_id: obj.parentEmailId,
        subject: obj.subject,
        email_contents: obj.emailContents,
        sent_timestamp: obj.sentOn,
        imported_from_id: obj.importedFromId,
        global_message_id: obj.globalMessageId,
      },
    ];
  }
  async getIdForUniqueMessageId(
    uniqueId: string | null,
  ): Promise<string | null> {
    if (!uniqueId) return null;
    try {
      const result = await query(
        (sql) =>
          sql`select email_id from emails where global_message_id = ${uniqueId}`,
      );
      return result.length === 1 ? (result[0].email_id as string) : null;
    } catch (error) {
      AbstractObjectRepository.logDatabaseError(this.source, error);
    }
    return null;
  }
}

const mapAttachmentRecordToSummary = (
  record: Record<string, unknown>,
): EmailMessageAttachmentSummary => ({
  attachmentId: record.attachment_id as number,
  emailId: record.email_id as string,
  fileName: record.file_name as string,
  filePath: record.file_path as string,
