import { BaseDrizzleRepository } from '../_baseDrizzleRepository';
import { emails } from '@/drizzle/schema';
import { drizDbWithInit } from '@/lib/drizzle-db';
import { ValidationError } from '@/lib/react-util/errors/validation-error';
import { eq } from 'drizzle-orm';

/**
 * Base repository interface supports object repository implementation
 */
type BaseEmailDrizzleRepository = BaseDrizzleRepository<EmailDomain, 'emailId'>;

/**
 * Domain model for Email entity used by the repository
 * This flattens the complex EmailMessage structure for database operations
 */
export type EmailDomain = {
  /** Unique identifier of the email */
  emailId: string;
  /** ID of the sender contact */
  senderId: number;
  /** Email subject */
  subject: string;
  /** Email body content */
  emailContents: string;
  /** When the email was sent */
  sentTimestamp: Date | string;
  /** Thread ID if part of a conversation */
  threadId?: number | null;
  /** Parent email ID if this is a reply */
  parentId?: string | null;
  /** Import source identifier */
  importedFromId?: string | null;
  /** Global message identifier */
  globalMessageId?: string | null;
};

/**
 * Maps a database record to an EmailDomain object
 *
 * @param record - Database record from Drizzle query
 * @returns Mapped EmailDomain object
 */
const mapRecordToEmailDomain = (
  record: Record<string, unknown>,
): EmailDomain => ({
  emailId: record.emailId as string,
  senderId: record.senderId as number,
  subject: record.subject as string,
  emailContents: record.emailContents as string,
  sentTimestamp: record.sentTimestamp as string,
  threadId: record.threadId as number | null,
  parentId: record.parentId as string | null,
  importedFromId: record.importedFromId as string | null,
  globalMessageId: record.globalMessageId as string | null,
});

/**
 * Maps a database record to an EmailDomainSummary object (for list operations)
 *
 * @param record - Database record from Drizzle query
 * @returns Mapped EmailDomainSummary object
 */
const mapRecordToEmailDomainSummary = (
  record: Record<string, unknown>,
): Partial<EmailDomain> => ({
  emailId: record.emailId as string,
  senderId: record.senderId as number,
  subject: record.subject as string,
  sentTimestamp: record.sentTimestamp as string,
  threadId: record.threadId as number | null,
  parentId: record.parentId as string | null,
  importedFromId: record.importedFromId as string | null,
  globalMessageId: record.globalMessageId as string | null,
  // Note: emailContents is excluded from summary
});

/**
 * EmailDrizzleRepository provides Drizzle ORM-based data access
 * for email records. This repository handles the core email entity
 * operations using the drizzle data access layer.
 *
 * The repository operates on a flattened domain model that can be
 * easily converted to the API response format by higher-level services.
 *
 * @example
 * ```typescript
 * const repository = new EmailDrizzleRepository();
 *
 * // Create a new email
 * const newEmail = await repository.create({
 *   senderId: 123,
 *   subject: "Test Email",
 *   emailContents: "Hello World",
 *   sentTimestamp: new Date()
 * });
 *
 * // Get paginated list
 * const emails = await repository.list({ page: 1, num: 10 });
 *
 * // Get single email
 * const email = await repository.get('email-uuid');
 * ```
 */
export class EmailDrizzleRepository
  extends BaseDrizzleRepository<EmailDomain, 'emailId'>
  implements BaseEmailDrizzleRepository
{
  constructor() {
    super({
      table: emails,
      recordMapper: mapRecordToEmailDomain,
      summaryMapper: mapRecordToEmailDomainSummary,
      tableName: 'emails',
      idField: 'emailId',
    });
  }

  /**
   * Validates email data for repository operations
   *
   * @param method - The repository method being called
   * @param obj - The data object to validate
   * @throws {ValidationError} When validation fails
   */
  protected validate<TMethod extends keyof BaseEmailDrizzleRepository>(
    method: TMethod,
    obj: Record<string, unknown>,
  ): void {
    super.validate(method, obj);

    const email = obj as Partial<EmailDomain>;

    switch (method) {
      case 'create':
        if (!email.senderId || !email.subject || !email.emailContents) {
          throw new ValidationError({
            field: 'senderId||subject||emailContents',
            source: 'EmailDrizzleRepository::create',
          });
        }
        break;
      case 'update':
        if (!email.emailId) {
          throw new ValidationError({
            field: 'emailId',
            source: 'EmailDrizzleRepository::update',
          });
        }
        // At least one field besides emailId must be provided for update
        const updateFields = [
          'senderId',
          'subject',
          'emailContents',
          'sentTimestamp',
          'threadId',
          'parentId',
          'importedFromId',
          'globalMessageId',
        ];
        if (
          !updateFields.some(
            (field) => email[field as keyof EmailDomain] !== undefined,
          )
        ) {
          throw new ValidationError({
            field: 'At least one field is required for update',
            source: 'EmailDrizzleRepository::update',
          });
        }
        break;
      default:
        break;
    }
  }

  /**
   * Prepares data for database insert operations
   * Maps domain object fields to database column names
   *
   * @param model - Email domain object without ID
   * @returns Database insert data
   */
  protected prepareInsertData(
    model: Omit<EmailDomain, 'emailId'>,
  ): Record<string, unknown> {
    return {
      sender_id: model.senderId,
      subject: model.subject,
      email_contents: model.emailContents,
      sent_timestamp: model.sentTimestamp || new Date(),
      thread_id: model.threadId || null,
      parent_id: model.parentId || null,
      imported_from_id: model.importedFromId || null,
      global_message_id: model.globalMessageId || null,
    };
  }

  /**
   * Prepares data for database update operations
   * Maps domain object fields to database column names
   *
   * @param model - Partial email domain object with ID
   * @returns Database update data
   */
  protected prepareUpdateData(
    model: Partial<EmailDomain>,
  ): Record<string, unknown> {
    const updateData: Record<string, unknown> = {};

    if (model.senderId !== undefined) updateData.sender_id = model.senderId;
    if (model.subject !== undefined) updateData.subject = model.subject;
    if (model.emailContents !== undefined)
      updateData.email_contents = model.emailContents;
    if (model.sentTimestamp !== undefined)
      updateData.sent_timestamp = model.sentTimestamp;
    if (model.threadId !== undefined) updateData.thread_id = model.threadId;
    if (model.parentId !== undefined) updateData.parent_id = model.parentId;
    if (model.importedFromId !== undefined)
      updateData.imported_from_id = model.importedFromId;
    if (model.globalMessageId !== undefined)
      updateData.global_message_id = model.globalMessageId;

    return updateData;
  }

  /**
   * Finds an email by its global message ID
   *
   * @param globalMessageId - The global message identifier to search for
   * @returns Promise resolving to email domain object or null if not found
   */
  async findByGlobalMessageId(
    globalMessageId: string,
  ): Promise<EmailDomain | null> {
    try {
      const record = await drizDbWithInit((db) =>
        db
          .select()
          .from(this.config.table)
          .where(eq(emails.globalMessageId, globalMessageId))
          .limit(1)
          .execute()
          .then((x) => x.at(0)),
      );
      return record ? this.config.recordMapper(record) : null;
    } catch (error) {
      this.logDatabaseError('findByGlobalMessageId', error);
      throw error;
    }
  }
}

import {
  EmailDrizzleRepository,
  EmailDomain,
} from './email-drizzle-repository';
import { EmailMessage } from '@/data-models/api/email-message';
import { ContactSummary } from '@/data-models/api/contact';
import { query } from '@/lib/neondb';
import { log } from '@/lib/logger';

/**
 * Request model for creating emails via the service
 */
export type CreateEmailRequest = {
  senderId: number;
  subject: string;
  body: string;
  sentOn?: Date | string;
  threadId?: number | null;
  parentEmailId?: string | null;
  recipients: Array<{
    recipientId: number;
    recipientName?: string;
    recipientEmail?: string;
  }>;
  sender?: {
    contactId: number;
  };
};

/**
 * Request model for updating emails via the service
 */
export type UpdateEmailRequest = {
  emailId: string;
  senderId?: number;
  subject?: string;
  body?: string;
  sentOn?: Date | string;
  threadId?: number | null;
  parentEmailId?: string | null;
  recipients?: Array<{
    recipientId: number;
    recipientName?: string;
    recipientEmail?: string;
  }>;
  sender?: {
    contactId: number;
  };
};

/**
 * EmailService provides business logic layer for email operations.
 * It bridges between the API layer and the data access layer, handling
 * complex operations like fetching related data (contacts, recipients)
 * and converting between different data models.
 *
 * This service abstracts the complexity of working with the drizzle repository
 * and provides a clean interface for the API routes.
 *
 * @example
 * ```typescript
 * const service = new EmailService();
 *
 * // Get emails with full contact information
 * const emails = await service.getEmailsSummary({ page: 1, num: 10 });
 *
 * // Create new email with recipients
 * const newEmail = await service.createEmail({
 *   senderId: 123,
 *   subject: "Test",
 *   body: "Hello",
 *   recipients: [{ recipientId: 456 }]
 * });
 * ```
 */
export class EmailService {
  private repository: EmailDrizzleRepository;

  constructor() {
    this.repository = new EmailDrizzleRepository();
  }

  /**
   * Retrieves a single email by ID with full details
   *
   * @param emailId - The email ID to retrieve
   * @returns Promise resolving to email with full details or null
   */
  async getEmailById(emailId: string): Promise<EmailMessage | null> {
    try {
      const emailDomain = await this.repository.get(emailId);
      if (!emailDomain) {
        return null;
      }

      // Fetch sender information
      const senderResult = await query(
        (sql) =>
          sql`SELECT contact_id, name, email FROM contacts WHERE contact_id = ${emailDomain.senderId}`,
      );

      const sender: ContactSummary =
        senderResult.length > 0
          ? {
              contactId: senderResult[0].contact_id as number,
              name: senderResult[0].name as string,
              email: senderResult[0].email as string,
            }
          : {
              contactId: emailDomain.senderId,
              name: 'Unknown',
              email: 'unknown@example.com',
            };

      // Fetch recipients information
      const recipientsResult = await query(
        (sql) => sql`
          SELECT 
            c.contact_id as recipient_id,
            c.name as recipient_name,
            c.email as recipient_email
          FROM email_recipients er
          LEFT JOIN contacts c ON er.recipient_id = c.contact_id
          WHERE er.email_id = ${emailId}
            AND c.contact_id IS NOT NULL
        `,
      );

      const recipients: ContactSummary[] = recipientsResult.map((rec) => ({
        contactId: rec.recipient_id as number,
        name: rec.recipient_name as string,
        email: rec.recipient_email as string,
      }));

      return {
        emailId: emailDomain.emailId,
        sender,
        subject: emailDomain.subject,
        body: emailDomain.emailContents,
        sentOn: emailDomain.sentTimestamp,
        threadId: emailDomain.threadId || null,
        parentEmailId: emailDomain.parentId || null,
        importedFromId: emailDomain.importedFromId || null,
        globalMessageId: emailDomain.globalMessageId || null,
        recipients,
      };
    } catch (error) {
      log((l) => l.error({ source: 'EmailService::getEmailById', error }));
      throw error;
    }
  }

  /**
   * Creates a new email with recipients
   *
   * @param request - Email creation request
   * @returns Promise resolving to created email with full details
   */
  async createEmail(request: CreateEmailRequest): Promise<EmailMessage> {
    try {
      // Support taking senderId from either the direct field or sender object
      const senderId = request.senderId ?? request.sender?.contactId;

      if (!senderId) {
        throw new Error('Sender ID is required');
      }

      // Create the email using the repository
      const emailDomain: Omit<EmailDomain, 'emailId'> = {
        senderId,
        subject: request.subject,
        emailContents: request.body,
        sentTimestamp: request.sentOn || new Date(),
        threadId: request.threadId || null,
        parentId: request.parentEmailId || null,
      };

      const createdEmail = await this.repository.create(emailDomain);

      // Insert recipients
      if (request.recipients && request.recipients.length > 0) {
        await this.insertRecipients(createdEmail.emailId, request.recipients);
      }

      // Create document unit for the email content
      const importDate = createdEmail.sentTimestamp
        ? new Date(createdEmail.sentTimestamp)
        : new Date();
      await query(
        (sql) => sql`
          INSERT INTO document_units (email_id, content, created_on, document_type)
          VALUES (${createdEmail.emailId}, ${createdEmail.emailContents}, ${importDate}, 'email')
        `,
      );

      // Return the full email details
      const fullEmail = await this.getEmailById(createdEmail.emailId);
      if (!fullEmail) {
        throw new Error('Failed to retrieve created email');
      }

      log((l) =>
        l.verbose({
          message: '[[AUDIT]] - Email created via service:',
          resultset: fullEmail,
        }),
      );

      return fullEmail;
    } catch (error) {
      log((l) => l.error({ source: 'EmailService::createEmail', error }));
      throw error;
    }
  }

  /**
   * Updates an existing email
   *
   * @param request - Email update request
   * @returns Promise resolving to updated email with full details
   */
  async updateEmail(request: UpdateEmailRequest): Promise<EmailMessage> {
    try {
      // Support taking senderId from either the direct field or sender object
      const senderId = request.senderId ?? request.sender?.contactId;

      // Build update object
      const updateData: Partial<EmailDomain> &
        Required<Pick<EmailDomain, 'emailId'>> = {
        emailId: request.emailId,
      };

      if (senderId !== undefined) updateData.senderId = senderId;
      if (request.subject !== undefined) updateData.subject = request.subject;
      if (request.body !== undefined) updateData.emailContents = request.body;
      if (request.sentOn !== undefined)
        updateData.sentTimestamp = request.sentOn;
      if (request.threadId !== undefined)
        updateData.threadId = request.threadId;
      if (request.parentEmailId !== undefined)
        updateData.parentId = request.parentEmailId;

      const updatedEmail = await this.repository.update(updateData);

      // Update recipients if provided
      if (request.recipients && request.recipients.length > 0) {
        await this.insertRecipients(request.emailId, request.recipients, true);
      }

      // Return the full email details
      const fullEmail = await this.getEmailById(updatedEmail.emailId);
      if (!fullEmail) {
        throw new Error('Failed to retrieve updated email');
      }

      log((l) =>
        l.verbose({
          message: '[[AUDIT]] - Email updated via service:',
          resultset: fullEmail,
        }),
      );

      return fullEmail;
    } catch (error) {
      log((l) => l.error({ source: 'EmailService::updateEmail', error }));
      throw error;
    }
  }

  /**
   * Deletes an email by ID
   *
   * @param emailId - The email ID to delete
   * @returns Promise resolving to true if deleted, false if not found
   */
  async deleteEmail(emailId: string): Promise<boolean> {
    try {
      const result = await this.repository.delete(emailId);

      log((l) =>
        l.verbose({
          message: '[[AUDIT]] - Email deleted via service:',
          emailId,
          success: result,
        }),
      );

      return result;
    } catch (error) {
      log((l) => l.error({ source: 'EmailService::deleteEmail', error }));
      throw error;
    }
  }

  /**
   * Finds an email by its global message ID
   *
   * @param globalMessageId - The global message ID to search for
   * @returns Promise resolving to email ID or null if not found
   */
  async findEmailIdByGlobalMessageId(
    globalMessageId: string,
  ): Promise<string | null> {
    try {
      const email =
        await this.repository.findByGlobalMessageId(globalMessageId);
      return email?.emailId || null;
    } catch (error) {
      log((l) =>
        l.error({
          source: 'EmailService::findEmailIdByGlobalMessageId',
          error,
        }),
      );
      throw error;
    }
  }

  /**
   * Inserts or updates recipients for an email
   *
   * @param emailId - The email ID
   * @param recipients - Array of recipient information
   * @param replaceExisting - Whether to delete existing recipients first
   */
  private async insertRecipients(
    emailId: string,
    recipients: Array<{
      recipientId: number;
      recipientName?: string;
      recipientEmail?: string;
    }>,
    replaceExisting = false,
  ): Promise<void> {
    try {
      if (replaceExisting) {
        // Delete existing recipients
        await query(
          (sql) =>
            sql`DELETE FROM email_recipients WHERE email_id = ${emailId}`,
        );
      }

      // Insert new recipients
      for (const recipient of recipients) {
        await query(
          (sql) => sql`
            INSERT INTO email_recipients (email_id, recipient_id)
            VALUES (${emailId}, ${recipient.recipientId})
            ON CONFLICT (email_id, recipient_id) DO NOTHING
          `,
        );
      }
    } catch (error) {
      log((l) => l.error({ source: 'EmailService::insertRecipients', error }));
      throw error;
    }
  }
}

/**
 * @module googleEmailImport
 *
 * This module provides functions to interact with the Google email import API.
 * It includes functionalities to search for emails, load email details, and queue emails for import.
 *
 * Functions:
 * - `searchEmails`: Searches for emails based on provided criteria.
 * - `loadEmail`: Loads the details of a specific email by its ID.
 * - `queueEmailImport`: Queues an email for import by its ID.
 */
import type {
  AdditionalRequestParams,
  ApiRequestHelper,
} from '@/lib/send-api-request';
import { apiRequestHelperFactory } from '@/lib/send-api-request';

import siteMap from '@/lib/site-util/url-builder';
import type { PaginatedResultset } from '@/data-models/_types';
import type {
  EmailSearchResult,
  ImportResponse,
  ImportSourceMessage,
  MessageImportStatusWithChildren,
} from '@/data-models/api/import/email-message';

const apiRequest = <TResult>(
  cb: (
    api: ApiRequestHelper,
    builder: typeof siteMap.api.email.import.google,
  ) => TResult,
): TResult => {
  const apiHelper = apiRequestHelperFactory({ area: 'email/import/google' });
  const builder = siteMap.api.email.import.google;
  return cb(apiHelper, builder);
};

/**
 * Searches for emails based on the provided criteria.
 *
 * @param from - The sender's email address to filter by.
 * @param to - The recipient's email address to filter by.
 * @param label - An array of labels to filter by.
 * @param page - The page number for pagination (default is 1).
 * @param limit - The number of results per page (default is 100).
 * @returns A promise that resolves to a paginated result set of email search results.
 */
export const searchEmails = (
  {
    from,
    to,
    label,
    page = 1,
    limit = 100,
  }: {
    from?: string;
    to?: string;
    label?: string[];
    page?: number;
    limit?: number;
  },
  params?: AdditionalRequestParams,
) => {
  if (from === 'm.sean.o@gmail.com') {
    to = '@plsas.org';
  }

  return apiRequest((api, builder) =>
    api.get<PaginatedResultset<EmailSearchResult, string | undefined>>(
      {
        url: builder.search({
          from,
          to,
          label,
          page,
          limit,
        }),
        action: 'search',
      },
      params,
    ),
  );
};
/**
 * Loads the details of a specific email by its ID.
 *
 * @param emailId - The ID of the email to load.
 * @returns A promise that resolves to the details of the email.
 */
export const loadEmail = (emailId: string, params?: AdditionalRequestParams) =>
  apiRequest((api, builder) =>
    api.get<ImportSourceMessage>(
      {
        url: builder.page('message', emailId),
        action: 'load',
      },
      params,
    ),
  );

/**
 * Queues an email for import by its ID.
 *
 * @param emailId - The ID of the email to queue for import.
 * @returns A promise that resolves to the details of the queued email.
 */
export const queueEmailImport = (
  emailId: string,
  params?: AdditionalRequestParams,
) =>
  apiRequest((api, builder) => {
    return api.post<ImportSourceMessage>(
      {
        url: builder.page('message', emailId),
        action: 'queue',
        input: {},
      },
      params,
    );
  });

/**
 * Stages an email for import by its ID.
 *
 * @param emailId - The ID of the email to queue for import.
 * @returns A promise that resolves to the details of the queued email.
 */
export const createStagingRecord = (
  emailId: string,
  params?: AdditionalRequestParams,
) =>
  apiRequest((api, builder) =>
    api.put<ImportSourceMessage>(
      {
        url: builder.message.page(emailId),
        action: 'stage',
        input: {},
      },
      params,
    ),
  );

/**
 * Stages an email for import by its ID.
 *
 * @param emailId - The ID of the email to queue for import.
 * @returns A promise that resolves to the details of the imported email.
 */
export const importEmailRecord = (
  emailId: string,
  params?: AdditionalRequestParams,
) =>
  apiRequest((api, builder) =>
    api.post<ImportResponse>(
      {
        url: builder.message.page(emailId),
        action: 'import',
        input: {},
      },
      params,
    ),
  );

/**
 * Queries the import status of a message by its email ID.
