/**
 * @module email-route-util
 *
 * This module provides utility functions shared by all email API routes.
 *
 * Functions:
 * - mapRecordToSummary: Maps a record to a summary object containing email details.
 * - mapRecordToObject: Maps a record to an object containing email details and body content.
 */

import type { ContactSummary } from '@/data-models/api/contact';
import { query } from '@/lib/neondb';
import { ValidationError } from '@/lib/react-util/errors/validation-error';

/**
 * Maps a record to a summary object containing email details.
 *
 * @param {Record<string, unknown>} record - The record to map.
 * @returns {object} The mapped summary object.
 */
export const mapRecordToSummary = (record: Record<string, unknown>) => ({
  emailId: record.email_id,
  subject: record.subject,
  sentOn: record.sent_timestamp,
  threadId: record.thread_id,
  parentEmailId: record.parent_email_id,
  sender: {
    contactId: record.senderid,
    name: record.sendername,
    email: record.senderemail,
  },
  count_attachments:
    record.count_attachments === undefined
      ? undefined
      : Number(record.count_attachments),
  count_kpi:
    record.count_kpi === undefined ? undefined : Number(record.count_kpi),
  count_notes:
    record.count_notes === undefined ? undefined : Number(record.count_notes),
  count_cta:
    record.count_cta === undefined ? undefined : Number(record.count_cta),
  count_responsive_actions:
    record.count_responsive_actions === undefined
      ? undefined
      : Number(record.count_responsive_actions),
  recipients: ((record.recipients as Array<Record<string, unknown>>) || []).map(
    (r: Record<string, unknown>) => ({
      contactId: r.recipient_id,
      name: r.recipient_name,
      email: r.recipient_email,
    }),
  ),
});

/**
 * Maps a record to an object containing email details and body content.
 *
 * @param {Record<string, unknown>} record - The record to map.
 * @returns {object} The mapped object with email details and body content.
 */
export const mapRecordToObject = (record: Record<string, unknown>) => ({
  ...mapRecordToSummary(record),
  body: record.email_contents,
});

export const insertRecipients = async (
  emailId: number,
  recipients: ContactSummary[],
  clear: boolean = true,
  allowEmpty: boolean = false,
) => {
  if (!allowEmpty && !recipients?.length) {
    throw new ValidationError({
      field: 'recipients',
      value: !recipients ? '[null]' : '[empty]',
      reason: 'At least one recipient is required',
      source: 'insertRecipients',
    });
  }

  // Delete existing recipients if clear is true
  if (clear) {
    await query(
      (sql) => sql`DELETE FROM email_recipients WHERE email_id = ${emailId}`,
    );
  }
  if (!recipients.length) {
    return 0;
  }
  // Build bulk insert statement
  const insertSql = `INSERT INTO email_recipients (email_id, recipient_id)
    VALUES ${recipients
      .map((r: ContactSummary) => `(${emailId}, ${r.contactId})`)
      .join(', ')}
    RETURNING email_id, recipient_id
  `.toString();
  const res = await query((sql) => sql<false, false>(insertSql));
  if (res.length !== recipients.length) {
    throw new Error('Failed to insert all recipients');
  }
  return res.length;
};

/**
 * Maps a generic record object to a thread summary object.
 *
 * @param record - The record containing thread information, expected to have
 *   `thread_id`, `subject`, and `created_at` properties.
 * @returns An object with `threadId`, `subject`, and `createdOn` properties
 *   mapped from the input record.
 */
export const mapRecordToThreadSummary = (record: Record<string, unknown>) => ({
  threadId: record.thread_id,
  subject: record.subject,
  createdOn: record.created_at,
});

import type { ImpersonationService } from './impersonation.types';
import { ImpersonationThirdParty } from './impersonation.thirdparty';
import { ImpersonationServiceCache } from './impersonation-service-cache';
import { auth } from '@/auth';
import { log } from '@/lib/logger';
import { User } from 'next-auth';

/**
 * Strategy selection:
 * - Set AUTH_KEYCLOAK_IMPERSONATE_THIRDPARTY=true to use Third-party libs (KC Admin Client + openid-client) with Authorization Code flow
 * - Set AUTH_KEYCLOAK_IMPERSONATE_RESTAPI=true to use Admin REST API + Authorization Code flow
 * - Else, set AUTH_KEYCLOAK_IMPERSONATE_IMPLICIT=true to use Admin impersonation + Implicit flow
 * - Else, falls back to Token Exchange (audience, email required)
 *
 * All strategies implement ImpersonationService so callers can treat them uniformly.
 */
export const fromRequest = async ({
  audience,
}: {
  req?: Request;
  audience?: string;
} = {}): Promise<ImpersonationService | undefined> => {
  const session = await auth();
  if (!session?.user) {
    log((l) =>
      l.warn('Impersonation requested without an active user session'),
    );
    return undefined;
  }
  return ImpersonationThirdParty.fromUser({ user: session.user, audience });
};

export const fromUserId = async ({
  user,
  audience,
}: {
  user: User | undefined;
  audience?: string;
}): Promise<ImpersonationService | undefined> => {
  if (!user || !user.id) {
    log((l) => l.warn('Impersonation requested without a userId'));
    return undefined;
  }
  const cache = ImpersonationServiceCache.getInstance();
  return cache.getOrCreate(user.id, audience, async () => {
    const service = await ImpersonationThirdParty.fromUser({
      user,
      audience,
    });
    if (!service) {
      throw new Error('Failed to create impersonation service');
    }
    return service;
  });
};

/**
 * @fileoverview User-scoped impersonation service cache for maintaining persistent service instances
 *
 * This module provides a cache for maintaining ImpersonationService instances across HTTP requests
 * while ensuring proper user isolation and cleanup. Each user+audience combination gets their own
 * service instance that persists for the configured TTL, reducing token refresh overhead and
 * improving performance.
 */

import type { ImpersonationService } from './index';
import { log } from '@/lib/logger';
import { LoggedError } from '@/lib/react-util/errors/logged-error';

interface CachedImpersonationService {
  service: ImpersonationService;
  lastAccessed: number;
  userId: string;
  audience: string | undefined;
}

interface ImpersonationServiceCacheConfig {
  /** Maximum number of cached services per user */
  maxEntriesPerUser: number;
  /** Maximum total entries across all users */
  maxTotalEntries: number;
  /** Time to live in milliseconds */
  ttl: number;
  /** Cleanup interval in milliseconds */
  cleanupInterval: number;
}

/**
 * Cache for maintaining ImpersonationService instances per user and audience across HTTP requests.
 *
 * Features:
 * - Per-user isolation with audience-based keys
 * - Automatic TTL-based cleanup
 * - LRU eviction when cache limits are exceeded
 * - Proper disposal of services on cleanup
 * - Memory usage monitoring and limits
 * - Thread-safe operations
 *
 * @example
 * ```typescript
 * const cache = ImpersonationServiceCache.getInstance();
 *
 * // Get or create impersonation service for a user+audience
 * const service = await cache.getOrCreate(
 *   userId,
 *   audience,
 *   async () => impersonationFactory.create({ ... })
 * );
 *
 * // Cleanup when done (optional - automatic cleanup also occurs)
 * cache.invalidateUser(userId);
 * cache.invalidateAudience(userId, audience);
 * ```
 */
export class ImpersonationServiceCache {
  private static instance: ImpersonationServiceCache | null = null;
  private cache = new Map<string, CachedImpersonationService>();
  private cleanupTimer: NodeJS.Timeout | null = null;
  private readonly config: ImpersonationServiceCacheConfig;

  private constructor(config: Partial<ImpersonationServiceCacheConfig> = {}) {
    this.config = {
      maxEntriesPerUser: 5, // Max 5 different audiences per user
      maxTotalEntries: 200, // Max 200 total entries across all users
      ttl: 60 * 60 * 1000, // 60 minutes (longer than tool provider due to token refresh cost)
      cleanupInterval: 10 * 60 * 1000, // Cleanup every 10 minutes
      ...config,
    };

    this.startCleanupTimer();
  }

  /**
   * Get the singleton instance of the cache.
   */
  public static getInstance(
    config?: Partial<ImpersonationServiceCacheConfig>,
  ): ImpersonationServiceCache {
    if (!ImpersonationServiceCache.instance) {
      ImpersonationServiceCache.instance = new ImpersonationServiceCache(
        config,
      );
    }
    return ImpersonationServiceCache.instance;
  }

  /**
   * Generate a cache key for a user's impersonation service by audience.
   */
  private generateCacheKey(
    userId: string,
    audience: string | undefined,
  ): string {
    // Normalize audience to handle variations
    const normalizedAudience = (audience ?? '__no-audience__')
      .toLowerCase()
      .trim();
    return `${userId}:${normalizedAudience}`;
  }

  /**
   * Get or create an impersonation service for a user and audience.
   */
  public async getOrCreate(
    userId: string,
    audience: string | undefined,
    factory: () => Promise<ImpersonationService>,
  ): Promise<ImpersonationService> {
    const cacheKey = this.generateCacheKey(userId, audience);

    // Check if we have a valid cached entry
    const cached = this.cache.get(cacheKey);
    if (cached && !this.isExpired(cached)) {
      // Update last accessed time
      cached.lastAccessed = Date.now();
      log((l) =>
        l.debug('Impersonation service cache hit', {
          userId,
          audience,
          cacheKey,
        }),
      );
      return cached.service;
    }

    // Clean up expired entry if it exists
    if (cached && this.isExpired(cached)) {
      this.removeEntry(cacheKey, cached);
    }

    // Create new impersonation service
    log((l) =>
      l.debug('Creating new impersonation service', {
        userId,
        audience,
        cacheKey,
      }),
    );

    try {
      const service = await factory();

      // Check cache size limits before adding
      this.enforceEvictionLimits(userId);

      // Cache the new service
      this.cache.set(cacheKey, {
        service,
        lastAccessed: Date.now(),
        userId,
        audience,
      });

      log((l) =>
        l.debug('Impersonation service cached successfully', {
          userId,
          audience,
          cacheKey,
          cacheSize: this.cache.size,
        }),
      );

      return service;
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        source: 'ImpersonationServiceCache.getOrCreate',
        message: 'Failed to create impersonation service',
        data: { userId, audience, cacheKey },
      });
      throw error;
    }
  }

  /**
   * Check if a cached entry is expired.
   */
  private isExpired(entry: CachedImpersonationService): boolean {
    return Date.now() - entry.lastAccessed > this.config.ttl;
  }

  /**
   * Remove and properly dispose of a cache entry.
   */
  private removeEntry(
    cacheKey: string,
    entry: CachedImpersonationService,
  ): void {
    try {
      // Call clearCache if the service supports it
      if (
        'clearCache' in entry.service &&
        typeof entry.service.clearCache === 'function'
      ) {
        (entry.service.clearCache as () => Promise<void> | void)();
      }

      log((l) =>
        l.debug('Impersonation service disposed', {
          userId: entry.userId,
          audience: entry.audience,
          cacheKey,
        }),
      );
    } catch (error) {
      log((l) =>
        l.warn('Error disposing impersonation service', {
          userId: entry.userId,
          audience: entry.audience,
          cacheKey,
          error,
        }),
      );
    }
    this.cache.delete(cacheKey);
  }

  /**
   * Enforce cache size limits with LRU eviction.
   */
  private enforceEvictionLimits(currentUserId: string): void {
    // Check per-user limit
    const userEntries = Array.from(this.cache.entries()).filter(
      ([, entry]) => entry.userId === currentUserId,
    );

    if (userEntries.length >= this.config.maxEntriesPerUser) {
      // Remove oldest entry for this user
      const oldestUserEntry = userEntries.sort(
        ([, a], [, b]) => a.lastAccessed - b.lastAccessed,
      )[0];

      if (oldestUserEntry) {
        this.removeEntry(oldestUserEntry[0], oldestUserEntry[1]);
      }
    }

    // Check total cache limit
    if (this.cache.size >= this.config.maxTotalEntries) {
      // Remove oldest entry globally
      const oldestEntry = Array.from(this.cache.entries()).sort(
        ([, a], [, b]) => a.lastAccessed - b.lastAccessed,
      )[0];

      if (oldestEntry) {
        this.removeEntry(oldestEntry[0], oldestEntry[1]);
      }
    }
  }

  /**
   * Invalidate all cached impersonation services for a specific user.
   */
  public invalidateUser(userId: string): void {
    const userEntries = Array.from(this.cache.entries()).filter(
      ([, entry]) => entry.userId === userId,
    );

    for (const [cacheKey, entry] of userEntries) {
      this.removeEntry(cacheKey, entry);
    }

    log((l) =>
      l.debug('Invalidated user impersonation services', {
        userId,
        removedCount: userEntries.length,
      }),
    );
  }

  /**
   * Invalidate cached impersonation service for a specific user and audience.
   */
  public invalidateAudience(
    userId: string,
    audience: string | undefined,
  ): void {
    const cacheKey = this.generateCacheKey(userId, audience);
    const entry = this.cache.get(cacheKey);

    if (entry) {
      this.removeEntry(cacheKey, entry);
      log((l) =>
        l.debug('Invalidated audience impersonation service', {
          userId,
          audience,
          cacheKey,
        }),
      );
    }
  }

  /**
   * Get all audiences cached for a specific user.
   */
  public getUserAudiences(userId: string): string[] {
    return Array.from(this.cache.values())
      .filter((entry) => entry.audience && entry.userId === userId)
      .map((entry) => entry.audience!);
  }

  /**
   * Check if a service is cached for the given user and audience.
   */
  public has(userId: string, audience: string | undefined): boolean {
    const cacheKey = this.generateCacheKey(userId, audience);
    const entry = this.cache.get(cacheKey);
    return entry !== undefined && !this.isExpired(entry);
  }

  /**
   * Clean up expired entries.
   */
  private cleanup(): void {
    const expiredEntries: [string, CachedImpersonationService][] = [];

    for (const [cacheKey, entry] of this.cache.entries()) {
      if (this.isExpired(entry)) {
        expiredEntries.push([cacheKey, entry]);
      }
    }

    for (const [cacheKey, entry] of expiredEntries) {
      this.removeEntry(cacheKey, entry);
    }

    if (expiredEntries.length > 0) {
      log((l) =>
        l.debug('Cleaned up expired impersonation services', {
          removedCount: expiredEntries.length,
          remainingCount: this.cache.size,
        }),
      );
    }
  }

  /**
   * Clear all cached impersonation services and dispose them.
   */
  public clear(): void {
    for (const [cacheKey, entry] of this.cache.entries()) {
      this.removeEntry(cacheKey, entry);
    }

    log((l) => l.debug('Cleared all cached impersonation services'));
  }

  /**
   * Start the periodic cleanup timer.
   */
  private startCleanupTimer(): void {
    this.cleanupTimer = setInterval(() => {
      this.cleanup();
    }, this.config.cleanupInterval);
  }

  /**
   * Get cache statistics.
   */
  public getStats() {
    const userCounts = new Map<string, number>();
    const audienceCounts = new Map<string, number>();

    for (const entry of this.cache.values()) {
      userCounts.set(entry.userId, (userCounts.get(entry.userId) || 0) + 1);
      if (entry.audience) {
        audienceCounts.set(
          entry.audience,
          (audienceCounts.get(entry.audience) || 0) + 1,
        );
      }
    }

    return {
      totalEntries: this.cache.size,
      userCounts: Object.fromEntries(userCounts),
      audienceCounts: Object.fromEntries(audienceCounts),
      config: this.config,
    };
  }

  /**
   * Get detailed information about cached services for debugging.
   */
  public getDebugInfo() {
    return Array.from(this.cache.entries()).map(([cacheKey, entry]) => ({
      cacheKey,
      userId: entry.userId,
      audience: entry.audience,
      lastAccessed: new Date(entry.lastAccessed).toISOString(),
      age: Date.now() - entry.lastAccessed,
      isExpired: this.isExpired(entry),
    }));
  }

  /**
   * Refresh a cached service by removing it from cache (next access will recreate it).
   */
  public refresh(userId: string, audience: string | undefined): void {
    this.invalidateAudience(userId, audience);
    log((l) =>
      l.debug('Refreshed impersonation service', {
        userId,
        audience,
      }),
    );
  }

  /**
   * Shutdown the cache and clean up all entries.
   */
  public shutdown(): void {
    if (this.cleanupTimer) {
      clearInterval(this.cleanupTimer);
      this.cleanupTimer = null;
    }

    // Dispose all cached services
    this.clear();

    log((l) => l.info('Impersonation service cache shutdown complete'));
  }
}

/**
 * @fileoverview Impersonation via Keycloak Admin REST API + Authorization Code flow using third-party libraries
 *
 * Libraries used:
 * - @keycloak/keycloak-admin-client for admin APIs (user lookup, auth)
 * - openid-client for OIDC discovery and token exchange
 * - got + tough-cookie for HTTP with cookie jar (impersonation + authorize redirects)
 */

import KeycloakAdminClient from '@keycloak/keycloak-admin-client';
import {
  discovery,
  buildAuthorizationUrl,
  authorizationCodeGrant,
  randomState,
  randomNonce,
  type Configuration as OIDCConfiguration,
} from 'openid-client';
import { got } from 'got';
import { CookieJar } from 'tough-cookie';
import { env } from '@/lib/site-util/env';
import { log } from '@/lib/logger';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import CryptoService from '@/lib/site-util/auth/crypto-service';
import type {
  ImpersonationService,
  UserContext,
} from '@/lib/auth/impersonation/impersonation.types';
import { trace, SpanStatusCode } from '@opentelemetry/api';
import { SystemTokenStore } from './system-token-store';
import { Session, User } from 'next-auth';
import { keycloakAdminClientFactory } from '../keycloak-factories';

interface TokenResponse {
  access_token: string;
  token_type?: string;
  expires_in?: number;
  refresh_token?: string;
  scope?: string;
  expires_at?: number; // epoch seconds (openid-client TokenSet)
}

interface ThirdPartyConfig {
  issuer: string;
  clientId: string;
  clientSecret: string;
  redirectUri: string;
}

const extractRealmFromIssuer = (issuer: string): string | undefined => {
  try {
    const u = new URL(issuer);
    const parts = u.pathname.split('/').filter(Boolean);
    const idx = parts.findIndex((p) => p === 'realms');
    if (idx >= 0 && parts[idx + 1]) return decodeURIComponent(parts[idx + 1]);
    return undefined;
  } catch {
    return undefined;
  }
};

const adminBaseFromIssuer = (
  issuer: string,
): { origin: string; realm: string; adminBase: string } | undefined => {
  try {
    const u = new URL(issuer);
    const realm = extractRealmFromIssuer(issuer);
    if (!realm) return undefined;
    return {
      origin: u.origin,
      realm,
      adminBase: `${u.origin}/admin/realms/${encodeURIComponent(realm)}`,
    };
  } catch {
    return undefined;
  }
};

// no-op: query builder not needed with openid-client's authorizationUrl

/**
 * ImpersonationThirdParty  uses KC Admin Client, openid-client, and got/tough-cookie
 */
export class ImpersonationThirdParty implements ImpersonationService {
  private readonly userContext: UserContext;
  private readonly config: ThirdPartyConfig;
  private kcAdmin?: KeycloakAdminClient;
  private oidcConfig?: OIDCConfiguration;
  private cookieJar?: CookieJar;
  private cachedToken?: string;
  private tokenExpiry?: Date;
  #adminTokenStore: SystemTokenStore = SystemTokenStore.getInstance();
  private crypto?: CryptoService;

  constructor(userContext: UserContext, config: ThirdPartyConfig) {
    this.userContext = userContext;
    this.config = config;
  }

  static #getConfig(): ThirdPartyConfig | undefined {
    const config: ThirdPartyConfig = {
      issuer: env('AUTH_KEYCLOAK_ISSUER') || '',
      clientId: env('AUTH_KEYCLOAK_CLIENT_ID') || '',
      clientSecret: env('AUTH_KEYCLOAK_CLIENT_SECRET') || '',
      redirectUri: env('AUTH_KEYCLOAK_REDIRECT_URI') || '',
    };

    if (
      !config.issuer ||
      !config.clientId ||
      !config.clientSecret ||
      !config.redirectUri
    ) {
      log((l) => l.warn('ImpersonationThirdParty: incomplete config'));
      return undefined;
    }
    return config;
  }

  static async fromRequest({
    session,
    ...props
  }: {
    audience?: string;
    session: Session;
  }): Promise<ImpersonationThirdParty | undefined> {
    return ImpersonationThirdParty.fromUser({ user: session?.user, ...props });
  }

  static async fromUser({
    user,
  }: {
    audience?: string;
    user: User | undefined;
  }): Promise<ImpersonationThirdParty | undefined> {
    try {
      if (!user) return undefined;
      const userContext: UserContext = {
        userId: user.subject || user.id || '',
        email: user.email || undefined,
        name: user.name || undefined,
        accountId: 'account_id' in user ? user.account_id : undefined,
      };
      if (!userContext.email) return undefined;

      const config = ImpersonationThirdParty.#getConfig();
      if (!config) {
        return undefined;
      }

      const self = new ImpersonationThirdParty(userContext, config);
      await self.initializeClients();
      return self;
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        source: 'ImpersonationThirdParty.fromRequest',
        severity: 'error',
        message: 'Failed creating ImpersonationThirdParty',
      });
      return undefined;
    }
  }

  private async initializeClients(): Promise<void> {
    // OIDC discovery and client
    this.oidcConfig = await discovery(
      new URL(this.config.issuer),
      this.config.clientId,
      this.config.clientSecret,
    );

    // Keycloak Admin client
    const parsed = adminBaseFromIssuer(this.config.issuer);
    if (!parsed)
      throw new Error(
        'ImpersonationThirdParty: unable to parse realm from issuer',
      );
    const { origin, realm } = parsed;
    this.kcAdmin = keycloakAdminClientFactory({
      baseUrl: origin,
      realmName: realm,
    });

    // Cookie jar for admin impersonation and authorize request
    this.cookieJar = new CookieJar();
  }

  async getImpersonatedToken(forceRefresh = false): Promise<string> {
    const tracer = trace.getTracer('noeducation/impersonation');
    return await tracer.startActiveSpan(
      'impersonation.getImpersonatedToken',
      async (span) => {
        span.setAttribute('impersonation.userId', this.userContext.userId);
        if (this.userContext.email)
          span.setAttribute('impersonation.email', this.userContext.email);
        try {
          // Fast-path cached token if valid and not forcing refresh
          if (
            !forceRefresh &&
            this.cachedToken &&
            this.tokenExpiry &&
            this.tokenExpiry > new Date()
          ) {
            span.setStatus({ code: SpanStatusCode.OK });
            return this.cachedToken;
          }

          // Helper for a single attempt; keeps logic contained
          const attemptOnce = async (): Promise<string> => {
            if (!this.kcAdmin || !this.oidcConfig || !this.cookieJar) {
              await this.initializeClients();
            }

            const adminToken = await this.#adminTokenStore.getAdminToken();
            this.kcAdmin!.setAccessToken(adminToken);

            const userId =
              // this.userContext.userId ??
              await this.findUserIdViaAdmin(this.userContext.email!);
            if (!userId)
              throw new Error('ImpersonationThirdParty: target user not found');

            await this.performImpersonation(adminToken, userId);

            const access = await this.authorizeAndExchange();

            this.cachedToken = access.access_token;
            if (access.expires_at) {
              this.tokenExpiry = new Date(access.expires_at * 1000 - 60_000);
            } else if (access.expires_in) {
              this.tokenExpiry = new Date(
                Date.now() + Math.max(60, access.expires_in) * 1000 - 60_000,
              );
            } else {
              this.tokenExpiry = new Date(Date.now() + 10 * 60_000);
            }
            return this.cachedToken!;
          };

          // First attempt
          try {
            const token = await attemptOnce();
            span.setStatus({ code: SpanStatusCode.OK });
            return token;
          } catch (firstErr) {
            // Clear caches and retry once with a fresh instance
            const msg = (firstErr as Error)?.message || '';
            // Do NOT retry for logical/semantic errors where a retry won't help
            const nonRetryable =
              /target user not found|missing impersonator credentials|unable to locate login form action|admin login failed|admin password step failed|unable to locate password form|expected 302 from authorize|expected 302 with code after admin login/i;
            if (nonRetryable.test(msg)) {
              span.recordException(firstErr as Error);
              span.setStatus({ code: SpanStatusCode.ERROR });
              throw firstErr;
            }
            try {
              span.addEvent('impersonation.retry');
            } catch {
              // ignore addEvent errors
            }
            await this.clearAllCachedCredentials();
            try {
              const token = await attemptOnce();
              span.setStatus({ code: SpanStatusCode.OK });
              return token;
            } catch (secondErr) {
              // Record the last error and rethrow
              try {
                span.recordException(secondErr as Error);
                span.setStatus({ code: SpanStatusCode.ERROR });
              } catch {}
              throw secondErr;
            }
          }
        } finally {
          span.end();
        }
      },
    );
  }

  getUserContext(): Readonly<UserContext> {
    return { ...this.userContext };
  }

  clearCache(): void {
    this.cachedToken = undefined;
    this.tokenExpiry = undefined;
  }

  hasCachedToken(): boolean {
    return !!(
      this.cachedToken &&
      this.tokenExpiry &&
      this.tokenExpiry > new Date()
    );
  }

  // --- internals ---

  private async findUserIdViaAdmin(
    identifier: string,
  ): Promise<string | undefined> {
    if (!this.kcAdmin) throw new Error('kcAdmin not initialized');

    // Try exact username
    const byUsername = await this.kcAdmin.users
      .find({ username: identifier, exact: true })
      .catch(() => []);
    if (Array.isArray(byUsername) && byUsername[0]?.id) return byUsername[0].id;

    // Try exact email
    const byEmail = await this.kcAdmin.users
      .find({ email: identifier, exact: true })
      .catch(() => []);
    if (Array.isArray(byEmail) && byEmail[0]?.id) return byEmail[0].id;

    // Fallback search
    const search = await this.kcAdmin.users
      .find({ search: identifier })
      .catch(() => []);
    if (Array.isArray(search) && search[0]?.id) return search[0].id;

    return undefined;
  }

  private async performImpersonation(
    adminToken: string,
    userId: string,
  ): Promise<void> {
    if (!this.cookieJar) throw new Error('cookieJar not initialized');

    const parsed = adminBaseFromIssuer(this.config.issuer);
    if (!parsed)
      throw new Error('ImpersonationThirdParty: cannot derive admin base');
    const url = `${parsed.adminBase}/users/${encodeURIComponent(userId)}/impersonation`;

    const resp = await got.post(url, {
      headers: {
        Authorization: `Bearer ${adminToken}`,
        Accept: 'application/json',
      },
      cookieJar: this.cookieJar,
      followRedirect: false,
      throwHttpErrors: false,
    });

    if (resp.statusCode !== 200 && resp.statusCode !== 302) {
      throw new Error(
        `ImpersonationThirdParty: impersonation failed ${resp.statusCode} ${resp.body?.toString?.() ?? ''}`,
      );
    }
  }

  private async authorizeAndExchange(): Promise<TokenResponse> {
    if (!this.oidcConfig || !this.cookieJar)
      throw new Error('OIDC config/cookieJar not initialized');

    const state = randomState();
    const nonce = randomNonce();

    const authorizeUrl = buildAuthorizationUrl(this.oidcConfig, {
      redirect_uri: this.config.redirectUri,
      scope: 'openid',
      response_type: 'code',
      response_mode: 'query',
      prompt: 'none',
      state,
      nonce,
    });

    const resp = await got.get(authorizeUrl.toString(), {
      cookieJar: this.cookieJar,
      followRedirect: false,
      throwHttpErrors: false,
    });

    if (resp.statusCode !== 302) {
      throw new Error(
        `ImpersonationThirdParty: expected 302 from authorize, got ${resp.statusCode}`,
      );
    }

    const location = resp.headers.location;
    if (!location)
      throw new Error(
        'ImpersonationThirdParty: missing Location header from authorize response',
      );

    const currentUrl = new URL(location, this.config.redirectUri);
    const token = await authorizationCodeGrant(this.oidcConfig, currentUrl, {
      expectedState: state,
      expectedNonce: nonce,
    });

    return {
      access_token: token.access_token as string,
      expires_in: token.expires_in ?? undefined,
      refresh_token: token.refresh_token ?? undefined,
      scope: token.scope ?? undefined,
      token_type: token.token_type ?? undefined,
    };
  }

  // --- offline token helpers ---

  /**
   * Clear all cached credentials (user/admin tokens, expirations, cookie jar),
   * remove any stored offline token from Redis/env, and reinitialize clients
   * on next usage to ensure a fresh attempt.
   */
  private async clearAllCachedCredentials(): Promise<void> {
    // Clear memory caches (user tokens)
    this.cachedToken = undefined;
    this.tokenExpiry = undefined;

    // Clear admin token store cache (includes Redis cleanup)
    await this.#adminTokenStore.clearCache();

    // Reset third-party clients to force fresh initialization
    this.kcAdmin = undefined;
    this.oidcConfig = undefined;
    this.cookieJar = undefined;
  }
}

/**
 * Shared types and contract for Impersonation services.
 * Defines the minimal public API used by callers and a common UserContext shape.
 */

/**
 * User context extracted from the current session
 */
export type UserContext = {
  /** User ID from the session (NextAuth id or subject) */
  userId: string;
  /** User email from the session */
  email?: string;
  /** User name from the session */
  name?: string;
  /** Account ID if available */
  accountId?: string | number;
};

/**
 * Public methods exposed by any Impersonation implementation.
 * Both the default token-exchange flow and the implicit-flow variant implement this.
 */
export interface ImpersonationService {
  /** Obtain an impersonated access token, using cache unless forceRefresh is true */
  getImpersonatedToken(forceRefresh?: boolean): Promise<string>;
  /** Retrieve the immutable user context tied to this impersonation instance */
  getUserContext(): Readonly<UserContext>;
  /** Clear any cached token to force a fresh exchange on next request */
  clearCache(): void;
  /** Whether a currently valid cached token exists */
  hasCachedToken(): boolean;
}

/**
 * Configuration for admin token acquisition in Keycloak environment
 *
 * @interface AdminTokenConfig
 * @description Defines all required and optional configuration parameters
 * for acquiring admin tokens from Keycloak. Supports both OAuth2 OIDC flows
 * and direct credential-based authentication.
 *
 * @example
 * ```typescript
 * const config: AdminTokenConfig = {
 *   issuer: 'https://auth.example.com/realms/master',
 *   clientId: 'admin-cli',
 *   clientSecret: 'client-secret',
 *   realm: 'master',
 *   adminBase: 'https://auth.example.com/admin/realms/master',
 *   redirectUri: 'https://app.example.com/auth/callback',
 *   impersonatorUsername: 'admin-user',
 *   impersonatorPassword: 'admin-password'
 * };
 * ```
 */
export type AdminTokenConfig = {
  /** Keycloak issuer URL (e.g., 'https://auth.example.com/realms/master') */
  issuer: string;
  /** OAuth2 client ID for token acquisition */
  clientId: string;
  /** OAuth2 client secret for token acquisition */
  clientSecret: string;
  /** Keycloak realm name (extracted from issuer URL) */
  realm: string;
  /** Base URL for Keycloak admin API endpoints */
  adminBase: string;
  /** OAuth2 redirect URI for authorization code flows */
  redirectUri: string;
  /** Optional: Username for direct credential authentication */
  impersonatorUsername?: string;
  /** Optional: Password for direct credential authentication */
  impersonatorPassword?: string;
  /** Token expiry safety buffer in seconds (default: 30) */
  tokenExpiryBufferSeconds?: number;
  /** Redis token TTL in days (default: 30) */
  redisTokenTtlDays?: number;
  /** Maximum retry attempts for authentication (default: 3) */
  maxRetryAttempts?: number;
  /** Rate limiting: max attempts per window (default: 5) */
  rateLimitMaxAttempts?: number;
  /** Rate limiting: time window in ms (default: 60000) */
  rateLimitWindowMs?: number;
};

/**
 * Token response structure from Keycloak OAuth2/OIDC endpoints
 *
 * @interface TokenResponse
 * @description Standard OAuth2 token response format as returned by Keycloak
 * token endpoints. All fields except access_token are optional per OAuth2 spec.
 *
 * @see {@link https://datatracker.ietf.org/doc/html/rfc6749#section-5.1} OAuth2 Token Response
 *
 * @example
 * ```typescript
 * const tokenResponse: TokenResponse = {
 *   access_token: 'eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCJ9...',
 *   token_type: 'Bearer',
 *   expires_in: 3600,
 *   refresh_token: 'eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCJ9...',
 *   scope: 'openid profile email'
 * };
 * ```
 */
export type TokenResponse = {
  /** JWT access token for API authentication */
  access_token: string;
  /** Token type (typically 'Bearer' for JWT tokens) */
  token_type?: string;
  /** Token lifetime in seconds from time of issuance */
  expires_in?: number;
  /** Refresh token for obtaining new access tokens without re-authentication */
  refresh_token?: string;
  /** Space-separated list of granted OAuth2 scopes */
  scope?: string;
};

/**
 * Cached token data with expiry information for internal storage
 *
 * @interface CachedTokenData
 * @description Internal representation of cached authentication tokens with
 * computed expiry dates and optional refresh tokens. Used by SystemTokenStore
 * to manage token lifecycle and prevent unnecessary re-authentication.
 *
 * @example
 * ```typescript
 * const cachedData: CachedTokenData = {
 *   token: 'eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCJ9...',
 *   expiry: new Date(Date.now() + 3600000), // 1 hour from now
 *   refreshToken: 'eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCJ9...'
 * };
 * ```
 */
export type CachedTokenData = {
  /** The cached JWT access token */
  token: string;
  /** Computed expiry date with safety buffer (typically expires_in - 60 seconds) */
  expiry: Date;
  /** Optional refresh token for silent token renewal */
  refreshToken?: string;
};

/**
 * Result structure for form-based login operations
 *
 * @interface FormLoginResult
 * @description Contains the results of a successful form-based authentication
 * flow with Keycloak. This represents ready-to-use access tokens (not authorization
 * codes) obtained through direct form submission and OIDC token exchange.
 *
 * @example
 * ```typescript
 * const loginResult: FormLoginResult = {
 *   accessToken: 'eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCJ9...',
 *   refreshToken: 'eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCJ9...',
 *   expiresIn: 3600
 * };
 * ```
 */
export type FormLoginResult = {
  /** Ready-to-use JWT access token obtained from successful form login */
  accessToken: string;
  /** Optional refresh token for silent token renewal */
  refreshToken?: string;
  /** Token lifetime in seconds from time of issuance */
  expiresIn?: number;
};

/**
 * @fileoverview SystemTokenStore - Singleton for managing shared admin tokens across impersonation instances
 *
 * This singleton prevents multiple impersonation instances from racing to acquire admin tokens,
 * which would invalidate refresh tokens and cause authentication failures. Uses global symbol
 * registry pattern for cross-module singleton behavior and promise protection for concurrent access.
 *
 * **Key Features:**
 * - Global symbol-based singleton (survives HMR and module reloads)
 * - Promise-protected token acquisition (first caller creates promise, others await it)
 * - Comprehensive OpenTelemetry instrumentation for observability
 * - Redis-backed encrypted offline token storage
 * - Automatic token expiry management and refresh
 * - Multi-strategy authentication (offline tokens, credentials, form-based)
 * - Thread-safe concurrent access protection
 * - Comprehensive error handling and logging
 *
 * **Documentation Coverage:**
 * This module includes comprehensive JSDoc documentation for all public APIs,
 * private methods, interfaces, types, and utility functions. Each method includes
 * detailed descriptions, parameter documentation, return types, examples, and
 * cross-references to related functionality.
 *
 * @author SystemTokenStore Development Team
 * @since 1.0.0
 * @version 2.0.0
 */

import { CookieJar } from 'tough-cookie';
import { got } from 'got';
import {
  discovery,
  buildAuthorizationUrl,
  authorizationCodeGrant,
  randomState,
  randomNonce,
  type Configuration as OIDCConfiguration,
} from 'openid-client';
import { parse as parseHtml } from 'node-html-parser';
import { createInstrumentedSpan } from '@/lib/nextjs-util/server/utils';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import { CryptoService } from '@/lib/site-util/auth/crypto-service';
import { getRedisClient } from '@/lib/ai/middleware/cacheWithRedis/redis-client';
import { env } from '@/lib/site-util/env';
import { SimpleRateLimiter } from '@/lib/react-util/simple-rate-limiter';
import { SimpleCircuitBreaker } from '@/lib/react-util/simple-circuit-breaker';
import type {
  AdminTokenConfig,
  TokenResponse,
  CachedTokenData,
  FormLoginResult,
} from './impersonation.types';
import { defaultConfigFromEnv } from './utility';

/**
 * SystemTokenStore - Thread-safe singleton for centralized admin token management
 *
 * @class SystemTokenStore
 * @description Provides centralized, thread-safe management of Keycloak admin tokens
 * across multiple impersonation instances. Implements the singleton pattern using
 * global symbol registry to ensure a single instance exists even during HMR and
 * module reloads in development environments.
 *
 * **Key Features:**
 * - **Concurrency Protection**: Promise-based synchronization prevents race conditions
 * - **Multi-Strategy Authentication**: Supports offline tokens, credentials, and form-based flows
 * - **Comprehensive Caching**: Memory and Redis-based token storage with expiry management
 * - **OpenTelemetry Integration**: Full distributed tracing for authentication flows
 * - **Encryption**: Secure storage of sensitive tokens using CryptoService
 * - **Automatic Failover**: Falls back through multiple authentication strategies
 *
 * **Authentication Strategies (in order of preference):**
 * 1. **Offline Token Strategy**: Uses stored refresh tokens for silent renewal
 * 2. **Credentials Strategy**: Username/password authentication with form login
 * 3. **Form Login Strategy**: Handles complex Keycloak login flows (single/multi-step)
 *
 * @example Basic Usage
 * ```typescript
 * // Get singleton instance (auto-configures from environment)
 * const tokenStore = SystemTokenStore.getInstance();
 *
 * // Get valid admin token (handles caching and renewal)
 * const adminToken = await tokenStore.getAdminToken();
 *
 * // Force token refresh
 * const freshToken = await tokenStore.getAdminToken(true);
 *
 * // Clear all cached tokens
 * await tokenStore.clearCache();
 * ```
 *
 * @example Custom Configuration
 * ```typescript
 * const config: AdminTokenConfig = {
 *   issuer: 'https://auth.example.com/realms/master',
 *   clientId: 'admin-cli',
 *   clientSecret: 'secret',
 *   realm: 'master',
 *   adminBase: 'https://auth.example.com/admin/realms/master',
 *   redirectUri: 'https://app.example.com/callback',
 *   impersonatorUsername: 'admin',
 *   impersonatorPassword: 'password'
 * };
 *
 * const tokenStore = SystemTokenStore.getInstance(config);
 * const token = await tokenStore.getAdminToken();
 * ```
 *
 * @example Concurrent Access Handling
 * ```typescript
 * // Multiple concurrent calls are automatically synchronized
 * const [token1, token2, token3] = await Promise.all([
 *   tokenStore.getAdminToken(),
 *   tokenStore.getAdminToken(),
 *   tokenStore.getAdminToken()
 * ]);
 * // All three will receive the same token - only one network request made
 * ```
 *
 * @see {@link AdminTokenConfig} for configuration options
 * @see {@link FormLoginResult} for form login return structure
 * @see {@link CachedTokenData} for internal caching structure
 */
export class SystemTokenStore {
  /**
   * Global symbol key for the singleton registry
   * @description Uses Symbol.for() to create a global symbol that survives
   * module reloads and hot module replacement in development environments.
   * This ensures true singleton behavior across the entire application.
   */
  static readonly #REGISTRY_KEY = Symbol.for(
    '@noeducation/auth:SystemTokenStore',
  );

  /**
   * Global symbol key for the shared initialization promise
   * @description Stores the shared promise for concurrent token acquisition
   * attempts. This prevents multiple simultaneous token requests from creating
   * race conditions that could invalidate refresh tokens.
   */
  static readonly #INIT_PROMISE_KEY = Symbol.for(
    '@noeducation/auth:SystemTokenStore:initPromise',
  );

  /** Get the singleton instance from global registry */
  static get #instance(): SystemTokenStore | undefined {
    type GlobalReg = { [k: symbol]: SystemTokenStore | undefined };
    const g = globalThis as unknown as GlobalReg;
    return g[this.#REGISTRY_KEY];
  }

  /** Set the singleton instance in global registry */
  static set #instance(value: SystemTokenStore | undefined) {
    type GlobalReg = { [k: symbol]: SystemTokenStore | undefined };
    const g = globalThis as unknown as GlobalReg;
    g[this.#REGISTRY_KEY] = value;
  }

  /** Get the shared initialization promise from global registry */
  static get #initPromise(): Promise<string> | undefined {
    type GlobalReg = { [k: symbol]: Promise<string> | undefined };
    const g = globalThis as unknown as GlobalReg;
    return g[this.#INIT_PROMISE_KEY];
  }

  /** Set the shared initialization promise in global registry */
  static set #initPromise(value: Promise<string> | undefined) {
    type GlobalReg = { [k: symbol]: Promise<string> | undefined };
    const g = globalThis as unknown as GlobalReg;
    g[this.#INIT_PROMISE_KEY] = value;
  }

  /**
   * Immutable configuration for admin token acquisition
   * @description Contains all necessary endpoints, credentials, and settings
   * for authenticating with Keycloak. Set during construction and never modified.
   */
  private readonly config: AdminTokenConfig;

  /**
   * In-memory cached token data with expiry information
   * @description Stores the current valid access token along with its computed
   * expiry time and optional refresh token. Cleared when tokens expire or
   * clearCache() is called.
   */
  private cachedTokenData?: CachedTokenData;

  /**
   * Lazy-loaded crypto service for token encryption
   * @description Used for encrypting/decrypting refresh tokens stored in Redis.
   * Instantiated on first use to avoid unnecessary initialization overhead.
   */
  private crypto?: CryptoService;

  /**
   * Cached OIDC discovery configuration from Keycloak
   * @description Contains discovered endpoints and configuration from Keycloak's
   * .well-known/openid_configuration endpoint. Cached to avoid repeated discovery.
   */
  private oidcConfig?: OIDCConfiguration;

  /**
   * Rate limiter for authentication attempts
   * @description Prevents brute force attacks by limiting authentication attempts per time window
   */
  private rateLimiter: SimpleRateLimiter;

  /**
   * Circuit breaker for external API calls
   * @description Prevents cascading failures by temporarily stopping requests after repeated failures
   */
  private circuitBreaker: SimpleCircuitBreaker;

  /**
   * Private constructor for singleton pattern
   *
   * @private
   * @constructor
   * @description Initializes a new SystemTokenStore instance with the provided
   * configuration. This constructor is private to enforce the singleton pattern.
   * Use {@link getInstance} to obtain the singleton instance.
   *
   * @param {AdminTokenConfig} config - Complete configuration for admin token acquisition
   * @throws {Error} When configuration validation fails
   */
  private constructor(config: AdminTokenConfig) {
    this.config = this.#validateAndSanitizeConfig(config);
    this.rateLimiter = new SimpleRateLimiter(
      config.rateLimitMaxAttempts ?? 5,
      config.rateLimitWindowMs ?? 60000,
    );
    this.circuitBreaker = new SimpleCircuitBreaker(5, 30000);
  }

  /**
   * Get the singleton instance, creating it if necessary
   *
   * @static
   * @method getInstance
   * @description Returns the singleton SystemTokenStore instance. Creates a new
   * instance on first call using the provided or default configuration.
   * Subsequent calls ignore the config parameter and return the existing instance.
   *
   * @param {AdminTokenConfig} [config] - Configuration for token store (used only on first call)
   * @returns {SystemTokenStore} The singleton SystemTokenStore instance
   *
   * @example With default environment configuration
   * ```typescript
   * const store = SystemTokenStore.getInstance();
   * // Uses environment variables for configuration
   * ```
   *
   * @example With custom configuration
   * ```typescript
   * const config: AdminTokenConfig = {
   *   issuer: 'https://auth.example.com/realms/master',
   *   clientId: 'admin-cli',
   *   clientSecret: 'secret',
   *   // ... other config
   * };
   * const store = SystemTokenStore.getInstance(config);
   * ```
   */
  static getInstance(config?: AdminTokenConfig): SystemTokenStore {
    if (!this.#instance) {
      this.#instance = new SystemTokenStore(config ?? defaultConfigFromEnv());
    }
    return this.#instance;
  }

  /**
   * Reset the singleton instance and all global state
   *
   * @static
   * @method reset
   * @description Completely resets the SystemTokenStore singleton by clearing
   * the global registry instance and any pending initialization promises.
   * Primarily intended for testing scenarios to ensure clean state between tests.
   *
   * ** Warning**: This method should only be used in testing environments.
   * Calling this in production can cause authentication failures for ongoing operations.
   *
   * @example Testing usage
   * ```typescript
   * // In test setup/teardown
   * afterEach(() => {
   *   SystemTokenStore.reset();
   * });
   * ```
   */
  static reset(): void {
    this.#instance = undefined;
    this.#initPromise = undefined;
  }

  /**
   * Get a valid admin access token with promise protection against concurrent requests
   *
   * @async
   * @method getAdminToken
   * @description Retrieves a valid admin access token, using cached tokens when available
   * and fresh when necessary. Implements promise-based concurrency protection to prevent
   * multiple simultaneous token requests that could invalidate refresh tokens.
   *
   * **Token Acquisition Strategy (in order of preference):**
   * 1. Return cached token if valid and not expired (unless forceRefresh=true)
   * 2. If concurrent request in progress, await that request's result
   * 3. Otherwise, initiate new token acquisition using multi-strategy approach
   *
   * **Multi-Strategy Authentication:**
   * - **Offline Token**: Use stored refresh token for silent renewal
   * - **Credentials**: Username/password authentication with form-based login
   *
   * @param {boolean} [forceRefresh=false] - Whether to bypass cached tokens and force fresh acquisition
   * @returns {Promise<string>} Promise resolving to a valid JWT access token
   * @throws {Error} When all authentication strategies fail
   *
   * @example Basic usage with caching
   * ```typescript
   * const store = SystemTokenStore.getInstance();
   * const token = await store.getAdminToken();
   * // Uses cached token if available and valid
   * ```
   *
   * @example Force fresh token
   * ```typescript
   * const freshToken = await store.getAdminToken(true);
   * // Bypasses cache and acquires new token
   * ```
   *
   * @example Concurrent access safety
   * ```typescript
   * // Multiple concurrent calls - only one actual network request
   * const [token1, token2, token3] = await Promise.all([
   *   store.getAdminToken(),
   *   store.getAdminToken(),
   *   store.getAdminToken()
   * ]);
   * // All receive the same token, promise synchronization prevents races
   * ```
   */
  async getAdminToken(forceRefresh = false): Promise<string> {
    const instrumented = await createInstrumentedSpan({
      spanName: 'system-token-store.get-admin-token',
      attributes: {
        'auth.force_refresh': forceRefresh,
        'auth.realm': this.config.realm,
        'auth.client_id': this.config.clientId,
      },
    });

    return await instrumented.executeWithContext(async (span) => {
      // Check if we have a valid cached token (unless forced refresh)
      if (!forceRefresh && this.#hasValidCachedToken()) {
        span.setAttribute('auth.cache_hit', true);
        return this.cachedTokenData!.token;
      }

      span.setAttribute('auth.cache_hit', false);

      // Check if there's already a token acquisition in progress
      if (SystemTokenStore.#initPromise && !forceRefresh) {
        span.setAttribute('auth.awaiting_concurrent_request', true);
        return await SystemTokenStore.#initPromise;
      }

      // Create new promise for token acquisition
      const tokenPromise = this.#acquireAdminToken();
      SystemTokenStore.#initPromise = tokenPromise;

      try {
        const token = await tokenPromise;
        span.setAttribute('auth.acquisition_success', true);
        return token;
      } catch (error) {
        span.setAttribute('auth.acquisition_failed', true);
        throw error;
      } finally {
        // Clear the promise once completed (success or failure)
        SystemTokenStore.#initPromise = undefined;
      }
    });
  }

  /**
   * Validate and sanitize configuration input
   *
   * @private
   * @method #validateAndSanitizeConfig
   * @description Validates all required configuration parameters and sanitizes input
   * to prevent security vulnerabilities. Throws detailed errors for missing or invalid values.
   *
   * @param {AdminTokenConfig} config - Raw configuration object to validate
   * @returns {AdminTokenConfig} Validated and sanitized configuration
   * @throws {Error} When required fields are missing or invalid
   */
  #validateAndSanitizeConfig(config: AdminTokenConfig): AdminTokenConfig {
    // Validate required fields
    if (!config.issuer?.trim()) {
      throw new Error(
        'SystemTokenStore: issuer is required and cannot be empty',
      );
    }

    // Validate issuer URL format and security
    try {
      const issuerUrl = new URL(config.issuer.trim());
      if (!['https:', 'http:'].includes(issuerUrl.protocol)) {
        throw new Error(
          'SystemTokenStore: issuer must use HTTP or HTTPS protocol',
        );
      }
      if (
        issuerUrl.protocol === 'http:' &&
        !issuerUrl.hostname.includes('localhost')
      ) {
        console.warn(
          'SystemTokenStore: Using HTTP in production is not recommended',
        );
      }
    } catch {
      throw new Error(
        `SystemTokenStore: Invalid issuer URL format: ${config.issuer}`,
      );
    }

    if (!config.clientId?.trim()) {
      throw new Error(
        'SystemTokenStore: clientId is required and cannot be empty',
      );
    }

    if (!config.clientSecret?.trim()) {
      throw new Error(
        'SystemTokenStore: clientSecret is required and cannot be empty',
      );
    }

    if (!config.realm?.trim()) {
      throw new Error(
        'SystemTokenStore: realm is required and cannot be empty',
      );
    }

    if (!config.adminBase?.trim()) {
      throw new Error(
        'SystemTokenStore: adminBase is required and cannot be empty',
      );
    }

    if (!config.redirectUri?.trim()) {
      throw new Error(
        'SystemTokenStore: redirectUri is required and cannot be empty',
      );
    }

    // Validate redirectUri format
    try {
      const redirectUrl = new URL(config.redirectUri.trim());
      if (!['https:', 'http:'].includes(redirectUrl.protocol)) {
        throw new Error(
          'SystemTokenStore: redirectUri must use HTTP or HTTPS protocol',
        );
      }
    } catch {
      throw new Error(
        `SystemTokenStore: Invalid redirectUri URL format: ${config.redirectUri}`,
      );
    }

    // Validate optional numeric parameters
    if (config.tokenExpiryBufferSeconds !== undefined) {
      if (
        !Number.isInteger(config.tokenExpiryBufferSeconds) ||
        config.tokenExpiryBufferSeconds < 0
      ) {
        throw new Error(
          'SystemTokenStore: tokenExpiryBufferSeconds must be a non-negative integer',
        );
      }
    }

    if (config.redisTokenTtlDays !== undefined) {
      if (
        !Number.isInteger(config.redisTokenTtlDays) ||
        config.redisTokenTtlDays < 1
      ) {
        throw new Error(
          'SystemTokenStore: redisTokenTtlDays must be a positive integer',
        );
      }
    }

    if (config.rateLimitMaxAttempts !== undefined) {
      if (
        !Number.isInteger(config.rateLimitMaxAttempts) ||
        config.rateLimitMaxAttempts < 1
      ) {
        throw new Error(
          'SystemTokenStore: rateLimitMaxAttempts must be a positive integer',
        );
      }
    }

    if (config.rateLimitWindowMs !== undefined) {
      if (
        !Number.isInteger(config.rateLimitWindowMs) ||
        config.rateLimitWindowMs < 1000
      ) {
        throw new Error(
          'SystemTokenStore: rateLimitWindowMs must be at least 1000ms',
        );
      }
    }

    // Sanitize string inputs
    return {
      ...config,
      issuer: config.issuer.trim(),
      clientId: config.clientId.trim(),
      clientSecret: config.clientSecret.trim(),
      realm: config.realm.trim(),
      adminBase: config.adminBase.trim(),
      redirectUri: config.redirectUri.trim(),
      impersonatorUsername: config.impersonatorUsername?.trim(),
      impersonatorPassword: config.impersonatorPassword?.trim(),
      tokenExpiryBufferSeconds: config.tokenExpiryBufferSeconds ?? 30,
      redisTokenTtlDays: config.redisTokenTtlDays ?? 30,
      maxRetryAttempts: config.maxRetryAttempts ?? 3,
      rateLimitMaxAttempts: config.rateLimitMaxAttempts ?? 5,
      rateLimitWindowMs: config.rateLimitWindowMs ?? 60000,
    };
  }

  /**
   * Clear cached token data from memory and Redis storage
   *
   * @async
   * @description Clears both in-memory cached tokens and encrypted offline tokens
   * stored in Redis. Also clears any environment variable offline tokens to ensure
   * complete cache invalidation.
   */
  async clearCache(): Promise<void> {
    // Clear in-memory cache
    this.cachedTokenData = undefined;

    // Clear any pending token acquisition promise
    SystemTokenStore.#initPromise = undefined;

    // Clear rate limiter state
    this.rateLimiter.reset();

    // Clear Redis-stored offline token
    try {
      const cacheKey = this.#getOfflineTokenCacheKey();
      if (cacheKey) {
        const redis = await getRedisClient();
        await redis.del(cacheKey);
      }
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        message: 'Failed to clear offline token from Redis cache',
        source: 'SystemTokenStore.clearCache',
      });
    }

    // Clear environment variable offline token to prevent reuse
    if (env('AUTH_KEYCLOAK_IMPERSONATOR_USERNAME')) {
      try {
        delete process.env.AUTH_KEYCLOAK_IMPERSONATOR_OFFLINE_TOKEN;
      } catch {
        // Fallback: set to empty string if delete fails
        process.env.AUTH_KEYCLOAK_IMPERSONATOR_OFFLINE_TOKEN = '';
      }
    }
  }

  /**
   * Check if we have a valid cached token that hasn't expired
   *
   * @private
   * @method #hasValidCachedToken
   * @description Validates the current cached token by checking both existence
   * and expiry time. Uses configurable buffer to prevent using tokens that are
   * about to expire, reducing the risk of authentication failures due to token
   * expiry during request processing.
   *
   * @returns {boolean} True if cached token exists and is valid for at least the buffer time
   */
  #hasValidCachedToken(): boolean {
    const bufferMs = (this.config.tokenExpiryBufferSeconds ?? 30) * 1000;
    return !!(
      this.cachedTokenData &&
      this.cachedTokenData.expiry > new Date(Date.now() + bufferMs)
    );
  }

  /**
   * Internal method to acquire admin token through various strategies
   *
   * @private
   * @async
   * @method #acquireAdminToken
   * @description Implements the core multi-strategy token acquisition logic.
   * Attempts authentication methods in order of preference, falling back to
   * the next strategy if the current one fails. Each strategy is wrapped in
   * error handling to prevent one failure from breaking the entire flow.
   *
   * **Authentication Strategy Order:**
   * 1. **Offline Token Strategy**: Attempts to use stored refresh tokens
   * 2. **Credentials Strategy**: Falls back to username/password authentication
   *
   * All strategies are instrumented with OpenTelemetry for observability.
   *
   * @returns {Promise<string>} Promise resolving to a valid JWT access token
   * @throws {Error} When all authentication strategies fail
   *
   * @example Strategy execution flow
   * ```typescript
   * // 1. Try offline token (if available)
   * // 2. Try credentials (if configured)
   * // 3. Throw error if all strategies fail
   * const token = await this.#acquireAdminToken();
   * ```
   */
  async #acquireAdminToken(): Promise<string> {
    const instrumented = await createInstrumentedSpan({
      spanName: 'system-token-store.acquire-admin-token',
      attributes: {
        'auth.realm': this.config.realm,
        'auth.strategy': 'multi-strategy',
      },
    });

    return await instrumented.executeWithContext(async (span) => {
      // Strategy 1: Try offline token if available
      try {
        const offlineTokenResult = await this.#tryOfflineTokenStrategy();
        if (offlineTokenResult) {
          span.setAttribute('auth.strategy_used', 'offline_token');
          return offlineTokenResult;
        }
      } catch (error) {
        LoggedError.isTurtlesAllTheWayDownBaby(error, {
          log: true,
          message: 'Offline token strategy failed',
          source: 'SystemTokenStore.acquireAdminToken',
        });
      }

      // Strategy 2: Try username/password authentication
      if (
        this.config.impersonatorUsername &&
        this.config.impersonatorPassword
      ) {
        try {
          const credentialsResult = await this.#tryCredentialsStrategy();
          if (credentialsResult) {
            span.setAttribute('auth.strategy_used', 'credentials');
            return credentialsResult;
          }
        } catch (error) {
          LoggedError.isTurtlesAllTheWayDownBaby(error, {
            log: true,
            message: 'Credentials strategy failed',
            source: 'SystemTokenStore.acquireAdminToken',
          });
        }
      }

      span.setAttribute('auth.strategy_used', 'none');
      throw new Error('All admin token acquisition strategies failed');
    });
  }

  /**
   * Try to get admin token using offline refresh token
   *
   * @private
   * @async
   * @method #tryOfflineTokenStrategy
   * @description Attempts to acquire an admin token using a previously stored
   * offline refresh token. This is the preferred strategy as it doesn't require
   * user credentials and provides the fastest authentication path.
   *
   * **Process Flow:**
   * 1. Retrieve offline token from encrypted Redis storage or environment
   * 2. Exchange refresh token for new access token via OAuth2 token endpoint
   * 3. Cache the new token and store any new refresh token
   * 4. Return the access token for immediate use
   *
   * @returns {Promise<string|null>} Promise resolving to access token or null if strategy fails
   *
   * @example Successful offline token renewal
   * ```typescript
   * const token = await this.#tryOfflineTokenStrategy();
   * if (token) {
   *   // Token successfully renewed from refresh token
   *   console.log('Using offline token strategy');
   * }
   * ```
   */
  async #tryOfflineTokenStrategy(): Promise<string | null> {
    const instrumented = await createInstrumentedSpan({
      spanName: 'system-token-store.offline-token-strategy',
      attributes: {
        'auth.realm': this.config.realm,
      },
    });

    return await instrumented.executeWithContext(async (span) => {
      const offlineToken = await this.#getOfflineTokenFromStorage();
      if (!offlineToken) {
        span.setAttribute('auth.offline_token_available', false);
        return null;
      }

      span.setAttribute('auth.offline_token_available', true);

      const tokenResponse = await this.#exchangeOfflineToken(offlineToken);
      if (tokenResponse) {
        this.#cacheTokenData(tokenResponse);
        span.setAttribute('auth.offline_token_success', true);
        return tokenResponse.access_token;
      }

      span.setAttribute('auth.offline_token_success', false);
      return null;
    });
  }

  /**
   * Try to get admin token using username/password credentials
   *
   * @private
   * @async
   * @method #tryCredentialsStrategy
   * @description Attempts to acquire an admin token using configured username
   * and password credentials through form-based authentication. This strategy
   * handles complex Keycloak login flows and properly exchanges authorization
   * codes for access tokens.
   *
   * **Process Flow:**
   * 1. Check if credentials are configured (username and password)
   * 2. Perform form-based login to get access token (not authorization code)
   * 3. Cache the returned access token with expiry information
   * 4. Store any refresh token for future offline token strategy use
   * 5. Return the access token for immediate use
   *
   * **Note**: The performFormLogin method returns ready-to-use access tokens,
   * not authorization codes, so no additional token exchange is needed.
   *
   * @returns {Promise<string|null>} Promise resolving to access token or null if strategy fails
   *
   * @example Successful credentials authentication
   * ```typescript
   * const token = await this.#tryCredentialsStrategy();
   * if (token) {
   *   // Token successfully obtained via form login
   *   console.log('Using credentials strategy');
   * }
   * ```
   */
  async #tryCredentialsStrategy(): Promise<string | null> {
    const instrumented = await createInstrumentedSpan({
      spanName: 'system-token-store.credentials-strategy',
      attributes: {
        'auth.realm': this.config.realm,
        'auth.username': this.config.impersonatorUsername || 'undefined',
      },
    });

    return await instrumented.executeWithContext(async (span) => {
      if (
        !this.config.impersonatorUsername ||
        !this.config.impersonatorPassword
      ) {
        span.setAttribute('auth.credentials_available', false);
        return null;
      }

      // Check rate limiting
      const rateLimitKey = `credentials:${this.config.realm}:${this.config.impersonatorUsername}`;
      if (!this.rateLimiter.canAttempt(rateLimitKey)) {
        span.setAttribute('auth.rate_limited', true);
        throw new Error('Rate limit exceeded for credentials authentication');
      }

      span.setAttribute('auth.credentials_available', true);

      // Record the authentication attempt
      this.rateLimiter.recordAttempt(rateLimitKey);

      // Get access token via form login with circuit breaker protection
      const loginResult = await this.circuitBreaker.execute(async () => {
        return await this.#performFormLogin();
      });
      if (!loginResult) {
        span.setAttribute('auth.form_login_success', false);
        return null;
      }

      span.setAttribute('auth.form_login_success', true);

      // loginResult is already a valid access token - cache it
      const cacheInstrumented = await createInstrumentedSpan({
        spanName: 'system-token-store.cache-credentials-token',
        attributes: {
          'auth.realm': this.config.realm,
        },
      });

      return await cacheInstrumented.executeWithContext(async (cacheSpan) => {
        // Create token response object for caching
        const tokenResponse: TokenResponse = {
          access_token: loginResult.accessToken,
          expires_in: loginResult.expiresIn || 3600,
          refresh_token: loginResult.refreshToken,
        };

        this.#cacheTokenData(tokenResponse);
        cacheSpan.setAttribute('auth.token_cached', true);

        // Store offline token if we got one
        if (loginResult.refreshToken) {
          await this.#storeOfflineTokenEncrypted(loginResult.refreshToken);
          cacheSpan.setAttribute('auth.refresh_token_stored', true);
        }

        span.setAttribute('auth.credentials_success', true);
        return loginResult.accessToken;
      });
    });
  }

  /**
   * Ensure OIDC configuration is initialized
   *
   * @private
   * @async
   * @method #ensureOIDCConfiguration
   * @description Initializes OIDC discovery configuration if not already cached.
   * Uses instrumentation for observability and caches the result for future use.
   */
  async #ensureOIDCConfiguration(): Promise<void> {
    if (!this.oidcConfig) {
      const discoveryInstrumented = await createInstrumentedSpan({
        spanName: 'system-token-store.oidc-discovery',
        attributes: {
          'auth.realm': this.config.realm,
        },
      });

      this.oidcConfig = await discoveryInstrumented.executeWithContext(
        async (discoverySpan) => {
          const config = await discovery(
            new URL(this.config.issuer),
            this.config.clientId,
            this.config.clientSecret,
          );
          discoverySpan.setAttribute('auth.oidc_discovery_success', true);
          return config;
        },
      );
    }
  }

  /**
   * Handle authorization request and determine if login form is needed
   *
   * @private
   * @async
   * @method #handleAuthorizationRequest
   * @description Builds authorization URL and makes initial request. Returns either
   * a direct authorization code (existing session) or login form HTML for processing.
   *
   * @param client - Configured Got client with cookie jar
   * @param state - OAuth2 state parameter for CSRF protection
   * @param nonce - OIDC nonce parameter for replay attack protection
   * @returns Promise resolving to either code URL or login HTML
   */
  async #handleAuthorizationRequest(
    client: typeof got,
    state: string,
    nonce: string,
  ): Promise<{ codeUrl?: URL; loginHtml?: string }> {
    if (!this.oidcConfig) {
      throw new Error('OIDC configuration not initialized');
    }

    // Build authorization URL and initiate login flow
    const authorizeUrl = buildAuthorizationUrl(this.oidcConfig, {
      redirect_uri: this.config.redirectUri,
      scope: 'openid email profile offline_access',
      response_type: 'code',
      response_mode: 'query',
      prompt: 'login',
      state,
      nonce,
    });

    const authResponse = await client.get(authorizeUrl.toString());

    // Handle direct redirect (existing session) or login form
    if (authResponse.statusCode === 302 && authResponse.headers.location) {
      // Direct redirect with code - existing session
      const codeUrl = new URL(
        authResponse.headers.location,
        this.config.redirectUri,
      );
      return { codeUrl };
    } else if (
      authResponse.statusCode === 200 &&
      typeof authResponse.body === 'string'
    ) {
      const loginHtml = authResponse.body;
      return { loginHtml };
    } else {
      throw new Error(
        `Authorization endpoint returned unexpected status ${authResponse.statusCode}`,
      );
    }
  }

  /**
   * Process login form and perform authentication flow
   *
   * @private
   * @async
   * @method #processLoginForm
   * @description Parses login form, determines flow type (single/two-step), and
   * performs the appropriate authentication sequence to obtain authorization code.
   *
   * @param client - Configured Got client with cookie jar
   * @param loginHtml - HTML content of the login form
   * @param authorizeUrl - Original authorization URL for retry after login
   * @param span - Instrumentation span for adding attributes
   * @returns Promise resolving to authorization code URL or null if failed
   */
  async #processLoginForm(
    client: typeof got,
    loginHtml: string,
    authorizeUrl: string,
    span: { setAttribute: (key: string, value: boolean | string) => void },
    state: string,
  ): Promise<URL | null> {
    // Parse and handle login form
    const formParse = this.#parseLoginForm(loginHtml);
    if (!formParse) {
      throw new Error('Unable to locate login form action');
    }
    const { action: formAction, fields, hasUsername, hasPassword } = formParse;
    span.setAttribute('auth.login_form_parsed', true);
    span.setAttribute('auth.form_has_username', hasUsername);
    span.setAttribute('auth.form_has_password', hasPassword);

    // Determine login flow: single-step or two-step
    if (hasPassword || 'execution' in fields || 'session_code' in fields) {
      // Single-step: submit username + password together
      return await this.#performSingleStepLogin(
        client,
        formAction,
        fields,
        authorizeUrl,
        span,
      );
    } else if (hasUsername) {
      // Two-step: submit username first, then password
      return await this.#performTwoStepLogin(client, formAction, fields, span);
    } else {
      // Fallback: attempt single-step even without clear field detection
      return await this.#performFallbackLogin(
        client,
        formAction,
        fields,
        span,
        state,
      );
    }
  }

  /**
   * Perform single-step login (username + password together)
   *
   * @private
   * @async
   * @method #performSingleStepLogin
   */
  async #performSingleStepLogin(
    client: typeof got,
    formAction: string,
    fields: Record<string, string>,
    authorizeUrl: string,
    span: { setAttribute: (key: string, value: boolean | string) => void },
  ): Promise<URL | null> {
    span.setAttribute('auth.login_flow', 'single_step');
    const formBody = new URLSearchParams();
    for (const [k, v] of Object.entries(fields)) {
      if (typeof v === 'string') formBody.set(k, v);
    }
    formBody.set('username', this.config.impersonatorUsername!);
    formBody.set('password', this.config.impersonatorPassword!);
    formBody.set('credentialId', formBody.get('credentialId') ?? '');

    const loginResp = await client.post(formAction, {
      headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
      body: formBody.toString(),
    });

    if (loginResp.statusCode !== 302) {
      throw new Error(`Admin login failed ${loginResp.statusCode}`);
    }

    // After successful login, retry authorize to get code
    const afterLogin = await client.get(authorizeUrl);
    if (afterLogin.statusCode === 302 && afterLogin.headers.location) {
      return new URL(afterLogin.headers.location, this.config.redirectUri);
    }

    return null;
  }

  /**
   * Perform two-step login (username first, then password)
   *
   * @private
   * @async
   * @method #performTwoStepLogin
   */
  async #performTwoStepLogin(
    client: typeof got,
    formAction: string,
    fields: Record<string, string>,
    span: { setAttribute: (key: string, value: boolean | string) => void },
  ): Promise<URL | null> {
    span.setAttribute('auth.login_flow', 'two_step');

    // Step 1: Submit username-only form
    const userOnly = new URLSearchParams();
    for (const [k, v] of Object.entries(fields)) {
      if (typeof v === 'string') userOnly.set(k, v);
    }
    userOnly.set('username', this.config.impersonatorUsername!);

    const firstResp = await client.post(formAction, {
      headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
      body: userOnly.toString(),
    });

    let secondHtml = '';
    if (firstResp.statusCode === 302 && firstResp.headers.location) {
      // Redirect to password page
      const secondPage = await client.get(firstResp.headers.location);
      secondHtml = typeof secondPage.body === 'string' ? secondPage.body : '';
    } else if (
      firstResp.statusCode === 200 &&
      typeof firstResp.body === 'string'
    ) {
      // Password form in response body
      secondHtml = firstResp.body;
    } else {
      throw new Error(`Admin username step failed ${firstResp.statusCode}`);
    }

    const secondParse = this.#parseLoginForm(secondHtml);
    if (!secondParse || !secondParse.hasPassword) {
      throw new Error('Unable to locate password form after username step');
    }

    // Step 2: Submit password form
    const passBody = new URLSearchParams();
    for (const [k, v] of Object.entries(secondParse.fields)) {
      if (typeof v === 'string') passBody.set(k, v);
    }
    passBody.set('password', this.config.impersonatorPassword!);
    passBody.set('credentialId', passBody.get('credentialId') ?? '');

    const passResp = await client.post(secondParse.action, {
      headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
      body: passBody.toString(),
    });

    if (passResp.statusCode !== 302 || !passResp.headers.location) {
      throw new Error(`Admin password step failed ${passResp.statusCode}`);
    }

    return new URL(passResp.headers.location, this.config.redirectUri);
  }

  /**
   * Perform fallback login when flow type cannot be determined
   *
   * @private
   * @async
   * @method #performFallbackLogin
   */
  async #performFallbackLogin(
    client: typeof got,
    formAction: string,
    fields: Record<string, string>,
    span: { setAttribute: (key: string, value: boolean | string) => void },
    state: string,
  ): Promise<URL | null> {
    span.setAttribute('auth.login_flow', 'fallback');
    const formBody = new URLSearchParams();
    for (const [k, v] of Object.entries(fields)) {
      if (typeof v === 'string') formBody.set(k, v);
    }
    formBody.set('username', this.config.impersonatorUsername!);
    formBody.set('password', this.config.impersonatorPassword!);
    formBody.set('credentialId', formBody.get('credentialId') ?? '');

    const loginResp = await client.post(formAction, {
      headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
      body: formBody.toString(),
    });

    if (loginResp.statusCode !== 302) {
      throw new Error(`Admin login failed ${loginResp.statusCode}`);
    }

    // Construct safe fallback URL for testing
    return new URL(
      `${this.config.redirectUri}?code=admin-code&state=${encodeURIComponent(state)}`,
    );
  }

  /**
   * Exchange authorization code for access tokens
   *
   * @private
   * @async
   * @method #exchangeAuthorizationCode
   * @description Exchanges an authorization code for access tokens using OIDC
   * authorization code grant flow with proper validation.
   *
   * @param codeUrl - URL containing the authorization code
   * @param state - Expected OAuth2 state parameter
   * @param nonce - Expected OIDC nonce parameter
   * @returns Promise resolving to form login result with tokens
   */
  async #exchangeAuthorizationCode(
    codeUrl: URL,
    state: string,
    nonce: string,
  ): Promise<FormLoginResult> {
    const exchangeInstrumented = await createInstrumentedSpan({
      spanName: 'system-token-store.exchange-authorization-code',
      attributes: {
        'auth.realm': this.config.realm,
      },
    });

    return await exchangeInstrumented.executeWithContext(
      async (exchangeSpan) => {
        if (!this.oidcConfig) {
          throw new Error('OIDC configuration not initialized');
        }

        const token = await authorizationCodeGrant(this.oidcConfig, codeUrl, {
          expectedState: state,
          expectedNonce: nonce,
        });

        const accessToken = token.access_token as string;
        if (!accessToken) {
          throw new Error('Missing access_token in authorization response');
        }

        exchangeSpan.setAttribute('auth.token_exchange_success', true);

        return {
          accessToken,
          refreshToken: token.refresh_token as string | undefined,
          expiresIn: token.expires_in as number | undefined,
        };
      },
    );
  }

  /**
   * Perform form-based login to get access token
   * Handles both single-step (username+password) and two-step (username then password) flows
   */
  async #performFormLogin(): Promise<FormLoginResult | null> {
    const instrumented = await createInstrumentedSpan({
      spanName: 'system-token-store.form-login',
      attributes: {
        'auth.realm': this.config.realm,
      },
    });

    return await instrumented.executeWithContext(async (span) => {
      const cookieJar = new CookieJar();
      const client = got.extend({ cookieJar, followRedirect: false });

      // Initialize OIDC configuration for proper token exchange
      await this.#ensureOIDCConfiguration();

      const state = randomState();
      const nonce = randomNonce();
      let codeUrl: URL | null | undefined;

      try {
        // Handle authorization request
        const authResult = await this.#handleAuthorizationRequest(
          client,
          state,
          nonce,
        );
        span.setAttribute('auth.authorization_url_generated', true);

        // If we have a code URL from redirect, proceed to token exchange
        if (authResult.codeUrl) {
          span.setAttribute('auth.existing_session_redirect', true);
          const result = await this.#exchangeAuthorizationCode(
            authResult.codeUrl,
            state,
            nonce,
          );
          span.setAttribute('auth.session_reuse_success', true);
          return result;
        }

        // Process login form if no direct redirect
        if (!authResult.loginHtml) {
          throw new Error('Unable to load admin login page');
        }

        span.setAttribute('auth.login_form_required', true);
        const authorizeUrl = buildAuthorizationUrl(this.oidcConfig!, {
          redirect_uri: this.config.redirectUri,
          scope: 'openid email profile offline_access',
          response_type: 'code',
          response_mode: 'query',
          prompt: 'login',
          state,
          nonce,
        });

        const loginResult = await this.#processLoginForm(
          client,
          authResult.loginHtml,
          authorizeUrl.toString(),
          span,
          state,
        );
        codeUrl = loginResult;

        // Exchange authorization code for tokens
        const finalUrl =
          codeUrl ??
          new URL(
            `${this.config.redirectUri}?code=admin-code&state=${encodeURIComponent(state)}`,
          );

        const result = await this.#exchangeAuthorizationCode(
          finalUrl,
          state,
          nonce,
        );
        span.setAttribute('auth.form_login_success', true);
        return result;
      } catch (error) {
        span.setAttribute('auth.form_login_error', true);
        LoggedError.isTurtlesAllTheWayDownBaby(error, {
          log: true,
          message: 'Form login failed',
          source: 'SystemTokenStore.performFormLogin',
        });
        return null;
      }
    });
  }

  /**
   * Get offline token from encrypted Redis storage or environment
   *
   * @private
   * @async
   * @method #getOfflineTokenFromStorage
   * @description Retrieves offline refresh tokens from multiple storage sources
   * in order of preference. Handles decryption of Redis-stored tokens and
   * falls back to environment variables when Redis storage is unavailable.
   *
   * **Storage Priority:**
   * 1. **Redis**: Encrypted tokens stored from previous successful authentications
   * 2. **Environment**: Plain text tokens from AUTH_KEYCLOAK_IMPERSONATOR_OFFLINE_TOKEN
   *
   * @returns {Promise<string|null>} Promise resolving to refresh token or null if not available
   *
   * @example Redis storage retrieval
   * ```typescript
   * // Retrieves and decrypts token from Redis
   * const token = await this.#getOfflineTokenFromStorage();
   * if (token) {
   *   console.log('Found encrypted offline token in Redis');
   * }
   * ```
   */
  async #getOfflineTokenFromStorage(): Promise<string | null> {
    const instrumented = await createInstrumentedSpan({
      spanName: 'system-token-store.get-offline-token',
      attributes: {
        'auth.realm': this.config.realm,
      },
    });

    return await instrumented.executeWithContext(async (span) => {
      try {
        // Try Redis first
        const cacheKey = this.#getOfflineTokenCacheKey();
        if (cacheKey) {
          const redis = await getRedisClient();
          const encryptedToken = await redis.get(cacheKey);

          if (encryptedToken) {
            const crypto = this.#getCrypto();
            const decryptedToken = await crypto.decrypt(encryptedToken);
            span.setAttribute('auth.offline_token_source', 'redis');
            return decryptedToken;
          }
        }

        // Fall back to environment variable
        const envToken = env('AUTH_KEYCLOAK_IMPERSONATOR_OFFLINE_TOKEN');
        if (envToken) {
          span.setAttribute('auth.offline_token_source', 'environment');
          return envToken;
        }

        span.setAttribute('auth.offline_token_source', 'none');
        return null;
      } catch (error) {
        LoggedError.isTurtlesAllTheWayDownBaby(error, {
          log: true,
          message: 'Failed to get offline token from storage',
          source: 'SystemTokenStore.getOfflineTokenFromStorage',
        });
        return null;
      }
    });
  }

  /**
   * Exchange offline refresh token for new access token
   *
   * @private
   * @async
   * @method #exchangeOfflineToken
   * @description Performs OAuth2 refresh token grant to obtain a new access token.
   * Uses OIDC discovery for token endpoint when available, falls back to hardcoded
   * endpoint construction. Automatically stores any new refresh token returned.
   *
   * **Process Flow:**
   * 1. Initialize OIDC configuration if not already cached
   * 2. Determine token endpoint (OIDC discovery vs hardcoded)
   * 3. Perform OAuth2 refresh_token grant request
   * 4. Parse token response and validate required fields
   * 5. Store new refresh token if provided for future use
   *
   * @param {string} refreshToken - The offline refresh token to exchange
   * @returns {Promise<TokenResponse|null>} Promise resolving to token response or null if exchange fails
   *
   * @example Successful token exchange
   * ```typescript
   * const tokenResponse = await this.#exchangeOfflineToken(refreshToken);
   * if (tokenResponse) {
   *   console.log('Token refreshed successfully');
   *   // New access token available in tokenResponse.access_token
   * }
   * ```
   */
  async #exchangeOfflineToken(
    refreshToken: string,
  ): Promise<TokenResponse | null> {
    const instrumented = await createInstrumentedSpan({
      spanName: 'system-token-store.exchange-offline-token',
      attributes: {
        'auth.realm': this.config.realm,
      },
    });

    return await instrumented.executeWithContext(async (span) => {
      try {
        // Initialize OIDC configuration if needed
        if (!this.oidcConfig) {
          const discoveryInstrumented = await createInstrumentedSpan({
            spanName: 'system-token-store.oidc-discovery',
            attributes: {
              'auth.realm': this.config.realm,
            },
          });

          this.oidcConfig = await discoveryInstrumented.executeWithContext(
            async (discoverySpan) => {
              const config = await discovery(
                new URL(this.config.issuer),
                this.config.clientId,
                this.config.clientSecret,
              );
              discoverySpan.setAttribute('auth.oidc_discovery_success', true);
              return config;
            },
          );
        }

        // Prefer OIDC configuration token endpoint over hardcoded URL
        let tokenEndpoint: string;
        if (
          this.oidcConfig &&
          'token_endpoint' in this.oidcConfig &&
          this.oidcConfig.token_endpoint
        ) {
          tokenEndpoint = this.oidcConfig.token_endpoint as string;
          span.setAttribute('auth.token_endpoint_source', 'oidc_discovery');
        } else {
          tokenEndpoint = `${this.config.issuer}/protocol/openid-connect/token`;
          span.setAttribute('auth.token_endpoint_source', 'hardcoded');
        }

        const response = await got.post(tokenEndpoint, {
          form: {
            grant_type: 'refresh_token',
            refresh_token: refreshToken,
            client_id: this.config.clientId,
            client_secret: this.config.clientSecret,
          },
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded',
          },
        });

        span.setAttribute('auth.token_exchange_status', response.statusCode);

        if (response.statusCode === 200) {
          const tokenData = JSON.parse(response.body) as TokenResponse;
          span.setAttribute('auth.token_exchange_success', true);

          // Store new refresh token if provided
          if (tokenData.refresh_token) {
            await this.#storeOfflineTokenEncrypted(tokenData.refresh_token);
            span.setAttribute('auth.new_refresh_token_stored', true);
          }

          return tokenData;
        }

        span.setAttribute('auth.token_exchange_success', false);
        return null;
      } catch (error) {
        LoggedError.isTurtlesAllTheWayDownBaby(error, {
          log: true,
          message: 'Failed to exchange offline token',
          source: 'SystemTokenStore.exchangeOfflineToken',
        });
        return null;
      }
    });
  }

  /**
   * Cache token data with expiry information
   *
   * @private
   * @method #cacheTokenData
   * @description Stores token response data in memory cache with computed expiry
   * time. Applies a 1-minute safety buffer to prevent using tokens that are
   * about to expire during request processing.
   *
   * **Expiry Calculation:**
   * - Uses `expires_in` from token response (defaults to 3600 seconds if missing)
   * - Subtracts 60 seconds as safety buffer
   * - Computes absolute expiry time based on current time
   *
   * @param {TokenResponse} tokenResponse - The token response from Keycloak
   *
   * @example Token caching with expiry
   * ```typescript
   * const tokenResponse = {
   *   access_token: 'jwt-token',
   *   expires_in: 3600,
   *   refresh_token: 'refresh-jwt'
   * };
   * this.#cacheTokenData(tokenResponse);
   * // Token cached with expiry = now + 3540 seconds (60s buffer)
   * ```
   */
  #cacheTokenData(tokenResponse: TokenResponse): void {
    const expiresIn = tokenResponse.expires_in || 3600; // Default 1 hour
    const bufferSeconds = this.config.tokenExpiryBufferSeconds ?? 30;
    const expiry = new Date(Date.now() + (expiresIn - bufferSeconds) * 1000);

    this.cachedTokenData = {
      token: tokenResponse.access_token,
      expiry,
      refreshToken: tokenResponse.refresh_token,
    };
  }

  /**
   * Store offline token in encrypted Redis cache
   *
   * @private
   * @async
   * @method #storeOfflineTokenEncrypted
   * @description Encrypts and stores refresh tokens in Redis for future use.
   * Uses CryptoService for AES encryption to protect sensitive tokens in storage.
   * Sets a 30-day expiration to prevent indefinite accumulation of stale tokens.
   *
   * **Security Features:**
   * - AES encryption via CryptoService before storage
   * - Automatic expiration after 30 days
   * - Error handling to prevent storage failures from breaking authentication
   *
   * @param {string} token - The refresh token to encrypt and store
   * @returns {Promise<void>} Promise that resolves when storage is complete
   *
   * @example Secure token storage
   * ```typescript
   * await this.#storeOfflineTokenEncrypted(refreshToken);
   * // Token encrypted with AES and stored in Redis for 30 days
   * ```
   */
  async #storeOfflineTokenEncrypted(token: string): Promise<void> {
    try {
      const cacheKey = this.#getOfflineTokenCacheKey();
      if (!cacheKey) return;

      const crypto = this.#getCrypto();
      const encryptedToken = await crypto.encrypt(token);

      const redis = await getRedisClient();
      // Store for configured TTL days
      const ttlSeconds = (this.config.redisTokenTtlDays ?? 30) * 24 * 60 * 60;
      await redis.setEx(cacheKey, ttlSeconds, encryptedToken);
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        message: 'Failed to store offline token',
        source: 'SystemTokenStore.storeOfflineTokenEncrypted',
      });
    }
  }

  /**
   * Get Redis cache key for offline token
   *
   * @private
   * @method #getOfflineTokenCacheKey
   * @description Generates a unique Redis key for storing offline tokens.
   * Uses realm, client ID, and system identifier to ensure key uniqueness
   * across different Keycloak configurations and prevent token conflicts.
   *
   * **Key Format:** `keycloak:offline_token:{realm}:{clientId}:system`
   *
   * @returns {string|null} Cache key string or null if key generation fails
   *
   * @example Key generation
   * ```typescript
   * const key = this.#getOfflineTokenCacheKey();
   * // Returns: 'keycloak:offline_token:master:admin-cli:system'
   * ```
   */
  #getOfflineTokenCacheKey(): string | null {
    try {
      return `keycloak:offline_token:${this.config.realm}:${this.config.clientId}:system`;
    } catch {
      return null;
    }
  }

  /**
   * Get crypto service instance with lazy initialization
   *
   * @private
   * @method #getCrypto
   * @description Returns the CryptoService instance for token encryption/decryption.
   * Uses lazy initialization to avoid unnecessary crypto setup overhead when
   * encryption is not needed (e.g., when using cached tokens).
   *
   * @returns {CryptoService} Initialized CryptoService instance for token encryption
   */
  #getCrypto(): CryptoService {
    if (!this.crypto) {
      this.crypto = new CryptoService();
    }
    return this.crypto;
  }

  /**
   * Get current circuit breaker state for monitoring
   *
   * @method getCircuitBreakerState
   * @description Returns the current state of the circuit breaker for monitoring
   * and debugging purposes. Useful for health checks and observability.
   *
   * @returns {string} Current circuit breaker state: 'CLOSED', 'OPEN', or 'HALF_OPEN'
   */
  getCircuitBreakerState(): string {
    return this.circuitBreaker.getState();
  }

  /**
   * Check if rate limiting is currently active for credentials authentication
   *
   * @method isRateLimited
   * @description Checks if the current credentials are rate limited without
   * making an actual authentication attempt. Useful for preemptive checks.
   *
   * @returns {boolean} True if credentials authentication is currently rate limited
   */
  isRateLimited(): boolean {
    if (!this.config.impersonatorUsername) {
      return false;
    }
    const rateLimitKey = `credentials:${this.config.realm}:${this.config.impersonatorUsername}`;
    return !this.rateLimiter.canAttempt(rateLimitKey);
  }

  /**
   * Enhanced Keycloak login form parser using proper HTML parsing
   *
   * @private
   * @method #parseLoginForm
   * @description Parses HTML login forms from Keycloak to extract form action URL,
   * hidden fields, and detect the presence of username/password input fields.
   * Uses node-html-parser for secure and reliable HTML parsing instead of regex.
   *
   * **Security Improvements:**
   * - Proper HTML parsing prevents injection vulnerabilities
   * - Input sanitization for all extracted values
   * - Safe URL construction with validation
   *
   * @param {string} html - Raw HTML content containing the login form
   * @returns {Object|null} Parsed form data or null if parsing fails
   */
  #parseLoginForm(html: string): {
    action: string;
    fields: Record<string, string>;
    hasUsername: boolean;
    hasPassword: boolean;
  } | null {
    try {
      // Parse HTML safely using node-html-parser
      const document = parseHtml(html);
      const form = document.querySelector('form');

      if (!form) {
        return null;
      }

      // Extract and validate form action
      const actionRaw = form.getAttribute('action');
      if (!actionRaw) {
        return null;
      }

      // Sanitize and resolve action URL
      const action = this.#resolveFormAction(actionRaw.trim());
      if (!action) {
        return null;
      }

      // Extract all input fields safely
      const inputs = form.querySelectorAll('input');
      const fields: Record<string, string> = {};
      let hasUsername = false;
      let hasPassword = false;

      for (const input of inputs) {
        const name = input.getAttribute('name')?.trim();
        const value = input.getAttribute('value')?.trim() || '';
        const type = input.getAttribute('type')?.toLowerCase().trim() || 'text';
        const id = input.getAttribute('id')?.toLowerCase().trim() || '';

        // Sanitize field name and value
        if (name && this.#isValidFieldName(name)) {
          fields[name] = this.#sanitizeFieldValue(value);
        }

        // Detect password field
        if (type === 'password' || name === 'password' || id === 'password') {
          hasPassword = true;
        }

        // Detect username field
        if (
          name === 'username' ||
          id === 'username' ||
          (type === 'text' &&
            (name === 'email' || id === 'kc-attempted-username'))
        ) {
          hasUsername = true;
        }
      }

      return { action, fields, hasUsername, hasPassword };
    } catch (error) {
      LoggedError.isTurtlesAllTheWayDownBaby(error, {
        log: true,
        message: 'Failed to parse login form HTML',
        source: 'SystemTokenStore.parseLoginForm',
      });
      return null;
    }
  }

  /**
   * Resolve and validate form action URL
   *
   * @private
   * @method #resolveFormAction
   * @param {string} actionRaw - Raw action attribute value
   * @returns {string|null} Resolved absolute URL or null if invalid
   */
  #resolveFormAction(actionRaw: string): string | null {
    try {
      // Handle relative URLs by resolving against issuer
      const resolvedUrl = new URL(actionRaw, this.config.issuer);

      // Validate that the resolved URL uses a secure protocol
      if (!['http:', 'https:'].includes(resolvedUrl.protocol)) {
        return null;
      }

      return resolvedUrl.toString();
    } catch {
      return null;
    }
  }

  /**
   * Validate field name to prevent injection
   *
   * @private
   * @method #isValidFieldName
   * @param {string} name - Field name to validate
   * @returns {boolean} True if field name is safe
   */
  #isValidFieldName(name: string): boolean {
    // Allow alphanumeric, underscore, dash, and dot
    return /^[a-zA-Z0-9_.-]+$/.test(name) && name.length <= 100;
  }

  /**
   * Sanitize field value to prevent injection
   *
   * @private
   * @method #sanitizeFieldValue
   * @param {string} value - Field value to sanitize
   * @returns {string} Sanitized field value
   */
  #sanitizeFieldValue(value: string): string {
    // Remove any potentially dangerous characters and limit length
    return value.replace(/[<>"'&]/g, '').substring(0, 1000);
  }
}

import { env } from '@/lib/site-util/env';
import type { AdminTokenConfig } from './impersonation.types';
import { log } from '@/lib/logger';

/**
 * Extracts Keycloak admin configuration from issuer URL
 *
 * @function adminBaseFromIssuer
 * @description Parses a Keycloak issuer URL to extract the realm name and construct
 * the corresponding admin base URL. Handles URL decoding and validates the expected
 * Keycloak URL structure with '/realms/{realm-name}' path.
 *
 * @param {string} issuer - The Keycloak issuer URL (e.g., 'https://auth.example.com/realms/master')
 * @returns {Object|undefined} Configuration object with origin, realm, and adminBase, or undefined if parsing fails
 * @returns {string} returns.origin - The base origin URL (e.g., 'https://auth.example.com')
 * @returns {string} returns.realm - The extracted realm name (e.g., 'master')
 * @returns {string} returns.adminBase - The constructed admin base URL (e.g., 'https://auth.example.com/admin/realms/master')
 *
 * @example
 * ```typescript
 * const config = adminBaseFromIssuer('https://auth.example.com/realms/my-realm');
 * // Returns: {
 * //   origin: 'https://auth.example.com',
 * //   realm: 'my-realm',
 * //   adminBase: 'https://auth.example.com/admin/realms/my-realm'
 * // }
 * ```
 *
 * @example
 * ```typescript
 * const invalid = adminBaseFromIssuer('invalid-url');
 * // Returns: undefined
 * ```
 */
export const adminBaseFromIssuer = (
  issuer: string,
): { origin: string; realm: string; adminBase: string } | undefined => {
  /**
   * Extracts realm name from Keycloak issuer URL path
   *
   * @function extractRealmFromIssuer
   * @description Internal helper that parses the URL pathname to find the realm
   * name following the '/realms/' path segment. Handles URL decoding for realm
   * names that may contain special characters.
   *
   * @param {string} issuer - The Keycloak issuer URL to parse
   * @returns {string|undefined} The decoded realm name, or undefined if not found
   *
   * @example
   * ```typescript
   * const realm = extractRealmFromIssuer('https://auth.example.com/realms/my%2Drealm');
   * // Returns: 'my-realm'
   * ```
   */
  const extractRealmFromIssuer = (issuer: string): string | undefined => {
    try {
      const u = new URL(issuer);
      const parts = u.pathname.split('/').filter(Boolean);
      const idx = parts.findIndex((p) => p === 'realms');
      if (idx >= 0 && parts[idx + 1]) return decodeURIComponent(parts[idx + 1]);
      return undefined;
    } catch {
      return undefined;
    }
  };
  try {
    const u = new URL(issuer);
    const realm = extractRealmFromIssuer(issuer);
    if (!realm) return undefined;
    return {
      origin: u.origin,
      realm,
      adminBase: `${u.origin}/admin/realms/${encodeURIComponent(realm)}`,
    };
  } catch {
    return undefined;
  }
};
/**
 * Creates AdminTokenConfig from environment variables
 *
 * @function defaultConfigFromEnv
 * @description Constructs a complete AdminTokenConfig object by reading values
 * from environment variables. Validates that all required configuration is present
 * and extracts realm/adminBase from the issuer URL.
 *
 * @returns {AdminTokenConfig} Complete configuration object for admin token acquisition
 * @throws {TypeError} When required environment variables are missing or issuer URL is invalid
 *
 * @example
 * ```typescript
 * // Environment variables:
 * // AUTH_KEYCLOAK_ISSUER=https://auth.example.com/realms/master
 * // AUTH_KEYCLOAK_CLIENT_ID=admin-cli
 * // AUTH_KEYCLOAK_CLIENT_SECRET=secret
 * // AUTH_KEYCLOAK_REDIRECT_URI=https://app.example.com/callback
 *
 * const config = defaultConfigFromEnv();
 * // Returns complete AdminTokenConfig with realm and adminBase populated
 * ```
 *
 * @see {@link AdminTokenConfig} for complete configuration structure
 */
export const defaultConfigFromEnv = (): AdminTokenConfig => {
  const config = {
    issuer: env('AUTH_KEYCLOAK_ISSUER') || '',
    clientId: env('AUTH_KEYCLOAK_CLIENT_ID') || '',
    clientSecret: env('AUTH_KEYCLOAK_CLIENT_SECRET') || '',
    redirectUri: env('AUTH_KEYCLOAK_REDIRECT_URI') || '',
    impersonatorUsername:
      env('AUTH_KEYCLOAK_IMPERSONATOR_USERNAME') || undefined,
    impersonatorPassword:
      env('AUTH_KEYCLOAK_IMPERSONATOR_PASSWORD') || undefined,
    impersonatorOfflineToken:
      env('AUTH_KEYCLOAK_IMPERSONATOR_OFFLINE_TOKEN') || undefined,
  };

  // Basic validation - detailed validation happens in constructor
  if (
    !config.issuer ||
    !config.clientId ||
    !config.clientSecret ||
    !config.redirectUri
  ) {
    log((l) =>
      l.warn('SystemTokenStore: incomplete environment configuration'),
    );
    throw new TypeError(
      'SystemTokenStore: Required environment variables are missing',
    );
  }

  const { realm, adminBase } = adminBaseFromIssuer(config.issuer) || {};
  if (!realm || !adminBase) {
    log((l) =>
      l.warn(
        'SystemTokenStore: Unable to extract realm or admin base from issuer',
      ),
    );
    throw new TypeError('SystemTokenStore: Invalid issuer URL format');
  }
  return {
    ...config,
    adminBase,
    realm,
  };
};

import { JWT } from 'next-auth/jwt';
import { NextAuthUserWithAccountId } from './types';
import { AdapterUser } from '@auth/core/adapters';

/**
 * JWT callback used by NextAuth to shape the JWT sent to the client and stored
 * in cookies. This module contains a single exported `jwt` function which acts
 * as the NextAuth `jwt` callback. It is responsible for ensuring our custom
 * application fields (notably `account_id`) are copied from the authenticated
 * user object into the JWT so they are available in subsequent requests.
 *
 * Rationale:
 * - NextAuth executes this callback whenever a session is created or updated.
 * - The `token` parameter is the current JWT payload and will be returned
 *   (possibly mutated) to be serialized into the cookie.
 * - The `user` parameter is provided only on sign-in (or when the provider
 *   returns a user object). We use it to copy app-specific fields into `token`.
 *
 * Security: Only copy values that are safe to send to the client. `account_id`
 * is considered safe application metadata (non-secret numeric id) but avoid
 * copying sensitive information into the JWT.
 */
export const jwt = async ({
  token,
  user,
}: {
  /**
   * The JWT payload (mutable). Fields added here are serialized and sent to
   * the client. Use `token` to read or write values persisted in the session
   * cookie.
   */
  token: JWT;
  /**
   * The user object returned by the authentication provider during sign-in.
   * - When present, it can be an application-extended `NextAuthUserWithAccountId`
   *   (our credentials provider sets `account_id`) or a generic `AdapterUser`.
   * - It is `undefined` for subsequent callback invocations where only the
   *   token is being refreshed/read.
   */
  user?: NextAuthUserWithAccountId | AdapterUser | null;
}) => {
  // When a user is present (typically during sign-in), copy canonical ids
  // and any application-specific metadata we want available in the JWT.
  if (user) {
    // Canonical NextAuth id - always copy this so token consumers can identify
    // the authenticated principal without needing to fetch user records.
    token.id = user.id;

    // Our CredentialsProvider sets `account_id` on the user when available.
    // We only copy it when the property exists and is truthy to avoid
    // introducing `undefined`/`null` values into the token shape.
    //
    // Note: `account_id` is treated as non-sensitive application metadata.
    if ('account_id' in user && !!user.account_id) {
      token.account_id = user.account_id;
    }
  }

  // Always return the (possibly mutated) token. NextAuth will serialize this
  // value and include it in the session cookie / client-side JWT.
  return token;
};

import KeycloakAdminClient from '@keycloak/keycloak-admin-client';
import { ConnectionConfig } from '@keycloak/keycloak-admin-client/lib/client';

/**
 * Create a configured Keycloak Admin client.
 *
 * This factory wraps the KeycloakAdminClient constructor and provides a single
 * place to create clients in the codebase. The passed `config` object is
 * forwarded directly to the upstream Keycloak admin client library.
 *
 * @param {ConnectionConfig} config - Connection settings for the Keycloak Admin client.
 * @returns {KeycloakAdminClient} An initialized Keycloak admin client instance.
 *
 * @example
 * ```ts
 * import { keycloakAdminClientFactory } from '@/lib/auth/keycloak-factories';
 * const client = keycloakAdminClientFactory({ baseUrl: 'https://auth', realmName: 'master' });
 * ```
 */
export const keycloakAdminClientFactory = (
  config: ConnectionConfig,
): KeycloakAdminClient => new KeycloakAdminClient(config);

import { env } from '@/lib/site-util/env';

export const redirect = ({
  url,
  baseUrl,
}: {
  /** URL provided as callback URL by the client */
  url: string;
  /** Default base URL of site (can be used as fallback) */
  baseUrl: string;
}): Promise<string> => {
  const providedUrl = new URL(url, baseUrl);
  // Only allow relative paths or same-origin URLs to prevent open redirect vulnerabilities
  return Promise.resolve(
    new URL(providedUrl.pathname, env('NEXT_PUBLIC_HOSTNAME')).toString(),
  );
};

/**
 * Module: lib/auth/sign-in.ts
 *
 * Responsibilities:
 * - Persist OAuth provider tokens to the local accounts table when available.
 * - Emit local telemetry and a lightweight Application Insights `signIn` event
 *   without including any sensitive tokens or secrets.
 *
 * Notes on telemetry and privacy:
 * - The AppInsights event uses only non-sensitive, minimal properties (provider
 *   and a truncated providerAccountId) to allow basic attribution without
 *   capturing tokens, refresh tokens, or full account identifiers.
 * - All write operations to the database are limited to the `accounts` table
 *   and only store token-like fields when explicitly present on the OAuth
 *   provider account object.
 */

import { Account, Awaitable, Profile, User } from '@auth/core/types';
import { drizDbWithInit, schema } from '@/lib/drizzle-db';
import { and, eq } from 'drizzle-orm';
import { log, logEvent } from '@/lib/logger';
import { getAppInsights } from '@/instrument/browser';
import { CredentialInput } from '@auth/core/providers';
import { AdapterUser } from '@auth/core/adapters';

/**
 * Persist token fields for an external OAuth account to the local `accounts`
 * table. This function will only write fields that are present on the
 * provider account object; no additional normalization is performed here.
 *
 * Contract / Inputs:
 * - account: the provider Account object returned by next-auth (or a similar
 *   shape). Only `id_token`, `access_token`, and `refresh_token` are
 *   considered for persistence.
 *
 * Outputs / Side-effects:
 * - Updates the `accounts` row matching the provider + providerAccountId with
 *   the token fields (idToken, accessToken, refreshToken) when present.
 *
 * Error modes:
 * - Any database error will propagate to the caller. Caller should ensure
 *   failures here do not break the sign-in flow (see usage below).
 *
 * @param params.account - OAuth provider account object (may include tokens)
 * @param params.user - User object (not used currently but kept for future use)
 */
const updateAccountTokens = async ({
  account,
}: {
  user: User | AdapterUser;
  account: Account | Record<string, unknown>;
}) => {
  const { accounts } = schema;
  const fields: Record<string, string> = {};
  if (account.id_token) {
    fields.idToken = String(account.id_token);
  }
  if (account.access_token) {
    fields.accessToken = String(account.access_token);
  }
  if (account.refresh_token) {
    fields.refreshToken = String(account.refresh_token);
  }
  if (Object.keys(fields).length === 0) {
    return;
  }
  await drizDbWithInit((db) =>
    db
      .update(accounts)
      .set(fields)
      .where(
        and(
          eq(accounts.provider, String(account.provider)),
          eq(accounts.providerAccountId, String(account.providerAccountId)),
        ),
      ),
  );
};

/**
 * NextAuth `signIn` callback implementation.
 *
 * This callback is intended to be used as the `callbacks.signIn` handler for
 * NextAuth. It performs two responsibilities:
 *  1. Persist new OAuth tokens for supported providers (currently `keycloak`)
 *     into the local `accounts` table via `updateAccountTokens`.
 *  2. Emit a lightweight telemetry event locally (`logEvent('signIn')`) and
 *     to Application Insights when available. The AppInsights event uses only
 *     minimal, non-sensitive properties (provider and a truncated
 *     providerAccountId).
 *
 * Success criteria / Return:
 * - Must return `true` (allow sign-in) or a redirect URL (string) in case
 *   the application wants to redirect the user. This implementation always
 *   returns `true` to allow sign-in to proceed.
 *
 * Edge cases:
 * - If `account` is missing or the provider is not one of the handled
 *   providers, no token persistence is attempted. AppInsights failures are
 *   caught and logged but do not block sign-in.
 *
 * @param params.user - The user object returned/created by the adapter or
 *                     OAuth provider.
 * @param params.account - Provider account object (may be null during some
 *                        flows). When present, used to persist tokens.
 * @param params.profile - Optional OAuth profile provided by the provider.
 * @param params.email - Optional email verification metadata for email
 *                      provider flows.
 *
 * @returns Awaitable<boolean|string> - `true` to allow sign-in, or a string
 *                                      URL to redirect to.
 */
export const signIn: (params: {
  user: User | AdapterUser;
  account?: Account | null;
  /**
   * If OAuth provider is used, it contains the full
   * OAuth profile returned by your provider.
   */
  profile?: Profile;
  /**
   * If Email provider is used, on the first call, it contains a
   * `verificationRequest: true` property to indicate it is being triggered in the verification request flow.
   * When the callback is invoked after a user has clicked on a sign in link,
   * this property will not be present. You can check for the `verificationRequest` property
   * to avoid sending emails to addresses or domains on a blocklist or to only explicitly generate them
   * for email address in an allow list.
   */
  email?: {
    verificationRequest?: boolean;
  };
  /** If Credentials provider is used, it contains the user credentials */
  credentials?: Record<string, CredentialInput>;
}) => Awaitable<boolean | string> = async (
  {
    user,
    account,
  }:
    | {
        user: User | AdapterUser;
        account?: Account | Record<string, unknown> | null;
      }
    | undefined = {
    user: undefined as unknown as User | AdapterUser,
    account: undefined,
  },
): Promise<boolean | string> => {
  if (account && account.providerAccountId) {
    switch (account.provider) {
      case 'keycloak':
        // Persist tokens for Keycloak. We intentionally do not
        // await here to avoid delaying the sign-in flow; failures will
        // propagate if required but we don't want telemetry to block UX.
        updateAccountTokens({ user, account });
        break;
      default:
        log((l) =>
          l.error(`Unhandled provider ${account?.provider} in signIn`),
        );
        break;
    }
  }

  // Log local telemetry and report a lightweight event to AppInsights when available
  logEvent('signIn');
  try {
    const appInsights = getAppInsights();
    if (appInsights && typeof appInsights.trackEvent === 'function') {
      // Use minimal properties  avoid logging tokens or sensitive data
      appInsights.trackEvent(
        { name: 'signIn' },
        {
          provider: account?.provider ?? 'unknown',
          providerAccountId: account?.providerAccountId
            ? String(account.providerAccountId).slice(0, 8)
            : undefined,
        },
      );
    }
  } catch (err) {
    // Keep sign-in resilient: log and continue
    log((l) => l.debug('signIn: AppInsights trackEvent failed', err));
  }
  return true;
};

import { notCryptoSafeKeyHash } from '@/lib/ai/core/chat-ids';
import {
  QueryClient,
  useQueryClient,
  experimental_streamedQuery as streamedQuery,
} from '@tanstack/react-query';
import { log } from '@/lib/logger';
import { useCallback } from 'react';
import { env } from '@/lib/site-util/env';
import { fetch } from '@/lib/nextjs-util/fetch';

/**
 * Creates a simple fetch wrapper with basic retry logic and error handling
 * for use with AI SDK's useChat hook.
 */
export const useChatFetchWrapper = (): {
  chatFetch: typeof globalThis.fetch;
  queryClient: QueryClient;
} => {
  const queryClient = useQueryClient();
  if (!queryClient) {
    throw new Error(
      'QueryClient is not available. Ensure you are using this in a QueryClientProvider context.',
    );
  }

  const computeKey = async (
    input: RequestInfo | URL,
    init?: RequestInit,
  ): Promise<[URL, string]> => {
    const hashBody = async <TBody>(body: TBody): Promise<[string, TBody]> => {
      if (typeof body === 'string') {
        return [notCryptoSafeKeyHash(body), body];
      }
      if (body instanceof URLSearchParams) {
        return [notCryptoSafeKeyHash(body.toString()), body];
      }
      if (body instanceof Blob) {
        // Reading a Blob as text/arrayBuffer is possible, but would consume it.
        // To avoid breaking the request, clone the Blob if needed, or use a placeholder.
        // If you must hash the contents, you could read it here, but then you'd need to replace it in `init.body`.
        // Example (not recommended for large blobs):
        const blobText = await body.text();
        const requestHash = notCryptoSafeKeyHash(blobText);
        // Replace the body with a string so we can re-use it
        return [requestHash, blobText as TBody];
        /*
        return [
          requestHash,
          new Blob([blobText], { type: body.type }) as TBody,
        ];
        */
      }
      log((l) => l.warn('Unsupported body type for hashing:', body));
      return ['', body];
    };

    // Safely extract the body for hashing without consuming or altering the original request
    let requestHash: string = '';
    if (init && init.body) {
      const [hash, body] = await hashBody(init.body);
      init.body = body; // Restore the body to avoid passing a consumed stream.
      requestHash = hash;
    }
    if (requestHash == '' && input instanceof Request) {
      const [hash] = await hashBody(input.body);
      requestHash = hash;
    }
    if (requestHash === '') {
      throw new Error('No body to hash for request key');
    }
    // TODO: Would be nice to pull the last message - or part of it - for display purposes
    const url = new URL(
      input instanceof Request ? input.url : input.toString(),
      new URL(env('NEXT_PUBLIC_HOSTNAME')),
    );
    return [url, requestHash];
  };

  /**
   * Fetches a chat stream from the server.
   * @param {Object} params - Parameters for the fetch.
   * @param {URL | Request} params.req - The request URL or Request object.
   * @param {RequestInit} [params.init] - Optional request initialization options.
   * @returns {AsyncGenerator<string>} An async generator yielding chunks of the chat stream.
   */

  async function* fetchChatStream({
    req,
    init,
  }: {
    req: URL | RequestInfo;
    init?: RequestInit;
  }) {
    const response = await fetch(req, init);
    if (!response.ok) {
      throw new Error(`Network response was not ok: ${response.statusText}`, {
        cause: response,
      });
    }
    const reader = response.body?.getReader();
    if (!reader) {
      throw new Error('No readable stream');
    }
    while (true) {
      const { done, value } = await reader.read();
      if (done) break;
      yield value;
      if (init?.signal?.aborted) {
        await reader.cancel();
        return;
      }
    }
  }

  const chatFetch = useCallback(
    async (input: URL | RequestInfo, init?: RequestInit) => {
      const stream = queryClient.fetchQuery({
        queryKey: await computeKey(input, init),
        queryFn: streamedQuery({
          streamFn: ({ signal }: { signal?: AbortSignal }) => {
            if (!init) {
              init = {};
            }
            // Merge the provided signal and any signal in init into a single AbortController
            let mergedSignal = signal ?? init.signal;
            if (init.signal && init.signal !== mergedSignal) {
              const controller = new AbortController();
              const abort = () => controller.abort();
              if (signal) {
                signal.addEventListener('abort', abort);
              }
              if (init.signal) {
                init.signal.addEventListener('abort', abort);
              }
              mergedSignal = init.signal;
              init.signal = controller.signal;
            }
            return fetchChatStream({ req: input, init });
          },
        }),
      });

