      });
      return new Response(readableStream, {
        headers: {
          'Content-Type': 'text/plain',
          'Transfer-Encoding': 'chunked',
        },
      });
    },
    [queryClient],
  );
  return {
    chatFetch,
    queryClient,
  };
};

