          if (
            !current ||
            current.page !== page ||
            current.pageSize !== pageSize ||
            JSON.stringify(current.sortModel) !== JSON.stringify(sortModel) ||
            JSON.stringify(current.filterModel) !== JSON.stringify(filterModel)
          ) {
            return {
              page,
              pageSize,
              sortModel,
              filterModel,
            };
          }
          return current;
        });

        const queryKey = createQueryKey(
          String(url),
          page,
          pageSize,
          sortModel,
          filterModel,
        );

        return new Promise<GridGetRowsResponse>((resolve, reject) => {
          const timeout = setTimeout(() => {
            try {
              if (isSuccess) {
                log((l) =>
                  l.warn(
                    'getRows::query timed out - resolving with last known good data to prevent promise memory leak',
                    'queryKey',
                    queryKey,
                    'url',
                    url,
                  ),
                );
                resolve(data);
              }

              log((l) =>
                l.warn(
                  'getRows::query timed out - rejecting promise',
                  'queryKey',
                  queryKey,
                  'url',
                  url,
                ),
              );
              // Reject the promise if it takes too long
              reject(
                new Error(
                  `Query for ${queryKey.join(', ')} timed out after 30 seconds`,
                ),
              );
            } catch (err) {
              log((l) =>
                l.warn(
                  'Unexpected error caught in getRows proxy resolution.',
                  err,
                ),
              );
            }
          }, 60 * 1000); // 60 seconds timeout
          const wrapCleanup = <X, Y>(cb: (y: X) => Y): ((y: X) => Y) => {
            return (arg: X) => {
              clearTimeout(timeout);
              return cb(arg);
            };
          };
          pendingQueries.current.push([
            wrapCleanup(resolve),
            wrapCleanup(reject),
          ]);
        });
      } catch (err: unknown) {
        log((l) =>
          l.verbose(
            'getRows::error occurred - rethrowing to react query for disposition',
            err,
          ),
        );
        throw err;
      }
    },
    [url, isSuccess, data, setCurrentQueryParams],
  );

  // Memoize the data source object to prevent unnecessary re-renders
  return useMemo<ExtendedGridDataSource>(() => {
    return {
      getRows,
      updateRow,
      onDataSourceError,
      isLoading: /*!hasMounted || */ isLoading || updateRowMutation.isPending,
      loadError: queryError ? queryError.message : null,
    };
  }, [
    getRows,
    updateRow,
    onDataSourceError,
    // hasMounted,
    isLoading,
    updateRowMutation.isPending,
    queryError,
  ]);
};

import type {
  GridRowId,
  GridRowIdGetter,
  GridRowModel,
  GridValidRowModel,
} from '@mui/x-data-grid-pro';

/**
 * Application-global map of grid id getters from fiven fields
 */
const GridRowGetterDictionary = new Map<string, GridRowIdGetter>();

/**
 * Returns a memoized row ID getter function for a specified field.
 *
 * This hook retrieves a function that extracts the row ID from a given row object
 * using the provided field name. The getter function is cached for each unique field
 * to avoid unnecessary re-creation and improve performance.
 *
 * @param field - The name of the field in the row object to use as the row ID.
 * @returns A function that takes a row and returns its ID based on the specified field.
 * @throws If the row does not contain the required field.
 */
export const useGetRowId = (
  field: string,
): GridRowIdGetter<GridValidRowModel> => {
  let ret = GridRowGetterDictionary.get(field);
  if (!ret) {
    ret = (row: GridRowModel): GridRowId => {
      const rowId = row?.[field] as string;
      if (!rowId) {
        throw new Error(`Row is missing required field: ${field}`);
      }
      return rowId as GridRowId;
    };
    // Cache the getter for future use
    // This allows us to avoid creating a new function every time this hook is called
    // and instead re-uses the same function instance for a given field name.
    GridRowGetterDictionary.set(field, ret);
  }
  return ret;
};

/**
 * Webpack plugin: StripRscPrefixPlugin
 * --------------------------------------------------------------
 * Purpose
 *   Next.js (React Server Components / SSR) sometimes emits source maps whose
 *   `sources` entries are prefixed with synthetic roots like:
 *     /(rsc)/./lib/ai/middleware/chat-history/import-incoming-message.ts
 *     /(rsc)/lib/ai/... or (rsc)/./lib/...
 *     /(ssr)/... (observed for certain server bundles)
 *   These virtual path segments break debugger resolution (VS Code / Chrome)
 *   because no real directory named `(rsc)` or `(ssr)` exists inside the
 *   workspace. The result is: "Source not found" when setting breakpoints.
 *
 * What this plugin does
 *   - Runs during the `PROCESS_ASSETS_STAGE_DEV_TOOLING` stage.
 *   - Scans every emitted `*.map` asset.
 *   - If any source path contains `(rsc)` or `(ssr)` at its start (optionally
 *     preceded by a slash) with optional `./` segments, it rewrites the prefix
 *     to a single leading slash `/` so the remainder of the path becomes
 *     relative to the project root (which standard sourceMapPathOverrides can
 *     then map to the workspace).
 *
 * Regex logic (see `normalize`):
 *     ^\/?\((?:rsc|ssr)\)\/(?:\.\/*)?
 *   Breakdown:
 *     ^            : start of string
 *     \/?          : optional leading slash
 *     \(           : literal '('
 *     (?:rsc|ssr)  : either 'rsc' or 'ssr'
 *     \)           : literal ')'
 *     \/           : following slash
 *     (?:\.\/*)?  : zero or one sequence of "./" possibly with extra slashes
 *
 * Why fix the grouping
 *   The previous pattern `^\/?\(rsc|ssr\)\/` incorrectly alternated between
 *   the literal substring "(rsc" and "ssr)" (due to the placement of `|`),
 *   so it would *never* match fully-formed prefixes `(rsc)/` or `(ssr)/`.
 *   This patch corrects the alternation so both markers normalize properly.
 *
 * Performance considerations
 *   - Operates only on `.map` assets.
 *   - Parses JSON once per map containing the tokens; unaffected maps are
 *     skipped quickly via `includes` guard.
 *   - Complexity is O(n) in number of maps + O(m) in number of sources entries.
 *
 * Safety / Failure modes
 *   - Malformed JSON source maps are ignored (best-effort approach).
 *   - Non-matching sources remain untouched.
 *   - If Next.js changes its virtual prefix format, extend the regex.
 *
 * Integration (Next.js `next.config.{js,ts}`):
 *   import { StripRscPrefixPlugin } from './lib/config/strip-rsc-prefix-plugin';
 *   export default {
 *     webpack(config) {
 *       config.plugins.push(StripRscPrefixPlugin);
 *       return config;
 *     }
 *   };
 *
 * Testing suggestions
 *   - Build (or dev) with a known RSC map, then grep the resulting `.map` for
 *     `"/(rsc)/"` before and after enabling the plugin.
 *   - Add a Jest snapshot test that feeds a small fabricated source map object
 *     through the `normalize` logic (extracted if you want stronger tests).
 */
import type { NextConfig } from 'next';
import type { WebpackCompilation, WebpackPlugin, WebpackCompiler, RawSourceClassProps, WebpackAsset } from "./types";

/**
 * Webpack plugin object normalizing nextjs source maps.
 * @public
 */
export const StripRscPrefixPlugin: WebpackPlugin = {
  apply(compiler: WebpackCompiler) {
    compiler.hooks.compilation.tap(
      'StripRscPrefixPlugin',
      (compilation: WebpackCompilation) => {
        compilation.hooks.processAssets.tap(
          {
            name: 'StripRscPrefixPlugin',
            stage:
              compiler.webpack.Compilation.PROCESS_ASSETS_STAGE_DEV_TOOLING,
          },
          (assets: Record<PropertyKey, RawSourceClassProps | WebpackAsset>) => {
            for (const [name, asset] of Object.entries(assets)) {
              if (!name.endsWith('.map')) continue;
              const raw = String(
                typeof asset.source === 'function'
                  ? asset.source()
                  : asset.source,
              );
              if (!raw.includes('(rsc)') && !raw.includes('(ssr)')) continue;
              try {
                const map = JSON.parse(raw);

                // Normalize any leading (rsc)/, /(rsc)/, (rsc)/./, /(rsc)/./
                // Pattern explanation:
                // ^         -> start
                // /?        -> optional leading slash
                // \(rsc\)/  -> literal (rsc)/
                // (?:\./)*  -> optional one or more './'
                /**
                 * Normalize a single source path by removing a leading synthetic
                 * `(rsc)` / `(ssr)` prefix (optionally with leading slash and `./`),
                 * collapsing it to a single root slash.
                 * @param s Raw source path value from a source map.
                 * @returns Normalized path (unchanged if no synthetic prefix).
                 */
                const normalize = (s: string): string => {
                  if (!s.includes('(rsc)') && !s.includes('(ssr)')) return s;
                  // Correct grouping: match "(rsc)" or "(ssr)" token at start.
                  let out = s.replace(/^\/?\((?:rsc|ssr)\)\/(?:\.\/*)?/, '/');
                  // Guard against accidental doubled slashes after replacement.
                  out = out.replace(/^\/+/, '/');
                  return out;
                };

                map.sources = map.sources.map((s: string) => normalize(s));

                compilation.updateAsset(
                  name,
                  new compiler.webpack.sources.RawSource(JSON.stringify(map)),
                );
              } catch {
                // ignore malformed map
              }
            }
          },
        );
      },
    );
  },
};

export const withStripRscPrefixPlugin = <
  TArg extends NextConfig
>(
  nextConfig: TArg
): TArg => {
    const originalWebpack = nextConfig.webpack;
    return {
      ...nextConfig,
      webpack: ((config, args) => {
        config = originalWebpack?.(config, args) ?? config;
        config.plugins.push(StripRscPrefixPlugin);
        return config;
      }) as NextConfig['webpack'],
    } as TArg;
};

import { drizzle } from 'drizzle-orm/postgres-js';
import schema, { DbDatabaseType } from './schema';
import { isPromise } from '@/lib/typescript/_guards';
import { LoggedError } from '../react-util';

/**
 * Drizzle DB connection helpers
 *
 * This module lazily initializes a Drizzle ORM `DbDatabaseType` instance using
 * a pg driver provided by `../neondb/connection`. The initialized instance is
 * cached on `globalThis.__obapps` so it survives module reloads and is shared
 * across the process. Two convenience entry points are provided:
 *
 * - `drizDbWithInit()`  async initializer that will wait for initialization
 *   and optionally run a callback with the initialized db.
 * - `drizDb()`  synchronous accessor that returns the initialized db or
 *   throws when initialization is in progress. It also accepts a callback
 *   which is invoked with the db when available.
 */

export { schema };

/**
 * Webpack-safe singleton keys using Symbol.for() to ensure uniqueness across all chunks.
 * This approach prevents module duplication issues where different webpack bundles
 * would otherwise create separate singleton instances.
 */
const DB_INSTANCE_KEY = Symbol.for('@noeducation/drizzle-db-instance');
const DB_PROMISE_KEY = Symbol.for('@noeducation/drizzle-db-promise');
const PG_DRIVER_KEY = Symbol.for('@noeducation/pg-driver-factory');

/**
 * Global registry interface using symbols for webpack-safe singleton access.
 * Symbols created with Symbol.for() are globally registered and shared across
 * all webpack chunks, preventing duplicate singleton instances.
 */
interface GlobalDbRegistry {
  [DB_INSTANCE_KEY]?: DbDatabaseType;
  [DB_PROMISE_KEY]?: Promise<DbDatabaseType>;
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  [PG_DRIVER_KEY]?: any;
}

/**
 * Accessor for the currently stored Drizzle DB initialization promise.
 * Returns undefined before initialization has occurred; otherwise resolves
 * to {@link drizDb}.
 */
const get_DrizDbPromise = (): Promise<DbDatabaseType> | undefined =>
  (globalThis as GlobalDbRegistry)[DB_PROMISE_KEY];

/**
 * Setter for the Drizzle DB initialization promise. Passing `undefined`
 * clears the stored promise.
 */
const set_DrizDbPromise = (
  db: Promise<DbDatabaseType> | undefined,
): Promise<DbDatabaseType> | undefined => {
  (globalThis as GlobalDbRegistry)[DB_PROMISE_KEY] = db;
  return db;
};

/**
 * Returns the initialized Drizzle DB instance if available, or undefined.
 */
const get_DrizDb = (): DbDatabaseType | undefined =>
  (globalThis as GlobalDbRegistry)[DB_INSTANCE_KEY];

interface DrizDbInitOverloads {
  /**
   * Wait for Drizzle DB initialization and return the db instance.
   */
  (): Promise<DbDatabaseType>;
  /**
   * Wait for initialization and invoke the provided callback with the db.
   * The callback may return a value or a promise; the return type is preserved.
   */
  <T>(
    then: (db: DbDatabaseType) => T,
  ): T extends Promise<infer R> ? Promise<R> : Promise<T>;
}

const getPostgresDriver = async () => {
  let connection = (globalThis as GlobalDbRegistry)[PG_DRIVER_KEY];
  if (!connection) {
    try {
      const postgresql = await import('../neondb/connection');
      connection = postgresql.pgDbWithInit;
      (globalThis as GlobalDbRegistry)[PG_DRIVER_KEY] = connection;
    } catch (error) {
      throw new Error('Failed to load postgresjs driver', {
        cause: error,
      });
    }
  }
  return connection();
};

/**
 * Ensure the Drizzle DB is initialized and optionally run a callback with the db.
 *
 * When called without a callback the function resolves to the initialized
 * `DbDatabaseType`. When called with a callback the callback will be invoked
 * once the db is available and its result is returned (promisified if needed).
 */
export const drizDbWithInit: DrizDbInitOverloads = <T>(
  cb?: (db: DbDatabaseType) => T,
) => {
  const resolver = async (db: DbDatabaseType) => {
    if (cb) {
      const fnRet = cb(db);
      const pRet = isPromise<T>(fnRet) ? await fnRet : fnRet;
      return pRet;
    }
    return Promise.resolve(db) as T;
  };
  const drizDb = get_DrizDb();
  if (!!drizDb) {
    return Promise.resolve(drizDb).then(resolver);
  }
  let drizDbPromise = get_DrizDbPromise();
  if (!drizDbPromise) {
    drizDbPromise = getPostgresDriver()
      // eslint-disable-next-line @typescript-eslint/no-explicit-any
      .then((sql: any) => {
        const value = drizzle(sql, {
          casing: 'snake_case',
          schema,
        });
        (globalThis as GlobalDbRegistry)[DB_INSTANCE_KEY] = value;
        return value;
      });
    if (drizDbPromise) {
      set_DrizDbPromise(drizDbPromise);
      drizDbPromise.catch((err) => {
        const le = LoggedError.isTurtlesAllTheWayDownBaby(err, {
          log: true,
          message: 'Error initializing Drizzle DB',
          extra: {
            cause: err,
          },
          source: 'drizDbWithInit',
        });
        // Reset stored promise on error so future callers can retry initialization
        set_DrizDbPromise(undefined);
        throw le;
      });
    } else {
      throw new Error(
        'Failed to initialize Drizzle DB - could not create dbPromise',
      );
    }
  }
  return drizDbPromise.then(resolver) as Promise<T>;
};

interface DrizDbOverloads {
  /** Return the initialized Drizzle DB synchronously. Throws when initialization is in progress. */
  (): DbDatabaseType;
  /** Invoke the provided function with the initialized db and return its result (promisified if needed). */
  <T>(
    fn: (driz: DbDatabaseType) => T,
  ): T extends Promise<infer R> ? Promise<R> : Promise<T>;
}

/**
 * Synchronous accessor for the Drizzle DB instance.
 *
 * If the DB is already initialized the function returns the instance or, when
 * a callback is provided, invokes the callback with the db. When the DB is not
 * yet initialized this function will throw, signaling callers to retry or
 * await initialization via `drizDbWithInit()`.
 */
export const drizDb: DrizDbOverloads = <T>(
  fn?: (driz: DbDatabaseType) => T,
) => {
  const drizDb = get_DrizDb();
  if (drizDb) {
    if (fn) {
      const fnRet = fn(drizDb);
      return isPromise(fnRet) ? fnRet : Promise.resolve(fnRet);
    }
    return get_DrizDb();
  }
  // Queue up initialization if not already started so callers can observe it.
  const dbWithInit = drizDbWithInit();
  dbWithInit.catch((err) => {
    LoggedError.isTurtlesAllTheWayDownBaby(err, {
      log: true,
      message: 'Error initializing Drizzle DB',
      extra: {
        cause: err,
      },
      source: 'drizDb initialize',
    });
    return Promise.resolve();
  });
  throw new Error(
    'Drizzle DB is being initialized; please try your call again later.',
    {
      cause: {
        code: 'DB_INITIALIZING',
        retry: true,
        enqueue: dbWithInit.then,
      },
    },
  );
};

import { eq, and } from 'drizzle-orm';
import {
  documentRelationship,
  documentRelationshipReason,
  documentUnits,
  documentProperty,
} from '@/drizzle/schema';
import type {
  DatabaseType,
  DbTransactionType,
  DocumentPropertyType,
  DocumentRelationshipType,
} from './drizzle-types';
import { schema } from './schema';
import { log } from '@/lib/logger';
import { LoggedError } from '../react-util';
import { newUuid } from '../typescript';
import { EmailPropertyTypeTypeId } from '@/data-models/api/email-properties/property-type';

/**
 * Resolves a relationship reason into a valid relationship reason ID.
 *
 * @param db - The database instance or transaction to use.
 * @param reason - The reason to resolve, which can be a string, number, or undefined.
 * @param add - Whether to add the reason to the database if it does not exist. Defaults to true.
 * @returns The resolved relationship reason ID, or undefined if the reason is invalid.
 *
 * @example
 * ```typescript
 * const reasonId = await getDocumentRelationReason({
 *   db,
 *   reason: 'Duplicate',
 *   add: true,
 * });
 * console.log(reasonId); // Outputs the ID of the reason
 * ```
 */
export const getDocumentRelationReason = async ({
  db,
  reason,
  add = true,
}: {
  db: DatabaseType | DbTransactionType;
  add?: boolean;
  reason: string | number | undefined | null;
}) => {
  if (typeof reason === 'number') {
    return reason;
  }
  const normalized = reason?.toString()?.trim()?.toLowerCase();
  if (!normalized) {
    return undefined;
  }
  const alreadyNumber = Number(normalized);
  if (!isNaN(alreadyNumber)) {
    return alreadyNumber;
  }
  const rows =
    (await db
      .select({
        relationReasonId: schema.documentRelationshipReason.relationReasonId,
      })
      .from(schema.documentRelationshipReason)
      .where(eq(schema.documentRelationshipReason.description, normalized))
      .limit(1)
      .execute()) ?? [];
  if (rows.length > 0) {
    const { relationReasonId } = rows[0];
    if (relationReasonId || !add) {
      return relationReasonId;
    }
  }
  const [{ relationReasonId: newRelationReasonId } = {}] = await db
    .insert(documentRelationshipReason)
    .values({ description: normalized })
    .returning();
  return newRelationReasonId;
};

type LikeDocumentRelationshipType = Omit<
  DocumentRelationshipType,
  'timestamp' | 'relationshipReasonId'
> & {
  relationshipReasonId: number | string;
};

/**
 * Adds document relationships to the database.
 *
 * @param db - The database transaction to use.
 * @param addDocumentRelations - An array of document relationships to add.
 * @returns An array of added document relationships.
 *
 * @example
 * ```typescript
 * const relationships = await addDocumentRelations({
 *   db,
 *   addDocumentRelations: [
 *     {
 *       sourceDocumentId: 1,
 *       targetDocumentId: 2,
 *       relationshipReasonId: 'Duplicate',
 *     },
 *   ],
 * });
 * console.log(relationships); // Outputs the added relationships
 * ```
 */
export const addDocumentRelations = async ({
  db,
  addDocumentRelations,
}: {
  db: DbTransactionType;
  addDocumentRelations?: Array<LikeDocumentRelationshipType>;
}): Promise<Array<DocumentRelationshipType>> => {
  if (!addDocumentRelations?.length) {
    return [];
  }
  try {
    const values = (
      await Promise.all(
        addDocumentRelations.map(
          ({
            sourceDocumentId,
            targetDocumentId,
            relationshipReasonId: reason,
          }) =>
            new Promise<Omit<DocumentRelationshipType, 'timestamp'>>(
              async (resolve, reject) => {
                try {
                  // Resolve incoming reason into a valid relationship id
                  const relationshipReasonId = await getDocumentRelationReason({
                    db,
                    reason,
                  });
                  if (!relationshipReasonId) {
                    throw new Error('Failed to get relationship reason ID');
                  }
                  // Check if the relationship already exists

                  const exists = await db
                    .select()
                    .from(schema.documentRelationship)
                    .where(
                      and(
                        eq(
                          schema.documentRelationship.sourceDocumentId,
                          sourceDocumentId,
                        ),
                        eq(
                          schema.documentRelationship.targetDocumentId,
                          targetDocumentId,
                        ),
                        eq(
                          schema.documentRelationship.relationshipReasonId,
                          relationshipReasonId,
                        ),
                      ),
                    )
                    .limit(1)
                    .execute();
                  if (exists && exists.length > 0) {
                    log((l) =>
                      l.warn(
                        'Document relationship already exists - skipping',
                        {
                          sourceDocumentId,
                          targetDocumentId,
                          relationshipReasonId,
                        },
                      ),
                    );
                    resolve(undefined as unknown as DocumentRelationshipType);
                  } else {
                    resolve({
                      sourceDocumentId,
                      targetDocumentId,
                      relationshipReasonId,
                    });
                  }
                } catch (error) {
                  reject(error);
                }
              },
            ),
        ),
      )
    ).filter(Boolean);
    if (!values.length) {
      return [];
    }
    return await db.insert(documentRelationship).values(values).returning();
  } catch (error) {
    throw LoggedError.isTurtlesAllTheWayDownBaby(error, {
      log: true,
      source: 'addDocumentRelations',
      data: { addDocumentRelations },
    });
  }
};

type AddNoteRetVal<TNotes> =
  TNotes extends Array<string>
    ? Promise<Array<DocumentPropertyType>>
    : TNotes extends string
      ? Promise<DocumentPropertyType>
      : TNotes extends Array<{
            policyBasis: string[];
            tags: string[];
            note: string;
          }>
        ? Promise<Array<DocumentPropertyType>>
        : TNotes extends { policyBasis: string[]; tags: string[]; note: string }
          ? Promise<DocumentPropertyType>
          : TNotes extends []
            ? Promise<Array<DocumentPropertyType>>
            : never;

export const addNotesToDocument = async <
  TNotes extends
    | string
    | Array<string>
    | { policyBasis: string[]; tags: string[]; note: string }
    | Array<{ policyBasis: string[]; tags: string[]; note: string }>,
>({
  db,
  notes: notesFromProps,
  documentId: documentIdFromProps,
}: {
  db: DbTransactionType | DatabaseType;
  notes: TNotes;
  documentId: number;
}): Promise<AddNoteRetVal<TNotes>> => {
  if (!('documentUnits' in db.query)) {
    throw new Error(
      'Invalid database instance - must be a transaction or query builder',
    );
  }
  // If db is a query builder, we need to get the actual db instance
  const record = await db.query.documentUnits.findFirst({
    where: eq(schema.documentUnits.unitId, documentIdFromProps),
    columns: {
      emailId: true,
      attachmentId: true,
      documentType: true,
    },
    with: {
      docProp: {
        columns: {
          documentId: true, // Ensure we get the document ID
        },
      },
    },
  });
  if (!record) {
    throw new Error('Email ID not found for the document');
  }
  const { emailId, attachmentId, documentType, docProp } = record;
  const documentId =
    documentType === 'email' || documentType === 'attachment'
      ? documentIdFromProps
      : (docProp?.documentId ?? documentIdFromProps);

  let notes: Array<{ policyBasis: string[]; tags: string[]; note: string }>;
  if (Array.isArray(notesFromProps)) {
    notes = notesFromProps.map((note) =>
      typeof note === 'string'
        ? {
            policyBasis: [],
            tags: [],
            note,
          }
        : {
            policyBasis: note.policyBasis || [],
            tags: note.tags || [],
            note: note.note,
          },
    );
  } else if (typeof notesFromProps === 'string') {
    notes = [
      {
        policyBasis: [],
        tags: [],
        note: notesFromProps,
      },
    ];
  } else {
    notes = [notesFromProps];
  }

  // iterate through records, assinging id's and building insert records
  const inputRecords = notes.map(({ policyBasis, tags, note }) => {
    const propertyId = newUuid();
    const createdOn = new Date(Date.now()).toISOString();
    const documentRecord = {
      documentPropertyId: propertyId,
      emailId,
      attachmentId,
      documentType: 'note',
      content: note,
      createdOn,
    };
    const documentPropertyRecord: DocumentPropertyType = {
      propertyId,
      documentId,
      documentPropertyTypeId: EmailPropertyTypeTypeId.Note,
      createdOn,
      propertyValue: note,
      policyBasis,
      tags,
    };
    return {
      documentRecord,
      documentPropertyRecord,
    };
  });
  const normalizedNotes = inputRecords.map((r) => r.documentPropertyRecord);

  await db.transaction(async (tx) => {
    await tx
      .insert(documentUnits)
      .values(inputRecords.map((r) => r.documentRecord));
    await tx.insert(documentProperty).values(normalizedNotes);
  });

  return Array.isArray(notesFromProps)
    ? (normalizedNotes as unknown as AddNoteRetVal<TNotes>)
    : (normalizedNotes[0] as unknown as AddNoteRetVal<TNotes>);
};

/**
 * @fileoverview Drizzle Database Type Definitions
 *
 * This module provides comprehensive type definitions for database operations in the NoEducation application.
 * It extracts and exports TypeScript types from the Drizzle schema to ensure type safety across all
 * database interactions, including SELECT operations, query building, and transaction handling.
 *
 * The module serves as the central type registry for:
 * - Table select types (inferred from schema definitions)
 * - Query builder types (for type-safe query construction)
 * - Individual entity types (for specific domain objects)
 * - Transaction types (for atomic operations)
 *
 * This approach provides:
 * - **Type Safety**: Compile-time validation of database operations
 * - **Intellisense**: Rich autocompletion in IDEs
 * - **Refactoring Safety**: Automatic updates when schema changes
 * - **Documentation**: Self-documenting code through types
 *
 * @module lib/drizzle-db/drizzle-types
 * @version 1.0.0
 * @since 2025-07-16
 *
 * @example
 * ```typescript
 * import type {
 *   EmailType,
 *   DocumentUnitQueryShape,
 *   DatabaseType
 * } from '@/lib/drizzle-db/drizzle-types';
 *
 * // Type-safe entity handling
 * function processEmail(email: EmailType) {
 *   console.log(`Processing email: ${email.subject}`);
 * }
 *
 * // Type-safe query building
 * const queryOptions: DocumentUnitQueryShape = {
 *   where: eq(documentUnits.emailId, emailId),
 *   with: { documentProperty: true }
 * };
 * ```
 */

import type { FirstParameter } from '@/lib/typescript';
import type { DbFullSchemaType, DbDatabaseType } from './schema';

/**
 * Type alias for the complete database instance with full schema support.
 *
 * This type represents the configured database instance that includes all tables,
 * relations, and query methods. It provides the foundation for all database
 * operations and type inference throughout the application.
 *
 * @typedef {DbDatabaseType} DatabaseType
 *
 * @example
 * ```typescript
 * import type { DatabaseType } from '@/lib/drizzle-db/drizzle-types';
 *
 * function performDatabaseOperation(db: DatabaseType) {
 *   // Access to all schema tables and relations
 *   return db.query.users.findMany();
 * }
 * ```
 *
 * @see {@link DbDatabaseType} - The underlying database type from schema
 */
export type DatabaseType = DbDatabaseType;

/**
 * Utility type that extracts SELECT result types from all schema tables.
 *
 * This mapped type iterates through the complete schema and extracts the `$inferSelect`
 * type from each table that supports SELECT operations. It uses conditional type
 * mapping to include only tables that have the `$inferSelect` property.
 *
 * The type works by:
 * 1. Iterating through all keys in `DbFullSchemaType`
 * 2. Checking if each entity has a `$inferSelect` property
 * 3. Including only those entities in the final type
 * 4. Extracting the actual SELECT type from each entity
 *
 * This provides a centralized registry of all table result types, ensuring
 * consistency across the application when working with database query results.
 *
 * @typedef {object} TableSelectTypes
 *
 * @example
 * ```typescript
 * import type { TableSelectTypes } from '@/lib/drizzle-db/drizzle-types';
 *
 * // Access specific table result types
 * type UserResult = TableSelectTypes['users'];
 * type EmailResult = TableSelectTypes['emails'];
 *
 * // Use in functions that process query results
 * function processUsers(users: UserResult[]) {
 *   users.forEach(user => console.log(user.email));
 * }
 * ```
 *
 * @see {@link DbFullSchemaType} - The complete schema type used for extraction
 */
export type TableSelectTypes = {
  [K in keyof DbFullSchemaType as '$inferSelect' extends keyof DbFullSchemaType[K]
    ? K
    : never]: DbFullSchemaType[K] extends { $inferSelect: unknown }
    ? DbFullSchemaType[K]['$inferSelect']
    : never;
};

/**
 * Type alias for the complete database schema.
 *
 * This provides a more semantic name for the full schema type, making it
 * clear when working with schema-level operations versus specific table types.
 *
 * @typedef {DbFullSchemaType} SchemaType
 *
 * @example
 * ```typescript
 * import type { SchemaType } from '@/lib/drizzle-db/drizzle-types';
 *
 * function getTableFromSchema<T extends keyof SchemaType>(
 *   schema: SchemaType,
 *   tableName: T
 * ): SchemaType[T] {
 *   return schema[tableName];
 * }
 * ```
 *
 * @see {@link DbFullSchemaType} - The underlying schema type
 */
export type SchemaType = DbFullSchemaType;

