/**
 * Re-export of the database transaction type from the schema module.
 *
 * This provides a convenient import path for transaction types without
 * requiring direct access to the schema module.
 *
 * @see {@link DbTransactionType} - The transaction type from schema module
 */
export type { DbTransactionType } from './schema';

/**
 * Namespace containing organized type collections for easier access.
 *
 * This namespace provides a structured way to access related types,
 * making it easier to import and use type collections in a organized manner.
 *
 * @namespace Types
 *
 * @example
 * ```typescript
 * import { Types } from '@/lib/drizzle-db/drizzle-types';
 *
 * // Access table types
 * type UserType = Types.Tables['users'];
 *
 * // Access query types
 * type UserQuery = Types.Queries['users'];
 * ```
 */
// eslint-disable-next-line @typescript-eslint/no-namespace
export namespace Types {
  /**
   * Collection of all table SELECT result types.
   *
   * @typedef {TableSelectTypes} Tables
   */
  export type Tables = TableSelectTypes;

  /**
   * Collection of all query builder types.
   *
   * @typedef {QueryBuilders} Queries
   */
  export type Queries = QueryBuilders;
}

import { drizDbWithInit } from '@/lib/drizzle-db';
import { isValidUuid } from '@/lib/ai/tools/utility';
import { redirect, notFound } from 'next/navigation';
import { LoggedError } from '../react-util';
import { isRedirectError } from 'next/dist/client/components/redirect-error';

/**
 * Resolves an email ID from either a UUID or document unit ID, with automatic redirects.
 *
 * @param emailIdParam - The route parameter that could be either an email ID (UUID) or document unit ID (number)
 * @param currentPath - The current path to redirect to with the resolved email ID
 * @returns The resolved email ID if valid, otherwise triggers redirect or 404
 */
export async function resolveEmailIdWithRedirect(
  emailIdParam: string,
  currentPath: string,
): Promise<string> {
  // If no email id provided, show 404
  if (!emailIdParam) {
    notFound();
  }

  // Check if it's already a valid UUID (email ID)
  if (isValidUuid(emailIdParam)) {
    return emailIdParam;
  }

  // Try to parse as document unit ID (numeric)
  const documentId = Number(emailIdParam);
  if (!documentId || Number.isNaN(documentId)) {
    // Invalid format - not UUID and not a valid number
    notFound();
  }

  // Look up the email ID associated with this document unit ID
  try {
    const doc = await (
      await drizDbWithInit()
    ).query.documentUnits.findFirst({
      where: (d, { eq, isNotNull, and }) =>
        and(eq(d.unitId, documentId), isNotNull(d.emailId)),
      columns: {
        unitId: true,
        emailId: true,
      },
    });

    if (!doc || !doc.emailId) {
      // No matching document found
      notFound();
    }

    // Document ID was used - redirect to the email ID equivalent
    const redirectPath = currentPath.replace(`[emailId]`, doc.emailId);
    redirect(redirectPath);
  } catch (error) {
    if (isRedirectError(error)) {
      throw error;
    }
    LoggedError.isTurtlesAllTheWayDownBaby(error, {
      log: true,
      source: 'EmailIdResolver',
      message: 'Failed to resolve document ID to email ID',
      critical: true,
    });
    notFound();
  }
}

/**
 * Resolves an email ID from either a UUID or document unit ID, without redirects.
 * Used for cases where you just need the email ID value.
 *
 * @param emailIdParam - The route parameter that could be either an email ID (UUID) or document unit ID (number)
 * @returns The resolved email ID if valid, otherwise null
 */
export async function resolveEmailId(
  emailIdParam: string,
): Promise<string | null> {
  // If no email id provided, return null
  if (!emailIdParam) {
    return null;
  }

  // Check if it's already a valid UUID (email ID)
  if (isValidUuid(emailIdParam)) {
    return emailIdParam;
  }

  // Try to parse as document unit ID (numeric)
  const documentId = Number(emailIdParam);
  if (!documentId || Number.isNaN(documentId)) {
    // Invalid format - not UUID and not a valid number
    return null;
  }

  // Look up the email ID associated with this document unit ID
  try {
    const doc = await (
      await drizDbWithInit()
    ).query.documentUnits.findFirst({
      where: (d, { eq }) => eq(d.unitId, documentId),
      columns: {
        unitId: true,
        emailId: true,
      },
    });

    return doc?.emailId || null;
  } catch (error) {
    console.error('Error resolving document ID to email ID:', error);
    return null;
  }
}

import {
  ImportSourceMessage,
  ImportStage,
  ImportStageValues,
} from '@/data-models/api/import/email-message';
import {
  TransactionalImportStageManager,
  StageProcessorContext,
  AdditionalStageOptions,
} from '../types';
import { query, queryExt } from '@/lib/neondb';
import { CustomAppInsightsEvent, log } from '@/lib/logger';
import { NextRequest } from 'next/server';
import { NextApiRequest } from 'next';

/**
 * The `TransactionalStateManagerBase` class provides a base implementation for managing
 * the state of import transactions. It implements the `TransactionalImportStageManager` interface
 * and provides methods to begin, commit, and rollback transactions, as well as to execute
 * stage processors.
 *
 */
export class TransactionalStateManagerBase
  implements TransactionalImportStageManager
{
  static readonly NullId = 'null-id' as const;
  static readonly calculateNextStage = (stage: ImportStage) => {
    if (stage === 'completed') {
      return 'completed';
    }
    const thisStageIndex = ImportStageValues.indexOf(stage);
    if (thisStageIndex === -1) {
      throw new Error(`Invalid stage: ${stage}`);
    }
    return ImportStageValues[thisStageIndex + 1];
  };
  #transactionId: string | null;
  readonly #stage: ImportStage;
  readonly #nextStage: ImportStage;
  #activeTransaction: ImportSourceMessage | undefined;
  #request: NextRequest | NextApiRequest;
  #skipStageBump = false;
  protected importEvent: CustomAppInsightsEvent | undefined;

  constructor(stage: ImportStage, { req }: AdditionalStageOptions) {
    this.#stage = stage;

    this.#nextStage =
      stage === 'completed'
        ? stage
        : ImportStageValues[ImportStageValues.indexOf(stage) + 1];

    this.#transactionId = TransactionalStateManagerBase.NullId;
    this.#request = req;
  }
  get request(): NextRequest | NextApiRequest {
    return this.#request;
  }
  get requireRequest(): NextRequest | NextApiRequest {
    if (!this.#request) {
      throw new Error('Request is required');
    }
    return this.#request;
  }
  /**
   * Gets the transaction ID of the currently active transaction.
   * If there is no active transaction, returns a null identifier.
   *
   * @returns {string} The transaction ID or a null identifier.
   */
  protected get txId(): string {
    return this.#transactionId ?? TransactionalStateManagerBase.NullId;
  }
  /**
   * Gets the current import stage.
   *
   * @returns {ImportStage} The current stage of the import process.
   */
  get stage(): ImportStage {
    return this.#stage;
  }

  /**
   * Gets the next stage of the import process.
   *
   * @returns {ImportStage} The next stage of the import process.
   */
  get nexStage(): ImportStage {
    return this.#nextStage;
  }

  /**
   * Begins a new import transaction.
   *
   * @template TStage - The type of the import stage.
   * @param {StageProcessorContext} options - The context for the stage processor, containing the target and next stage.
   * @returns {Promise<StageProcessorContext>} - A promise that resolves to the stage processor context.
   * @throws {Error} - Throws an error if a transaction is already in progress.
   */
  public begin(options: StageProcessorContext): Promise<StageProcessorContext> {
    if (this.#activeTransaction) {
      throw new Error('Transaction already in progress');
    }
    if (options.target && typeof options.target === 'object') {
      this.setTransaction(options.target);
    }

    return Promise.resolve(options);
  }

  /**
   * Sets the current transaction with the provided target.
   *
   * @param target - The transaction target to set. Must be an object with an `id` property.
   * @param skipStageBump - A flag indicating whether to skip the stage bump when tx commits.
   * @throws {Error} If a transaction is already in progress.
   * @throws {Error} If the target is not a valid object.
   *
   * @remarks
   * This method initializes a new transaction by setting the `#transactionId` and `#activeTransaction`
   * properties. It also logs an audit message indicating that the import transaction has started.
   */
  protected setTransaction(
    target: ImportSourceMessage,
    skipStageBump?: boolean,
  ): boolean {
    if (this.#activeTransaction) {
      throw new Error('Transaction already in progress');
    }
    if (
      !target ||
      typeof target !== 'object' ||
      !target.raw ||
      !target.raw.id
    ) {
      return false;
    }
    this.#transactionId = target.id ?? TransactionalStateManagerBase.NullId;
    this.#activeTransaction = JSON.parse(JSON.stringify(target));
    if (skipStageBump === true) {
      this.#skipStageBump = true;
    }
    log((l) =>
      l.info({
        message: '[AUDIT]: Import Transaction Started.',
        stage: this.stage,
        txId: this.txId,
      }),
    );
    return true;
  }
  /**
   * Commits the current stage of the import process.
   *
   * @template TStage - The type of the import stage.
   * @param {StageProcessorContext} ctx - The context of the current stage processor.
   * @returns {Promise<StageProcessorContext>} - A promise that resolves to the updated stage processor context.
   * @throws {Error} - Throws an error if there is no active transaction or if the staging message update fails.
   */
  public async commit(
    ctx: StageProcessorContext,
  ): Promise<StageProcessorContext> {
    if (this.importEvent) {
      this.importEvent.dispose();
      log((l) => l.info(this.importEvent!)).then(
        () => (this.importEvent = undefined),
      );
    }
    if (!this.#skipStageBump) {
      const work = ctx;
      work.currentStage = ctx.nextStage as ImportStage;
      if (work.target) {
        work.target.stage = work.currentStage;
      }
      if (ctx.currentStage !== 'completed') {
        work.nextStage = TransactionalStateManagerBase.calculateNextStage(
          ctx.nextStage,
        );
      }
    }
    if (!this.#activeTransaction) {
      return ctx;
    }
    const id = this.txId;
    if (id === TransactionalStateManagerBase.NullId) {
      if (ctx.currentStage !== 'new') {
        log((l) => l.error(new Error('Transaction ID is null')));
      }
    } else if (!this.#skipStageBump) {
      if (ctx.currentStage === 'completed') {
        const result = await query(
          (sql) =>
            sql`DELETE FROM staging_message WHERE id = ${id} RETURNING id`,
        );
        if (!result.length) {
          throw new Error('Failed to delete staging message');
        }
      } else {
        const result = await queryExt(
          (sql) =>
            sql`UPDATE staging_message SET stage = ${ctx.currentStage} WHERE id = ${id}`,
        );
        if (!result.rowCount) {
          throw new Error('Failed to update staging message');
        }
      }
      log((l) =>
        l.info({
          message: '[AUDIT]: Import Transaction Committed.',
          stage: this.#stage,
          txId: this.#activeTransaction?.id,
        }),
      );
    }
    this.#resetTransaction();
    return ctx;
  }

  #resetTransaction(): void {
    this.#activeTransaction = undefined;
    this.#transactionId = TransactionalStateManagerBase.NullId;
    this.#skipStageBump = false;
  }

  /**
   * Rolls back the current transaction.
   *
   * If the transaction ID is null, logs an error message.
   * Otherwise, updates the stage of the message in the staging_message table
   * to the current stage and logs a warning message indicating that the
   * import transaction has been rolled back.
   *
   * After rolling back, sets the active transaction to null.
   *
   * @returns {Promise<void>} A promise that resolves when the rollback is complete.
   */
  public async rollback(): Promise<void> {
    if (this.importEvent) {
      this.importEvent.dispose();
      log((l) => l.info(this.importEvent!)).then(
        () => (this.importEvent = undefined),
      );
    }
    const id = this.txId;
    if (!id || id === TransactionalStateManagerBase.NullId) {
      log((l) => l.verbose({ message: 'No active transaction to roll back' }));
      this.#resetTransaction();
      return;
    }
    const result = await query(
      (sql) =>
        sql`UPDATE staging_message SET stage = ${
          this.#stage
        } WHERE id = ${id} RETURNING id`,
    );

    if (!result.length) {
      throw new Error('Rollback failed: no rows updated');
    }

    log((l) =>
      l.warn({
        message: '[AUDIT]: Import Transaction Rolled Back.',
        stage: this.#stage,
        txId: this.txId,
      }),
    );
    this.#resetTransaction();
  }
  /**
   * Executes the stage processor with the given context.
   *
   * @template TStage - The type of the import stage.
   * @param {StageProcessorContext} ctx - The context for the stage processor.
   * @returns {Promise<StageProcessorContext>} A promise that resolves to the stage processor context.
   * @throws {Error} Throws an error indicating that the method is not implemented.
   */
  public run(
    // eslint-disable-next-line @typescript-eslint/no-unused-vars
    ctx: StageProcessorContext,
  ): Promise<StageProcessorContext> {
    throw new Error('Method not implemented.');
  }
}

import {
  EmailPropertyRepository,
  EmailPropertyTypeRepository,
} from '@/lib/api/email/properties';

import { ImportStage } from '@/data-models/api/import/email-message';
import {
  EmailProperty,
  EmailPropertyCategoryTypeId,
} from '@/data-models/api/email-properties/property-type';
import {
  StageProcessorContext,
  ImportStageManagerFactory,
  AdditionalStageOptions,
} from '../types';
import { TransactionalStateManagerBase } from '../default/transactional-statemanager';
import { AggregateError } from '@/lib/react-util/errors/aggregate-error';
import { isError } from '@/lib/react-util/utility-methods';
import { LoggedError } from '@/lib/react-util/errors/logged-error';
import { CustomAppInsightsEvent, log } from '@/lib/logger';
import { newUuid } from '@/lib/typescript';
const EmailPropertyTypeMap: Map<string, number> = new Map();
const parseEmailId = (x: string) => {
  const match = x.match(/<([^>]+)>/);
  return match ? match[1] : x;
};
type ParseHeaderArrayProps = {
  split?: string;
  parse?: (arg0: string) => string;
};
const HeadersWithArrayValueMap: Record<string, ParseHeaderArrayProps> = {
  To: { split: ',' },
  Cc: { split: ',' },
  Bcc: { split: ',' },
  'In-Reply-To': { split: ' ', parse: parseEmailId },
  References: { split: ' ', parse: parseEmailId },
  'Return-Path': { parse: parseEmailId },
  'Message-ID': { parse: parseEmailId },
} as const;
const HeadersWithArrayValueKeys: ReadonlyArray<
  keyof typeof HeadersWithArrayValueMap
> = Object.keys(HeadersWithArrayValueMap);
const indexOfHeaderWithArrayKey = (headerName: string): number =>
  HeadersWithArrayValueKeys.findIndex(
    (x: string) => x.toLowerCase() === headerName.toLowerCase(),
  );
const getHeaderWithArraySplitBy = (
  headerName: string,
): ParseHeaderArrayProps | undefined => {
  const indexOf = indexOfHeaderWithArrayKey(headerName);
  return indexOf === -1
    ? undefined
    : HeadersWithArrayValueMap[HeadersWithArrayValueKeys[indexOf]];
};

type HeaderFailedResult = { status: 'failure'; error: Error };
type HeaderSuccessResult = { status: 'success'; result: EmailProperty };
type HeaderSaveResult = HeaderSuccessResult | HeaderFailedResult;

class HeaderStateManager extends TransactionalStateManagerBase {
  #importEvent: CustomAppInsightsEvent | undefined;

  constructor(stage: ImportStage, options: AdditionalStageOptions) {
    super(stage, options);
  }

  async begin(context: StageProcessorContext) {
    this.#importEvent = new CustomAppInsightsEvent('import::headers');
    this.#importEvent.startTimer('header-import');
    super.begin(context);
    // Load a map of currently known email header identifiers
    if (EmailPropertyTypeMap.size === 0) {
      const emailPropertyRepository: EmailPropertyTypeRepository =
        new EmailPropertyTypeRepository();
      const data =
        await emailPropertyRepository.listForCategory('Email Header');
      data.results.forEach((property) => {
        EmailPropertyTypeMap.set(property.name, property.typeId as number);
      });
    }
    return context;
  }
  async run(context: StageProcessorContext) {
    const { target, currentStage } = context;
    if (typeof target !== 'object') {
      throw new Error(`Expected source message: ${currentStage}`);
    }
    // Get us some repositories
    const emailPropertyRepository: EmailPropertyRepository =
      new EmailPropertyRepository();
    const emailPropertyTypeRepository = new EmailPropertyTypeRepository();

    type HeaderUploadStatus = {
      name: string;
      status: 'success' | 'failure' | 'skipped';
      typeId: number;
      reason?: string;
    };

    const operations = target.raw?.payload?.headers?.map((header) => {
      if (!header || !header.name || !header.value) {
        const noHeaderRet = Promise.resolve<HeaderUploadStatus>({
          name: header.name ?? 'Missing',
          status: 'skipped',
          typeId: -1,
        });
        return noHeaderRet;
      }
      const { name: headerName, value: headerValue } = header;
      return new Promise<HeaderUploadStatus>(async (resolve, reject) => {
        let typeId = -1;
        try {
          typeId = EmailPropertyTypeMap.get(headerName) ?? -1;
          if (typeId === -1) {
            const newPropertyType = await emailPropertyTypeRepository.create({
              categoryId: EmailPropertyCategoryTypeId.EmailHeader,
              name: headerName,
              createdOn: new Date(),
            });
            if (!newPropertyType || !newPropertyType.typeId) {
              throw new Error(
                'An unexpected failure occurred while creating a new email property type.',
              );
            }
            // then add it to our local cache for re-use and move on
            typeId = newPropertyType.typeId as number;
            EmailPropertyTypeMap.set(headerName, typeId);
          }

          const headerParseOptions = getHeaderWithArraySplitBy(headerName);
          if (headerParseOptions) {
            const parts = headerParseOptions.split
              ? headerValue.split(headerParseOptions.split)
              : [headerValue];
            const valueParser = headerParseOptions.parse ?? ((x: string) => x);
            const operations = await Promise.all(
              parts.map((part) => {
                const newProperty: EmailProperty = {
                  documentId: target.documentId!,
                  value: valueParser(part).trim(),
                  propertyId: newUuid(),
                  createdOn: new Date(),
                  typeId: typeId,
                };
                return emailPropertyRepository.create(newProperty).then(
                  (result) => {
                    if (!result || !result.propertyId) {
                      return {
                        status: 'failure',
                        error: new Error(
                          'An unexpected failure occurred while creating a new email property.',
                        ),
                      } as HeaderSaveResult;
                    }
                    this.#importEvent!.increment('processed');
                    return { status: 'success', result } as HeaderSaveResult;
                  },
                  (error) => ({
                    status: 'failure',
                    error: isError(error) ? error : new Error(error),
                  }),
                );
              }),
            );
            const allErrors = operations.filter(
              (x) => x.status === 'failure',
            ) as HeaderFailedResult[];
            if (allErrors.length > 0) {
              throw new AggregateError(
                'An unexpected failure occurred while importing message headers.',
                ...allErrors.map((x) => x.error),
              );
            }
            const successfulOps = operations as HeaderSuccessResult[];
            const { documentId, propertyId } = successfulOps[0].result;
            log((l) =>
              l.verbose({
                message: `Created array of email property for email header ${headerName}: ${operations.length} items created.`,
                headerName,
                documentId,
                propertyId,
                typeId,
                count: operations.length,
              }),
            );
          } else {
            const newProperty: EmailProperty = {
              documentId: target.documentId!,
              value: headerValue,
              propertyId: newUuid(),
              createdOn: new Date(),
              typeId,
            };
            const result = await emailPropertyRepository.create(newProperty);
            if (!result || !result.propertyId) {
              throw new Error(
                'An unexpected failure occurred while creating a new email property.',
              );
            }
            log((l) =>
              l.verbose({
                message: `Created email property for email header `,
                headerName,
                documentId: result.documentId,
                propertyId: result.propertyId,
                typeId: result.typeId,
              }),
            );
            this.#importEvent!.increment('processed');
          }
          resolve({ name: headerName, status: 'success', typeId });
        } catch (error) {
          const le = LoggedError.isTurtlesAllTheWayDownBaby(error, {
            log: true,
            source: 'HeaderStateManager',
            message:
              'An unexpected error occurred while processing email headers.',
          });
          resolve({
            name: headerName,
            status: 'failure',
            typeId,
            reason: le.message,
          });
        }
        reject(
          new Error(
            'An unexpected error occurred while processing email headers.',
          ),
        );
      });
    }) as Promise<HeaderUploadStatus>[];
    const allHeaders = await Promise.all(operations);
    if (allHeaders.some((header) => header.status === 'failure')) {
      throw new Error(
        'An unexpected error occurred while processing email headers.',
      );
    }
    return context;
  }
}

/**
 * Factory function to create an ImportStageManager instance.
 *
 * @param options - The options to configure the manager.
 * @returns An ImportStageManager instance configured with the provided options.
 */
const managerFactory: ImportStageManagerFactory = (
  stage: ImportStage,
  addOps: AdditionalStageOptions,
) => new HeaderStateManager(stage, addOps);

export default managerFactory;

import {
  AdditionalStageOptions,
  ImportStageManagerFactory,
  StageProcessorContext,
} from '../types';
import { TransactionalStateManagerBase } from '../default/transactional-statemanager';
import { ImportStage } from '@/data-models/api/import/email-message';
import { createStagingRecord } from '@/lib/api/email/import/google';
import { log } from '@/lib/logger';

class StagedManager extends TransactionalStateManagerBase {
  constructor(stage: ImportStage, options: AdditionalStageOptions) {
    super(stage, options);
  }

  async run(context: StageProcessorContext) {
    const { target, currentStage } = context;
    let { providerEmailId } = context;

    if (typeof providerEmailId !== 'string' || !providerEmailId) {
      if (!target?.raw.id) {
        throw new Error(
          `Invalid state for staging - provider email id not found.`,
        );
      }
      providerEmailId = target.raw.id;
      log((l) =>
        l.warn(
          'ProviderEmailId value pulled from raw output; is this a retry?',
        ),
      );
    }
    const req = this.requireRequest;
    const responseMessage = await createStagingRecord(providerEmailId, {
      req,
    }).awaitable;
    if (!responseMessage) {
      throw new Error(
        `An unexpected failure occurred queuing email ${providerEmailId}.`,
      );
    }
    this.setTransaction(responseMessage);
    log((l) =>
      l.info({
        message: '[AUDIT]: Email import queued successfully.',
        providerEmailId,
        stage: currentStage,
      }),
    );
    return {
      ...context,
      target: responseMessage,
    };
  }
}

/**
 * Factory function to create an ImportStageManager supporting the staged stage.
 *
 * @param stage - The import stage to manage.
 * @param options - The options to configure the ImportStageManager.
 * @returns An ImportStageManager instance.
 */
const managerFactory: ImportStageManagerFactory = (
  stage: ImportStage,
  options: AdditionalStageOptions,
) => new StagedManager(stage, options);

export default managerFactory;

import { LoggedError } from '@/lib/react-util/errors/logged-error';
import { AttachmentStagedResult, StageAttachmentProps } from './types';
import { queueAttachment } from './attachment-download';
import { StagedAttachmentRepository } from '@/lib/api/email/import/staged-attachment';
import { gmail_v1 } from '@googleapis/gmail';

/**
 * Stages an email attachment for further processing by creating a staged attachment record
 * and queuing it for import. Handles errors and returns the status of the operation.
 *
 * @param req - The request object, typically containing user/session context.
 * @param stagedMessageId - The identifier for the staged email message.
 * @param part - The message part representing the attachment, conforming to Gmail API schema.
 * @returns A promise that resolves to an object indicating the status of the staging operation,
 *          including the partId and error message if applicable.
 *
 * @example
 * ```typescript
 * const result = await stageAttachment({
 *   req,
 *   stagedMessageId: 'abc123',
 *   part: {
 *     partId: '2',
 *     filename: 'document.pdf',
 *     mimeType: 'application/pdf',
 *     body: { size: 1024, attachmentId: 'att456' },
 *     headers: [{ name: 'Content-Type', value: 'application/pdf' }]
 *   }
 * });
 * if (result.status === 'success') {
 *   console.log('Attachment staged:', result.partId);
 * } else {
 *   console.error('Failed to stage attachment:', result.error);
 * }
 * ```
 */
export const stageAttachment = async ({
  req,
  stagedMessageId,
  part,
}: StageAttachmentProps): Promise<AttachmentStagedResult> => {
  const {
    partId = 'unknown',
    filename,
    mimeType,
  } = part as gmail_v1.Schema$MessagePart;
  try {
    if (partId === 'unknown') {
      throw new Error('partId is required');
    }
    const repository = new StagedAttachmentRepository();
    const model = await repository.create({
      stagedMessageId,
      partId: Number(partId),
      filename: filename ?? `attachment-${partId}`,
      mimeType:
        mimeType ??
        part.headers?.find((h) => h.name === 'Content-Type')?.value ??
        null,
      storageId: null,
      imported: false,
      size: part.body!.size ?? 0,
      fileOid: null,
      attachmentId: part.body!.attachmentId!,
    });

    await queueAttachment({
      id: `${stagedMessageId}:${model.partId}`,
      job: { model: { ...model, stagedMessageId: stagedMessageId } },
      req: req,
    });

    return {
      status: 'success',
      partId: partId!,
    };
  } catch (error) {
    const e = LoggedError.isTurtlesAllTheWayDownBaby(error, {
      log: true,
      source: 'email-import',
      data: { stagedMessageId, partId, filename, mimeType },
    });
    return {
      status: 'error',
      error: e.message,
      partId: partId ?? 'unknown',
    };
  }
};

/**
 * Recursively queues staged attachments for processing.
 *
 * This function takes a staged message part and, if it contains an attachment,
 * stages it for processing. If the part contains nested parts, it recursively
 * processes each sub-part, collecting all resulting promises into a flat array.
 *
 * @param req - The request object used for staging attachments.
 * @param stagedMessageId - The identifier for the staged message.
 * @param part - The message part to process, which may contain attachments and/or nested parts.
 * @returns An array of promises, each resolving to an `AttachmentStagedResult` for a staged attachment.
 *
 * @example
 * ```typescript
 * const results = await Promise.all(
 *   queueStagedAttachments({
 *     req,
 *     stagedMessageId: 'abc123',
 *     part: emailPart,
 *   })
 * );
 * ```
 */
export const queueStagedAttachments = ({
  req,
  stagedMessageId,
  part: partFromProps,
}: StageAttachmentProps): Array<Promise<AttachmentStagedResult>> => {
  const partItems = [];
  if (partFromProps.filename && partFromProps.body?.attachmentId) {
    partItems.push(
      stageAttachment({ req, stagedMessageId, part: partFromProps }),
    );
  }
  return partFromProps.parts
    ? [
        ...partItems,
        ...partFromProps.parts.flatMap((part) =>
          queueStagedAttachments({ req, stagedMessageId, part }),
        ),
      ]
    : partItems;
};

