export function getRecoveryActions(error: Error): RecoveryAction[] {
  const errorType = classifyError(error);
  const strategy = recoveryStrategies.find((s) => s.errorType === errorType);
  return strategy?.actions || [];
}


export function getDefaultRecoveryAction(error: Error): RecoveryAction | null {
  const errorType = classifyError(error);
  const strategy = recoveryStrategies.find((s) => s.errorType === errorType);

  if (!strategy) return null;

  const defaultActionId = strategy.defaultAction;
  if (!defaultActionId) return strategy.actions[0] || null;

  return strategy.actions.find((a) => a.id === defaultActionId) || null;
}


export async function attemptAutoRecovery(error: Error): Promise<boolean> {
  const defaultAction = getDefaultRecoveryAction(error);

  if (!defaultAction || !defaultAction.automatic) {
    return false;
  }

  try {
    if (defaultAction.delay) {
      await new Promise((resolve) => setTimeout(resolve, defaultAction.delay));
    }

    await defaultAction.action();
    return true;
  } catch (recoveryError) {
    console.error('Auto-recovery failed:', recoveryError);
    return false;
  }
}

/**
 * Types for the error reporting module.
 *
 * These are extracted so other modules (and tests) can consume the
 * ErrorReporter surface without importing the implementation.
 */

/**
 * Error severity levels for reporting and prioritization
 */
export enum ErrorSeverity {
  LOW = 'low',
  MEDIUM = 'medium',
  HIGH = 'high',
  CRITICAL = 'critical',
}

/**
 * Known environment strings used by the reporter
 */
export type KnownEnvironmentType = 'development' | 'staging' | 'production';

/**
 * Error context information for better debugging
 */
export interface ErrorContext {
  userId?: string;
  sessionId?: string;
  source?: string;
  userAgent?: string;
  url?: string;
  timestamp?: Date;
  componentStack?: string;
  errorBoundary?: string;
  breadcrumbs?: string[];
  additionalData?: Record<string, unknown>;
  error?: Error;
}

/**
 * Error report structure for external monitoring services
 */
export interface ErrorReport {
  error: Error;
  severity: ErrorSeverity;
  context: ErrorContext;
  fingerprint?: string;
  tags?: Record<string, string>;
}

/**
 * Configuration for error reporting
 */
export interface ErrorReporterConfig {
  enableStandardLogging: boolean;
  enableConsoleLogging: boolean;
  enableExternalReporting: boolean;
  enableLocalStorage: boolean;
  maxStoredErrors: number;
  environment: KnownEnvironmentType;
}

/**
 * Interface describing the runtime surface of the ErrorReporter class.
 *
 * Implementations should match this shape so callers can depend on the
 * contract rather than the concrete class.
 */
export interface ErrorReporterInterface {
  reportError(
    error: Error | unknown,
    severity?: ErrorSeverity,
    context?: Partial<ErrorContext>,
  ): Promise<void>;

  reportBoundaryError(
    error: Error,
