} from '@/lib/hooks/types';

/**
 * Fetches memory health status from the API health endpoint
 *
 * @async
 * @function fetchMemoryHealth
 * @description Retrieves current health status for memory services and all subsystems.
 * Performs API request to /api/health endpoint, processes the response, and normalizes
 * status values. Missing subsystems are defaulted to 'error' status to ensure proper
 * error indication in the UI.
 *
 * @returns {Promise<MemoryHealthData>} Promise that resolves to processed health data
 * @throws {Error} When API request fails or memory data is unavailable
 *
 * @example
 * ```typescript
 * try {
 *   const health = await fetchMemoryHealth();
 *   console.log('Overall status:', health.status);
 *   console.log('DB status:', health.subsystems.db);
 * } catch (error) {
 *   console.error('Health check failed:', error.message);
 * }
 * ```
 *
 * @see {@link MemoryHealthData} for return type structure
 * @see {@link HealthCheckResponse} for raw API response structure
 */
const fetchMemoryHealth = async (): Promise<MemoryHealthData> => {
  try {
    // Fetch health status with no-cache to ensure fresh data
    const response = await fetch('/api/health', {
      method: 'GET',
      headers: { 'Content-Type': 'application/json' },
      cache: 'no-store',
    });

    if (!response.ok) {
      throw new Error(
        `Health check API request failed with status ${response.status}`,
      );
    }

    const data: HealthCheckResponse = await response.json();

    // Extract memory status from the response
    const memoryData = data.memory;

    if (!memoryData) {
      throw new Error('Memory health status not available in response');
    }

    // Map the API status code to standardized health status enum
    const healthStatus: HealthStatus = (() => {
      switch (memoryData.status as HealthStatus | 'ok') {
        case 'ok':
          return 'healthy';
        case 'warning':
          return 'warning';
        case 'error':
          return 'error';
        default:
          return 'warning'; // Default to warning for unknown status codes
      }
    })();

    // Extract subsystem statuses with error fallbacks for missing services
    const subsystems: MemorySubsystemStatus = {
      db: memoryData.db?.status || 'error',
      vectorStore: memoryData.vectorStore?.status || 'error',
      graphStore: memoryData.graphStore?.status || 'error',
      historyStore: memoryData.historyStore?.status || 'error',
      authService: memoryData.authService?.status || 'error',
    };

    return {
      status: healthStatus,
      subsystems,
    };
  } catch (error) {
    // Log error with context for debugging purposes
    LoggedError.isTurtlesAllTheWayDownBaby(error, {
      log: true,
      context: 'Fetching memory health status',
    });
    throw error;
  }
};

/**
 * Stable function to calculate refresh interval based on current health status
 *
 * @function stableGetRefreshInterval
 * @description React Query callback that determines how often to refetch health data
 * based on the current health status. Uses stable reference to prevent unnecessary
 * re-renders and query restarts.
 *
 * @param {Query<MemoryHealthData, Error, MemoryHealthData, readonly unknown[]>} query - React Query instance
 * @returns {number} Refresh interval in milliseconds
 *
 * @see {@link getRefreshInterval} for interval calculation logic
 */
const stableGetRefreshInterval = (
  query: Query<MemoryHealthData, Error, MemoryHealthData, readonly unknown[]>,
) => getRefreshInterval(query.state.data?.status || 'warning');

/**
 * Stable exponential backoff retry delay calculator
 *
 * @function stableRetryDelay
 * @description Calculates retry delay using exponential backoff with maximum cap.
 * Uses stable reference to prevent React Query from recreating retry configuration.
 *
 * @param {number} attemptIndex - Zero-based retry attempt number
 * @returns {number} Delay in milliseconds before next retry attempt
 *
 * @example
 * ```typescript
 * // Attempt 0: 1000ms (1s)
 * // Attempt 1: 2000ms (2s)
 * // Attempt 2: 4000ms (4s)
 * // Attempt 3: 8000ms (8s)
 * // Attempt 4: 16000ms (16s)
 * // Attempt 5+: 30000ms (30s) - capped
 * ```
 */
const stableRetryDelay = (attemptIndex: number) =>
  Math.min(1000 * 2 ** attemptIndex, 30000);

/**
 * Stable query key for React Query caching
 *
 * @constant {readonly ['memoryHealth']} stableQueryKey
 * @description Immutable query key used by React Query for cache identification.
 * Uses const assertion to ensure type safety and reference stability.
 */
const stableQueryKey = ['memoryHealth'] as const;


export const useMemoryHealth = (): MemoryStatusHookResult => {
  const query = useQuery<MemoryHealthData, Error>({
    queryKey: stableQueryKey,
    queryFn: fetchMemoryHealth,
    staleTime: 1000, // Consider data stale after 1 second for real-time monitoring
    refetchOnWindowFocus: false, // Prevent excessive API calls on window focus
    refetchInterval: stableGetRefreshInterval, // Dynamic refresh based on health status
    retry: 3, // Retry failed requests up to 3 times
    retryDelay: stableRetryDelay, // Exponential backoff with 30s maximum
  });

  // Extract commonly used values with safe defaults
  const healthStatus = query.data?.status || 'warning';
  const subsystems = query.data?.subsystems;
  const refreshInterval = getRefreshInterval(healthStatus);

  return {
    ...query,
    healthStatus,
    subsystems,
    refreshInterval,
  };
};

import { useQuery } from '@tanstack/react-query';
import type {
  ApiResponse,
  ModelStat,
  QueueInfo,
  QueueSummary,
} from '@/types/statistics';
import { fetch } from '@/lib/nextjs-util/fetch';
import { LoggedError } from '../react-util';

const STATISTICS_STALE_TIME = 30 * 1000; // 30 seconds
const STATISTICS_CACHE_TIME = 5 * 60 * 1000; // 5 minutes

/**
 * Fetches model statistics from the API
 */
const fetchModelStatistics = async (
  source: 'database' | 'redis' = 'database',
): Promise<ModelStat[]> => {
  const params = new URLSearchParams({ source });
  try {
    const response = await fetch(`/api/ai/chat/stats/models?${params}`);

    if (!response.ok) {
      throw new Error('Failed to fetch model statistics');
    }

    const data: ApiResponse<ModelStat[]> = await response.json();

    if (!data.success) {
      throw new Error(data.error || 'API returned error response');
    }

    return data.data;
  } catch (error) {
    throw LoggedError.isTurtlesAllTheWayDownBaby(error, {
      log: true,
      source: 'fetchModelStatistics',
    });
  }
};

/**
 * Fetches queue statistics from the API
 */
const fetchQueueStatistics = async (): Promise<{
  summary: QueueSummary;
  queues: QueueInfo[];
}> => {
  const response = await fetch('/api/ai/chat/stats/queues');

  if (!response.ok) {
    throw new Error('Failed to fetch queue statistics');
  }

  const data: ApiResponse<{ summary: QueueSummary; queues: QueueInfo[] }> =
    await response.json();

  if (!data.success) {
    throw new Error(data.error || 'API returned error response');
  }

  return data.data;
};


export const useModelStatistics = (
  source: 'database' | 'redis' = 'database',
) => {
  return useQuery({
    queryKey: ['modelStatistics', source],
    queryFn: () => fetchModelStatistics(source),
    staleTime: STATISTICS_STALE_TIME,
    gcTime: STATISTICS_CACHE_TIME,
    refetchInterval: STATISTICS_STALE_TIME,
    retry: 3,
    retryDelay: (attemptIndex) => Math.min(1000 * 2 ** attemptIndex, 30000),
  });
};


export const useQueueStatistics = () => {
  return useQuery({
    queryKey: ['queueStatistics'],
    queryFn: fetchQueueStatistics,
    staleTime: STATISTICS_STALE_TIME,
    gcTime: STATISTICS_CACHE_TIME,
    refetchInterval: STATISTICS_STALE_TIME,
    retry: 3,
    retryDelay: (attemptIndex) => Math.min(1000 * 2 ** attemptIndex, 30000),
  });
};


export const useStatistics = (
  modelSource: 'database' | 'redis' = 'database',
) => {
  const modelQuery = useModelStatistics(modelSource);
  const queueQuery = useQueueStatistics();

  return {
    models: modelQuery,
    queues: queueQuery,
    isLoading: modelQuery.isLoading || queueQuery.isLoading,
    isError: modelQuery.isError || queueQuery.isError,
    error: modelQuery.error || queueQuery.error,
    refetch: () => {
      modelQuery.refetch();
      queueQuery.refetch();
    },
  };
};

import { isError, isRecord, LoggedError } from '@/lib/react-util/core';
import {
  ApplicationInsightsBaseType,
  ApplicationInsightsCustomEventName,
  ApplicationInsightsEventBaseType,
  ApplicationInsightsExceptionBaseType,
  ATTR_EXCEPTION_MESSAGE,
  ATTR_EXCEPTION_STACKTRACE,
  ATTR_EXCEPTION_TYPE,
} from './constants';
import { CustomAppInsightsEvent } from './event';
import type { ILogger } from './types';

export class AbstractLogger implements ILogger {
  /**
   *
   */
  constructor() {}

  protected logInfoMessage(record: object): void {
    throw new Error('Method not implementedfor ' + JSON.stringify(record));
  }
  protected logErrorMessage(record: object): void {
    throw new Error('Method not implemented for ' + JSON.stringify(record));
  }
  protected logWarnMessage(record: object): void {
    throw new Error('Method not implemented for ' + JSON.stringify(record));
  }
  protected logDebugMessage(record: object): void {
    throw new Error('Method not implemented for ' + JSON.stringify(record));
  }
  protected logFatalMessage(record: object): void {
    throw new Error('Method not implemented for ' + JSON.stringify(record));
  }
  protected logVerboseMessage(record: object): void {
    throw new Error('Method not implemented for ' + JSON.stringify(record));
  }
  protected logSillyMessage(record: object): void {
    throw new Error('Method not implemented for ' + JSON.stringify(record));
  }
  protected logTraceMessage(record: object): void {
    throw new Error('Method not implemented for ' + JSON.stringify(record));
  }

  protected buildLogRecord(
    message: string | Record<string, unknown> | Error | object,
    ...args: unknown[]
  ): [object] {
    let record: Record<string, unknown> & { [Symbol.toStringTag]?: string };
    const properties = {} as Record<string, unknown>;
    const attributes = {} as Record<string, unknown>;
    let sliceArgOffset = 0;
    if (typeof message === 'string') {
      record = { message };
      if (args.length > 0) {
        if (typeof args[0] === 'object' && args[0] !== null) {
          const recordBody = {
            ...(record.body ?? {}),
            ...('body' in args[0] ? (args[0].body ?? {}) : {}),
          };
          record = {
            ...record,
            ...args[0],
            body: recordBody,
          };
          if (Object.keys(recordBody).length === 0) {
            delete record.body;
          }
          sliceArgOffset = 1;
        }
      }
      return this.buildLogRecord(record, ...args.slice(sliceArgOffset));
    }
    if ('dispose' in message && typeof message.dispose === 'function') {
      message.dispose();
    }
    let stringValue: string;
    // Error / exception messages
    if (isError(message)) {
      stringValue = !!message.message
        ? typeof message.message === 'object'
          ? 'body' in message && !!message.body
            ? String(message.body)
            : String(message.message)
          : String(message.message)
        : message.toString();

      record = {
        [ApplicationInsightsBaseType]: ApplicationInsightsExceptionBaseType,
        ...message,
        [ATTR_EXCEPTION_STACKTRACE]: message.stack,
        [ATTR_EXCEPTION_TYPE]:
          'source' in message ? (message.source ?? message.name) : message.name,
        [ATTR_EXCEPTION_MESSAGE]: stringValue,
      };
      delete record.stack;
      if ('source' in record) {
        delete record.source;
      }
    } else if (CustomAppInsightsEvent.isCustomAppInsightsEvent(message)) {
      // Custom Event messages
      stringValue = `AppInsights Event: ${message.event}`;
      record = {
        [ApplicationInsightsCustomEventName]: message.event,
        [ApplicationInsightsBaseType]: ApplicationInsightsEventBaseType,
        ...message,
        body: {
          measurements: message.measurements ?? {},
          ...('body' in message ? (message.body ?? {}) : {}),
        },
      };
      delete record.measurements;
      delete record.event;
    } else {
      if (!isRecord(message)) {
        throw new Error('Message is not a valid object');
      }
      // and everything else is a generic message
      stringValue = String(message.message ?? message.Message ?? message.body);
      record = {
        msg: stringValue,
        ...message,
      };
    }
    // Append any unprocessed arguments
    if (args.length > sliceArgOffset) {
      record = {
        ...record,
        // TraceArguments: args,
      };
    }
    Object.keys(record).forEach((key) => {
      if (
        typeof key !== 'string' ||
        key.startsWith('_') ||
        typeof record[key] === 'function'
      ) {
        delete record[key];
      }
    });
    // Cleanup message / Message hanger-ons
    delete record.message;
    delete record.Message;
    if (Object.keys(properties).length) {
      record.properties = {
        ...properties,
        ...(record.properties ?? {}),
      };
    }
    if (Object.keys(attributes).length) {
      record.attributes = {
        ...attributes,
        ...(record.attributes ?? {}),
      };
    }
    record[Symbol.toStringTag] = stringValue ?? 'No message provided';
    return [record];
  }

  info(message: string | object, ...args: unknown[]): void {
    this.logInfoMessage(...this.buildLogRecord(message, ...args));
  }
  error(message: string | object, ...args: unknown[]): void {
    const logArguments = this.buildLogRecord(
      LoggedError.isTurtlesAllTheWayDownBaby(message),
      ...args,
    );
    this.logErrorMessage(...logArguments);
  }
  warn(message: string | object, ...args: unknown[]): void {
    this.logWarnMessage(...this.buildLogRecord(message, ...args));
  }
  debug(message: string | object, ...args: unknown[]): void {
    this.logDebugMessage(...this.buildLogRecord(message, ...args));
  }
  fatal(message: string | object, ...args: unknown[]): void {
    this.logFatalMessage(...this.buildLogRecord(message, ...args));
  }
  verbose(message: string | object, ...args: unknown[]): void {
    this.logVerboseMessage(...this.buildLogRecord(message, ...args));
  }
  silly(message: string | object, ...args: unknown[]): void {
    this.logSillyMessage(...this.buildLogRecord(message, ...args));
  }
  trace(message: string | object, ...args: unknown[]): void {
    this.logTraceMessage(...this.buildLogRecord(message, ...args));
  }
}

export const AzureMonitorSampleRate = 'microsoft.sample_rate';
export const ApplicationInsightsBaseType = '_MS.baseType';
export const ApplicationInsightsCustomEventName = 'microsoft.custom_event.name';
export const ApplicationInsightsMessageName =
  'Microsoft.ApplicationInsights.Message';
export const ApplicationInsightsExceptionName =
  'Microsoft.ApplicationInsights.Exception';
export const ApplicationInsightsPageViewName =
  'Microsoft.ApplicationInsights.PageView';
export const ApplicationInsightsAvailabilityName =
  'Microsoft.ApplicationInsights.Availability';
export const ApplicationInsightsEventName =
  'Microsoft.ApplicationInsights.Event';
export const ApplicationInsightsMessageBaseType = 'MessageData';
export const ApplicationInsightsExceptionBaseType = 'ExceptionData';
export const ApplicationInsightsPageViewBaseType = 'PageViewData';
export const ApplicationInsightsAvailabilityBaseType = 'AvailabilityData';
export const ApplicationInsightsEventBaseType = 'EventData';
/**
 * The exception message.
 *
 * @example Division by zero
 * @example Can't convert 'int' object to str implicitly
 */
export const ATTR_EXCEPTION_MESSAGE = 'exception.message';
/**
 * A stacktrace as a string in the natural representation for the language runtime. The representation is to be determined and documented by each language SIG.
 *
 * @example "Exception in thread "main" java.lang.RuntimeException: Test exception\\n at com.example.GenerateTrace.methodB(GenerateTrace.java:13)\\n at com.example.GenerateTrace.methodA(GenerateTrace.java:9)\\n at com.example.GenerateTrace.main(GenerateTrace.java:5)\\n"
 */
export const ATTR_EXCEPTION_STACKTRACE = 'exception.stacktrace';
/**
 * The type of the exception (its fully-qualified class name, if applicable). The dynamic type of the exception should be preferred over the static type in languages that support it.
 *
 * @example java.net.ConnectException
 * @example OSError
 */
export const ATTR_EXCEPTION_TYPE = 'exception.type';
export enum KnownSeverityLevel {
  /** Verbose */
  Verbose = 'Verbose',
  /** Information */
  Information = 'Information',
  /** Warning */
  Warning = 'Warning',
  /** Error */
  Error = 'Error',
  /** Critical */
  Critical = 'Critical',
}
export const asKnownSeverityLevel = (input: unknown): KnownSeverityLevel => {
  if (typeof input === 'number') {
    if (input < 5){
      switch(input) {
        case 0:
          return KnownSeverityLevel.Verbose;
        case 1:
          return KnownSeverityLevel.Information;
        case 2:
          return KnownSeverityLevel.Warning;
        case 3:
          return KnownSeverityLevel.Error;
        case 4:
          return KnownSeverityLevel.Critical;
      }
    }
    if (input === 0) {
      return KnownSeverityLevel.Verbose;
    }
    if (input < 10) {
      return KnownSeverityLevel.Information;
    }
    if (input < 20) {
      return KnownSeverityLevel.Warning;
    }
    if (input < 30) {
      return KnownSeverityLevel.Error;
    }
    if (input < 40) {
      return KnownSeverityLevel.Critical;
    }      
    return KnownSeverityLevel.Error;
  }
  switch (String(input ?? '').toLocaleLowerCase()) {
    case 'verbose':
      return KnownSeverityLevel.Verbose;
    case 'information':
      return KnownSeverityLevel.Information;
    case 'warning':
      return KnownSeverityLevel.Warning;
    case 'error':
      return KnownSeverityLevel.Error;
    case 'critical':
      return KnownSeverityLevel.Critical;
  }
  return KnownSeverityLevel.Error;
}

import pino from 'pino';
import { env, isRunningOnServer } from '@/lib/site-util/env';
import { WrappedLogger } from './wrapped-logger';
import type { ILogger, EventSeverity, LogEventOverloads } from './types';
import { CustomAppInsightsEvent } from './event';

let _logger: ILogger;

/**
 * Returns a promise that resolves to an instance of ILogger.
 *
 * @returns {Promise<ILogger>} A promise that resolves to an ILogger instance.
 */
export const logger = (): Promise<ILogger> =>
  new Promise((resolve) => {
    if (!_logger) {
      if (isRunningOnServer()) {
        _logger = pino<'verbose' | 'silly', false>({
          level: env('LOG_LEVEL_SERVER') ?? 'info',
          name: 'app',
          timestamp: pino.stdTimeFunctions.isoTime,
          customLevels: { verbose: 5, silly: 1 },
          useOnlyCustomLevels: false,
        });
      } else {
        const isJest = process.env.JEST_WORKER_ID !== undefined;
        const transport = isJest
          ? undefined
          : {
              target: 'pino-pretty',
              options: {
                colorize: true,
              },
            };

        _logger = pino<'verbose' | 'silly', false>({
          level: env('NEXT_PUBLIC_LOG_LEVEL_CLIENT') ?? 'info',
          name: 'app',
          timestamp: pino.stdTimeFunctions.isoTime,
          customLevels: { verbose: 5, silly: 1 },
          useOnlyCustomLevels: false,
          transport,
        });
      }
    }
    resolve(new WrappedLogger(_logger));
  });

const resolvedPromise = Promise.resolve();

/**
 * Executes a callback function with the provided logger instance.
 *
 * @param cb - A callback function that takes a logger instance as an argument.
 */
export const log = (cb: (l: ILogger) => void) => {
  if (_logger) {
    const cbRet = cb(_logger);
    return cbRet !== undefined ? Promise.resolve(cbRet) : resolvedPromise;
  }
  return logger().then(cb);
};

/**
 * @remarks
 * This helper function provides multiple overloads for logging events.
 * @method
 * @name LogEventOverloads#(eventName: string): Promise<ILogger>
 * @description Logs an event with the specified name.
 * @param {string} eventName - The name of the event to log.
 * @returns {Promise<ILogger>} A promise that resolves to an ILogger instance.
 *
 * @method
 * @name LogEventOverloads#(eventName: string, measurements: Record<string, number>): Promise<ILogger>
 * @description Logs an event with the specified name and measurements.
 * @param {string} eventName - The name of the event to log.
 * @param {Record<string, number>} measurements - A record of measurements associated with the event.
 * @returns {Promise<ILogger>} A promise that resolves to an ILogger instance.
 *
 * @method
 * @name LogEventOverloads#(severity: EventSeverity, eventName: string): Promise<ILogger>
 * @description Logs an event with the specified severity and name.
 * @param {EventSeverity} severity - The severity level of the event.
 * @param {string} eventName - The name of the event to log.
 * @returns {Promise<ILogger>} A promise that resolves to an ILogger instance.
 *
 * @method
 * @description Logs an event with the specified severity, name, and measurements.
 * @param {EventSeverity} severity - The severity level of the event.
 * @param {string} eventName - The name of the event to log.
 * @param {Record<string, number>} measurements - A record of measurements associated with the event.
 * @returns {Promise<ILogger>} A promise that resolves to an ILogger instance.
 *
 * @example
 * ```typescript
 * logEvent('UserLoggedIn', { userId: 123 });
 * logEvent('error', 'ErrorOccurred');
 * ```
 */
export const logEvent: LogEventOverloads = (
  severityOrEvent: EventSeverity | string,
  eventOrMeasurements?: string | Record<string, number | string>,
  measurements?: Record<string, number | string>,
) => {
  let severity: EventSeverity = 'info';
  let event: CustomAppInsightsEvent;
  if (measurements) {
    // Only one thing we can be if the third param has a value
    event = new CustomAppInsightsEvent(
      eventOrMeasurements as string,
      measurements,
    );
    severity = severityOrEvent as EventSeverity;
  } else if (eventOrMeasurements) {
    // If the second param is a string, we can assume it's an event name
    if (typeof eventOrMeasurements === 'string') {
      event = new CustomAppInsightsEvent(eventOrMeasurements);
      severity = severityOrEvent as EventSeverity;
    } else {
      // Otherwise, we can assume it's a measurement
      event = new CustomAppInsightsEvent('measurement', eventOrMeasurements);
    }
  } else {
    event = new CustomAppInsightsEvent(severityOrEvent);
  }
  return log((l) => l[severity](event));
};

/**
 * Represents a custom event with optional measurements and a dispose function.
 *
 * @property {string} event - The name of the event.
 * @property {Record<string, number>} [measurements] - Optional measurements associated with the event.
 * @property {() => void} [dispose] - Optional function to dispose of the event.
 */
export type ICustomAppInsightsEvent = {
  /**
   * The name of the event.
   */
  event: string;
  /**
   * Optional measurements associated with the event.
   */
  measurements?: Record<string, string | number>;
  /**
   * @returns void
   * @description Disposes of the event and clears any associated resources.
   */
  dispose?: () => void;
};

/**
 * Represents a custom event with measurements and timers.
 */
export class CustomAppInsightsEvent implements ICustomAppInsightsEvent {
  /**
   * Checks if the given object is an instance of ICustomAppInsightsEvent.
   * @param check - The object to check.
   * @returns True if the object is an instance of ICustomAppInsightsEvent, false otherwise.
   */
  static isCustomAppInsightsEvent(
    check: unknown,
  ): check is ICustomAppInsightsEvent {
    return (
      typeof check === 'object' &&
      check !== null &&
      'event' in check &&
      typeof check.event === 'string'
    );
  }
  /**
   * A map to store timer start times.
   */
  readonly #timers = new Map<string, Date>();
  /**
   * The name of the event.
   */
  event: string;
  /**
   * A record to store measurement values.
   */
  readonly measurements: Record<string, number | string>;

  /**
   * Creates an instance of CustomAppInsightsEvent.
   * @param event - The name of the event.
   * @param measurements - Optional initial measurements.
   */
  constructor(event: string, measurements?: Record<string, number | string>) {
    this.event = event;
    this.measurements = measurements ?? {};
  }
  /**
   * Increments the value of a measurement by a specified amount.
   * @param name - The name of the measurement.
   * @param value - The amount to increment by. Defaults to 1.
   */
  increment(name: string, value: number = 1): void {
    const current = Number(this.measurements[name] ?? 0);
    if (!Number.isFinite(current)) {
      throw new Error(
        `Cannot increment measurement "${name}": value "${this.measurements[name]}" is not a finite number.`
      );
    }
    this.measurements[name] = current + value;
  }
  /**
   * Starts a timer for a specified measurement.
   * @param name - The name of the timer.
   * @throws Will throw an error if a timer with the same name already exists.
   */
  startTimer(name: string): void {
    if (this.#timers.has(name)) {
      throw new Error(`Timer with name ${name} already exists.`);
    }
    this.#timers.set(name, new Date());
  }
  /**
   * Stops a timer for a specified measurement and records the elapsed time.
   * @param name - The name of the timer.
   * @throws Will throw an error if a timer with the specified name does not exist.
   */
  stopTimer(name: string): void {
    const now = new Date();
    if (!this.#timers.has(name)) {
      throw new Error(`Timer with name ${name} does not exist.`);
    }
    this.measurements[name] = now.getTime() - this.#timers.get(name)!.getTime();
    this.#timers.delete(name);
  }
  /**
   * Disposes of all active timers and records their elapsed times.
   */
  dispose(): void {
    const now = new Date();
    this.#timers.forEach((start, name) => {
      this.measurements[name] = now.getTime() - start.getTime();
    });
    this.#timers.clear();
  }
}

import type { SimpleLogger, ILogger } from './types';
import { log } from './core';

/**
 * Represents a single log record for the simple scoped logger.
 * @property source - The source or context of the log message.
 * @property timestamp - The ISO timestamp when the log was created.
 * @property message - The main log message, as a string or object.
