          // This promise resolved (even if value is null/undefined)
          acc.fulfilled.push(result.value);
        }
      } else {
        // This promise rejected
        acc.rejected.push(result.reason);
      }
      return acc;
    },
    { fulfilled: [], rejected: [], pending: [] } as CategorizedPromiseResult<T>,
  );
};

