/**
 * Factory function for creating validated server environment configuration.
 *
 * This function provides type-safe access to server-side environment variables
 * with comprehensive validation and fallback handling. It ensures that all
 * required configuration is present and valid before the application starts.
 *
 * @returns Validated server environment configuration object
 *
 * @throws {ZodError} When required environment variables are missing or invalid
 *
 * @example
 * ```typescript
 * // Get validated server environment
 * const env = serverEnvFactory();
 *
 * // Access configuration with full type safety
 * const dbUrl = env.DATABASE_URL;
 * const aiModel = env.AZURE_OPENAI_DEPLOYMENT_HIFI;
 * const logLevel = env.LOG_LEVEL_SERVER;
 * ```
 *
 * @remarks
 * - Returns empty object when running on client-side (SSR safety)
 * - Falls back to client environment if DATABASE_URL is missing
 * - Validates all environment variables against their schemas
 * - Provides default values for optional configuration
 */
export const serverEnvFactory = (): ServerEnvType => {
  try {
    return isRunningOnClient()
      ? ({} as ServerEnvType)
      : serverEnvSchema.parse(buildRawInstance());
  } catch (e) {
    // Check an environment variable to verify really are running on server
    if ((process.env.DATABASE_URL ?? '').length === 0) {
      // We aren't - suppress (arguably could return client here)
      return clientEnvFactory() as unknown as ServerEnvType;
    }
    // Otherwise, rethrow the error
    throw e;
  }
};

import { createContext, useContext } from 'react';
import {
  AllFeatureFlagsDefault,
  type KnownFeatureType,
  type FeatureFlagStatus,
} from './known-feature';

/**
 * Lightweight feature flags API and provider.
 * This module is intentionally independent of Flagsmith's React bindings
 * so it can be used in server-rendered pages and hydrated on the client.
 */
export type FeatureFlagsApi = {
  getFlag: (
    key: KnownFeatureType,
    defaultValue?: FeatureFlagStatus,
  ) => FeatureFlagStatus;
  getFlags: (
    keys: KnownFeatureType[],
    defaults?: FeatureFlagStatus[],
  ) => FeatureFlagStatus[];
  getAllFlags: () => Partial<Record<KnownFeatureType, FeatureFlagStatus>>;
  isEnabled: (key: KnownFeatureType | string) => boolean;
  getFlagState: (key: KnownFeatureType | string) => {
    value: FeatureFlagStatus | undefined;
    isLoading: boolean;
    error: null;
  };
  readonly isLoaded: boolean;
  readonly error?: Error | null;
  readonly isFetching: boolean;
  readonly isDefault?: true;
};

export const defaultFlags = AllFeatureFlagsDefault as unknown as Record<
  KnownFeatureType,
  FeatureFlagStatus
>;

export const FeatureFlagsContext = createContext<FeatureFlagsApi | undefined>(
  undefined,
);

export function useFeatureFlagsContext(): FeatureFlagsApi {
  const ctx = useContext(FeatureFlagsContext);
  if (!ctx) {
    return {
      getFlag: (key, defaultValue) => defaultFlags[key] ?? defaultValue,
      getFlags: (keys, defaults) =>
        keys.map((k, i) => defaultFlags[k] ?? defaults?.[i]),
      getAllFlags: () => ({ ...defaultFlags }),
      isEnabled: (key) =>
        Boolean(defaultFlags[key as KnownFeatureType] ?? false),
      getFlagState: (key) => ({
        value: defaultFlags[key as KnownFeatureType],
        isLoading: false,
        error: null,
      }),
      isLoaded: false,
      isFetching: true,
      isDefault: true,
    } as FeatureFlagsApi;
  }
  return ctx;
}

export default FeatureFlagsContext;

import type { IUrlBuilder, MappedPageOverloads } from './_types';
