   * Instances should only be created through the static Instance property, which ensures
   * a single global instance throughout the application.
   *
   * @private
   */
  private constructor() {
    // Private constructor to enforce singleton pattern
  }

  #toStorageKey(symbol: string | symbol): SingletonStorageKey {
    return typeof symbol === 'symbol' ? symbol : Symbol.for(symbol);
  }

  #selectStorage(
    config: SingletonConfig | undefined,
  ): SingletonStorageStrategy {
    return config?.weakRef ? this.#weakStorage : this.#strongStorage;
  }

  #ensureWeakValue(value: unknown): asserts value is object {
    if (typeof value !== 'object' || value === null) {
      throw new TypeError(
        'Weak reference singletons require a non-null object value.',
      );
    }
  }

  #lookupExisting<T>(key: SingletonStorageKey): T | undefined {
    const preferred = this.#storageByKey.get(key);
    if (preferred) {
      const result = preferred.get(key);
      if (result !== undefined) {
        return result as T;
      }
      this.#storageByKey.delete(key);
    }

    const strong = this.#strongStorage.get(key);
    if (strong !== undefined) {
      this.#storageByKey.set(key, this.#strongStorage);
      return strong as T;
    }

    const weak = this.#weakStorage.get(key);
    if (weak !== undefined) {
      this.#storageByKey.set(key, this.#weakStorage);
      return weak as T;
    }

    return undefined;
  }

  #store(
    key: SingletonStorageKey,
    storage: SingletonStorageStrategy,
    value: unknown,
  ): void {
    if (storage === this.#weakStorage) {
      this.#ensureWeakValue(value);
      this.#strongStorage.delete(key);
    } else {
      this.#weakStorage.delete(key);
    }
    storage.set(key, value);
    this.#storageByKey.set(key, storage);
  }

  /**
   * Retrieves an existing singleton instance by symbol key.
   *
   * This method attempts to retrieve a singleton instance that was previously
   * created and stored using the provided symbol key. It searches both WeakMap
   * and global storage, returning undefined if no instance exists.
   *
   * Unlike getOrCreate, this method never creates new instances and only returns
   * existing ones.
   *
   * @template T - The expected type of the singleton value (defaults to unknown)
   * @template S - The symbol or string type used as the storage key
   * @param symbol - The symbol or string key used to identify the singleton
   * @returns The existing singleton instance, or undefined if not found
   *
   * @example
   * ```typescript
   * // Try to get an existing database connection
   * const db = provider.get<DatabaseConnection>('database');
   *
   * if (db) {
   *   // Use existing connection
   *   await db.query('SELECT * FROM users');
   * } else {
   *   // Create new connection
   *   const newDb = provider.getOrCreate('database', createConnection);
   * }
   * ```
   */
  get<T = unknown, S extends string | symbol = string>(
    symbol: S,
  ): T | undefined {
    const key = this.#toStorageKey(symbol);
    return this.#lookupExisting<T>(key);
  }
  /**
   * Retrieves an existing singleton or creates a new one using the provided factory.
   *
   * This method implements the core singleton pattern: it first checks if a singleton
   * instance already exists for the given symbol. If found, it returns the existing
   * instance. If not found, it creates a new instance using the factory function and
   * stores it according to the configuration.
   *
   * The method supports configurable storage strategies, allowing choice between
   * strong references (global scope) and weak references (WeakMap) for better
   * memory management.
   *
   * @template T - The type of singleton value being created
   * @template S - The symbol or string type used as the storage key
   * @param symbol - The symbol or string key for the singleton
   * @param factory - Factory function that creates the singleton instance
   * @param config - Optional configuration for storage strategy (defaults to strong references)
   * @returns The existing or newly created singleton instance
   *
   * @throws {TypeError} When the factory function returns null or undefined
   *
   * @example
   * ```typescript
   * // Create a database connection singleton
   * const db = provider.getOrCreate('database', () => {
   *   return new DatabaseConnection(process.env.DB_URL);
   * });
   *
   * // Create a cache with weak references for memory efficiency
   * const cache = provider.getOrCreate('cache', () => new Map(), {
   *   weakRef: true
   * });
   *
   * // Multiple calls return the same instance
   * const db1 = provider.getOrCreate('database', createDb);
   * const db2 = provider.getOrCreate('database', createDb);
   * console.log(db1 === db2); // true
   * ```
   */
  getOrCreate<T, S extends string | symbol = string>(
    symbol: S,
    factory: () => IsNotNull<T>,
    config: SingletonConfig = {},
  ): T {
    const key = this.#toStorageKey(symbol);
    const existing = this.#lookupExisting<T>(key);
    if (existing !== undefined) {
      return existing;
    }

    const value = factory();
    if (value === undefined || value === null) {
      throw new TypeError(
        'Factory for global singleton cannot return null or undefined.',
      );
    }

    const storage = this.#selectStorage(config);
    this.#store(key, storage, value);
    return value;
  }
  /**
   * Checks if a singleton instance exists for the given symbol.
   *
   * This method checks whether a singleton has been created and is being tracked
   * by this provider instance. It checks both WeakMap and global storage to ensure
   * the singleton actually exists.
   *
   * @template S - The symbol or string type used as the storage key
   * @param symbol - The symbol or string key to check
   * @returns True if a singleton exists for the key, false otherwise
   *
   * @example
   * ```typescript
   * // Check if a service has been initialized
   * if (provider.has('user-service')) {
   *   console.log('User service is available');
   * } else {
   *   console.log('User service needs to be initialized');
   * }
   *
   * // Safe retrieval pattern
   * const service = provider.has('api-client')
   *   ? provider.get('api-client')
   *   : provider.getOrCreate('api-client', createApiClient);
   * ```
   */
  has<S extends string | symbol = string>(symbol: S): boolean {
    const key = this.#toStorageKey(symbol);
    const preferred = this.#storageByKey.get(key);
    if (preferred?.has(key)) {
      return true;
    }
    this.#storageByKey.delete(key);

    if (this.#strongStorage.has(key)) {
      this.#storageByKey.set(key, this.#strongStorage);
      return true;
    }

    if (this.#weakStorage.has(key)) {
      this.#storageByKey.set(key, this.#weakStorage);
      return true;
    }

    return false;
  }
  /**
   * Manually sets a singleton instance for the given symbol.
   *
   * This method allows direct assignment of singleton instances without using a factory.
   * It's useful for setting pre-created instances or overriding existing singletons.
   * The storage strategy (strong vs weak references) can be configured.
   *
   * When weakRef is true, the instance is stored in WeakMap and can be garbage collected
   * if no other references exist. When false, it's stored globally with strong references.
   *
   * @template T - The type of singleton value being stored
   * @template S - The symbol or string type used as the storage key
   * @param symbol - The symbol or string key for the singleton
   * @param value - The singleton instance to store (must not be null or undefined)
   * @param config - Optional configuration for storage strategy (defaults to strong references)
   *
   * @throws {TypeError} When the value parameter is null or undefined
   *
   * @example
   * ```typescript
   * // Set a pre-configured logger instance
   * const logger = new Logger({ level: 'info' });
   * provider.set('logger', logger);
   *
   * // Set a cache with weak references for memory efficiency
   * const cache = new Map();
   * provider.set('cache', cache, { weakRef: true });
   *
   * // Override an existing singleton
   * provider.set('database', newDatabaseConnection);
   * ```
   */
  set<T, S extends string | symbol = string>(
    symbol: S,
    value: IsNotNull<T>,
    config: SingletonConfig = {},
  ): void {
    if (value === null || value === undefined) {
      throw new TypeError('Cannot set singleton value to null or undefined.');
    }

    const key = this.#toStorageKey(symbol);
    const storage = this.#selectStorage(config);
    this.#store(key, storage, value);
  }
  /**
   * Clears all singleton instances managed by this provider.
   *
   * This method removes all singletons that were created through this provider instance,
   * cleaning up both WeakMap and global storage. It's useful for testing, application
   * shutdown, or when a complete reset is needed.
   *
   * The method iterates through all tracked keys and removes the corresponding instances
   * from both storage locations (WeakMap and global scope) to ensure complete cleanup.
   *
   * @example
   * ```typescript
   * // Clear all singletons during testing
   * afterEach(() => {
   *   provider.clear();
   * });
   *
   * // Reset application state
   * provider.clear();
   * initializeApplication();
   *
   * // Graceful shutdown
   * process.on('SIGTERM', () => {
   *   provider.clear();
   *   process.exit(0);
   * });
   * ```
   */
  clear(): void {
    this.#strongStorage.clear();
    this.#weakStorage.clear();
    this.#storageByKey.clear();
  }
  /**
   * Deletes a specific singleton instance by symbol key.
   *
   * This method removes a single singleton instance from both WeakMap and global storage,
   * allowing it to be garbage collected if no other references exist. It's useful for
   * cleaning up specific resources or resetting individual singletons.
   *
   * @template S - The symbol or string type used as the storage key
   * @param symbol - The symbol or string key of the singleton to delete
   *
   * @example
   * ```typescript
   * // Remove a specific service
   * provider.delete('user-service');
   *
   * // Reset a cache
   * provider.delete('app-cache');
   * const newCache = provider.getOrCreate('app-cache', () => new Map());
   *
   * // Clean up temporary singletons
   * provider.delete('temp-session');
   * ```
   */
  delete<S extends string | symbol = string>(symbol: S): void {
    const key = this.#toStorageKey(symbol);
    this.#strongStorage.delete(key);
    this.#weakStorage.delete(key);
    this.#storageByKey.delete(key);
  }
}

/**
 * @fileoverview Strong reference storage implementation for singleton management.
 *
 * This module provides a storage strategy that maintains strong references to singleton
 * instances on the global object. Unlike weak references, strong references prevent
 * garbage collection, ensuring singletons persist for the lifetime of the application.
 *
 * This is the recommended storage strategy for most singleton use cases where you want
 * guaranteed persistence and don't need automatic cleanup.
 *
 * @module lib/typescript/singleton-provider/storage-strong-ref
 * @see {@link SingletonStorageStrategy} for the interface this implements
 */

import {
  GlobalWithMyGlobal,
  SingletonStorageKey,
  SingletonStorageStrategy,
} from './types';


export class StrongReferenceStorage implements SingletonStorageStrategy {
  /**
   * Internal set tracking all keys that have been set.
   * Used for efficient clear() operations and to prevent memory leaks.
   * @private
   */
  #keys: Set<SingletonStorageKey> = new Set();

  /**
   * Reference to the global object with proper typing.
   * Stores singleton instances directly on globalThis.
   * @private
   */
  #global = globalThis as GlobalWithMyGlobal<unknown, SingletonStorageKey>;

  /**
   * Retrieves a singleton instance from storage.
   *
   * @param {SingletonStorageKey} key - The unique key (typically a Symbol) identifying the singleton
   * @returns {unknown | undefined} The stored instance, or undefined if not found
   *
   * @example
   * const myKey = Symbol('myService');
   * storage.set(myKey, myService);
   * const instance = storage.get(myKey); // Returns myService
   * const missing = storage.get(Symbol('notFound')); // Returns undefined
   */
  get(key: SingletonStorageKey): unknown | undefined {
    return this.#global[key];
  }

  /**
   * Stores a singleton instance with strong reference semantics.
   *
   * The instance is stored directly on the global object and will not be garbage
   * collected until explicitly deleted. The key is tracked internally for cleanup operations.
   *
   * @param {SingletonStorageKey} key - The unique key (typically a Symbol) to store the instance under
   * @param {unknown} value - The singleton instance to store
   * @returns {void}
   *
   * @example
   * const myKey = Symbol('myService');
   * const myService = new MyService();
   * storage.set(myKey, myService); // Stores with strong reference
   *
   * @example
   * // Updating an existing singleton
   * storage.set(myKey, oldInstance);
   * storage.set(myKey, newInstance); // Replaces the old instance
   */
  set(key: SingletonStorageKey, value: unknown): void {
    this.#global[key] = value;
    this.#keys.add(key);
  }

  /**
   * Checks if a singleton instance exists in storage.
   *
   * @param {SingletonStorageKey} key - The key to check
   * @returns {boolean} True if an instance exists for the key, false otherwise
   *
   * @example
   * const myKey = Symbol('myService');
   * storage.has(myKey); // false
   * storage.set(myKey, myService);
   * storage.has(myKey); // true
   * storage.delete(myKey);
   * storage.has(myKey); // false
   *
   * @note Returns false for keys that were set to undefined
   */
  has(key: SingletonStorageKey): boolean {
    return this.#global[key] !== undefined;
  }

  /**
   * Removes a singleton instance from storage.
   *
   * Deletes the instance from the global object and removes the key from internal tracking.
   * After deletion, the instance may be garbage collected if no other references exist.
   *
   * @param {SingletonStorageKey} key - The key of the singleton to remove
   * @returns {void}
   *
   * @example
   * const myKey = Symbol('myService');
   * storage.set(myKey, myService);
   * storage.delete(myKey); // Removes the singleton
   * storage.get(myKey); // Returns undefined
   *
   * @example
   * // Safe to call on non-existent keys
   * storage.delete(Symbol('nonExistent')); // No error, no-op
   */
  delete(key: SingletonStorageKey): void {
    delete this.#global[key];
    this.#keys.delete(key);
  }

  /**
   * Removes all tracked singleton instances from storage.
   *
   * Iterates through all keys that have been set and removes them from the global object.
   * This is useful for cleanup in testing environments or when resetting application state.
   *
   * @returns {void}
   *
   * @example
   * // Cleanup all singletons
   * storage.set(Symbol('service1'), service1);
   * storage.set(Symbol('service2'), service2);
   * storage.set(Symbol('service3'), service3);
   * storage.clear(); // Removes all three services
   *
   * @example
   * // Common pattern in test cleanup
   * afterEach(() => {
   *   storage.clear(); // Reset singleton state between tests
   * });
   *
   * @note Only removes keys that were tracked by this storage instance.
   * Does not affect other global properties.
   */
  clear(): void {
    for (const key of this.#keys) {
      delete this.#global[key];
    }
    this.#keys.clear();
  }
}

/**
 * Configuration options for singleton pattern implementations.
 *
 * This type defines optional configuration parameters for singleton creation and
 * management, particularly focusing on memory management strategies. The configuration
 * allows tuning the singleton behavior for different use cases and performance requirements.
 *
 * @typedef {Object} SingletonConfig
 *
 * @property {boolean} [weakRef] - Optional flag indicating whether to use WeakRef
 *   for singleton storage. When enabled, singletons can be garbage collected if
 *   no other references exist, preventing memory leaks in long-running applications.
 *   When disabled, singletons are stored with strong references and persist until
 *   explicitly cleared. Defaults to false for backward compatibility.
 *
 * @example
 * ```typescript
 * // Standard singleton with strong references
 * const standardConfig: SingletonConfig = {
 *   weakRef: false
 * };
 *
 * // Memory-efficient singleton with weak references
 * const memoryEfficientConfig: SingletonConfig = {
 *   weakRef: true
 * };
 *
 * // Default configuration (equivalent to not specifying config)
 * const defaultConfig: SingletonConfig = {};
 * ```
 *
 * @see {@link globalSingleton} in `/lib/typescript/_generics.ts` for singleton implementation
 * @since 1.0.0
 */
export type SingletonConfig = {
  weakRef?: boolean;
};

/**
 * Key type used for singleton storage strategies.
 */
export type SingletonStorageKey = symbol;

/**
 * Interface defining the storage strategy for singleton instances.
 * This interface abstracts the underlying storage mechanism, allowing
 * for different implementations such as in-memory maps, weak maps, or
 * custom storage solutions.
 * @internal
 * @since 1.0.0
 * @version 1.0.0
 * @example
 * ```typescript
 * // Example implementation using a simple Map
 * class MapStorageStrategy implements SingletonStorageStrategy {
 *   private storage = new Map<SingletonStorageKey, unknown>();
 *   get(key: SingletonStorageKey): unknown | undefined {
 *     return this.storage.get(key);
 *   }
 *   set(key: SingletonStorageKey, value: unknown): void {
 *     this.storage.set(key, value);
 *   }
 *   has(key: SingletonStorageKey): boolean {
 *     return this.storage.has(key);
 *   }
 *   delete(key: SingletonStorageKey): void {
 *     this.storage.delete(key);
 *   }
 *   clear(): void {
 *     this.storage.clear();
 *   }
 * }
 * ```
 *
 */
export interface SingletonStorageStrategy {
  get(key: SingletonStorageKey): unknown | undefined;
  set(key: SingletonStorageKey, value: unknown): void;
  has(key: SingletonStorageKey): boolean;
  delete(key: SingletonStorageKey): void;
  clear(): void;
}
/**
 * Extended global object type with singleton storage capabilities.
 *
 * This utility type extends the globalThis object to include optional properties
 * for storing singleton instances keyed by symbols. It provides type-safe access
 * to globally stored singletons while maintaining compatibility with the standard
 * global object interface.
 *
 * @internal
 * @template T - The type of singleton value stored globally
 * @template S - The symbol type used as the storage key
 */
export type GlobalWithMyGlobal<T, S extends symbol> = typeof globalThis & {
  [K in S]?: T;
};

/**
 * @fileoverview Converts Zod schemas to human-readable JSON structure strings.
 *
 * This module provides utilities for transforming Zod validation schemas into
 * readable string representations that show the expected structure of data.
 * Useful for documentation, debugging, and displaying schema information to users.
 *
 * @module lib/typescript/zod-to-json-structure
 * @example
 * ```typescript
 * import { z } from 'zod';
 * import { zodToStructure } from '@/lib/typescript/zod-to-json-structure';
 *
 * const userSchema = z.object({
 *   name: z.string(),
 *   age: z.number().optional(),
 *   email: z.string()
 * });
 *
 * console.log(zodToStructure(userSchema));
 * // Output: {
 * //   name: <string>,
 * //   age: /* [optional] *\/ <number>,
 * //   email: <string>,
 * // }
 * ```
 */

import {
  ZodTypeAny,
  ZodObject,
  ZodArray,
  ZodOptional,
  ZodString,
  ZodNumber,
  ZodBoolean,
  ZodDefault,
  ZodNullable,
  ZodDate,
} from 'zod';

/**
 * Function signature for converting a SchemaField to a string representation.
 * Supports both parameterless calls (default indent) and calls with custom indentation.
 *
 * @callback SchemaFieldToString
 * @returns {string} The string representation with default indentation
 *
 * @callback SchemaFieldToString
 * @param {number} indent - The indentation level (number of spaces = indent * 2)
 * @returns {string} The string representation with specified indentation
 */
interface SchemaFieldToString {
  (): string;
  (indent: number): string;
}

/**
 * Base properties shared by all schema field types.
 * Represents common metadata extracted from Zod schemas.
 *
 * @typedef {Object} BasedSchemaField
 * @property {string} type - The primitive type name (e.g., 'string', 'number', 'object', 'array')
 * @property {boolean} [optional] - Whether the field is optional (Zod optional() or has default())
 * @property {boolean} [nullable] - Whether the field accepts null values (Zod nullable())
 * @property {string} [description] - Description extracted from Zod schema's .describe() method
 * @property {SchemaFieldToString} toString - Function to convert field to string representation
 */
type BasedSchemaField = {
  type: string;
  optional?: boolean;
  nullable?: boolean;
  description?: string;
  toString: SchemaFieldToString;
};

/**
 * Discriminated union representing different types of schema fields.
 * Each variant corresponds to a specific Zod schema type.
 *
 * @typedef {Object} SchemaField
 *
 * Array variant:
 * @property {'array'} type - Indicates this is an array type
 * @property {SchemaField} items - Schema for array elements
 *
 * Object variant:
 * @property {'object'} type - Indicates this is an object type
 * @property {Record<string, SchemaField>} properties - Map of property names to their schemas
 *
 * Primitive variants (number, boolean, date):
 * @property {'number'|'boolean'|'date'} type - The primitive type
 * @property {false|never} nullable - These types cannot be nullable in the schema
 *
 * String variant:
 * @property {'string'} type - Indicates this is a string type
 *
 * Any variant (fallback):
 * @property {'any'} type - Indicates an unrecognized or generic type
 * @property {Record<string, SchemaField>} [properties] - Optional properties if object-like
 * @property {SchemaField} [items] - Optional items if array-like
 */
type SchemaField =
  | (BasedSchemaField & {
      type: 'array';
      items: Exclude<SchemaField, 'optional'>;
    })
  | (BasedSchemaField & {
      type: 'object';
      properties: Record<string, SchemaField>;
    })
  | (BasedSchemaField & {
      type: 'number' | 'boolean' | 'date';
      nullable: false | never;
    })
  | (BasedSchemaField & {
      type: 'string';
    })
  | (BasedSchemaField & {
      properties?: Record<string, SchemaField>;
      items?: Exclude<SchemaField, 'optional'>;
      type: 'any';
    });

/**
 * Adds a trailing comma to the last line of a multi-line string.
 * Intelligently places the comma before any trailing comments (optional/nullable flags or descriptions).
 *
 * Used when building object/array representations to ensure proper JSON-like formatting.
 *
 * @param {string} input - The multi-line string to process
 * @returns {string} The input string with a comma added to the last line before comments
 *
 * @example
 * withComma("name: <string> \/\* user's name *\/")
 * // Returns: "name: <string>, \/\* user's name *\/"
 *
 * @example
 * withComma("age: \/\* [optional] *\/ <number>")
 * // Returns: "age: \/\* [optional] *\/ <number>,"
 */
const withComma = (input: string): string => {
  const current = input.split('\n');
  if (!current.length) {
    return input;
  }
  const lastIndex = current.length - 1;
  const lastItem = current[lastIndex];
  let flagIndex = lastItem.indexOf('/* [optional');
  if (flagIndex === -1) {
    flagIndex = lastItem.indexOf('/* [nullable');
  }
  let descriptionCommentIndex = lastItem.lastIndexOf('/*');
  if (descriptionCommentIndex === flagIndex) {
    descriptionCommentIndex = -1;
  }
  current[lastIndex] =
    descriptionCommentIndex > -1
      ? `${lastItem.slice(0, descriptionCommentIndex - 1)},${lastItem.slice(descriptionCommentIndex - 1)}`
      : lastItem + ',';
  return current.join('\n');
};
/**
 * Converts a Zod schema into a SchemaField representation.
 * Recursively processes nested schemas (objects, arrays) and handles wrapper types
 * like optional, nullable, and default.
 *
 * @param {ZodTypeAny} schema - The Zod schema to convert
 * @returns {SchemaField} A structured representation of the schema with type info and metadata
 *
 * @example
 * const schema = z.object({ name: z.string(), age: z.number().optional() });
 * const field = zodToSchemaField(schema);
 * // field.type === 'object'
 * // field.properties.name.type === 'string'
 * // field.properties.age.optional === true
 *
 * @internal This is an internal helper used by zodToStructure
 */
const zodToSchemaField = (schema: ZodTypeAny): SchemaField => {
  let ret: Partial<SchemaField> = { description: schema.description };

  /**
   * Unwraps wrapper schemas (optional, nullable, default, effects) to get the inner type.
   * Recursively processes the unwrapped schema.
   *
   * @returns {SchemaField} The unwrapped schema field
   * @throws {Error} If unable to unwrap the schema
   */
  const unwrapType = () => {
    // Handle effects/unwrapping
    if ('schema' in schema._def) {
      return zodToSchemaField(schema._def.schema);
    }
    if (
      'unwrap' in schema &&
      schema.unwrap &&
      typeof schema.unwrap === 'function'
    ) {
      return zodToSchemaField(schema.unwrap());
    }
    return zodToSchemaField(schema._def.innerType);
  };
  // Type detection: Check Zod schema type and extract relevant information
  if (schema instanceof ZodOptional || schema instanceof ZodDefault) {
    // Optional fields: unwrap and mark as optional
    ret = unwrapType();
    ret.optional = true;
  } else if (schema instanceof ZodNullable) {
    // Nullable fields: unwrap and mark as nullable
    ret = unwrapType();
    ret.nullable = true;
  } else if (schema instanceof ZodString) {
    // String primitive
    ret.type = 'string';
  } else if (schema instanceof ZodNumber) {
    // Number primitive
    ret.type = 'number';
  } else if (schema instanceof ZodBoolean) {
    // Boolean primitive
    ret.type = 'boolean';
  } else if (schema instanceof ZodDate) {
    // Date type
    ret.type = 'date';
  } else if (schema instanceof ZodArray) {
    // Array type: recursively process element schema
    ret.type = 'array';
    if (ret.type === 'array') {
      ret.items = zodToSchemaField(schema.element);
    }
  } else if (schema instanceof ZodObject) {
    // Object type: recursively process all properties
    ret.type = 'object';
    if (ret.type === 'object') {
      const shape = schema.shape as Record<string, ZodTypeAny>;
      ret.properties = {};
      for (const key in shape) {
        ret.properties[key] = zodToSchemaField(shape[key]);
      }
    }
  } else {
    // Fallback for unrecognized types
    ret.type = 'any';
  }
  /**
   * Converts this SchemaField to a formatted string representation.
   * Handles indentation, type annotations, optional/nullable flags, and descriptions.
   *
   * @param {number} [indent=0] - Current indentation level (each level = 2 spaces)
   * @returns {string} Formatted string representation of the schema
   */
  ret.toString = (indent: number = 0) => {
    let builder = '';
    switch (ret.type) {
      case 'array':
        // Format: [ <element-type>, ... ] as Array<type>
        builder = `[ ${ret.items ? withComma(ret.items.toString(indent + 1)) : withComma('unknown')} ... ] as Array<${ret.items?.type ?? 'any'}>`;
        break;
      case 'object':
        // Format: { key: <type>, ... } with proper indentation
        const indentStr = ' '.repeat((indent + 1) * 2);
        builder =
          '{' +
          Object.entries(ret.properties ?? {})
            .map(([key, value]) =>
              withComma(`\n${indentStr}${key}: ${value.toString(indent + 1)}`),
            )
            .join('') +
          `\n${' '.repeat(indent * 2)}}`;
        break;
      case undefined:
        // Fallback for undefined type
        builder = '<any>';
        break;
      default:
        // Primitive types: <string>, <number>, etc.
        builder = `<${ret.type}>`;
        break;
    }
    // Add optional/nullable flags as comment prefix
    if (ret.optional) {
      builder = `/* [optional${ret.nullable ? ', nullable' : ''}] */ ${builder}`;
    } else if (ret.nullable) {
      builder = `/* [nullable] */ ${builder}`;
    }
    // Add description as comment suffix
    if (ret.description) {
      builder = `${builder} /* ${ret.description} */`;
    }
    return builder;
  };
  return ret as SchemaField;
};


export const zodToStructure = (schema: ZodTypeAny): string => {
  const asField = zodToSchemaField(schema);
  return asField.toString();
};

